import React, { useEffect, useState } from "react";
import {
  InputLabel,
  MenuItem,
  TextField,
  FormControl,
  Select,
} from "@mui/material";
import loanDataService from "../../Company/Service";
import { useParams } from "react-router-dom";
import CompanyModal from "./CompanyModal";
function UpdateCompany() {
  const [isCompanyModalOpen, setIsCompanyModalOpen] = useState(false);
  const [isCompanyTradeModalOpen, setIsCompanyTradeModalOpen] = useState(false);
  const [companyTypes, setCompanyTypes] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);  
  
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }
  // company modal start
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }

  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
//   const handleInputChange = (event) => {
//     const { name, value } = event.target;
//     setCompany({ ...company, [name]: value });
//   };
  function handleGeneralCloseModal() {
    isCompanyTradeModalOpen(false);
  }
  const { Id } = useParams();
  console.log(Id)
  const [company, setCompany] = useState({
    Id: Id,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  });
 
  const upadteCompanyById = () => {
    loanDataService
      .getCompanyById(Id)
      .then((res) => {
        setCompany({
          ...company,
          Name: res.data.Name,
          NoOfBranch: res.data.NoOfBranch,
          EstablishmentYear: res.data.EstablishmentYear,
          TINNumber: res.data.TINNumber,
          TINCertificate: res.data.TINCertificate,
          CompanyTypeID: res.data.CompanyTypeID,
          EntityTypeID: res.data.EntityTypeID,
          VATNumber: res.data.VATNumber,
          VATCertificate: res.data.VATCertificate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    upadteCompanyById();
  }, []);
  const fetchCompanies = () => {
    loanDataService
      .getCompanyType()
      .then((response) => {
        console.log(response.data.Result);
        setCompanyTypes(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchCompanies();
  }, []);
  const fetchCompanyEntity = () => {
    loanDataService
      .getEntitype()
      .then((response) => {
        console.log(response.data.Result);
        setEntityTypes(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchCompanyEntity();
  }, []);
  return (
    <div >
        
        </div>      
  );
}

export default UpdateCompany;
