                      sx={{ m: 1, minWidth: 120 }}
                    >
                      <InputLabel id="regionlbl" color="secondary">
                        {" "}
                        Region{" "}
                      </InputLabel>
                      <Select
                        color="secondary"
                        onChange={(event) =>
                          (address.RegionID = event.target.value)
                        }
                        labelId="regionlbl"
                        id="regionSelect"
                        label="Region"
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        {region.map((newRegion) => (
                          <MenuItem
                            key={newRegion.Id}
                            RegionID={newRegion.Id}
                            value={newRegion.Id}
                          >
                            {newRegion.Name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>

                    <TextField
                      id="ZoneSubCity"
                      label="Zone Sub City"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.ZoneSubCity}
                      onChange={handleInputChangeAdress}
                      name="ZoneSubCity"
                    />
                    <TextField
                      id="Woreda"
                      label="Woreda"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.Woreda}
                      onChange={handleInputChangeAdress}
                      name="Woreda"
                    />
                    <TextField
                      id="City"
                      label="City"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.City}
                      onChange={handleInputChangeAdress}
                      name="City"
                    />
                    <TextField
                      id="Kebele"
                      label="Kebele"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.Kebele}
                      onChange={handleInputChangeAdress}
                      name="Kebele"
                    />
                    <TextField
                      id="HouseNo"
                      label="HouseNo"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.HouseNo}
                      onChange={handleInputChangeAdress}
                      name="HouseNo"
                    />
                    <TextField
                      id="BuildingNo"
                      label="BuildingNo"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.BuildingNo}
                      onChange={handleInputChangeAdress}
                      name="BuildingNo"
                    />
                    <TextField
                      id="StreetAddress"
                      label="StreetAddress"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={address.StreetAddress}
                      onChange={handleInputChangeAdress}
                      name="StreetAddress"
                    />
                  </div>
                )}
              </div>

              <div className="flex justify-between">
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleBack}
                >
                  Back
                </button>
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleSkip}
                >
                  Skip
                </button>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => {
                    handleNext();
                    saveAddress();
                  }}
                >
                  Save and Next
                </button>
              </div>
            </div>
          )}
          {activeStep === 2 && (
            <div>
              <div className="submit-form">
                {submitted ? (
                  <div>
                    <h4>You submitted successfully!</h4>
                    <button
                      className="btn btn-success"
                      onClick={newContactInfo}
                    >
                      Add
                    </button>
                  </div>
                ) : (
                  <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    <TextField
                      id="DesignatedPersonFName"
                      label="Designated Person First Name"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.DesignatedPersonFName}
                      onChange={handleInputChangeContactInfo}
                      name="DesignatedPersonFName"
                    />
                    <TextField
                      id="DesignatedPersonMName"
                      label="Designated Person Middle Name"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.DesignatedPersonMName}
                      onChange={handleInputChangeContactInfo}
                      name="DesignatedPersonMName"
                    />
                    <TextField
                      id="DesignatedPersonLName"
                      label="Designated Person Last Name"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.DesignatedPersonLName}
                      onChange={handleInputChangeContactInfo}
                      name="DesignatedPersonLName"
                    />
                    <TextField
                      id="Position"
                      label="Position"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.Position}
                      onChange={handleInputChangeContactInfo}
                      name="Position"
                    />
                    <TextField
                      id="MobileNo"
                      label="Mobile Number"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.MobileNo}
                      onChange={handleInputChangeContactInfo}
                      name="MobileNo"
                    />
                    <TextField
                      id="Telephone"
                      label="Telephone"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.Telephone}
                      onChange={handleInputChangeContactInfo}
                      name="Telephone"
                    />
                    <TextField
                      id="Email"
                      label="Email"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={contactInfo.Email}
                      onChange={handleInputChangeContactInfo}
                      name="Email"
                    />
                  </div>
                )}
              </div>
              <div className="flex justify-between">
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleBack}
                >
                  Back
                </button>
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleSkip}
                >
                  Skip
                </button>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => {
                    handleNext();
                    saveContactInfo();
                  }}
                >
                  Save and next
                </button>
              </div>
            </div>
          )}
          {activeStep === 3 && (
            <div>
              <div className="submit-form">
                {submitted ? (
                  <div>
                    <h4>You submitted successfully!</h4>
                    <button
                      className="btn btn-success"
                      onClick={newAccountInfo}
                    >
                      Add
                    </button>
                  </div>
                ) : (
                  <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    <TextField
                      id="AccountNo"
                      label="Account Number"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={accountInfo.AccountNo}
                      onChange={handleInputChangeAccountInfo}
                      name="AccountNo"
                    />
                    <TextField
                      id="CustomerId"
                      label="Customer Id"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={accountInfo.CustomerId}
                      onChange={handleInputChangeAccountInfo}
                      name="CustomerId"
                    />
                    <TextField
                      id="Branch"
                      label="Branch"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={accountInfo.Branch}
                      onChange={handleInputChangeAccountInfo}
                      name="Branch"
                    />
                    <TextField
                      id="District"
                      label="District"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={accountInfo.District}
                      onChange={handleInputChangeAccountInfo}
                      name="District"
                    />
                    <TextField
                      id="Other"
                      label="Other"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={accountInfo.Other}
                      onChange={handleInputChangeAccountInfo}
                      name="Other"
                    />
                  </div>
                )}
              </div>
              <div className="flex justify-between">
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleBack}
                >
                  Back
                </button>
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleSkip}
                >
                  Skip
                </button>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => {
                    handleNext();
                    saveAccountInfo();
                  }}
                >
                  save and next
                </button>
              </div>
            </div>
          )}
          {activeStep === 4 && (
            <div>
              <div className="submit-form">
                {submitted ? (
                  <div>
                    <h4>You submitted successfully!</h4>
                    <button className="btn btn-success" onClick={newTradeInfo}>
                      Add
                    </button>
                  </div>
                ) : (
                  <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    <TextField
                      id="TradeRegistrationNo"
                      label="Trade Registration Nnumber"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.TradeRegistrationNo}
                      onChange={handleInputChangeTradeInfo}
                      name="TradeRegistrationNo"
                    />
                    <TextField
                      id="UploadTradeRegCerti"
                      label="Upload Trade Reg Certificate"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.UploadTradeRegCerti}
                      onChange={handleInputChangeTradeInfo}
                      name="UploadTradeRegCerti"
                    />
                    <TextField
                      id="TradeLicenseNo"
                      label="Trade License Number"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.TradeLicenseNo}
                      onChange={handleInputChangeTradeInfo}
                      name="TradeLicenseNo"
                    />
                    <TextField
                      id="IssueDate"
                      label="IssueDate"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.IssueDate}
                      onChange={handleInputChangeTradeInfo}
                      name="IssueDate"
                    />
                    <TextField
                      id="RenewedDate"
                      label="Renewed Date"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.RenewedDate}
                      onChange={handleInputChangeTradeInfo}
                      name="RenewedDate"
                    />
                    <TextField
                      id="ValidityDate"
                      label="Validity Date"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.ValidityDate}
                      onChange={handleInputChangeTradeInfo}
                      name="ValidityDate"
                    />
                    <FormControl variant="standard" sx={{ minWidth: 120 }}>
                      <InputLabel id="countrylbl" color="secondary">
                        {" "}
                        TradeSector{" "}
                      </InputLabel>
                      <Select
                        color="secondary"
                        onChange={(event) => {
                          handleTradeIndustryType(event.target.value);
                          setHoldTradeSectorId(event.target.value);
                        }}
                        labelId="countrylbl"
                        id="countrySelect"
                        label="Country"
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        {tradeSector.map((newTradeSector) => (
                          <MenuItem
                            key={newTradeSector.Id}
                            CountryID={newTradeSector.Id}
                            value={newTradeSector.Id}
                          >
                            {newTradeSector.Name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    
                    <p>
                      {tradeIndustryType.map((trade) => (
                        <>
                         <i>{trade.Name}</i>
                        <br />
                        </>                       
                      ))}
                    </p>
                    <FormControl variant="standard" sx={{ minWidth: 120 }}>
                      <InputLabel id="countrylbl" color="secondary">
                        {" "}
                        TradeSector{" "}
                      </InputLabel>
                      <Select
                        color="secondary"
                        onChange={(event) => {
                          // handleTradeIndustryType(event.target.value);
                          setHoldTradeSectorId(event.target.value);
                        }}
                        labelId="countrylbl"
                        id="countrySelect"
                        label="Country"
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        {tradeIndustryType.map((newTradeIndustrayType) => (
                          <MenuItem
                            key={newTradeIndustrayType.Id}
                            CountryID={newTradeIndustrayType.Id}
                            value={newTradeIndustrayType.Id}
                          >
                            {newTradeIndustrayType.Name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    {/* tiztaw */}
                    <TextField
                      id="TradeBusinessTypeID"
                      label="TradeBusinessTypeID"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.TradeBusinessTypeID}
                      onChange={handleInputChangeTradeInfo}
                      name="TradeBusinessTypeID"
                    />
                    <TextField
                      id="UploadTradeLicense"
                      label="Upload Trade License"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={tradeInfo.UploadTradeLicense}
                      onChange={handleInputChangeTradeInfo}
                      name="UploadTradeLicense"
                    />
                  </div>
                )}
              </div>
              <div className="flex justify-between">
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleBack}
                >
                  Back
                </button>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={saveTradeInfo}
                >
                  submit
                </button>
              </div>
            </div>
          )}
          {activeStep === 5 && (
            <div>
              <h2 className="text-2xl font-bold mb-4">
                Thank you for submitting the form!
              </h2>
            </div>
          )}
        </div>
        {/* <table className="table table-bordered table-striped" border="1">
          <thead>
            <th> Number </th>
            <th> Name </th>
            <th> No Branch </th>
            <th> Establishment Year </th>
            <th> TIN Number </th>
            <th> TIN Certificate </th>
            <th> VAT Number </th>
            <th> VAT Certificate </th>
          </thead>
          <tbody>
            {companies.map((company, index) => (
              // <tr key={loan.loan_id} rowSpan="3">
              <tr key={index}>
                <td>{company.Name}</td>
                <td>{company.NoOfBranch}</td>
                <td>{company.EstablishmentYear}</td>
                <td>{company.TINNumber}</td>
                <td>{company.TINCertificate}</td>
                <td>{company.VATNumber}</td>
                <td>{company.VATCertificate}</td>
              </tr>
            ))}
          </tbody>
        </table> */}
      </div>
    </div>
  );
};

export default LeftNav;
