import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanIconButton from "../../../Common/LoanIconButton";
import { BsPlus } from "react-icons/bs";
import { IconButton } from "@material-tailwind/react";
import LoanTextField from "../../../Common/LoanTextField";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {
  InputLabel,
  TextField,
  MenuItem,
  FormControl,
  Select,
} from "@mui/material";
import CommonServices from "../../Common/CommonServices";
const AddWidowedStatus = ({ SoleId }) => {
  //upload
  const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
  const [selectedPathUnmarriege, setSelectedPathUnmarriege] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  //upload 1
  const uploadMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  //upload 2
  const uploadUnMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathUnmarriege(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //endupload2
  const [dateOfWidowed, setDateOfWidowed] = useState(new Date());
  const [unMarriedCertficateIssueDate, setUnMarriedCertficateIssueDate] =
    useState(new Date());
  const [unMarriedCertficateExpiredDate, setUnMarriedCertficateExpiredDate] =
    useState(new Date());
  const [submitted, setSubmitted] = useState(false);
  const [isCompanyMaritalStatusModalOpen, setIsCompanyMaritalStatusModalOpen] =
    useState(false);
  function handleGeneralCloseModal() {
    isCompanyMaritalStatusModalOpen(false);
  }
  function handleCompanyMaritalStatusCloseModal() {
    setIsCompanyMaritalStatusModalOpen(false);
  }
  const [toggleWidowedInfo, setToggleWidowedInfo] = useState(false);
  const initialMartailStatusWidowedState = {
    Id: null,
    SoleAndPartnerShipID: SoleId,
    DateOfWidowed: "",
    MarraigeCertificate: "",
    MarraigeCertificateIssuer: "",
    UnmarriedCertificate: "",
    UnmarraiedCertificateIssuer: "",
    UnmarriedCertificateIssuDate: "",
    UnmarriedCertificateExpiredDate: "",
    CourtDecision: "",
  };
  const [martailStatusWidowed, setMartailStatusWidowed] = useState(
    initialMartailStatusWidowedState
  );
  const newMartailStatusWidowed = () => {
    setMartailStatusWidowed(initialMartailStatusWidowedState);
    setSubmitted(false);
  };
  const handleInputChangeMartailStatusWidowed = (event) => {
    const { name, value } = event.target;
    setMartailStatusWidowed({ ...martailStatusWidowed, [name]: value });
  };
  const saveWidowed = () => {
    var data = {
      SoleAndPartnerShipID: martailStatusWidowed.SoleAndPartnerShipID,
      DateOfWidowed: martailStatusWidowed.DateOfWidowed,
      MarraigeCertificate: selectedPathIdentity,
      MarraigeCertificateIssuer: martailStatusWidowed.MarraigeCertificateIssuer,
      UnmarriedCertificate: selectedPathUnmarriege,
      UnmarraiedCertificateIssuer:
        martailStatusWidowed.UnmarraiedCertificateIssuer,
      UnmarriedCertificateIssuDate:
        martailStatusWidowed.UnmarriedCertificateIssuDate,
      UnmarriedCertificateExpiredDate:
        martailStatusWidowed.UnmarriedCertificateExpiredDate,
      CourtDecision: martailStatusWidowed.CourtDecision,
    };
    CommonServices.UnmarriedSole(data, SoleId)
      .then((response) => {
        setMartailStatusWidowed({
          Id: response.data.Id,
          SoleAndPartnerShipID: response.data.SoleAndPartnerShipID,
          UnMarriedCertficate: response.data.UnMarriedCertficate,
          UnMarriedCertficateIssueDate:
            response.data.UnMarriedCertficateIssueDate,
          UnMarriedCertficateExpiredDate:
            response.data.UnMarriedCertficateExpiredDate,
          UnMarriedCertficateIssuer: response.data.UnMarriedCertficateIssuer,
        });
        setSubmitted(true);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <button
        onClick={() => setToggleWidowedInfo(!toggleWidowedInfo)}
        className="text-purple-600 rounded-md font-medium "
      >
        Add Widowed
      </button>

      {toggleWidowedInfo && (
        <div className="submit-form">
          {submitted ? (
            <div>
              <h4>You submitted successfully!</h4>
            </div>
          ) : (
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
             
              <p>
                Date Of Widowed:
                <input
                  id="DateOfWidowed"
                  name="DateOfWidowed"
                  label="DateOfWidowed"
                  placeholder="DateOfWidowed"
                  type="date"
                  value={
                    dateOfWidowed.getFullYear().toString() +
                    "-" +
                    (dateOfWidowed.getMonth() + 1).toString().padStart(2, 0) +
                    "-" +
                    dateOfWidowed.getDate().toString().padStart(2, 0)
                  }
                  onChange={(e) => {
                    setDateOfWidowed(new Date(e.target.value));
                    martailStatusWidowed.DateOfWidowed = dateOfWidowed;
                  }}
                />{" "}
              </p>
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <LoanTextField
                id="MarraigeCertificateIssuer"
                label="MarraigeCertificateIssuer"
                value={martailStatusWidowed.MarraigeCertificateIssuer}
                onChange={handleInputChangeMartailStatusWidowed}
                name="MarraigeCertificateIssuer"
              />
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onClick={uploadUnMarriegeCertificate}
                >
                  Upload UnMarriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <LoanTextField
                id="UnmarraiedCertificateIssuer"
                label="UnmarraiedCertificateIssuer"
                value={martailStatusWidowed.UnmarraiedCertificateIssuer}
                onChange={handleInputChangeMartailStatusWidowed}
                name="UnmarraiedCertificateIssuer"
              />
              <p>
                UnMarried Certficate Issue Date:
                <input
                  id="UnMarriedCertficateIssueDate"
                  name="UnMarriedCertficateIssueDate"
                  label="UnMarriedCertficateIssueDate"
                  placeholder="UnMarriedCertficateIssueDate"
                  type="date"
                  value={
                    unMarriedCertficateIssueDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateIssueDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateIssueDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateIssueDate(new Date(e.target.value));
                    martailStatusWidowed.UnmarriedCertificateIssuDate =
                      unMarriedCertficateIssueDate;
                  }}
                />{" "}
              </p>
              <p>
                UnMarried Certficate Expired Date:
                <input
                  id="UnmarriedCertificateExpiredDate"
                  name="UnmarriedCertificateExpiredDate"
                  label="UnmarriedCertificateExpiredDate"
                  placeholder="UnmarriedCertificateExpiredDate"
                  type="date"
                  value={
                    unMarriedCertficateExpiredDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateExpiredDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateExpiredDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateExpiredDate(new Date(e.target.value));
                    martailStatusWidowed.UnmarriedCertificateExpiredDate =
                      unMarriedCertficateExpiredDate;
                  }}
                />{" "}
              </p>
              <LoanTextField
                id="CourtDecision"
                label="CourtDecision"
                value={martailStatusWidowed.CourtDecision}
                onChange={handleInputChangeMartailStatusWidowed}
                name="CourtDecision"
              />
            </div>
          )}
          <div>
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveWidowed();
              }}
            >
              save
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
export default AddWidowedStatus;
