import api from "../../API/Api";
const getAccountDetial = (CompanyId) => {
  return api.get(`/company/${CompanyId}/AccountDetail`, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addAccountDetials = (CompanyId, data) => {
  return api.post(`/company/${CompanyId}/AccountDetail`,data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const AccountDetailSrvice = {
    getAccountDetial,
    addAccountDetials,
   
  };
  export default AccountDetailSrvice;
