                  color="secondary"
                  onChange={(event) =>
                    (editSoleOtherBank.LoanStatusId = event.target.value)
                  }
                  labelId="LoanStatusId"
                  id="LoanStatusId"
                  label="LoanStatusId"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {loanStatus.map((yourLoanStatus) => (
                    <MenuItem
                      key={yourLoanStatus.Id}
                      LoanStatusId={yourLoanStatus.Id}
                      value={yourLoanStatus.Id}
                    >
                      {yourLoanStatus.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <LoanTextField
                id="NoOfDaysInArrears"
                label="NoOfDaysInArrears"
                value={editSoleOtherBank.NoOfDaysInArrears}
                onChange={handleInputChangeEditOtherBank}
                name="NoOfDaysInArrears"
              />
              <LoanTextField
                id="BalanceOverdue"
                label="BalanceOverdue"
                value={editSoleOtherBank.BalanceOverdue}
                onChange={handleInputChangeEditOtherBank}
                name="BalanceOverdue"
              />
              <LoanTextField
                id="NoOfRestructuring"
                label="NoOfRestructuring"
                value={editSoleOtherBank.NoOfRestructuring}
                onChange={handleInputChangeEditOtherBank}
                name="NoOfRestructuring"
              />
              <LoanTextField
                id="RestructuredCreditReferenceNumber"
                label="RestructuredCreditReferenceNumber"
                value={editSoleOtherBank.RestructuredCreditReferenceNumber}
                onChange={handleInputChangeEditOtherBank}
                name="RestructuredCreditReferenceNumber"
              />
              {/* SectorTypeId */}
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  Sector Type
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) => {
                    setSelectedSectorId(event.target.value);
                    industry(event.target.value);
                    editSoleOtherBank.SectorTypeId = event.target.value;
                  }}
                  labelId="SectorTypeId"
                  id="SectorTypeSelected"
                  label="SectorTypeId"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {selectedSector.map((yourSector) => (
                    <MenuItem
                      key={yourSector.Id}
                      SectorTypeId={yourSector.Id}
                      value={yourSector.Id}
                    >
                      {yourSector.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  industry Type
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (editSoleOtherBank.IndustryTypeId = event.target.value)
                  }
                  labelId="IndustryTypeId"
                  id="IndustryTypeSelected"
                  label="IndustryTypeId"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {getIndustry.map((yourIndustry) => (
                    <MenuItem
                      key={yourIndustry.Id}
                      IndustryTypeId={yourIndustry.Id}
                      value={yourIndustry.Id}
                    >
                      {yourIndustry.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              {/* IndustryTypeId */}
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onChange={
                    (editSoleOtherBank.CreditInformationDocument =
                      creditInformationDocument)
                  }
                  onClick={uploadCreditInformationDocument}
                >
                  Tax Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default OtherBank;
