import api from "../API/Api";

const addCompany = (data,Id) => {
  return api.post(`/company/${Id}`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addAdress = (data, holdId) => {
  return api.post(`/company/${holdId}/Address`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addContactInfo = (data, holdId) => {
  return api.post(`/company/${holdId}/contactInformation`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const getCompanyType = () => {
  return api.get("/companyType");
};
const getEntitype = () => {
  return api.get("/entityType");
};
const getCountry = () => {
  return api.get("/country");
};
const getRegion = () => {
  return api.get("/region");
};

const users = () => {
  return api.get("/user");
};
const companies = () => {
  return api.get("/company", {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addAccountInfo = (data, holdId) => {
  return api.post(`/company/${holdId}/accountInfo`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addATradeInfo = (data, holdId) => {
  return api.post(`/company/${holdId}/tradeInformation`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addSoleAndPartnership = (data, holdId) => {
  return api.post(`/company/${holdId}/SoleAndPartnership`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const getTradeSectorId = () => {
  return api.get(
    "/InternationalTradeIndustryBusinessSectorController/tradeSector"
  );
};

const getTradeIndustryType = (sectorId) => {
  return api.get(
    `/InternationalTradeIndustryBusinessSectorController/tradeIndustryType/BySectorID/${sectorId}`,
    {
      headers: {
        Authorization: `bearer ${localStorage.getItem("token")}`,
        "Content-Type": "application/json",
      },
    }
  );
};
const getTradeBusinessTypeId = (tradeIndustryTypeId) => {
  return api.get(
    `/InternationalTradeIndustryBusinessSectorController/tradeIndustryTyp/ByIndustrytypeId/${tradeIndustryTypeId}`,
    {
      headers: {
        Authorization: `bearer ${localStorage.getItem("token")}`,
        "Content-Type": "application/json",
      },
    }
  );
};
const getMaritalStatus = () => {
  return api.get("/MaritalStatusType", {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const deleteCompanyById = (id) => {
  return api.delete(`/company/${id}`, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
// ad7b2788-d8cb-45bd-3429-08db93f54b89 well cont
