  return api.get("/company/ad7b2788-d8cb-45bd-3429-08db93f54b89/Address", {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
//well use
const getCompanyByuserId = () => {
  return api.get("/company/ad7b2788-d8cb-45bd-3429-08db93f54b89/Address", {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });   
};
