  addCompany,
  getCompanyType,
  users,
  getEntitype,
  addAdress,
  getCountry,
  getRegion,
  addContactInfo,
  companies,
  addAccountInfo,
  addATradeInfo,
  getTradeSectorId,
  getTradeIndustryType,
  getTradeBusinessTypeId,
  addSoleAndPartnership,
  getMaritalStatus,
  deleteCompanyById,
  companyAdress,
};
export default loanDataService;
