import { Link } from 'react-router-dom';
import { useState } from 'react';
import classNames from 'classnames';
import SpecificEligibilityLoanData from '../Data/SpecificEligibilityLoanData.json';

const LoanProductLine = () => {

    const [cardBackgroundColors, setCardBackgroundColors] = useState({});

    const handleCardMouseEnter = (index) => {
        const colors = ['bg-red-500', 'bg-pink-500', 'bg-purple-500', 'bg-indigo-500'];
        const randomColor = colors[Math.floor(Math.random() * colors.length)];
        setCardBackgroundColors({ [index]: randomColor });
    };
    return (
        <div className={`mx-w-[1240px] mx-auto grid md:grid-cols-3 gap-8 ease-in-out`}>
            {
                SpecificEligibilityLoanData.product_line.map(productLine => (
                    <div key={productLine.id}>
                        <div className={`${classNames('p-4',
                            'rounded',
                            'cursor-pointer',
                            cardBackgroundColors[productLine.id + 1])}w-full border-2 shadow-xl flex flex-col p-4 my-4 rounded-lg hover:scale-105 duration-300`}
                            onMouseEnter={() => handleCardMouseEnter(productLine.id)}>
                            <header className="text-2xl font-bold text-center py-1 bg-[purple] text-white rounded-lg">
                                {
                                    productLine.name
                                }
                            </header>
                            <p>
                                <hr />
                            </p>
                            <article>
                                <header>
                                    <h2 className='px-4 font-semibold'>Product Group</h2>
                                </header>
                                <ul>
                                    {
                                        productLine.group.map((productGroup, index) => (
                                            <>
                                                <li className='p-2' key={productGroup.id}>
                                                    <Link to={`/product/${productLine.id}/${productGroup.id}`}>
                                                        <h3 className='hover:font-bold hover:text-[purple] hover:underline first:capitalize hover:uppercase'>
                                                            <strong className='px-2'>{index + 1} </strong>{productGroup.name}
                                                        </h3>
                                                    </Link>
                                                </li>
                                            </>
                                        ))}
                                </ul>
                            </article>
                        </div>
                    </div>
                ))
            }
        </div>
    );
}

export default LoanProductLine;
