import AddAccountDetail from "./AddAccountDetail";
import EditAccountDetail from "./EditAccountDetail";
import AccountDetailSrvice from "./AccountDetailSrvice";
import ViewCreditFacilityUtilizition from "./creditFacilityUtilizition/ViewCreditFacilityUtilizition";
import ViewCreditPerformance from "./creditPerformance/ViewCreditPefrormance";
import CreditFacilityUtilizitionService from "./creditFacilityUtilizition/CreditFacilityUtilizitionService";
import CreditPerformanceService from "./creditPerformance/CreditPerformanceService";
import DepositPerformanceService from "./depositPerformance/DepositPerformanceService";
import FcyInflowPerformanceService from "./fcyInflowPerformance/FcyInflowPerformanceService";
import ViewFCYInflowPefrormance from "./fcyInflowPerformance/ViewFcyInflowPerformance";
import ViewDepositPefrormance from "./depositPerformance/ViewDepositPefrormance";
const FetchAccountDetial = () => {
  const navigate = useNavigate();
  const { companyId } = useParams();
  const [accountDetial, setAccountDetial] = useState([]);
  const [accountDetialId, setAccountDetialId] = useState([]);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [deleteAccountDetailError, setAccountDetailDeleteError] =
    useState(null);
  const [error, setError] = useState(null);
  const [AccountDetialErrorLoading, setAccountDetialErrorLoading] =
    useState(false);
  const [isAccountDetialEditModalOpen, setIsAccountDetialEditModalOpen] =
    useState(false);
  function handleAccountDetialEditOpenModal() {
    setIsAccountDetialEditModalOpen(true);
  }
  function handleAccountDetialEditCloseModal() {
    setIsAccountDetialEditModalOpen(false);
  }
  const [isAccountDetailsEditModalOpen, setIsAccountDetailsEditModalOpen] =
    useState(false);
  function handleAccountDetailsEditOpenModal() {
    setIsAccountDetailsEditModalOpen(true);
  }
  function handleAccountDetailsEditCloseModal() {
    setIsAccountDetailsEditModalOpen(false);
  }
  //credit utilizition
  const [
    isCreditFacilityUtilizitionModalOpen,
    setIsCreditFacilityUtilizitionModalOpen,
  ] = useState(false);
  function handleCreditFacilityUtilizitionOpenModal() {
    setIsCreditFacilityUtilizitionModalOpen(true);
  }
  function handleCreditFacilityUtilizitionCloseModal() {
    setIsCreditFacilityUtilizitionModalOpen(false);
  }
  //credit Performance
  const [isCreditPerformanceModalOpen, setIsCreditPerformanceModalOpen] =
    useState(false);
  function handleCreditPerformanceOpenModal() {
    setIsCreditPerformanceModalOpen(true);
  }
  function handleCreditPerformanceCloseModal() {
    setIsCreditPerformanceModalOpen(false);
  }
  //end credit performance
  //deposite performance
  const [isDepositePerformanceModalOpen, setIsDepositePerformanceModalOpen] =
    useState(false);
  function handleDepositePerformanceOpenModal() {
    setIsDepositePerformanceModalOpen(true);
  }
  function handleDepositePerformanceCloseModal() {
    setIsDepositePerformanceModalOpen(false);
  }
  //end deposite performance

  const [isFCYInflowPerformanceModalOpen, setIsFCYInflowPerformanceModalOpen] =
    useState(false);
  function handleFCYInflowPerformanceOpenModal() {
    setIsFCYInflowPerformanceModalOpen(true);
  }
  function handleFCYInflowPerformanceCloseModal() {
    setIsFCYInflowPerformanceModalOpen(false);
  }
  const getAccountDetial = () => {
    setAccountDetialErrorLoading(true);
    setTimeout(() => {
      AccountDetailSrvice.getAccountDetial(companyId)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setAccountDetial(response.data.Result);
            setAccountDetialErrorLoading(false);
          } else {
            setAccountDetialErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Account detial not found");
            setAccountDetialErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setAccountDetialErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getAccountDetial();
  }, []);
  /////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  const handleInputChangeAccountDetial = (event) => {
    const { name, value } = event.target;
    setAccountDetials({ ...accountDetials, [name]: value });
  };
  const initialAccountDetialsState = {
    Id: null,
    CompanyId: "",
    CustomerId: "",
    DepositAccount: "",
    DepositBalance: "",
  };
  const [accountDetials, setAccountDetials] = useState(
    initialAccountDetialsState
  );
  const newAccountDetials = () => {
    setAccountDetials(initialAccountDetialsState);
    // setSubmitted(false);
  };
  const saveAccountDetials = () => {
    var data = {
      CompanyId: companyId,
      CustomerId: accountDetials.CustomerId,
      DepositAccount: accountDetials.DepositAccount,
      DepositBalance: accountDetials.DepositBalance,
    };
    AccountDetailSrvice.addAccountDetials(data, companyId)
      .then((response) => {
        setAccountDetials({
          Id: response.data.Id,
          CompanyId: response.data.CompanyId,
          CustomerId: response.data.CustomerId,
          DepositAccount: response.data.DepositAccount,
          DepositBalance: response.data.DepositBalance,
        });
        if (response.data.Code === 200) {
          setAccountDetials(initialAccountDetialsState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          // getAccountDetial();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "addAccountDetails",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (accountDetials) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${accountDetials.DepositAccount}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deleteAccount",
      });
      AccountDetailSrvice.deleteAccountDetailsById(accountDetials.Id)
        .then(() => {})
        .catch((e) => {
          setAccountDetailDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  ///////////////////////////////////////////////////////////////////////////////
  const [editAccountDetail, setEditAccountDetail] = useState({
    //  Id: editAddresAccountDetailId,
    CustomerId: "",
    CompanyId: companyId,
    DepositAccount: "",
    DepositBalance: "",
  });
  const getAccountDetailId = (editAddresAccountDetailId) => {
    AccountDetailSrvice.getAccountDetailById(editAddresAccountDetailId)
      .then((res) => {
        setEditAccountDetail({
          ...editAccountDetail,
          Id: editAddresAccountDetailId,
          CompanyId: res.data.Result.CompanyId,
          CustomerId: res.data.Result.CustomerId,
          DepositAccount: res.data.Result.DepositAccount,
          DepositBalance: res.data.Result.DepositBalance,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateAccountDetailById = (e) => {
    e.preventDefault();
    AccountDetailSrvice.updateAccountDetailById(
      accountDetialId,
      editAccountDetail
    )
      .then((response) => {
        if (response.data.Code === 200) {
          // getAccountDetial();
          toast.success("Edited successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateAccountDetailSuccess",
          });
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateAccountDetailSuccess",
        });
      });
  };
  ////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
  const [creditFacilityUtilizition, setCreditFacilityUtilizition] = useState(
    []
  );
  const [creditPerformance, setCreditPerformance] = useState([]);
  const [fCYInflowPerformance, setFCYInflowPerformance] = useState([]);
  const [depositePerformance, SetDepositePerformance] = useState([]);
  const [
    deleteCreditFacilityUtilizitionError,
    setCreditFacilityUtilizitionDeleteError,
  ] = useState(null);
  const [
    CreditFacilityUtilizitionErrorLoading,
    setCreditFacilityUtilizitionErrorLoading,
  ] = useState(false);
  const getCreditFacilityUtilizition1 = (accountDetialId) => {
    setCreditFacilityUtilizitionErrorLoading(true);
    setTimeout(() => {
      CreditFacilityUtilizitionService.getCreditFacilityUtilizition(
        accountDetialId
      )
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setCreditFacilityUtilizition(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  // useEffect(() => {
  //   getCreditFacilityUtilizition();
  // }, []);
  ////////////////////////////////////////////////////////////////////////////////////////////////
  const handleBack = () => {
    alert("Handle Back");
  };
  const handleSkip = () => {
    alert("Handle Back");
  };
  const handleNext = () => {
    alert("Handle Next");
  };
  const getCreditPerformance = (accountDetialId) => {
    setCreditFacilityUtilizitionErrorLoading(true);
    setTimeout(() => {
      CreditPerformanceService.getCreditPerformance(accountDetialId)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setCreditPerformance(response.data.Result);
            debugger;
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Performance not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  // useEffect(() => {
  //   getCreditPerformance();
  // }, []);
  const getDepositePerformance = (accountDetialId) => {
    setTimeout(() => {
      DepositPerformanceService.getDepositePerformance(accountDetialId)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            SetDepositePerformance(response.data.Result);
          } else {
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("deposite Performance not found");
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  // useEffect(() => {
  //   getDepositePerformance();
  // }, []);
  const getFCYInflowPerformance = (accountDetialId) => {
    setTimeout(() => {
      FcyInflowPerformanceService.getFCYInflowPerformance(accountDetialId)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setFCYInflowPerformance(response.data.Result);
          } else {
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("deposite Performance not found");
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  // useEffect(() => {
  //   getFCYInflowPerformance();
  // }, []);
  return (
    <div className="flex">
      <CompanyNavigation />
      <div className="px-4 py-3">
        <div className="flex flex-wrap">
          <div className="w-full">
            <div>
              <h4 className="text-lg font-semibold eading-tight p-2">
                Account Details
              </h4>
              <EditAccountDetail
                isAccountDetailsEditModalOpen={isAccountDetailsEditModalOpen}
                handleAccountDetailsEditCloseModal={
                  handleAccountDetailsEditCloseModal
                }
                updateAccountDetailsById={updateAccountDetailById}
                editAccountDetail={editAccountDetail}
                setEditAccountDetail={setEditAccountDetail}
              />
              <AddAccountDetail
                newAccountDetials={newAccountDetials}
                accountDetial={accountDetial}
                handleInputChangeAccountDetial={handleInputChangeAccountDetial}
                handleBack={handleBack}
                handleSkip={handleSkip}
                handleNext={handleNext}
                saveAccountDetials={saveAccountDetials}
              />
              <ViewCreditFacilityUtilizition
                isCompanySoleOtherBankModalOpen={
                  isCreditFacilityUtilizitionModalOpen
                }
                handleCompanySoleOtherBankCloseModal={
                  handleCreditFacilityUtilizitionCloseModal
                }
                handleCompanySoleOtherBankOpenModal={
                  handleCreditFacilityUtilizitionOpenModal
                }
                creditFacilityUtilizition={creditFacilityUtilizition}
                // soleID={taxId}
                companyId={companyId}
              />
              <ViewCreditPerformance
                isCreditPerformanceModalOpen={isCreditPerformanceModalOpen}
                handleCreditPerformanceCloseModal={
                  handleCreditPerformanceCloseModal
                }
                setIsCreditPerformanceModalOpen={
                  setIsCreditPerformanceModalOpen
                }
                accountDetialId={accountDetialId}
                creditPerformance={creditPerformance}
              />
              <ViewDepositPefrormance
                isDepositePerformanceModalOpen={isDepositePerformanceModalOpen}
                handleDepositePerformanceCloseModal={
                  handleDepositePerformanceCloseModal
                }
                setIDepositePerformanceModalOpen={
                  setIsDepositePerformanceModalOpen
                }
                accountDetialId={accountDetialId}
                depositePerformance={depositePerformance}
              />
              {/* <ViewFCYInflowPefrormance
                isFCYInflowPerformanceModalOpen={
                  isFCYInflowPerformanceModalOpen
                }
                handleFCYInflowPerformanceCloseModal={
                  handleFCYInflowPerformanceCloseModal
                }
                setIFCYInflowPerformanceModalOpen={
                  setIsFCYInflowPerformanceModalOpen
                }
                accountDetialId={accountDetialId}
                fCYInflowPerformance={fCYInflowPerformance}
              />               */}
              <div>
                {error && <p className="text-red-500">{error}</p>}
                {!error && AccountDetialErrorLoading ? (
                  <span>
                    <FaSpinner
                      className="animate-spin rounded-full h-6 w-6 text-purple-600"
                      fontSize="small"
                      data-toggle="tooltip"
                      title="Click it to sign in"
                    />
                    Loading...
                  </span>
                ) : (
                  !error &&
                  !AccountDetialErrorLoading && (
                    <div className="overflow-x-auto">
                      <table className="min-w-full table table-bordered table-striped bg-purple-100">
                        <thead>
                          <tr className="bg-purple-200">
                            <th className="px-2 py-4"> CustomerId </th>
                            <th className="px-2 py-4"> DepositAccount </th>
                            <th className="px-2 py-4"> DepositBalance </th>
                            <th className="px-2 py-4"> Action </th>
                          </tr>
                        </thead>
                        <tbody>
                          {accountDetial.length === 0 ? (
                            <p className="text-red-600 p-4">Data not found</p>
                          ) : (
                            accountDetial.map((details, index) => (
                              <tr
                                key={index}
                                className="bg-white
                    border-b border-purple-200"
                              >
                                {/* <td className="p-2">{details.Id}</td> */}
                                <td className="p-2">{details.CustomerId}</td>
                                <td className="p-2">
                                  {details.DepositAccount}
                                </td>
                                <td className="p-2">
                                  {details.DepositBalance}
                                </td>
                                <td>
                                  <button
                                    type="submit"
                                    onClick={() => {
                                      handleCreditFacilityUtilizitionOpenModal();
                                      getCreditFacilityUtilizition1(details.Id);
                                    }}
                                    className="text-purple-600 rounded-md font-semibold "
                                  >
                                    Credit Facility Utilization
                                  </button>
                                </td>
                                <td ></td>
                                <td>
                                  <button
                                    type="submit"
                                    onClick={() => {
                                      handleCreditPerformanceOpenModal();
                                      getCreditPerformance(details.Id);
                                      setAccountDetialId(details.Id);
                                    }}
                                    className="text-purple-600 rounded-md font-semibold "
                                  >
                                    Credit performance
                                  </button>
                                </td>
                                <td>
                                  <button
                                    type="submit"
                                    onClick={() => {
                                      handleDepositePerformanceOpenModal();
                                      getDepositePerformance(details.Id);
                                      setAccountDetialId(details.Id);
                                    }}
                                    className="text-purple-600 rounded-md font-semibold "
                                  >
                                    Deposite performance
                                  </button>
                                </td>
                                <td>
                                  <button
                                    type="submit"
                                    onClick={() => {
                                      handleDelete(details);
                                    }}
                                    className="text-red-600 rounded-md font-semibold "
                                  >
                                    Delete
                                  </button>
                                </td>
                                <td>
                                  <button
                                    type="submit"
                                    onClick={() => {
                                      handleAccountDetailsEditOpenModal();
                                      getAccountDetailId(details.Id);
                                      setAccountDetialId(details.Id);
                                    }}
                                    className="text-purple-600 rounded-md font-semibold "
                                  >
                                    Edit
                                  </button>
                                </td>
                              </tr>
                            ))
                          )}
                        </tbody>
                      </table>
                    </div>
                  )
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default FetchAccountDetial;
