import { useState, useEffect } from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import CompanyModal from "../Common/CompanyModal";
import { ToastContainer, toast } from "react-toastify";
import SoleAndPartnershipService from "./SoleAndPartnershipService";
import CommonServices from "../Common/CommonServices";
import LoanTextField from "../../Common/LoanTextField";
const EditSole = ({
  isCompanySoleEditModalOpen,
  handleCompanySoleEditCloseModal,
  handleCompanySoleEditOpenModal,
  soleIdEdit,
}) => {
  const [country, setContry] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [editSole, setEditSole] = useState({
    Id: soleIdEdit,
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    ProofOfIdentity: "",
    MarriageDate: "",
    MarriageCertificate: "",
    MarriageCertificateIssuer: "",
    ProofOfIdentityIssueDate: "",
    ProofOfIdentityExpirDate: "",
  });
  const handleInputChangeEditSole = (event) => {
    const { name, value } = event.target;
    setEditSole({ ...editSole, [name]: value });
  };
  const getSoleById = () => {
    SoleAndPartnershipService.getSole(soleIdEdit)
      .then((res) => {
        setEditSole({
          ...editSole,
          Id: soleIdEdit,
          FirstName: res.data.Result.FirstName,
          MiddleName: res.data.Result.MiddleName,
          LastName: res.data.Result.LastName,
          NationalityID: res.data.Result.NationalityID,
          CountryOfOriginID: res.data.Result.CountryOfOriginID,
          CountryOfResidenceID: res.data.Result.CountryOfResidenceID,
          ProofOfIdentityTypeID: res.data.Result.ProofOfIdentityTypeID,
          ProofOfIdentity: res.data.Result.ProofOfIdentity,
          MarriageDate: res.data.Result.MarriageDate,
          MarriageCertificate: res.data.Result.MarriageCertificate,
          MarriageCertificateIssuer: res.data.Result.MarriageCertificateIssuer,
          ProofOfIdentityIssueDate: res.data.Result.ProofOfIdentityIssueDate,
          ProofOfIdentityExpirDate: res.data.Result.ProofOfIdentityExpirDate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getSoleById();
  }, []);
  const updateSoleById = (e) => {
    e.preventDefault();
    SoleAndPartnershipService.updateSoleById()
      .then((response) => {
        if (response.data.Code === 200) {
          getSoleById();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompanyAddressSuccess",
          });
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateCompanyAddressError",
        });
      });
  };
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setContry(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);
  const fetchProofOfIdentityType = () => {
    CommonServices.getProofOfIdentityTypes()
      .then((response) => {
        setProofOfIdentityType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchProofOfIdentityType();
  }, []);
  return (
    <CompanyModal
      isOpen={isCompanySoleEditModalOpen}
      onClose={handleCompanySoleEditCloseModal}
      title="Modify company basic information"
    >
      <div>
        <form onSubmit={updateSoleById}>
          <div className="grid grid-cols-1 gap-4 submit-form">
            <div className="submit-form ">
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="ZoneSubCity"
                label="ZoneSubCity"
                value={editSole.ZoneSubCity}
                onChange={handleInputChangeEditSole}
                name="ZoneSubCity"
              />
                <FormControl variant="standard" sx={{ width: "45ch" }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Country
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (editSole.CountryId = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((newContry) => (
                      <MenuItem
                        key={newContry.Id}
                        CountryID={newContry.Id}
                        value={newContry.Id}
                      >
                        {newContry.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                
                <LoanTextField
                id="ZoneSubCity"
                label="ZoneSubCity"
                value={editSole.ZoneSubCity}
                onChange={handleInputChangeEditSole}
                name="ZoneSubCity"
              />
                <TextField
                  id="ZoneSubCity"
                  label="Zone Sub City"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.ZoneSubCity}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      ZoneSubCity: e.target.value,
                    })
                  }
                  name="ZoneSubCity"
                />

                <TextField
                  id="Woreda"
                  label="Woreda"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.Woreda}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      Woreda: e.target.value,
                    })
                  }
                  name="Woreda"
                />
                <TextField
                  id="City"
                  label="City"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.City}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      City: e.target.value,
                    })
                  }
                  name="City"
                />
                <TextField
                  id="Kebele"
                  label="Kebele"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.Kebele}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      Kebele: e.target.value,
                    })
                  }
                  name="Kebele"
                />
                <TextField
                  id="HouseNo"
                  label="HouseNo"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.HouseNo}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      HouseNo: e.target.value,
                    })
                  }
                  name="HouseNo"
                />
                <TextField
                  id="BuildingNo"
                  label="BuildingNo"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.BuildingNo}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      BuildingNo: e.target.value,
                    })
                  }
                  name="BuildingNo"
                />
                <TextField
                  id="StreetAddress"
                  label="StreetAddress"
                  variant="standard"
                  color="secondary"
                  size=""
                  required
                  value={editSole.StreetAddress}
                  onChange={(e) =>
                    setEditSole({
                      ...editSole,
                      StreetAddress: e.target.value,
                    })
                  }
                  name="StreetAddress"
                />
              </div>
              <div className="flex justify-center items-center">
                <button
                  type="submit"
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                >
                  Update
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </CompanyModal>
  );
};

export default EditSole;
