import { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { FaSpinner } from "react-icons/fa";
import { ToastContainer, toast } from "react-toastify";
import LoanIconButton from "../../Common/LoanIconButton";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import CompanySeniorManagementStaffService from "./CompanySeniorManagementStaffService";
import CompanyNavigation from "../../Common/CompanyNavigation";
import { IoTrashOutline } from "react-icons/io5";
import AddSeniorManagementStaff from "./AddSeniorManagementStaff";
import EditSeniorManagementStaff from "./EditSeniorManagementStaff";
import CommonServices from "../Common/CommonServices";
const ViewSeniorManagementStaff = ({ companyType }) => {
  const [selectedCVDocument, setSelectedCVDocument] = useState(null);
  const [selectedWorkExperienceDocument, setSelectedWorkExperienceDocument] =
    useState(null);
  const { companyId } = useParams();
  const navigate = useNavigate();
  const [error, setError] = useState(null);
  const [seniorManagementStaff, setSeniorManagementStaff] = useState([]);
  const [
    seniorManagementStaffErrorLoading,
    setSeniorManagementStaffErrorLoading,
  ] = useState(false);
  const initialSeniorManagementStaffState = {
    Id: null,
    FullName: "",
    TotalWorkExperience: "",
    RelatedWorkExperience: "",
    Position: "",
    CVDocument: "",
    WorkExperienceDocument: "",
  };
  const [seniorManagementStaffs, setSeniorManagementStaffs] = useState(
    initialSeniorManagementStaffState
  );

  const [seniorManagementStaffId, setSeniorManagementStaffId] = useState();

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSeniorManagementStaffs({ ...seniorManagementStaffs, [name]: value });
  };

  const getSeniorManagementStaffs = () => {
    setSeniorManagementStaffErrorLoading(true);
    setTimeout(() => {
      CompanySeniorManagementStaffService.getCompanySeniorManagementStaffInformation(
        companyId
      )
        .then((response) => {
          debugger;
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setSeniorManagementStaff(response.data.Result);
            setSeniorManagementStaffErrorLoading(false);
          } else {
            setSeniorManagementStaffErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Employee not found");
            setSeniorManagementStaffErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setSeniorManagementStaffErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getSeniorManagementStaffs();
  }, []);

  const saveSeniorStaff = (e) => {
    e.preventDefault();
    var data = {
      FullName: seniorManagementStaffs.FullName,
      TotalWorkExperience: seniorManagementStaffs.TotalWorkExperience,
      RelatedWorkExperience: seniorManagementStaffs.RelatedWorkExperience,
      Position: seniorManagementStaffs.Position,
      CVDocument: selectedCVDocument,
      WorkExperienceDocument: selectedWorkExperienceDocument,
    };
    CompanySeniorManagementStaffService.addSeniorManagementStaff(
      data,
      companyId
    )
      .then((response) => {
        setSeniorManagementStaff({
          Id: response.data.Id,
          FullName: response.data.FullName,
          TotalWorkExperience: response.data.TotalWorkExperience,
          RelatedWorkExperience: response.data.RelatedWorkExperience,
          Position: response.data.Position,
          CVDocument: response.data.CVDocument,
          WorkExperienceDocument: response.data.WorkExperienceDocument,
        });
        if (response.data.Code === 200) {
          setSeniorManagementStaff(initialSeniorManagementStaffState);
          getSeniorManagementStaffs();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "addSeniorStaff",
          });
        }
      })
      .catch((error) => {
        console.log(error);
        // if (error.response.data.StatusCode === 404) {
        //   setError("Address not found");
        //   setAddressErrorLoading(false);
        // } else if (error.response.data.StatusCode === 400) {
        //   setError("Something went wrong");
        //   setAddressErrorLoading(false);
        // } else if (error.response.data.StatusCode === 401) {
        //   navigate("/login");
        // } else if (error.response.data.StatusCode === 403) {
        //   navigate("/login");
        // }
      });
  };
  const handleDelete = (staff) => {
    const confirm = window.confirm(
      `Are you sure you want to delete ${staff.FullName}?`
    );
    if (confirm) {
      CompanySeniorManagementStaffService.deleteSeniorManagementStaff(staff.Id)
        .then(() => {
          setSeniorManagementStaff((previousData) =>
            previousData.filter((comp) => comp.Id !== staff.Id)
          );
          toast.success("Deleting", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "deleteStaff",
          });
        })
        .catch((e) => {
          //   setCompanyDeleteError(e);
        });
    }
  };

  const [
    isSeniorManagementStaffEditModalOpen,
    setIsSeniorManagementStaffEditModalOpen,
  ] = useState(false);
  function handleSeniorManagementStaffEditCloseModal() {
    setIsSeniorManagementStaffEditModalOpen(false);
  }
  function handleSeniorManagementStaffEditOpenModal() {
    setIsSeniorManagementStaffEditModalOpen(true);
  }
  const [editSeniorStaff, setEditSeniorStaff] = useState({
    Id: seniorManagementStaffId,
    FullName: "",
    TotalWorkExperience: "",
    RelatedWorkExperience: "",
    Position: "",
    CVDocument: "",
    WorkExperienceDocument: "",
  });
  const getSelectedSeniorStaffInformation = (seniorManagementStaffId) => {
    CompanySeniorManagementStaffService.getCompanySeniorManagementStaffInformationById(
      seniorManagementStaffId
    )
      .then((res) => {
        setEditSeniorStaff({
          ...editSeniorStaff,
          Id: seniorManagementStaffId,
          FullName: res.data.Result.FullName,
          TotalWorkExperience: res.data.Result.TotalWorkExperience,
          RelatedWorkExperience: res.data.Result.RelatedWorkExperience,
          Position: res.data.Result.Position,
          CVDocument: res.data.Result.CVDocument,
          WorkExperienceDocument: res.data.Result.WorkExperienceDocument,
        });
        debugger;
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateSeniorManagementStaffById = (e) => {
    e.preventDefault();
    CompanySeniorManagementStaffService.updateCompanySeniorManagementStaffInformationById(
      seniorManagementStaffId,
      editSeniorStaff
    )
      .then((response) => {
        if (response.data.Code === 200) {
          toast.success("Modified successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateSeniorManagementStaffSuccess",
          });
          getSeniorManagementStaffs();
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateSeniorManagementStaffError",
        });
      });
  };
  //tiztaw
  // const [companyType, setCompanyType] = useState(null);
  // const fetchCompany = () => {
  //   CommonServices.getCompanyTypeFromAll(companyId)
  //     .then((response) => {
  //       setCompanyType(response.data.Result);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };
  // useEffect(() => {
  //   fetchCompany();
  // }, []);

  const renderButton = () => {
    let button;
    if (companyType.EntityType === "SOEs") {
      button = (
        <Link to={`/enterprise/soe-and-governmental/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="SOE or governmental"
          />
        </Link>
      );
    } else if (
      companyType.EntityType === "Sole Proprietor" ||
      companyType.EntityType === "Partnership"
    ) {
      button = (
        <Link to={`/enterprise/Sole-and-Partnership/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="Sole Or Partnership"
          />
        </Link>
      );
    } else if (
      companyType.EntityType === "Association" ||
      companyType.EntityType === "Union"
    ) {
      button = (
        <Link to={`/enterprise/Union-and-Association/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="Association or Union"
          />
        </Link>
      );
    } else {
      button = (
        <Link to={`/enterprise/plc-and-share/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="PLC"
          />
        </Link>
      );
    }
    return button;
  };
  //tiztaw
  return (
    <div className="flex">
      <CompanyNavigation />
      <div className="px-4 py-3">
        <div className="flex flex-wrap">
          <div className="w-full">
            <div>
              <h4 className="text-lg font-semibold eading-tight p-2">
                Senior management staff
              </h4>
              <AddSeniorManagementStaff
                seniorManagementStaff={seniorManagementStaff}
                handleInputChange={handleInputChange}
                saveSeniorStaff={saveSeniorStaff}
                setSelectedCVDocument={setSelectedCVDocument}
                setSelectedWorkExperienceDocument={
                  setSelectedWorkExperienceDocument
                }
                companyType={companyType}
              />
              <ToastContainer />

              <EditSeniorManagementStaff
                isSeniorManagementStaffEditModalOpen={
                  isSeniorManagementStaffEditModalOpen
                }
                handleSeniorManagementStaffEditCloseModal={
                  handleSeniorManagementStaffEditCloseModal
                }
                editSeniorStaff={editSeniorStaff}
                setEditSeniorStaff={setEditSeniorStaff}
                updateSeniorManagementStaffById={
                  updateSeniorManagementStaffById
                }
              />
              {error && <p className="text-red-500">{error}</p>}
              {!error && seniorManagementStaffErrorLoading ? (
                <span>
                  <FaSpinner
                    className="animate-spin rounded-full h-6 w-6 text-purple-600"
                    fontSize="small"
                    data-toggle="tooltip"
                    title="Click it to sign in"
                  />
                  Loading...
                </span>
              ) : (
                !error &&
                !seniorManagementStaffErrorLoading && (
                  <table className="border-purple-300 bg-white hover:bg-purple-300">
                    <thead className="bg-purple-100">
                      <tr className="border border-purple-300">
                        {/* <th className="px-2 py-4"> Id </th> */}
                        <th className="px-2 py-4"> Full Name </th>
                        <th className="px-2 py-4">Total Work Experience</th>
                        <th className="px-2 py-4"> Related Work Experience </th>
                        <th className="px-2 py-4"> Position </th>
                        <th className="px-2 py-4"> CV Document </th>
                        <th className="px-2 py-4">Work Experience Document</th>
                        <th className="px-2 py-4"> Action </th>
                      </tr>
                    </thead>
                    <tbody>
                      {seniorManagementStaff.length === 0 ? (
                        <p className="text-red-600 p-4">Data not found</p>
                      ) : (
                        seniorManagementStaff.map((staff, index) => (
                          <>
                            <tr
                              className="bg-white
                    border-b border-purple-200"
                              key={staff.Id}
                            >
                              {/* <td className="p-2">{index + 1}</td> */}
                              {/* <td className="p-2">{staff.Id}</td> */}
                              <td className="p-2">{staff.FullName}</td>
                              <td className="p-2">
                                {staff.TotalWorkExperience}
                              </td>
                              <td className="p-2">
                                {staff.RelatedWorkExperience}
                              </td>
                              <td className="p-2">{staff.Position}</td>
                              <td className="p-2">{staff.CVDocument}</td>
                              <td className="p-2">
                                {staff.WorkExperienceDocument}
                              </td>
                              <td>
                                <LoanIconButton
                                  style={{ color: "green" }}
                                  icon={<MdModeEdit />}
                                  text="Edit"
                                  onClick={() => {
                                    handleSeniorManagementStaffEditOpenModal();
                                    // handleSeniorManagementStaffEditOpenModal();
                                    // handleSeniorManagementStaffEditOpenModal();
                                    getSelectedSeniorStaffInformation(staff.Id);
                                    // getSeniorManagementsById(staff.Id);
                                    setSeniorManagementStaffId(staff.Id);
                                  }}
                                />
                                <>
                                  <LoanIconButton
                                    style={{ color: "red" }}
                                    icon={<IoTrashOutline />}
                                    text="Delete"
                                    onClick={() => {
                                      handleDelete(staff);
                                    }}
                                  >
                                    <ToastContainer />
                                  </LoanIconButton>
                                </>
                              </td>
                              <td>{renderButton()}</td>
                            </tr>
                          </>
                        ))
                      )}
                    </tbody>
                  </table>
                )
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewSeniorManagementStaff;
