import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../../Common/LoanIconButton";
import CompanyModal from "../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
import LoanTextField from "../../../Common/LoanTextField";
const AddCreditPerformance = ({
  // submitted,
  // handleBack,
  handleSkip,
  // handleNext,
  // newAccountDetials,
  creditPerformance,
  handleInputChangeCreditPerformance,
  saveCreditPerformance,
}) => {
  const [isCreditPerformanceModalOpen, setIsCreditPerformanceModalOpen] =
    useState(false);
  function handleCreditPerformanceOpenModal() {
    setIsCreditPerformanceModalOpen(true);
  }
  function handlCreditPerformanceCloseModal() {
    setIsCreditPerformanceModalOpen(false);
  }
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCreditPerformanceOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={isCreditPerformanceModalOpen}
        onClose={handlCreditPerformanceCloseModal}
        title="Add handle Credit Facility Utilizition "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="LoanAccount"
              label="LoanAccount"
              value={creditPerformance.LoanAccount}
              onChange={handleInputChangeCreditPerformance}
              name="LoanAccount"
            />
            <FormControl variant="standard" sx={{ minWidth: 120 }}>
              <InputLabel id="LoanTypeId" color="secondary">
                Loan Type{" "}
              </InputLabel>
              <Select
                color="secondary"
                onChange={(event) => {
                  creditPerformance.LoanTypeId = event.target.value;
                  setLoanTypeId(event.target.value);
                }}
                labelId="LoanTypeId"
                id="LoanTypeId"
                label="LoanTypeId"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {loanTypeIdForCreditUtilization.map((yourLoan) => (
                  <MenuItem
                    key={yourLoan.Id}
                    LoanTypeId={yourLoan.Id}
                    value={yourLoan.Id}
                  >
                    {yourLoan.Name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            {/* <LoanTextField
              id="LoanAccount"
              label="LoanAccount"
              value={creditFacilityUtilizition.LoanAccount}
              onChange={handleInputChangeCreditFacilityUtilizition}
              name="LoanAccount"
            /> */}
            <LoanTextField
              id="ApprovedAmount"
              label="ApprovedAmount"
              value={creditPerformance.ApprovedAmount}
              onChange={handleInputChangeCreditPerformance}
              name="ApprovedAmount"
            />
            {/* ApprovalDate */}
            <LoanTextField
              id="TotalCommitment"
              label="TotalCommitment"
              value={creditPerformance.TotalCommitment}
              onChange={handleInputChangeCreditPerformance}
              name="TotalCommitment"
            />
            {/* CurrencyId
            StartDate
            MaturityDate */}

            <LoanTextField
              id="RepaymentFrequency"
              label="RepaymentFrequency"
              value={creditPerformance.RepaymentFrequency}
              onChange={handleInputChangeCreditPerformance}
              name="RepaymentFrequency"
            />
            {/* ScheduledPayment             */}
            <LoanTextField
              id="LoanStatus"
              label="LoanStatus"
              value={creditPerformance.LoanStatus}
              onChange={handleInputChangeCreditPerformance}
              name="LoanStatus"
            />
            <LoanTextField
              id="Restructuring"
              label="Restructuring"
              value={creditPerformance.Restructuring}
              onChange={handleInputChangeCreditPerformance}
              name="Restructuring"
            />
            <LoanTextField
              id="LoanAccountRestructured"
              label="LoanAccountRestructured"
              value={creditPerformance.LoanAccountRestructured}
              onChange={handleInputChangeCreditPerformance}
              name="LoanAccountRestructured"
            />
            {/* SystemAdjustmentDate4WaiverAndExtension */}
            <LoanTextField
              id="NoOfScheduledPrincipalPaymentWaived"
              label="NoOfScheduledPrincipalPaymentWaived"
              value={creditPerformance.NoOfScheduledPrincipalPaymentWaived}
              onChange={handleInputChangeCreditPerformance}
              name="NoOfScheduledPrincipalPaymentWaived"
            />
            <LoanTextField
              id="NoOfScheduledRepaymentWaived"
              label="NoOfScheduledRepaymentWaived"
              value={creditPerformance.NoOfScheduledRepaymentWaived}
              onChange={handleInputChangeCreditPerformance}
              name="NoOfScheduledRepaymentWaived"
            />
            {/* AdjustmentMaturityDate */}
            <LoanTextField
              id="PrincipalOutstandingAmount"
              label="PrincipalOutstandingAmount"
              value={creditPerformance.PrincipalOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="PrincipalOutstandingAmount"
            />
            <LoanTextField
              id="InterestOutstandingAmount"
              label="InterestOutstandingAmount"
              value={creditPerformance.InterestOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="InterestOutstandingAmount"
            />
            <LoanTextField
              id="TotalOutstandingAmountPerLoanType"
              label="TotalOutstandingAmountPerLoanType"
              value={creditPerformance.TotalOutstandingAmountPerLoanType}
              onChange={handleInputChangeCreditPerformance}
              name="TotalOutstandingAmountPerLoanType"
            />
            <LoanTextField
              id="PrincipalArrears"
              label="PrincipalArrears"
              value={creditPerformance.PrincipalArrears}
              onChange={handleInputChangeCreditPerformance}
              name="PrincipalArrears"
            />
            <LoanTextField
              id="InterestArrears"
              label="InterestArrears"
              value={creditPerformance.InterestArrears}
              onChange={handleInputChangeCreditPerformance}
              name="InterestArrears"
            />
            <LoanTextField
              id="TotalArrears"
              label="TotalArrears"
              value={creditPerformance.TotalArrears}
              onChange={handleInputChangeCreditPerformance}
              name="TotalArrears"
            />
            <LoanTextField
              id="TotalApprovedLoanAmount"
              label="TotalApprovedLoanAmount"
              value={creditPerformance.TotalApprovedLoanAmount}
              onChange={handleInputChangeCreditPerformance}
              name="TotalApprovedLoanAmount"
            />
            <LoanTextField
              id="TotalOutstandingAmount"
              label="TotalOutstandingAmount"
              value={creditPerformance.TotalOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="TotalOutstandingAmount"
            />
            <LoanTextField
              id="CrossSelling"
              label="CrossSelling"
              value={creditPerformance.CrossSelling}
              onChange={handleInputChangeCreditPerformance}
              name="CrossSelling"
            />
            <LoanTextField
              id="NPLStatus"
              label="NPLStatus"
              value={creditPerformance.NPLStatus}
              onChange={handleInputChangeCreditPerformance}
              name="NPLStatus"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveCreditPerformance();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddCreditPerformance;
