import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import AddDivorcedStatus from "./AddDivorcedStatus";
import CommonServices from "../../Common/CommonServices";
const ViewDivorcedStatus = ({
  holdDivorced,
  SoleId,
  isCompanyMartailStatusDivorcedModalOpen,
  handleCompanyMartailStatusDivorcedCloseModal,
  handleCompanyMartailStatusDivorcedOpenModal,
}) => {
  const [unMarriedCertficateIssueDate, setUnMarriedCertficateIssueDate] =
    useState(new Date());
  const [unMarriedCertficateExpiredDate, setUnMarriedCertficateExpiredDate] =
    useState(new Date());
  const [divorceDate, setDivorceDate] = useState(new Date());
  //upload
  const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
  const [selectedPathUnmarriege, setSelectedPathUnmarriege] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };

  //upload 1
  const uploadMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  //upload 2
  const uploadUnMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathUnmarriege(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //endupload2
  const [
    isCompanyDivorcedInformationEditModalOpen,
    setIsCompanyDivorcedInformationEditModalOpen,
  ] = useState(false);
  function handleCompanyDivorcedEditCloseModal() {
    setIsCompanyDivorcedInformationEditModalOpen(false);
  }
  function handleCompanyDivorcedInformationEditOpenModal() {
    setIsCompanyDivorcedInformationEditModalOpen(true);
  }
  function handleCompanyDivorcedInformationEditCloseModal() {
    setIsCompanyDivorcedInformationEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editDivorcedInfo, setEditDivorcedInfo] = useState({
    SoleAndPartnershipId: SoleId,
    DivorceDate: "",
    MarriageCertificate: "",
    MarriageCertificateIssuer: "",
    UnMarriedCertficate: "",
    UnMarriedCertficateIssueDate: "",
    UnMarriedCertficateExpiredDate: "",
    UnMarriedCertficateIssuer: "",
    CourtDecision: "",
  });
  const handleInputChangeEditDivorcedStatus = (event) => {
    const { name, value } = event.target;
    setEditDivorcedInfo({ ...editDivorcedInfo, [name]: value });
  };
  const updateDivorcedById = (taxInformationId1) => {
    CommonServices.getDivorced(taxInformationId1)
      .then((res) => {
        setEditDivorcedInfo({
          ...editDivorcedInfo,
          SoleAndPartnershipId: res.data.Result.SoleAndPartnershipId,
          DivorceDate: res.data.Result.DivorceDate,
          MarriageCertificate: res.data.Result.MarriageCertificate,
          MarriageCertificateIssuer: res.data.Result.MarriageCertificateIssuer,
          UnMarriedCertficate: res.data.Result.UnMarriedCertficate,
          UnMarriedCertficateIssueDate:
            res.data.Result.UnMarriedCertficateIssueDate,
          UnMarriedCertficateExpiredDate:
            res.data.Result.UnMarriedCertficateExpiredDate,
          UnMarriedCertficateIssuer: res.data.Result.UnMarriedCertficateIssuer,
          CourtDecision: res.data.Result.CourtDecision,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateDivorcedById();
  }, []);
  const updateDivorcedInfo = () => {
    CommonServices.updateDivorced(taxInformationId, editDivorcedInfo)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateDivorcedInfo();
  }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMartailStatusDivorcedModalOpen}
        onClose={handleCompanyMartailStatusDivorcedCloseModal}
        title="View Divorced status information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdDivorced ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
                <th className="px-2 py-4"> Divorce Date</th>
                <th className="px-2 py-4"> Marriage Certificate</th>
                <th className="px-2 py-4"> Marriage Certificate Issuer</th>
                <th className="px-2 py-4"> UnMarried Certficate</th>
                <th className="px-2 py-4"> UnMarried Certficate Issue Date</th>
                <th className="px-2 py-4">
                  {" "}
                  UnMarried Certficate Expired Date
                </th>
                <th className="px-2 py-4"> UnMarried Certficate Issuer</th>
                <th className="px-2 py-4"> Court Decision</th>
                <th className="px-2 py-4"> Action </th>
              </thead>
              <tbody>
                <tr>
                  {/* <td>{holdDivorced.Id}</td> */}
                  <td>{holdDivorced.DivorceDate}</td>
                  <td>{holdDivorced.MarriageCertificate}</td>
                  <td>{holdDivorced.MarriageCertificateIssuer}</td>
                  <td>{holdDivorced.UnMarriedCertficate}</td>
                  <td>{holdDivorced.UnMarriedCertficateIssueDate}</td>
                  <td>{holdDivorced.UnMarriedCertficateExpiredDate}</td>
                  <td>{holdDivorced.UnMarriedCertficateIssuer}</td>
                  <td>{holdDivorced.CourtDecision}</td>
                  <td>
                    <button
                      onClick={() => {
                        handleCompanyDivorcedInformationEditOpenModal();
                        updateDivorcedById(holdDivorced.Id);
                        setTaxInformationId(holdDivorced.Id);
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Edit
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          ) : (
            ""
          )}
        </div>
        <div>
          {holdDivorced <= 0 && (
            <AddDivorcedStatus Add marital status information SoleId={SoleId} />
          )}
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyDivorcedInformationEditModalOpen}
        onClose={handleCompanyDivorcedEditCloseModal}
        title="widowed edit information"
      >
        <div>
          <form onSubmit={updateDivorcedInfo}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <p>
                Divorce Date:
                <input
                  id="DivorceDate"
                  name="DivorceDate"
                  label="DivorceDate"
                  placeholder="DivorceDate"
                  type="date"
                  value={
                    divorceDate.getFullYear().toString() +
                    "-" +
                    (divorceDate.getMonth() + 1).toString().padStart(2, 0) +
                    "-" +
                    divorceDate.getDate().toString().padStart(2, 0)
                  }
                  onChange={(e) => {
                    setDivorceDate(new Date(e.target.value));
                    editDivorcedInfo.DivorceDate = divorceDate;
                  }}
                />{" "}
              </p>
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onChange={
                    (editDivorcedInfo.MarriageCertificate =
                      selectedPathIdentity)
                  }
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <LoanTextField
                id="MarriageCertificateIssuer"
                label="MarriageCertificateIssuer"
                value={editDivorcedInfo.MarriageCertificateIssuer}
                onChange={handleInputChangeEditDivorcedStatus}
                name="MarriageCertificateIssuer"
              />
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onChange={
                    (editDivorcedInfo.UnMarriedCertficate =
                      selectedPathIdentity)
                  }
                  onClick={uploadUnMarriegeCertificate}
                >
                  Upload UnMarriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <p>
                UnMarried Certficate Issue Date:
                <input
                  id="UnMarriedCertficateIssueDate"
                  name="UnMarriedCertficateIssueDate"
                  label="UnMarriedCertficateIssueDate"
                  placeholder="UnMarriedCertficateIssueDate"
                  type="date"
                  value={
                    unMarriedCertficateIssueDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateIssueDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateIssueDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateIssueDate(new Date(e.target.value));
                    editDivorcedInfo.UnMarriedCertficateIssueDate =
                      unMarriedCertficateIssueDate;
                  }}
                />{" "}
              </p>
              <p>
                UnMarried Certficate Expired Date:
                <input
                  id="UnMarriedCertficateExpiredDate"
                  name="UnMarriedCertficateExpiredDate"
                  label="UnMarriedCertficateExpiredDate"
                  placeholder="UnMarriedCertficateExpiredDate"
                  type="date"
                  value={
                    unMarriedCertficateExpiredDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateExpiredDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateExpiredDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateExpiredDate(new Date(e.target.value));
                    editDivorcedInfo.UnMarriedCertficateExpiredDate =
                      unMarriedCertficateExpiredDate;
                  }}
                />{" "}
              </p>
              <LoanTextField
                id="UnMarriedCertficateIssuer"
                label="UnMarriedCertficateIssuer"
                value={editDivorcedInfo.UnMarriedCertficateIssuer}
                onChange={handleInputChangeEditDivorcedStatus}
                name="UnMarriedCertficateIssuer"
              />
              <LoanTextField
                id="CourtDecision"
                label="CourtDecision"
                value={editDivorcedInfo.CourtDecision}
                onChange={handleInputChangeEditDivorcedStatus}
                name="CourtDecision"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewDivorcedStatus;
