    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addAccountDetials = (data, CompanyId) => {
  debugger;
  return api.post(`/company/${CompanyId}/AccountDetail`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const deleteAccountDetailsById = (CompanyId) => {
  return api.delete(`/company/AccountDetail/${CompanyId}`, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
