    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const updateAccountDetailById = (Id, data) => {
  debugger;
  return api.put(`/company/AccountDetail/${Id}`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};

const getCreditFacilityUtilizition = (Id) => {
  return api.get(`/AccountDetail/${Id}/CreditFacilityUtilization`, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const addCreditFacilityUtilizition = (Id, data) => {
  debugger;
  return api.put(`/AccountDetail/${Id}/CreditFacilityUtili`, data, {
    headers: {
      Authorization: `bearer ${localStorage.getItem("token")}`,
      "Content-Type": "application/json",
    },
  });
};
const AccountDetailSrvice = {
  getAccountDetial,
  addAccountDetials,
  deleteAccountDetailsById,
  getAccountDetailById,
  updateAccountDetailById,
  getCreditFacilityUtilizition,
  addCreditFacilityUtilizition,
};
export default AccountDetailSrvice;
