import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { IconName } from "react-icons/fa";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import { IoTrashOutline } from "react-icons/io5";
import { useParams, useNavigate } from "react-router-dom";
import CompanyNavigation from "../../../Common/CompanyNavigation";
import LoanIconButton from "../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../Common/CompanyModal";
import { Link } from "react-router-dom";
import CommonServices from "../../Common/CommonServices";
import AddCreditFacilityUtilizition from "./AddCreditFacilityUtilizition";
import ViewPreshipmentFacility from "./preshipmentFacility/ViewPreshipmentFacility";
import CreditFacilityUtilizitionService from "./CreditFacilityUtilizitionService";
import PreshipmentFacilityService from "./preshipmentFacility/PreshipmentFacilityService";
import OverDraftFacilityService from "./overDraftFacility/OverDraftFacilityService";
import ViewOverDraftFacility from "./overDraftFacility/ViewOverDraftFacility";
import LCFacilityService from "./LCFacility/LCFacilityService";
import ViewLCFacility from "./LCFacility/ViewLCFacility";
import LGFacilityService from "./LGFacility/LGFacilityService";
import ViewLGFacility from "./LGFacility/ViewLGFacility";
import TermLoanService from "./TermLoan/TermLoanService";
import ViewTermLoan from "./TermLoan/ViewTermLoan";
import MerchandiseFacilityService from "./merchandiseFacility/MerchandiseFacilityService";
import ViewMerchandiseFacility from "./merchandiseFacility/ViewMerchandiseFacility";
const ViewCreditFacilityUtilizition = ({
  isCompanySoleOtherBankModalOpen,
  handleCompanySoleOtherBankCloseModal,
  creditFacilityUtilizition,
  accountDetialId,
}) => {
  const [soleAndPartnershipErrorLoading, setSoleAndPartnershipErrorLoading] =
    useState(false);
  const [
    CreditFacilityUtilizitionErrorLoading,
    setCreditFacilityUtilizitionErrorLoading,
  ] = useState(false);
  const navigate = useNavigate();
  const { companyId } = useParams();
  const [loanTypeId, setLoanTypeId] = useState([]);
  const [creditFacilityUtilizitionId, setCreditFacilityUtilizitionId] =
    useState([]);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [
    deleteCreditFacilityUtilizitionError,
    setCreditFacilityUtilizitionDeleteError,
  ] = useState(null);
  const [error, setError] = useState(null);

  const [
    isCreditFacilityUtilizitionEditModalOpen,
    setIsCreditFacilityUtilizitionEditModalOpen,
  ] = useState(false);
  function handleCreditFacilityUtilizitionEditOpenModal() {
    setIsCreditFacilityUtilizitionEditModalOpen(true);
  }
  function handleCreditFacilityUtilizitionEditCloseModal() {
    setIsCreditFacilityUtilizitionEditModalOpen(false);
  }
  // function handleCreditFacilityUtilizitionCloseModal() {
  //   setIsCreditFacilityUtilizitionModalOpen(false);
  // }
  // function handleCreditFacilityUtilizitionOpenModal() {
  //   setIsCreditFacilityUtilizitionModalOpen(true);
  // }
  function handleCreditFacilityUtilizitionEditCloseModal() {
    setIsCreditFacilityUtilizitionEditModalOpen(false);
  }
  const handleInputChangeCreditFacilityUtilizition = (event) => {
    const { name, value } = event.target;
    setCreditFacilityUtilizitions({
      ...creditFacilityUtilizitions,
      [name]: value,
    });
  };
  const initialCreditFacilityUtilizitionState = {
    Id: null,
    LoanTypeId: "",
    LoanAccount: "",
    BorrowingRshipLength: "",
  };
  const [creditFacilityUtilizitions, setCreditFacilityUtilizitions] = useState(
    initialCreditFacilityUtilizitionState
  );
  const newCreditFacilityUtilizition = () => {
    setCreditFacilityUtilizitions(initialCreditFacilityUtilizitionState);
    // setSubmitted(false);
  };
  const saveCreditFacilityUtilizition = () => {
    var data = {
      LoanTypeId: loanTypeId,
      LoanAccount: creditFacilityUtilizitions.LoanAccount,
      BorrowingRshipLength: creditFacilityUtilizitions.BorrowingRshipLength,
    };
    CreditFacilityUtilizitionService.addCreditFacilityUtilizition(
      data,
      accountDetialId
    )
      .then((response) => {
        setCreditFacilityUtilizitions({
          Id: response.data.Id,
          LoanTypeId: response.data.LoanTypeId,
          LoanAccount: response.data.LoanAccount,
          BorrowingRshipLength: response.data.BorrowingRshipLength,
        });
        if (response.data.Code === 200) {
          setCreditFacilityUtilizitions(initialCreditFacilityUtilizitionState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          creditFacilityUtilizition();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "CreditFacilityUtilizition",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (creditFacilityUtilizitions) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${creditFacilityUtilizitions.LoanAccount}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deleteCreditFacilityUtilizition",
      });
      CreditFacilityUtilizitionService.deleteCreditFacilityUtilizitionsById(
        creditFacilityUtilizitions.Id
      )
        .then(() => {
          creditFacilityUtilizition((previousData) =>
            previousData.filter(
              (comp) => comp.Id !== creditFacilityUtilizitions.Id
            )
          );
        })
        .catch((e) => {
          setCreditFacilityUtilizitionDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editCreditFacilityUtilizition, setEditCreditFacilityUtilizition] =
    useState({
      //  Id: editAddresAccountDetailId,
      LoanTypeId: "",
      LoanAccount: "",
      BorrowingRshipLength: "",
    });
  const getCreditFacilityUtilizitionId = (editCreditFacilityUtilizitionId) => {
    CreditFacilityUtilizitionService.getCreditFacilityUtilizitionById(
      editCreditFacilityUtilizitionId
    )
      .then((res) => {
        setEditCreditFacilityUtilizition({
          ...editCreditFacilityUtilizition,
          Id: editCreditFacilityUtilizitionId,
          LoanTypeId: res.data.Result.LoanTypeId,
          LoanAccount: res.data.Result.LoanAccount,
          BorrowingRshipLength: res.data.Result.BorrowingRshipLength,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateCreditFacilityUtilizitionById = (e) => {
    e.preventDefault();
    CreditFacilityUtilizitionService.updateCreditFacilityUtilizitionById(
      creditFacilityUtilizitionId,
      editCreditFacilityUtilizition
    )
      .then((response) => {
        if (response.data.Code === 200) {
          creditFacilityUtilizition();
          toast.success("Edited successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCreditFacilityUtilizitionSuccess",
          });
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateCreditFacilityUtilizitionSuccess",
        });
      });
  };

  const [loanTypeIdForCreditUtilization, setLoanTypeIdForCreditUtilization] =
    useState([]);
  const getLoanType = () => {
    setTimeout(() => {
      CommonServices.getLoanType()
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setLoanTypeIdForCreditUtilization(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getLoanType();
  }, []);

  const [isPreshipmentFacilityModalOpen, setIsPreshipmentFacilityModalOpen] =
    useState(false);
  function handlePreshipmentFacilityOpenModal() {
    setIsPreshipmentFacilityModalOpen(true);
  }
  function handlePreshipmentFacilityCloseModal() {
    setIsPreshipmentFacilityModalOpen(false);
  }
  const [preshipmentFacility, setPreshipmentFacility] = useState([]);
  const getPreshipmentFacility = (Id) => {
    setTimeout(() => {
      PreshipmentFacilityService.getAllPreshipmentByUtilizationId(Id)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setPreshipmentFacility(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  //over draft start
  const [isOverDraftFacilityModalOpen, setIsOverDraftFacilityModalOpen] =
    useState(false);
  function handleOverDraftFacilityOpenModal() {
    setIsOverDraftFacilityModalOpen(true);
  }
  function handleOverDraftFacilityCloseModal() {
    setIsOverDraftFacilityModalOpen(false);
  }
  const [overDraftFacility, setOverDraftFacility] = useState([]);
  const getOverDraftFacility = (Id) => {
    setTimeout(() => {
      OverDraftFacilityService.getAllOverDraftByUtilizationId(Id)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setOverDraftFacility(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  //LC Facility
  const [isLCFacilityModalOpen, setIsLCFacilityModalOpen] = useState(false);
  function handleLCFacilityOpenModal() {
    setIsLCFacilityModalOpen(true);
  }
  function handleLCFacilityCloseModal() {
    setIsLCFacilityModalOpen(false);
  }
  const [LCFacility, setLCFacility] = useState([]);
  const getLCFacility = (Id) => {
    setTimeout(() => {
      LCFacilityService.getAllLCByUtilizationId(Id)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setLCFacility(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  //LG Facility
  const [isLGFacilityModalOpen, setIsLGFacilityModalOpen] = useState(false);
  function handleLGFacilityOpenModal() {
    setIsLGFacilityModalOpen(true);
  }
  function handleLGFacilityCloseModal() {
    setIsLGFacilityModalOpen(false);
  }
  const [LGFacility, setLGFacility] = useState([]);
  const getLGFacility = (Id) => {
    setTimeout(() => {
      LGFacilityService.getAllLGByUtilizationId(Id)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setLGFacility(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  //Term loan
  const [isTermLoanModalOpen, setIsTermLoanModalOpen] = useState(false);
  function handleTermLoanOpenModal() {
    setIsTermLoanModalOpen(true);
  }
  function handleTermLoanCloseModal() {
    setIsTermLoanModalOpen(false);
  }
  const [termLoan, setTermLoan] = useState([]);
  const getTermLoan = (Id) => {
    setTimeout(() => {
      TermLoanService.getAllTermLoanByUtilizationId(Id)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setTermLoan(response.data.Result);
            setCreditFacilityUtilizitionErrorLoading(false);
          } else {
            setCreditFacilityUtilizitionErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit Facility Utilizition not found");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditFacilityUtilizitionErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
   //merchandise
   const [isMerchandiseFacilityModalOpen, setIsMerchandiseFacilityModalOpen] = useState(false);
   function handleMerchandiseFacilityOpenModal() {
     setIsMerchandiseFacilityModalOpen(true);
   }
   function handleMerchandiseFacilityCloseModal() {
     setIsMerchandiseFacilityModalOpen(false);
   }
   const [MerchandiseFacility, setMerchandiseFacility] = useState([]);
   const getMerchandiseFacility = (Id) => {
     setTimeout(() => {
       MerchandiseFacilityService.getAllMerchandiseByUtilizationId(Id)
         .then((response) => {
           if (
             response.data.Code === 200 ||
             response.data.Message === "Success"
           ) {
             setMerchandiseFacility(response.data.Result);
             setCreditFacilityUtilizitionErrorLoading(false);
           } else {
             setCreditFacilityUtilizitionErrorLoading(false);
             setError("Something went wrong");
           }
         })
         .catch((error) => {
           if (error.response.data.StatusCode === 404) {
             setError("Credit Facility Utilizition not found");
             setCreditFacilityUtilizitionErrorLoading(false);
           } else if (error.response.data.StatusCode === 400) {
             setError("Something went wrong");
             setCreditFacilityUtilizitionErrorLoading(false);
           } else if (error.response.data.StatusCode === 401) {
             navigate("/login");
           } else if (error.response.data.StatusCode === 403) {
             navigate("/login");
           }
         });
     }, 1000);
   };
  return (
    <div>
      <CompanyModal
        isOpen={isCompanySoleOtherBankModalOpen}
        onClose={handleCompanySoleOtherBankCloseModal}
        title=" credit utilazition"
      >
        <div className="px-4 py-3 ">
          <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll">
            {/* <div className="bg-gray-100 flex items-center justify-center h-screen"> */}
            <div className="w-flex flex-col bg-gray-800w-96 py-8 px-4 text-center">
              <div>
                <h4 className="text-lg font-semibold eading-tight p-2"></h4>
                <ViewMerchandiseFacility
                  isMerchandiseFacilityModalOpen={isMerchandiseFacilityModalOpen}
                  handleMerchandiseFacilityOpenModal={handleMerchandiseFacilityOpenModal}
                  handleMerchandiseFacilityCloseModal={handleMerchandiseFacilityCloseModal}
                  MerchandiseFacility={MerchandiseFacility}
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                />
                <ViewTermLoan
                  isTermLoanModalOpen={isTermLoanModalOpen}
                  handleTermLoanOpenModal={handleTermLoanOpenModal}
                  handleTermLoanCloseModal={handleTermLoanCloseModal}
                  termLoan={termLoan}
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                />
                <ViewLGFacility
                  isLGFacilityModalOpen={isLGFacilityModalOpen}
                  handleLGFacilityOpenModal={handleLGFacilityOpenModal}
                  handleLGFacilityCloseModal={handleLGFacilityCloseModal}
                  LGFacility={LGFacility}
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                />
                <ViewLCFacility
                  isLCFacilityModalOpen={isLCFacilityModalOpen}
                  handleLCFacilityOpenModal={handleLCFacilityOpenModal}
                  handleLCFacilityCloseModal={handleLCFacilityCloseModal}
                  LCFacility={LCFacility}
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                />
                <ViewOverDraftFacility
                  isOverDraftFacilityModalOpen={isOverDraftFacilityModalOpen}
                  handleOverDraftFacilityOpenModal={
                    handleOverDraftFacilityOpenModal
                  }
                  handleOverDraftFacilityCloseModal={
                    handleOverDraftFacilityCloseModal
                  }
                  overDraftFacility={overDraftFacility}
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                />
                <ViewPreshipmentFacility
                  isPreshipmentFacilityModalOpen={
                    isPreshipmentFacilityModalOpen
                  }
                  handlePreshipmentFacilityCloseModal={
                    handlePreshipmentFacilityCloseModal
                  }
                  setIsPreshipmentFacilityModalOpen={
                    setIsPreshipmentFacilityModalOpen
                  }
                  creditFacilityUtilizitionId={creditFacilityUtilizitionId}
                  preshipmentFacility={preshipmentFacility}
                />
                <AddCreditFacilityUtilizition
                  newAccountDetials={newCreditFacilityUtilizition}
                  creditFacilityUtilizition={creditFacilityUtilizition}
                  handleInputChangeCreditFacilityUtilizition={
                    handleInputChangeCreditFacilityUtilizition
                  }
                  setLoanTypeId={setLoanTypeId}
                  loanTypeIdForCreditUtilization={
                    loanTypeIdForCreditUtilization
                  }
                  saveCreditFacilityUtilizition={saveCreditFacilityUtilizition}
                />
                <div>
                  {error && <p className="text-red-500">{error}</p>}
                  {!error && soleAndPartnershipErrorLoading ? (
                    <span>
                      <FaSpinner
                        className="animate-spin rounded-full h-6 w-6 text-purple-600"
                        fontSize="small"
                        data-toggle="tooltip"
                        title="Click it to sign in"
                      />
                      Loading...
                    </span>
                  ) : (
                    !error &&
                    !soleAndPartnershipErrorLoading && (
                      <div className="overflow-x-auto">
                        <table className="min-w-full table table-bordered table-striped bg-purple-100">
                          <thead>
                            <tr className="bg-purple-200">
                              <th className="px-2 py-4"> LoanTypeId </th>
                              <th className="px-2 py-4"> LoanAccount </th>
                              <th className="px-2 py-4">
                                {" "}
                                BorrowingRshipLength{" "}
                              </th>
                              <th className="px-2 py-4"> Action </th>
                            </tr>
                          </thead>
                          <tbody>
                            {creditFacilityUtilizition.length === 0 ? (
                              <p className="text-red-600 p-4">Data not found</p>
                            ) : (
                              creditFacilityUtilizition.map(
                                (FacilityUtilizitions, index) => (
                                  <tr
                                    key={index}
                                    className="bg-white
                    border-b border-purple-200"
                                  >
                                    {/* <td className="p-2">
                                      {FacilityUtilizitions.Id}
                                    </td> */}
                                    <td className="p-2">
                                      {FacilityUtilizitions.LoanType}
                                    </td>
                                    <td className="p-2">
                                      {FacilityUtilizitions.LoanAccount}
                                    </td>
                                    <td className="p-2">
                                      {
                                        FacilityUtilizitions.BorrowingRshipLength
                                      }
                                    </td>

                                    <td className="p-2">
                                    <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" Merchandise facility"
                                          onClick={() => {
                                            handleMerchandiseFacilityOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getTermLoan(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                    <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" Term Loan"
                                          onClick={() => {
                                            handleTermLoanOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getTermLoan(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                      <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" LG Facility"
                                          onClick={() => {
                                            handleLGFacilityOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getLGFacility(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                      <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" LC Facility"
                                          onClick={() => {
                                            handleLCFacilityOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getLCFacility(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                      <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" Over draft Facility"
                                          onClick={() => {
                                            handleOverDraftFacilityOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getOverDraftFacility(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                      <>
                                        <LoanIconButton
                                          style={{ color: "green" }}
                                          text=" preshipment Facility"
                                          onClick={() => {
                                            handlePreshipmentFacilityOpenModal();
                                            setCreditFacilityUtilizitionId(
                                              FacilityUtilizitions.Id
                                            );
                                            getPreshipmentFacility(
                                              FacilityUtilizitions.Id
                                            );
                                          }}
                                        >
                                          <ToastContainer />
                                        </LoanIconButton>
                                      </>
                                      <LoanIconButton
                                        style={{ color: "green" }}
                                        icon={<MdModeEdit />}
                                        text="Edit"
                                        onClick={() => {
                                          handleCreditFacilityUtilizitionEditOpenModal();
                                          getCreditFacilityUtilizitionId(
                                            FacilityUtilizitions.Id
                                          );
                                          setCreditFacilityUtilizitionId(
                                            FacilityUtilizitions.Id
                                          );
                                        }}
                                      />
                                    </td>
                                  </tr>
                                )
                              )
                            )}
                          </tbody>
                        </table>
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCreditFacilityUtilizitionEditModalOpen}
        onClose={handleCreditFacilityUtilizitionEditCloseModal}
        title="Modify account detail information"
      >
        <div>
          <form onSubmit={updateCreditFacilityUtilizitionById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="countrylbl" color="secondary">
                      Loan Type{" "}
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) => {
                        editCreditFacilityUtilizition.LoanTypeId =
                          event.target.value;
                        setLoanTypeId(event.target.value);
                      }}
                      labelId="countrylbl"
                      id="countrySelect"
                      label="Country"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {loanTypeIdForCreditUtilization.map((yourLoan) => (
                        <MenuItem
                          key={yourLoan.Id}
                          LoanTypeId={yourLoan.Id}
                          value={yourLoan.Id}
                        >
                          {yourLoan.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <TextField
                    id="LoanAccount"
                    label="LoanAccount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditFacilityUtilizition.LoanAccount}
                    onChange={(e) =>
                      setEditCreditFacilityUtilizition({
                        ...editCreditFacilityUtilizition,
                        LoanAccount: e.target.value,
                      })
                    }
                    name="CustomerId"
                  />
                  <TextField
                    id="BorrowingRshipLength"
                    label="BorrowingRshipLength"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditFacilityUtilizition.BorrowingRshipLength}
                    onChange={(e) =>
                      setEditCreditFacilityUtilizition({
                        ...editCreditFacilityUtilizition,
                        BorrowingRshipLength: e.target.value,
                      })
                    }
                    name="CustomerId"
                  />
                </div>
                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                  >
                    Update
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewCreditFacilityUtilizition;
