import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import CommonServices from "../../Common/CommonServices";
const ViewMaritalStatus = ({
  holdMarried,
  guarantorId,
  isCompanyMartailStatusModalOpen,
  handleCompanyMartailStatusCloseModal,
  handleMaritalStatusModalOpen,
}) => {
  const [
    isCompanyMarriedInformationEditModalOpen,
    setIsCompanyMarriedInformationEditModalOpen,
  ] = useState(false);
  function handleCompanyMarriedEditCloseModal() {
    setIsCompanyMarriedInformationEditModalOpen(false);
  }
  function handleCompanyMarriedInformationEditOpenModal() {
    setIsCompanyMarriedInformationEditModalOpen(true);
  }
  function handleCompanyMarriedInformationEditCloseModal() {
    setIsCompanyMarriedInformationEditModalOpen(false);
  }
  //married tax
  const [isCompanyMarriedTaxModalOpen, setIsCompanyMarriedTaxModalOpen] =
    useState(false); 
  //end married tax
  const [taxInformationId, setTaxInformationId] = useState();
  const [marriedTaxId, setMarriedTaxId] = useState();
  const [editMarriedInfo, setEditMarriedInfo] = useState({
    Id: guarantorId,
    SpouseName: "",
    MarriageCertificate: "",
    MarriageCertificateIssuDate: "",
    TaxClerance: {
      Name: "",
      TinNo: "",
      Certificate: "",
      IssueDate: "",
      Expireddate: "",
      TaxDebit: "",
    },
  });  
  const handleInputChangeEditMarriedStatus = (event) => {
    const { name, value } = event.target;
    setEditMarriedInfo({ ...editMarriedInfo, [name]: value });
  };
  const handleInputChangeEditTaxClearance = (event) => {
    const { name, value } = event.target;
    setEditMarriedInfo({
      ...editMarriedInfo,
      TaxClerance: {
        ...editMarriedInfo.TaxClerance,
        [name]: value,
      },
    });
  };
  const updateMarriedById = (taxInformationId) => {
    CommonServices.getGuarantorMarried(taxInformationId)
      .then((res) => {
        setEditMarriedInfo({
          ...editMarriedInfo,
          SpouseName: res.data.Result.SpouseName,
          MarriageCertificate: res.data.Result.MarriageCertificate,
          MarriageCertificateIssuDate: res.data.Result.MarriageCertificateIssuDate,
          TaxClerance: {
            Name: res.data.Result.TaxClerance.Name,
            TinNo: res.data.Result.TaxClerance.TinNo,
            Certificate: editMarriedInfo.TaxClerance.Certificate,
            IssueDate: editMarriedInfo.TaxClerance.IssueDate,
            Expireddate: editMarriedInfo.TaxClerance.Expireddate,
            TaxDebit: editMarriedInfo.TaxClerance.TaxDebit,
          },
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateMarriedById();
  }, []);

  

  const updateMarriedInfo = () => {
    CommonServices.updateGuarantorMarried(taxInformationId, editMarriedInfo)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateMarriedInfo();
  }, []);
  //add 
  const [toggleGuarantorMarried, setToggleGuarantorMarried] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const initialGuarantorMarriedState = {
    Id: null,
    SpouseName: "",
    MarriageCertificate: "",
    MarriageCertificateIssuDate: "",
    TaxClerance: {
      Name: "",
      TinNo: "",
      Certificate: "",
      IssueDate: "",
      Expireddate: "",
      TaxDebit: "",
    },
  };
  const [guarantorMarried, setGuarantorMarried] = useState(
    initialGuarantorMarriedState
  );
  const handleInputChangeGuarantorMarried = (event) => {
    const { name, value } = event.target;
    setGuarantorMarried({ ...guarantorMarried, [name]: value });
  };
  const handleInputChangeTaxClearance = (event) => {
    const { name, value } = event.target;
    setGuarantorMarried({
      ...guarantorMarried,
      TaxClerance: {
        ...guarantorMarried.TaxClerance,
        [name]: value,
      },
    });
  };
  const saveGuarantorMarried = () => {
    var data = {
      SpouseName: guarantorMarried.SpouseName,
      MarriageCertificate: guarantorMarried.MarriageCertificate,
      MarriageCertificateIssuDate: guarantorMarried.MarriageCertificateIssuDate,
      TaxClerance: {
        Name: guarantorMarried.TaxClerance.Name,
        TinNo: guarantorMarried.TaxClerance.TinNo,
        Certificate: guarantorMarried.TaxClerance.Certificate,
        IssueDate: guarantorMarried.TaxClerance.IssueDate,
        Expireddate: guarantorMarried.TaxClerance.Expireddate,
        TaxDebit: guarantorMarried.TaxClerance.TaxDebit,
      },
    };
    CommonServices.addGuarantorMarried(guarantorId, data)
      .then((response) => {
        setGuarantorMarried({
          Id: response.data.Id,
          SpouseName: response.data.SpouseName,
          MarriageCertificate: response.data.MarriageCertificate,
          MarriageCertificateIssuDate:
            response.data.MarriageCertificateIssuDate,
          TaxClerance: {
            Name: response.data.TaxClerance.Name,
            TinNo: response.data.TaxClerance.TinNo,
            Certificate: response.data.TaxClerance.Certificate,
            IssueDate: response.data.TaxClerance.IssueDate,
            Expireddate: response.data.TaxClerance.Expireddate,
            TaxDebit: response.data.TaxClerance.TaxDebit,
          },
        });
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMartailStatusModalOpen}
        onClose={handleCompanyMartailStatusCloseModal}
        title="Add marital status information"      >
        <div className="rounded-lg overflow-hidden">       
          {holdMarried.TaxClerance ? (
           <table className="table table-bordered table-striped bg-purple-100">
           <thead>
             <th className="px-2 py-4">SpouseName</th>
             <th className="px-2 py-4">Marriage Certificate</th>
             <th className="px-2 py-4">Marriage Certificate Issu Date</th>
             <th className="px-2 py-4"> Tax Name</th>
             <th className="px-2 py-4"> TinNo</th>
             <th className="px-2 py-4"> Certificate</th>
             <th className="px-2 py-4"> Issue Date</th>
             <th className="px-2 py-4"> Expired date</th>
             <th className="px-2 py-4"> Tax Debit</th>
             <th className="px-2 py-4"> Action </th>
           </thead>
           <tbody>
             <>
               <tr>
                 <td>{holdMarried.Id}</td>
                 <td>{holdMarried.SpouseName}</td>
                 <td>{holdMarried.MarriageCertificate}</td>
                 <td>{holdMarried.MarriageCertificateIssuDate}</td>
                 <td>{holdMarried.TaxClerance.Name}</td>
                 <td>{holdMarried.TaxClerance.TinNo}</td>
                 <td>{holdMarried.TaxClerance.Certificate}</td>
                 <td>{holdMarried.TaxClerance.IssueDate}</td>
                 <td>{holdMarried.TaxClerance.Expireddate}</td>
                 <td>{holdMarried.TaxClerance.TaxDebit}</td>
                 <td>
                   <button
                     onClick={() => {
                         handleCompanyMarriedInformationEditOpenModal();
                         updateMarriedById(holdMarried.Id);
                         setTaxInformationId(holdMarried.Id);
                     }}
                     className="text-purple-600 rounded-md font-semibold "
                   >
                     Edit
                   </button>
                 </td>
               </tr>
             </>
             {/* ))} */}
           </tbody>
         </table>
          ) : (
            ""
          )}
          <button
            onClick={() => setToggleGuarantorMarried(!toggleGuarantorMarried)}
            className="text-purple-600 rounded-md font-medium "
          >
            Add guarantor
          </button>
          {toggleGuarantorMarried && (
            <div className="submit-form">
              {submitted ? (
                <div>
                  <h4>You submitted successfully!</h4>
                </div>
              ) : (
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <LoanTextField
                    id="SpouseName"
                    label="SpouseName"
                    value={guarantorMarried.SpouseName}
                    onChange={handleInputChangeGuarantorMarried}
                    name="SpouseName"
                  />
                  <LoanTextField
                    id="MarriageCertificate"
                    label="MarriageCertificate"
                    value={guarantorMarried.MarriageCertificate}
                    onChange={handleInputChangeGuarantorMarried}
                    name="MarriageCertificate"
                  />
                  <LoanTextField
                    id="MarriageCertificateIssuDate"
                    label="MarriageCertificateIssuDate"
                    value={guarantorMarried.MarriageCertificateIssuDate}
                    onChange={handleInputChangeGuarantorMarried}
                    name="MarriageCertificateIssuDate"
                  />
                  <LoanTextField
                    id="Name"
                    label="Name"
                    value={guarantorMarried.TaxClerance.Name}
                    onChange={handleInputChangeTaxClearance}
                    name="Name"
                  />
                  <LoanTextField
                    id="TinNo"
                    label="TinNo"
                    value={guarantorMarried.TaxClerance.TinNo}
                    onChange={handleInputChangeTaxClearance}
                    name="TinNo"
                  />
                  <LoanTextField
                    id="Certificate"
                    label="Certificate"
                    value={guarantorMarried.TaxClerance.Certificate}
                    onChange={handleInputChangeTaxClearance}
                    name="Certificate"
                  />
                  <LoanTextField
                    id="IssueDate"
                    label="IssueDate"
                    value={guarantorMarried.TaxClerance.IssueDate}
                    onChange={handleInputChangeTaxClearance}
                    name="IssueDate"
                  />
                  <LoanTextField
                    id="Expireddate"
                    label="Expireddate"
                    value={guarantorMarried.TaxClerance.Expireddate}
                    onChange={handleInputChangeTaxClearance}
                    name="Expireddate"
                  />
                  <LoanTextField
                    id="TaxDebit"
                    label="TaxDebit"
                    value={guarantorMarried.TaxClerance.TaxDebit}
                    onChange={handleInputChangeTaxClearance}
                    name="TaxDebit"
                  />
                </div>
              )}
              <div>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => {
                    saveGuarantorMarried();
                  }}
                >
                  save
                </button>
              </div>
            </div>
          )}
        </div>        
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyMarriedInformationEditModalOpen}
        onClose={handleCompanyMarriedEditCloseModal}
        title="Married edit information"
      >
        <div>
          <form onSubmit={updateMarriedInfo}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="SpouseName"
                label="SpouseName"
                value={editMarriedInfo.SpouseName}
                onChange={handleInputChangeEditMarriedStatus}
                name="SpouseName"
              />
              <LoanTextField
                id="MarriageCertificate"
                label="MarriageCertificate"
                value={editMarriedInfo.MarriageCertificate}
                onChange={handleInputChangeEditMarriedStatus}
                name="MarriageCertificate"
              />
              <LoanTextField
                id="MarriageCertificateIssuDate"
                label="MarriageCertificateIssuDate"
                value={editMarriedInfo.MarriageCertificateIssuDate}
                onChange={handleInputChangeEditMarriedStatus}
                name="MarriageCertificateIssuDate"
              />
              <LoanTextField
                id="Name"
                label="Name"
                value={editMarriedInfo.TaxClerance.Name}
                onChange={handleInputChangeEditTaxClearance}
                name="Name"
              />
              
              <LoanTextField
                id="TinNo"
                label="TinNo"
                value={editMarriedInfo.TaxClerance.TinNo}
                onChange={handleInputChangeEditTaxClearance}
                name="TinNo"
              />
              <LoanTextField
                id="Certificate"
                label="Certificate"
                value={editMarriedInfo.TaxClerance.Certificate}
                onChange={handleInputChangeEditTaxClearance}
                name="Certificate"
              />
              <LoanTextField
                id="IssueDate"
                label="IssueDate"
                value={editMarriedInfo.TaxClerance.IssueDate}
                onChange={handleInputChangeEditTaxClearance}
                name="IssueDate"
              />
              <LoanTextField
                id="Expireddate"
                label="Expireddate"
                value={editMarriedInfo.TaxClerance.Expireddate}
                onChange={handleInputChangeEditTaxClearance}
                name="Expireddate"
              />
              <LoanTextField
                id="Expireddate"
                label="Expireddate"
                value={editMarriedInfo.TaxClerance.Expireddate}
                onChange={handleInputChangeEditTaxClearance}
                name="Expireddate"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewMaritalStatus;
