import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import { useState, useEffect } from "react";
import LoanIconButton from "../../../../Common/LoanIconButton";
import CompanyModal from "../../../Common/CompanyModal";
// import CompanyModal from "../../../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { IoRefresh } from "react-icons/io5";
// import LoanTextField from "../../../Common/LoanTextField";
import CommonServices from "../../../Common/CommonServices";
import LoanTextField from "../../../../Common/LoanTextField";
const AddOverDraftFacility = ({
  submitted,
  handleBack,
  handleSkip,
  handleNext,  
  saveOverDraftFacility,  
  overDraftFacilitys,
  handleInputChangeOverDraftFacility,
  setOverDraftFacilitys,
}) => {
  const [isOverDraftModalOpen, setIsOverDraftModalOpen] =
    useState(false);
  function handleOverDraftOpenModal() {
    setIsOverDraftModalOpen(true);
  }
  function handlOverDraftCloseModal() {
    setIsOverDraftModalOpen(false);
  }
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [salesContractDate, setSalesContractDate] = useState(new Date());
  const [shipmentDate, setShipmentDate] = useState(new Date());
  const [lOT1ShipmentDate, setLOT1ShipmentDate] = useState(new Date());
  const [LOT1IncomingDate, setLOT1IncomingDate] = useState(new Date());
  const [LOT1DisbursementDate, setLOT1DisbursementDate] = useState(new Date());
  const [currency, setCurrency] = useState([]);
  const [country, setCountry] = useState([]);
  const [sectorType, setSectorType] = useState([]);
  const getCurrency = () => {
    CommonServices.getCurrency()
      .then((response) => {
        setCurrency(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getCurrency();
  }, []);
  const getCountry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setCountry(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getCountry();
  }, []);
  const getSectorType = () => {
    CommonServices.sector()
      .then((response) => {
        setSectorType(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getSectorType();
  }, []);
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleOverDraftOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={isOverDraftModalOpen}
        onClose={handlOverDraftCloseModal}
        title="Add over draft "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="ApprovedFacilityLimit"
              label="ApprovedFacilityLimit"
              value={overDraftFacilitys.ApprovedFacilityLimit}
              onChange={handleInputChangeOverDraftFacility}
              name="ApprovedFacilityLimit"
            />           
            <p>
              Start Date:
              <input
                id="StartDate"
                name="StartDate"
                label="StartDate"
                placeholder="StartDate"
                type="date"
                value={
                  startDate.getFullYear().toString() +
                  "-" +
                  (startDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  startDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setStartDate(new Date(e.target.value));
                  overDraftFacilitys.StartDate = startDate;
                }}
              />{" "}
            </p>
            <p>
            Expired Date:
              <input
                id="ExpiredDate"
                name="ExpiredDate"
                label="ExpiredDate"
                placeholder="ExpiredDate"
                type="date"
                value={
                  maturityDate.getFullYear().toString() +
                  "-" +
                  (maturityDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  maturityDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setMaturityDate(new Date(e.target.value));
                  overDraftFacilitys.ExpiredDate = maturityDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="Year"
              label="Year"
              value={overDraftFacilitys.Year}
              onChange={handleInputChangeOverDraftFacility}
              name="Year"
            />
            <LoanTextField
              id="Month"
              label="Month"
              value={preshipmentFacilitys.Month}
              onChange={handleInputChangePreshipmentFacility}
              name="Month"
            />           
            <LoanTextField
              id="HighestDebitBalance"
              label="HighestDebitBalance"
              value={preshipmentFacilitys.HighestDebitBalance}
              onChange={handleInputChangePreshipmentFacility}
              name="HighestDebitBalance"
            />            
            <LoanTextField
              id="HighestCreditBalance"
              label="HighestCreditBalance"
              value={preshipmentFacilitys.HighestCreditBalance}
              onChange={handleInputChangePreshipmentFacility}
              name="HighestCreditBalance"
            />  
            <LoanTextField
              id="LowestDebitBalance"
              label="LowestDebitBalance"
              value={preshipmentFacilitys.LowestDebitBalance}
              onChange={handleInputChangePreshipmentFacility}
              name="LowestDebitBalance"
            />
            <LoanTextField
              id="LowestCreditBalance"
              label="LowestCreditBalance"
              value={preshipmentFacilitys.LowestCreditBalance}
              onChange={handleInputChangePreshipmentFacility}
              name="LowestCreditBalance"
            />           
            <LoanTextField
              id="NoOfDaysInCreditBalanceInMonth"
              label="NoOfDaysInCreditBalanceInMonth"
              value={preshipmentFacilitys.NoOfDaysInCreditBalanceInMonth}
              onChange={handleInputChangePreshipmentFacility}
              name="NoOfDaysInCreditBalanceInMonth"
            />
            <LoanTextField
              id="TotalDebitTransactionBalanceInMonth"
              label="TotalDebitTransactionBalanceInMonth"
              value={preshipmentFacilitys.TotalDebitTransactionBalanceInMonth}
              onChange={handleInputChangePreshipmentFacility}
              name="TotalDebitTransactionBalanceInMonth"
            />           
            <LoanTextField
              id="TotalCreditTransactionBalanceInMonth"
              label="TotalCreditTransactionBalanceInMonth"
              value={preshipmentFacilitys.TotalCreditTransactionBalanceInMonth}
              onChange={handleInputChangePreshipmentFacility}
              name="TotalCreditTransactionBalanceInMonth"
            />
            <LoanTextField
              id="TurnoverPerPeriod"
              label="TurnoverPerPeriod"
              value={preshipmentFacilitys.TurnoverPerPeriod}
              onChange={handleInputChangePreshipmentFacility}
              name="TurnoverPerPeriod"
            />
              <LoanTextField
              id="NoOfDaysInCreditBalanceInPeriod"
              label="NoOfDaysInCreditBalanceInPeriod"
              value={preshipmentFacilitys.NoOfDaysInCreditBalanceInPeriod}
              onChange={handleInputChangePreshipmentFacility}
              name="NoOfDaysInCreditBalanceInPeriod"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                savePreshipmentFacility();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddOverDraftFacility;
