import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../../Common/LoanIconButton";
import CompanyModal from "../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
import LoanTextField from "../../../Common/LoanTextField";
const AddCreditPerformance = ({
  // submitted,
  // handleBack,
  handleSkip,
  // handleNext,
  // newAccountDetials,
  creditPerformances,
  handleInputChangeCreditPerformance,
  saveCreditPerformance,
  loanTypeIdForPerformance,
  setLoanTypeId,
  currency
}) => {
  const [isCreditPerformanceModalOpen, setIsCreditPerformanceModalOpen] =
    useState(false);
  function handleCreditPerformanceOpenModal() {
    setIsCreditPerformanceModalOpen(true);
  }
  function handlCreditPerformanceCloseModal() {
    setIsCreditPerformanceModalOpen(false);
  }
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );

  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCreditPerformanceOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={isCreditPerformanceModalOpen}
        onClose={handlCreditPerformanceCloseModal}
        title="Add handle Credit Facility Utilizition "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="LoanAccount"
              label="LoanAccount"
              value={creditPerformances.LoanAccount}
              onChange={handleInputChangeCreditPerformance}
              name="LoanAccount"
            />
            <FormControl variant="standard" sx={{ minWidth: 120 }}>
              <InputLabel id="LoanTypeId" color="secondary">
                Loan Type{" "}
              </InputLabel>
              <Select
                color="secondary"
                onChange={(event) => {
                  creditPerformances.LoanTypeId = event.target.value;
                  setLoanTypeId(event.target.value);
                }}
                labelId="LoanTypeId"
                id="LoanTypeId"
                label="LoanTypeId"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {loanTypeIdForPerformance.map((yourLoan) => (
                  <MenuItem
                    key={yourLoan.Id}
                    LoanTypeId={yourLoan.Id}
                    value={yourLoan.Id}
                  >
                    {yourLoan.Name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <LoanTextField
              id="ApprovedAmount"
              label="ApprovedAmount"
              value={creditPerformances.ApprovedAmount}
              onChange={handleInputChangeCreditPerformance}
              name="ApprovedAmount"
            />
            {/* ApprovalDate */}
            <p>
              Approval Date:
              <input
                id="ApprovalDate"
                name="ApprovalDate"
                label="ApprovalDate"
                placeholder="ApprovalDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  creditPerformances.ApprovalDate = approvalDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="TotalCommitment"
              label="TotalCommitment"
              value={creditPerformances.TotalCommitment}
              onChange={handleInputChangeCreditPerformance}
              name="TotalCommitment"
            />
            {/* CurrencyId
            StartDate
            
            MaturityDate */}
            <p>
              Start Date:
              <input
                id="StartDate"
                name="StartDate"
                label="StartDate"
                placeholder="StartDate"
                type="date"
                value={
                  startDate.getFullYear().toString() +
                  "-" +
                  (startDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  startDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setStartDate(new Date(e.target.value));
                  creditPerformances.StartDate = startDate;
                }}
              />{" "}
            </p>
            <p>
              Maturity Date:
              <input
                id="MaturityDate"
                name="MaturityDate"
                label="MaturityDate"
                placeholder="MaturityDate"
                type="date"
                value={
                  maturityDate.getFullYear().toString() +
                  "-" +
                  (maturityDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  maturityDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setMaturityDate(new Date(e.target.value));
                  creditPerformances.MaturityDate = maturityDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="RepaymentFrequency"
              label="RepaymentFrequency"
              value={creditPerformances.RepaymentFrequency}
              onChange={handleInputChangeCreditPerformance}
              name="RepaymentFrequency"
            />
            {/* ScheduledPayment             */}
            <p>
              Scheduled Payment:
              <input
                id="ScheduledPayment"
                name="ScheduledPayment"
                label="ScheduledPayment"
                placeholder="ScheduledPayment"
                type="date"
                value={
                  scheduledPayment.getFullYear().toString() +
                  "-" +
                  (scheduledPayment.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  scheduledPayment.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setScheduledPayment(new Date(e.target.value));
                  creditPerformances.ScheduledPayment = scheduledPayment;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="LoanStatus"
              label="LoanStatus"
              value={creditPerformances.LoanStatus}
              onChange={handleInputChangeCreditPerformance}
              name="LoanStatus"
            />
            <LoanTextField
              id="Restructuring"
              label="Restructuring"
              value={creditPerformances.Restructuring}
              onChange={handleInputChangeCreditPerformance}
              name="Restructuring"
            />
            <LoanTextField
              id="LoanAccountRestructured"
              label="LoanAccountRestructured"
              value={creditPerformances.LoanAccountRestructured}
              onChange={handleInputChangeCreditPerformance}
              name="LoanAccountRestructured"
            />
            {/* SystemAdjustmentDate4WaiverAndExtension */}
            <p>
              System Adjustment Date Waiver And Extension:
              <input
                id="SystemAdjustmentDate4WaiverAndExtension"
                name="SystemAdjustmentDate4WaiverAndExtension"
                label="SystemAdjustmentDate4WaiverAndExtension"
                placeholder="SystemAdjustmentDate4WaiverAndExtension"
                type="date"
                value={
                  systemAdjustmentDate4WaiverAndExtension
                    .getFullYear()
                    .toString() +
                  "-" +
                  (systemAdjustmentDate4WaiverAndExtension.getMonth() + 1)
                    .toString()
                    .padStart(2, 0) +
                  "-" +
                  systemAdjustmentDate4WaiverAndExtension
                    .getDate()
                    .toString()
                    .padStart(2, 0)
                }
                onChange={(e) => {
                  setSystemAdjustmentDate4WaiverAndExtension(
                    new Date(e.target.value)
                  );
                  creditPerformances.SystemAdjustmentDate4WaiverAndExtension =
                    systemAdjustmentDate4WaiverAndExtension;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="NoOfScheduledPrincipalPaymentWaived"
              label="NoOfScheduledPrincipalPaymentWaived"
              value={creditPerformances.NoOfScheduledPrincipalPaymentWaived}
              onChange={handleInputChangeCreditPerformance}
              name="NoOfScheduledPrincipalPaymentWaived"
            />
            <LoanTextField
              id="NoOfScheduledRepaymentWaived"
              label="NoOfScheduledRepaymentWaived"
              value={creditPerformances.NoOfScheduledRepaymentWaived}
              onChange={handleInputChangeCreditPerformance}
              name="NoOfScheduledRepaymentWaived"
            />
            {/* AdjustmentMaturityDate */}
            <p>
              System Adjustment Date Waiver And Extension:
              <input
                id="AdjustmentMaturityDate"
                name="AdjustmentMaturityDate"
                label="AdjustmentMaturityDate"
                placeholder="AdjustmentMaturityDate"
                type="date"
                value={
                  adjustmentMaturityDate.getFullYear().toString() +
                  "-" +
                  (adjustmentMaturityDate.getMonth() + 1)
                    .toString()
                    .padStart(2, 0) +
                  "-" +
                  adjustmentMaturityDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setAdjustmentMaturityDate(new Date(e.target.value));
                  creditPerformances.AdjustmentMaturityDate =
                    adjustmentMaturityDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="PrincipalOutstandingAmount"
              label="PrincipalOutstandingAmount"
              value={creditPerformances.PrincipalOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="PrincipalOutstandingAmount"
            />
            <LoanTextField
              id="InterestOutstandingAmount"
              label="InterestOutstandingAmount"
              value={creditPerformances.InterestOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="InterestOutstandingAmount"
            />
            <LoanTextField
              id="TotalOutstandingAmountPerLoanType"
              label="TotalOutstandingAmountPerLoanType"
              value={creditPerformances.TotalOutstandingAmountPerLoanType}
              onChange={handleInputChangeCreditPerformance}
              name="TotalOutstandingAmountPerLoanType"
            />
            <LoanTextField
              id="PrincipalArrears"
              label="PrincipalArrears"
              value={creditPerformances.PrincipalArrears}
              onChange={handleInputChangeCreditPerformance}
              name="PrincipalArrears"
            />
            <LoanTextField
              id="InterestArrears"
              label="InterestArrears"
              value={creditPerformances.InterestArrears}
              onChange={handleInputChangeCreditPerformance}
              name="InterestArrears"
            />
            <LoanTextField
              id="TotalArrears"
              label="TotalArrears"
              value={creditPerformances.TotalArrears}
              onChange={handleInputChangeCreditPerformance}
              name="TotalArrears"
            />
            <LoanTextField
              id="TotalApprovedLoanAmount"
              label="TotalApprovedLoanAmount"
              value={creditPerformances.TotalApprovedLoanAmount}
              onChange={handleInputChangeCreditPerformance}
              name="TotalApprovedLoanAmount"
            />
            <LoanTextField
              id="TotalOutstandingAmount"
              label="TotalOutstandingAmount"
              value={creditPerformances.TotalOutstandingAmount}
              onChange={handleInputChangeCreditPerformance}
              name="TotalOutstandingAmount"
            />
            <LoanTextField
              id="CrossSelling"
              label="CrossSelling"
              value={creditPerformances.CrossSelling}
              onChange={handleInputChangeCreditPerformance}
              name="CrossSelling"
            />
            <LoanTextField
              id="NPLStatus"
              label="NPLStatus"
              value={creditPerformances.NPLStatus}
              onChange={handleInputChangeCreditPerformance}
              name="NPLStatus"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveCreditPerformance();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddCreditPerformance;
