import { useState, useEffect } from "react";
import LoanIconButton from "../../Common/LoanIconButton";
import { BiSkipPrevious, BiSkipNext } from "react-icons/bi";
import { BsPlus } from "react-icons/bs";
import CompanyModal from "../Common/CompanyModal";
import LoanTextField from "../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import CommonServices from "../Common/CommonServices";
const AddSoleAndPartnership = ({
  handleSkip,
  saveSoleAndPartnership,
  soleAndPartnership,
  handleInputChangeSoleAndPartnership,
  country,
  proofOfIdentityType,
  setMartailStatusHold,
  maritalStatus,
  setSelectedPathIdentity,
}) => {
  const [fromDate, setFromDate] = useState(new Date());
  const [proofOfIdentityExpiredDate, setProofOfIdentityExpiredDate] = useState(new Date());
  const [selectedFiles, setSelectedFiles] = useState(undefined);

  const [currentFile, setCurrentFile] = useState(undefined);

  const [fileInfos, setFileInfos] = useState([]);

  const [progress, setProgress] = useState(0);

  const [message, setMessage] = useState("");

  const selectFileIdentity = (event) => {
    setSelectedFiles(event.target.files);
  };

  const uploadIdentity = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };

  //end upload

  const [
    isCompanySoleAndPartnershipModalOpen,
    setIsCompanySoleAndPartnershipModalOpen,
  ] = useState(false);

  function handleCompanySoleAndPartnershipModalOpen() {
    setIsCompanySoleAndPartnershipModalOpen(true);
  }
  function handleCompanySoleAndPartnershipCloseModal() {
    setIsCompanySoleAndPartnershipModalOpen(false);
  }
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCompanySoleAndPartnershipModalOpen}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
        <CompanyModal
          isOpen={isCompanySoleAndPartnershipModalOpen}
          onClose={handleCompanySoleAndPartnershipCloseModal}
          title="Add company sole and partnership information"
        >
          <div>
            <form onSubmit={saveSoleAndPartnership}>
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <LoanTextField
                  id="FirstName"
                  label="First Name"
                  value={soleAndPartnership.FirstName}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="FirstName"
                />
                <LoanTextField
                  id="MiddleName"
                  label="Middle Name"
                  value={soleAndPartnership.MiddleName}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="MiddleName"
                />
                <LoanTextField
                  id="LastName"
                  label="Last Name"
                  value={soleAndPartnership.LastName}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="LastName"
                />
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Nationality
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleAndPartnership.NationalityID = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Country Of Origin{" "}
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleAndPartnership.CountryOfOriginID =
                        event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Country Of Residence
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleAndPartnership.CountryOfResidenceID =
                        event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Proof Of Identity Type{" "}
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleAndPartnership.ProofOfIdentityTypeID =
                        event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {proofOfIdentityType.map((yourProofOfIdentityType) => (
                      <MenuItem
                        key={yourProofOfIdentityType.Id}
                        ProofOfIdentityTypeID={yourProofOfIdentityType.Id}
                        value={yourProofOfIdentityType.Id}
                      >
                        {yourProofOfIdentityType.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                {/* <LoanTextField
                  id="UploadProofOfIdentity"
                  label="Upload Proof Of Identity"
                  value={soleAndPartnership.UploadProofOfIdentity}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="UploadProofOfIdentity"
                /> */}
               
                <div>
                  {currentFile && (
                    <div className="progress">
                      <div
                        className="progress-bar progress-bar-info progress-bar-striped"
                        role="progressbar"
                        aria-valuenow={progress}
                        aria-valuemin="0"
                        aria-valuemax="100"
                        style={{ width: progress + "%" }}
                      >
                        {progress}%
                      </div>
                    </div>
                  )}

                  <label className="btn btn-default">
                    <input type="file" onChange={selectFileIdentity} />
                  </label>

                  <button
                    className="btn btn-success"
                    disabled={!selectedFiles}
                    onClick={uploadIdentity}
                  >
                    Upload Identity
                  </button>

                  <div className="alert alert-light" role="alert">
                    {message}
                  </div>
                </div>
                {/* <LoanTextField
                  id="ProofOfIdentityIssuedDate"
                  label="Proof Of Identity Issued Date"
                  value={soleAndPartnership.ProofOfIdentityIssuedDate}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="ProofOfIdentityIssuedDate"
                /> */}
                {/* <input
                  id="ProofOfIdentityIssuedDate"
                  type="date"
                  label="ProofOfIdentityIssuedDate"
                  name="ProofOfIdentityIssuedDate"                  
                  onChange={(event) =>
                    (soleAndPartnership.ProofOfIdentityIssuedDate =
                      event.target.value
                    )
                  }/> */}
                   <p>Proof Of IdentityIssuedDate: 
             <input
        id="ProofOfIdentityIssuedDate"
        name="ProofOfIdentityIssuedDate"
        label ="ProofOfIdentityIssuedDate"
        placeholder ="ProofOfIdentityIssuedDate"
        type="date"
        value={
          fromDate.getFullYear().toString() +  "-" + (fromDate.getMonth() + 1).toString().padStart(2, 0) + "-" + fromDate.getDate().toString().padStart(2, 0)
        }
        onChange={(e) => {
          setFromDate(new Date(e.target.value));
          soleAndPartnership.ProofOfIdentityIssuedDate = fromDate;
        }}
      />   </p>
      <p>Proof Of Identity Expired Date: 
             <input
        id="ProofOfIdentityExpiredDate"
        name="ProofOfIdentityExpiredDate"
        label ="ProofOfIdentityExpiredDate"
        placeholder ="ProofOfIdentityExpiredDate"
        type="date"
        value={
          proofOfIdentityExpiredDate.getFullYear().toString() +  "-" + (proofOfIdentityExpiredDate.getMonth() + 1).toString().padStart(2, 0) + "-" + proofOfIdentityExpiredDate.getDate().toString().padStart(2, 0)
        }
        onChange={(e) => {
          setProofOfIdentityExpiredDate(new Date(e.target.value));
          soleAndPartnership.ProofOfIdentityExpiredDate = proofOfIdentityExpiredDate;
        }}
      />   </p>
               <LoanTextField
                  id="ProofOfIdentityExpiredDate"
                  label="Proof Of Identity ExpiredDate"
                  value={soleAndPartnership.ProofOfIdentityExpiredDate}
                  onChange={handleInputChangeSoleAndPartnership}
                  name="ProofOfIdentityExpiredDate"
                />
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Martail Status
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) => {
                      soleAndPartnership.MartailStatusTypeId =
                        event.target.value;
                      setMartailStatusHold(
                        (soleAndPartnership.MartailStatusTypeId =
                          event.target.value)
                      );
                    }}
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {maritalStatus.map((maritalSt) => (
                      <MenuItem
                        key={maritalSt.Id}
                        NationalityID={maritalSt.Id}
                        value={maritalSt.Id}
                      >
                        <button>{maritalSt.Name}</button>
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </div>
              <div className="flex justify-center pt-4">
                {/* <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={handleBack}
                >
                  Back
                </button> */}
                <button
                  type="submit"
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  save
                </button>
              </div>
            </form>
          </div>
        </CompanyModal>
      </div>
    </>
  );
};

export default AddSoleAndPartnership;
