import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { registerSchema } from "../Account/AccountValidation";
import { yupResolver } from "@hookform/resolvers/yup";
import { useForm } from "react-hook-form";
const SignUp = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        FirstName: "",
        MiddleName: "",
        LastName: "",
        PhoneNumber: "",
        Password: "",
        Email: "",
        Otp: "",
        token: "",
    });
    const {
        register,       
        formState: { errors },
      } = useForm({
        resolver: yupResolver(registerSchema),
        mode: "onChange", // enable on-typing validation
      });
    const [errorMessage, setErrorMessage] = useState("");
    const [isValid, setIsValid] = useState(false);
    const [showPopup, setShowPopup] = useState(false);

    const handleSendOtpClick = () => {
        // Send OTP to phone number
        // After OTP is sent, you can set another state to indicate that the OTP has been sent
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateFormData()) {
            return;
        }
        try {
            const response = await axios.post(
                "https://10.3.6.135/api/v1/user",
                formData,
                {
                    // headers: {
                    //   "Content-Type": "application/json",
                    //   accept: "text/plain",
                    // },
                }
            );

            if (response.status === 200) {
                setShowPopup(true);
                console.log(response.data);
                console.log("user created successfully!");
            } else {
                console.log(response.data);
                setErrorMessage(response.data.message || "Failed to create user");
            }
        } catch (error) {
            console.error(error);
            setErrorMessage("Failed to create user");
        }
    };

    const handleOTPSubmit = async (event) => {
        event.preventDefault();
        try {
            const { PhoneNumber, OtpCode } = formData;
            const response = await axios.post(
                "https://10.3.6.135/api/v1/user/verifyOtp",
                { PhoneNumber: PhoneNumber, OtpCode: OtpCode },
                {
                    // headers: {
                    //   "Content-Type": "application/json",
                    // },
                }
            );
            if (response.status === 200) {
                console.log(response.data);
                navigate("/login");
                // setFormData({ ...formData, token: response.data.token });
                // handleSubmit(event);
            } else {
                console.log(response.data);
                setErrorMessage(response.data.message);
            }
        } catch (error) {
            setErrorMessage(error.message);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const validatePhoneNumber = (PhoneNumber) => {
        const phoneNumberRegex = /^\d{10}$/;
        return phoneNumberRegex.test(PhoneNumber);
    };

    const validateFormData = () => {
        let isValid = true;

        if (formData.FirstName.trim() === "") {
            setErrorMessage("Please enter your first name");
            isValid = false;
        }

        if (formData.LastName.trim() === "") {
            setErrorMessage("Please enter your last name");
            isValid = false;
        }

        if (formData.MiddleName.trim() === "") {
            setErrorMessage("Please enter your middle name");
            isValid = false;
        }

        if (
            formData.PhoneNumber.trim() === "" ||
            !validatePhoneNumber(formData.PhoneNumber.trim())
        ) {
            setErrorMessage("Please enter a valid phone number");
            isValid = false;
        }

        if (formData.Password.trim() === "") {
            setErrorMessage("Please enter a password");
            isValid = false;
        }

        if (formData.confirmPassword.trim() === "") {
            setErrorMessage("Please confirm your password");
            isValid = false;
        }

        if (formData.Password !== formData.confirmPassword) {
            setErrorMessage("Passwords do not match");
            isValid = false;
        }

        return isValid;
    };

    const handlePopupClose = () => {
        setShowPopup(false);
    };

    return (
        <>
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-2 lg:px-8">
                <div className="sm:mx-auto sm:w-full sm:max-w-sm">
                    <h2 className="mt-2 text-center text-2xl font-bold leading-9 tracking-tight text-gray-1000">
                        Create an account
                    </h2>
                </div>

                <div className="mt-3 sm:mx-auto sm:w-full sm:max-w-sm">
                    {errorMessage && (
                        <div
                            className="bg-red-100 border-l-4 border-red-500 text-red-700 p-2"
                            role="alert"
                        >
                            <p className="font-bold">Error:</p>
                            <p>{errorMessage}</p>
                        </div>
                    )}
                    <form className="space-y-2" onSubmit={handleSubmit}>
                        <div>
                            <label
                                htmlFor="first-name"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                First name
                            </label>
                            <div className="mt-2">
                                <input
                                    id="first-name"
                                    name="FirstName"
                                    type="text"
                                    autoComplete="name"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.FirstName}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <label
                                htmlFor="middle-name"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Middle name
                            </label>
                            <div className="mt-2">
                                <input
                                    id="middle-name"
                                    name="MiddleName"
                                    type="text"
                                    autoComplete="name"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.MiddleName}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <label
                                htmlFor="last-name"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Last name
                            </label>
                            <div className="mt-2">
                                <input
                                    id="last-name"
                                    name="LastName"
                                    type="text"
                                    autoComplete="name"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.LastName}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <label
                                htmlFor="Email"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Email
                            </label>
                            <div className="mt-2">
                                <input
                                    id="email"
                                    name="Email"
                                    type="text"
                                    autoComplete="name"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.Email}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>
                        <div className="mb-6">
          <TextField
            {...register("email")}
            sx={{
              "& > :not(style)": { mb: 1 },
            }}
            label="Email"
            variant="standard"
            onChange={handleChange}
            color="secondary"
            className={textInputClassName}
            InputProps={{
              endAdornment: isFieldValid && (
                <InputAdornment position="end">
                  <CheckCircleIcon color="success" />
                </InputAdornment>
              ),
            }}
            inputProps={register("email")}
            error={Boolean(errors.email)}
            helperText={errors.email && errors.email.message}
          />
        </div>
                        <div>
                            <label
                                htmlFor="phone-number"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Phone number
                            </label>
                            <div className="mt-2">
                                <input
                                    id="phone-number"
                                    name="PhoneNumber"
                                    type="tel"
                                    autoComplete="tel"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.PhoneNumber}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <label
                                htmlFor="password"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Password
                            </label>
                            <div className="mt-2">
                                <input
                                    id="password"
                                    name="Password"
                                    type="password"
                                    autoComplete="new-password"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    value={formData.Password}
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <label
                                htmlFor="confirm-password"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Confirm password
                            </label>
                            <div className="mt-2">
                                <input
                                    id="confirm-password"
                                    name="confirmPassword"
                                    type="password"
                                    autoComplete="new-password"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-500 focus:border-blue-500 sm:text-sm pl-2"
                                    onChange={handleChange}
                                />
                            </div>
                        </div>

                        <div>
                            <button
                                type="submit"
                                className="flex w-full justify-center rounded-md bg-fuchsia-700 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-fuchsia-900 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 "
                            >
                                Create Account
                            </button>
                        </div>
                    </form>

                    {/* {showPopup && (
            <div className="fixed z-50 inset-1 overflow-y flex items-center justify-center">
              <div className="bg-purple-500 rounded-lg w-1/2 p-8">
                
                <form onSubmit={handleOTPSubmit}>
                  <p className="text-black">
                    Verification code sent to {formData.PhoneNumber}. Please
                    enter the code below:
                  </p>
                  <div className="mt-4">
                    <label htmlFor="otp" className="tblack">
                      OTP:
                    </label>
                    <input
                      type="text"
                      id="otp"
                      name="OtpCode"
                      value={formData.OtpCode}
                      onChange={handleChange}
                      className="bg-gray-200 rounded-lg py-2 px-4 ml-4"
                    />
                  </div>
                  <div className="mt-8 flex justify-center">
                    <button
                      type="submit"
                      className="bg-yellow-500 text-black rounded-lg py-2 px-4 hover:bg-yellow-600 cursor-pointer"
                    >
                      Submit
                    </button>
                    <div className="sm:px-6 sm:flex sm:flex-row-reverse pt-3">
                    <span className="flex w-full rounded-md shadow-sm sm:ml-3 sm:w-auto">
                      <button
                        type="button"
                        className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                        onClick={handlePopupClose}
                      >
                        Close
                      </button>
                    </span>

                    <span className="mt-3 flex w-full rounded-md shadow-sm sm:mt-0 sm:w-auto">
                      <button
                        type="button"
                        className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                        onClick={handleSendOtpClick}
                      >
                        Send again
                      </button>
                    </span>
                  </div>
                    
                  </div>
                </form>
              
              </div>
            </div>
          )} */}

                    {showPopup && (
                        <div className="fixed z-50 inset-1 overflow-y flex items-center justify-center bg-gray-500 bg-opacity-25">
                            <div className="bg-white rounded-lg w-full sm:w-3/4 md:w-1/2 lg:w-1/3 p-8">
                                <form onSubmit={handleOTPSubmit}>
                                    <p className="text-black">
                                        Verification code sent to {formData.PhoneNumber}. Please
                                        enter the code below:
                                    </p>
                                    <div className="mt-4">
                                        <label htmlFor="otp" className="text-black">
                                            OTP:
                                        </label>
                                        <input
                                            type="text"
                                            id="otp"
                                            name="OtpCode"
                                            value={formData.OtpCode}
                                            onChange={handleChange}
                                            className="bg-gray-200 rounded-lg py-2 px-4 ml-4 w-full sm:w-3/4 md:w-2/3 lg:w-1/2"
                                        />
                                    </div>
                                    <div className="mt-8 flex justify-center sm:justify-between">
                                        <button
                                            type="submit"
                                            className="bg-purple-500 text-white rounded-lg py-2 px-4 hover:bg-purple-600 cursor-pointer"
                                        >
                                            Submit
                                        </button>
                                        <button
                                            type="button"
                                            className="bg-purple-500 text-white rounded-lg py-2 px-4 ml-4 hover:bg-purple-600 cursor-pointer"
                                            onClick={handleSendOtpClick}
                                        >
                                            Send again
                                        </button>
                                    </div>
                                    <div className="sm:px-6 sm:flex sm:flex-row-reverse pt-3">
                                        <span className="flex w-full rounded-md shadow-sm sm:ml-3 sm:w-auto">
                                            <button
                                                type="button"
                                                className="inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base leading-6 font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                                                onClick={handlePopupClose}
                                            >
                                                Close
                                            </button>
                                        </span>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}

                    {/* -----------------ERROR MESSAGE---------------------- */}
                    {errorMessage && <div>{errorMessage}</div>}
                </div>

                <div className="sm:mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
                    <div className="flex flex-col items-center justify-center">
                        <div className="mt-6">
                            <p className="text-sm font-medium text-gray-900">
                                Already have an account?{" "}
                                <Link
                                    to="/login"
                                    className="font-semibold leading-6 text-fuchsia-700 hover:text-yellow-500"
                                >
                                    Login
                                </Link>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default SignUp;
