import { useState, useEffect } from "react";
import {
  BsArrowLeftShort,
  BsChevronDown,
  BsPlus,
  BsSearch,
} from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link, Navigate } from "react-router-dom";
import { useParams, useNavigate } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from "@mui/material";
// import { BsArrowLeftShort, BsSearch, BsChevronDown, BsPlus } from "react-icons/bs";
import CompanyModal from "./CompanyModal";
import { IconButton } from "@mui/material";
import { BsWindowPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
const LeftNav = () => {
  let navigate = useNavigate();
  const initialCompanyState = {
    Id: null,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  };
  const [age, setAge] = useState("");
  const handleChange1 = (event) => {
    setAge(event.target.value);
  };
  const [activeStep, setActiveStep] = useState(0);
  const [open, setOpen] = useState(true);
  const [subMenuOpen, setSubMenuOpen] = useState(false);
  const [leftNav, setLeftNav] = useState();
  const [submitted, setSubmitted] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [companyTypes, setCompanyTypes] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [company, setCompany] = useState(initialCompanyState);
  //adress
  const initialAdressState = {
    Id: null,
    CountryID: "",
    RegionID: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  };
  const [address, setAddress] = useState(initialAdressState);
  //end address
  //start contact information
  const initialContactInfoState = {
    Id: null,
    DesignatedPersonFName: "",
    DesignatedPersonMName: "",
    DesignatedPersonLName: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
  };
  const [contactInfo, setContactInfo] = useState(initialContactInfoState);
  //end address
  //account Info
  const initialAccountInfoState = {
    Id: null,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    District: "",
    Other: "",
  };
  const [accountInfo, setAccountInfo] = useState(initialAccountInfoState);
  //trade information
  const initialTradeInfoState = {
    Id: null,
    TradeRegistrationNo: "",
    // UploadTradeRegCerti: "",
    TradeLicenseNo: "",
    IssueDate: "",
    RenewedDate: "",
    ValidityDate: "",
    TradeBusinessTypeID: "",
    // UploadTradeLicense: "",
  };
  const [tradeInfo, setTradeInfo] = useState(initialTradeInfoState);
  // const [country, setCountry] = useState("");
  const initialSoleAndPartnershipState = {
    Id: null,
    CompanyID: "",
    // TaxClearanceID: "",
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    MartailStatusID: "",
    // UploadTradeLicense: "",
  };
  const [soleAndPartnership, setSoleAndPartnership] = useState(
    initialSoleAndPartnershipState
  );

  const [city, setCity] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [selectedValue, setSelectedValue] = useState(null);
  //new
  const [country, setContry] = useState([]);
  const [region, setRegion] = useState([]);
  const [holdId, setHoldId] = useState([]);
  const [holdUserId, setHoldUserId] = useState([]);

  const [tradeSector, setTradeSector] = useState([]);
  const [holdTradeSectorId, setHoldTradeSectorId] = useState([]);
  const [tradeIndustryType, setTradeIndustryType] = useState([]);
  const [tradeIndustryTypeId, setTradeIndustryTypeId] = useState([]);
  const [tradeBusinessType, setTradeBusinessType] = useState([]);
  const [maritalStatus, setMaritalStatus] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCompanyModalOpen, setIsCompanyModalOpen] = useState(false);
  const [isCompanyAddressModalOpen, setIsCompanyAddressModalOpen] =
    useState(false);
  const [isCompanyContactModalOpen, setIsCompanyContactModalOpen] =
    useState(false);
  const [isCompanyAccountModalOpen, setIsCompanyAccountModalOpen] =
    useState(false);
  const [isCompanyTradeModalOpen, setIsCompanyTradeModalOpen] = useState(false);
  function handleGeneralCloseModal() {
    isCompanyTradeModalOpen(false);
  }
  const [
    isCompanySoleAndPrtinershipModalOpen,
    setIsCompanySoleAndPrtinershipModalOpen,
  ] = useState(false);
  function handleGeneralCloseModal() {
    isCompanySoleAndPrtinershipModalOpen(false);
  }
  function handleGeneralCloseModal() {
    isCompanyAccountModalOpen(false);
  }
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }
  // company modal start
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }

  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
  function handleCompanySoleAndPrtinershipOpenModal() {
    setIsCompanySoleAndPrtinershipModalOpen(true);
  }
  function handleCompanySoleAndPrtinershipCloseModal() {
    setIsCompanySoleAndPrtinershipModalOpen(false);
  }
  function handleCompanyTradeOpenModal() {
    setIsCompanyTradeModalOpen(true);
  }
  function handleCompanyTradeCloseModal() {
    setIsCompanyTradeModalOpen(false);
  }
  function handleCompanyAccountOpenModal() {
    setIsCompanyAccountModalOpen(true);
  }
  function handleCompanyAccountCloseModal() {
    setIsCompanyAccountModalOpen(false);
  }
  function handleCompanyAddressCloseModal() {
    setIsCompanyAddressModalOpen(false);
  }

  function handleCompanyAddressOpenModal() {
    setIsCompanyAddressModalOpen(true);
  }

  function handleCompanyContactCloseModal() {
    setIsCompanyContactModalOpen(false);
  }

  function handleCompanyContactOpenModal() {
    setIsCompanyContactModalOpen(true);
  }
  // company modal end
  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setCompany({ ...company, [name]: value });
  };
  let UserId=localStorage.getItem("UserId");
  const saveCompany = () => {
    var data = {
      Name: company.Name,
      NoOfBranch: company.NoOfBranch,
      EstablishmentYear: company.EstablishmentYear,
      TINNumber: company.TINNumber,
      TINCertificate: company.TINCertificate,
      CompanyTypeID: company.CompanyTypeID,
      EntityTypeID: company.EntityTypeID,
      VATNumber: company.VATNumber,
      VATCertificate: company.VATCertificate,
    };    
    loanDataService
      .addCompany(data,UserId)
      .then((response) => {
        setCompany({
          Id: response.data.Id,
          Name: response.data.Name,
          NoOfBranch: response.data.NoOfBranch,
          EstablishmentYear: response.data.EstablishmentYear,
          TINNumber: response.data.TINNumber,
          TINCertificate: response.data.TINCertificate,
          CompanyTypeID: response.data.CompanyTypeID,
          EntityTypeID: response.data.EntityTypeID,
          VATNumber: response.data.VATNumber,
          VATCertificate: response.data.VATCertificate,
        });
        setSubmitted(true);
        console.log(response.data);
        setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //contact Info
  const handleInputChangeContactInfo = (event) => {
    const { name, value } = event.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };
  const saveContactInfo = () => {
    var data = {
      DesignatedPersonFName: contactInfo.DesignatedPersonFName,
      DesignatedPersonMName: contactInfo.DesignatedPersonMName,
      DesignatedPersonLName: contactInfo.DesignatedPersonLName,
      Position: contactInfo.Position,
      MobileNo: contactInfo.MobileNo,
      Telephone: contactInfo.Telephone,
      Email: contactInfo.Email,
    };
    loanDataService
      .addContactInfo(data, holdId)
      .then((response) => {
        setContactInfo({
          Id: response.data.Id,
          DesignatedPersonFName: response.data.DesignatedPersonFName,
          DesignatedPersonMName: response.data.DesignatedPersonMName,
          DesignatedPersonLName: response.data.DesignatedPersonLName,
          Position: response.data.Position,
          MobileNo: response.data.MobileNo,
          Telephone: response.data.Telephone,
          Email: response.data.Email,
        });
        setSubmitted(true);
        console.log(response.data);
        // setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const newContactInfo = () => {
    setContactInfo(initialContactInfoState);
    setSubmitted(false);
  };
  //end contact info
  //account Info
  const handleInputChangeAccountInfo = (event) => {
    const { name, value } = event.target;
    setAccountInfo({ ...accountInfo, [name]: value });
  };
  const saveAccountInfo = () => {
    var data = {
      AccountNo: accountInfo.AccountNo,
      CustomerId: accountInfo.CustomerId,
      Branch: accountInfo.Branch,
      District: accountInfo.District,
      Other: accountInfo.Other,
    };
    loanDataService
      .addAccountInfo(data, holdId)
      .then((response) => {
        setAccountInfo({
          Id: response.data.Id,
          AccountNo: response.data.AccountNo,
          CustomerId: response.data.CustomerId,
          Branch: response.data.Branch,
          District: response.data.District,
          Other: response.data.Other,
        });
        setSubmitted(true);
        console.log(response.data);
        // setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const newAccountInfo = () => {
    setAccountInfo(initialAccountInfoState);
    setSubmitted(false);
  };
  //end Account Info
  //trade Info
  const handleInputChangeTradeInfo = (event) => {
    const { name, value } = event.target;
    setTradeInfo({ ...tradeInfo, [name]: value });
  };
  const saveTradeInfo = () => {
    var data = {
      TradeRegistrationNo: tradeInfo.TradeRegistrationNo,
