import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import { IoTrashOutline } from "react-icons/io5";
import { useParams, useNavigate } from "react-router-dom";
import CompanyNavigation from "../../../Common/CompanyNavigation";
import LoanIconButton from "../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../Common/CompanyModal";
import { Link } from "react-router-dom";
import CommonServices from "../../Common/CommonServices";
import AddCreditFacilityUtilizition from "./AddCreditPerformance";
import CreditPerformanceService from "./CreditPerformanceService";
import AddCreditPerformance from "./AddCreditPerformance";
import LoanTextField from "../../../Common/LoanTextField";
const ViewCreditPefrormance = ({
  isCreditPerformanceModalOpen,
  handleCreditPerformanceCloseModal,
  setIsCreditPerformanceModalOpen,
  accountDetialId,
  creditPerformance,
}) => {
  const [CreditPerformanceErrorLoading, setCreditPerformanceErrorLoading] =
    useState(false);
  const navigate = useNavigate();
  // const { companyId } = useParams();
  const [loanTypeId, setLoanTypeId] = useState([]);
  const [currencyId, setCurrencyId] = useState([]);
  const [creditPerformanceId, setCreditPerformanceId] = useState([]);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [deleteCreditPerformanceError, setCreditPerformanceDeleteError] =
    useState(null);
  const [error, setError] = useState(null);
  const [
    isCreditPerformanceEditModalOpen,
    setIsCreditPerformanceEditModalOpen,
  ] = useState(false);
  function handleCreditPerformanceEditOpenModal() {
    setIsCreditPerformanceEditModalOpen(true);
  }
  function handleCreditPerformanceEditCloseModal() {
    setIsCreditPerformanceEditModalOpen(false);
  }
  function handleCreditPerformanceEditOpenModal() {
    setIsCreditPerformanceEditModalOpen(true);
  }
  function handleCreditPerformanceEditCloseModal() {
    setIsCreditPerformanceEditModalOpen(false);
  }
  const handleInputChangeCreditPerformance = (event) => {
    const { name, value } = event.target;
    setCreditPerformances({
      ...creditPerformances,
      [name]: value,
    });
  };
  const initialCreditPerformanceState = {
    Id: null,
    LoanAccount: "",
    LoanTypeId: "",
    ApprovedAmount: "",
    ApprovalDate: "",
    TotalCommitment: "",
    CurrencyId: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequency: "",
    ScheduledPayment: "",
    LoanStatus: "",
    Restructuring: "",
    LoanAccountRestructured: "",
    SystemAdjustmentDate4WaiverAndExtension: "",
    NoOfScheduledPrincipalPaymentWaived: "",
    NoOfScheduledRepaymentWaived: "",
    AdjustmentMaturityDate: "",
    PrincipalOutstandingAmount: "",
    InterestOutstandingAmount: "",
    TotalOutstandingAmountPerLoanType: "",
    PrincipalArrears: "",
    InterestArrears: "",
    TotalArrears: "",
    TotalApprovedLoanAmount: "",
    TotalOutstandingAmount: "",
    CrossSelling: "",
    NPLStatus: "",
  };
  const [creditPerformances, setCreditPerformances] = useState(
    initialCreditPerformanceState
  );
  const newCreditPerformance = () => {
    setCreditPerformances(initialCreditPerformanceState);
    // setSubmitted(false);
  };
  const saveCreditPerformance = () => {
    var data = {
      LoanAccount: creditPerformances.LoanAccount,
      LoanTypeId: loanTypeId,
      ApprovedAmount: creditPerformances.ApprovedAmount,
      ApprovalDate: creditPerformances.ApprovalDate,
      TotalCommitment: creditPerformances.TotalCommitment,
      CurrencyId: creditPerformances.CurrencyId,
      StartDate: creditPerformances.StartDate,
      MaturityDate: creditPerformances.MaturityDate,
      RepaymentFrequency: creditPerformances.RepaymentFrequency,
      ScheduledPayment: creditPerformances.ScheduledPayment,
      LoanStatus: creditPerformances.LoanStatus,
      Restructuring: creditPerformances.Restructuring,
      LoanAccountRestructured: creditPerformances.LoanAccountRestructured,
      SystemAdjustmentDate4WaiverAndExtension:
        creditPerformances.SystemAdjustmentDate4WaiverAndExtension,
      NoOfScheduledPrincipalPaymentWaived:
        creditPerformances.NoOfScheduledPrincipalPaymentWaived,
      NoOfScheduledRepaymentWaived:
        creditPerformances.NoOfScheduledRepaymentWaived,
      AdjustmentMaturityDate: creditPerformances.AdjustmentMaturityDate,
      PrincipalOutstandingAmount: creditPerformances.PrincipalOutstandingAmount,
      InterestOutstandingAmount: creditPerformances.InterestOutstandingAmount,
      TotalOutstandingAmountPerLoanType:
        creditPerformances.TotalOutstandingAmountPerLoanType,
      PrincipalArrears: creditPerformances.PrincipalArrears,
      InterestArrears: creditPerformances.InterestArrears,
      TotalArrears: creditPerformances.TotalArrears,
      TotalApprovedLoanAmount: creditPerformances.TotalApprovedLoanAmount,
      TotalOutstandingAmount: creditPerformances.TotalOutstandingAmount,
      CrossSelling: creditPerformances.CrossSelling,
      NPLStatus: creditPerformances.NPLStatus,
    };
    CreditPerformanceService.addCreditPerformance(data, accountDetialId)
      .then((response) => {
        setCreditPerformances({
          Id: response.data.Id,
          LoanAccount: response.data.LoanAccount,
          LoanTypeId: response.data.LoanTypeId,
          ApprovedAmount: response.data.ApprovedAmount,
          ApprovalDate: response.data.ApprovalDate,
          TotalCommitment: response.data.TotalCommitment,
          CurrencyId: response.data.CurrencyId,
          StartDate: response.data.StartDate,
          MaturityDate: response.data.MaturityDate,
          RepaymentFrequency: response.data.RepaymentFrequency,
          ScheduledPayment: response.data.ScheduledPayment,
          LoanStatus: response.data.LoanStatus,
          Restructuring: response.data.Restructuring,
          LoanAccountRestructured: response.data.LoanAccountRestructured,
          SystemAdjustmentDate4WaiverAndExtension:
            response.data.SystemAdjustmentDate4WaiverAndExtension,
          NoOfScheduledPrincipalPaymentWaived:
            response.data.NoOfScheduledPrincipalPaymentWaived,
          NoOfScheduledRepaymentWaived:
            response.data.NoOfScheduledRepaymentWaived,
          AdjustmentMaturityDate: response.data.AdjustmentMaturityDate,
          PrincipalOutstandingAmount: response.data.PrincipalOutstandingAmount,
          InterestOutstandingAmount: response.data.InterestOutstandingAmount,
          TotalOutstandingAmountPerLoanType:
            response.data.TotalOutstandingAmountPerLoanType,
          PrincipalArrears: response.data.PrincipalArrears,
          InterestArrears: response.data.InterestArrears,
          TotalArrears: response.data.TotalArrears,
          TotalApprovedLoanAmount: response.data.TotalApprovedLoanAmount,
          TotalOutstandingAmount: response.data.TotalOutstandingAmount,
          CrossSelling: response.data.CrossSelling,
          NPLStatus: response.data.NPLStatus,
        });
        if (response.data.Code === 200) {
          setCreditPerformances(initialCreditPerformanceState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          creditPerformances();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "CreditPerformance",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (creditPerformance) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${creditPerformance.LoanAccount}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deleteCreditPerformance",
      });
      CreditPerformanceService.deleteCreditPerformanceById(creditPerformance.Id)
        .then(() => {
          creditPerformance((previousData) =>
            previousData.filter((comp) => comp.Id !== creditPerformance.Id)
          );
        })
        .catch((e) => {
          setCreditPerformanceDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editCreditPerformance, setEditCreditPerformance] = useState({
    //  Id: editAddresAccountDetailId,
    LoanAccount: "",
    LoanTypeId: "",
    ApprovedAmount: "",
    ApprovalDate: "",
    TotalCommitment: "",
    CurrencyId: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequency: "",
    ScheduledPayment: "",
    LoanStatus: "",
    Restructuring: "",
    LoanAccountRestructured: "",
    SystemAdjustmentDate4WaiverAndExtension: "",
    NoOfScheduledPrincipalPaymentWaived: "",
    NoOfScheduledRepaymentWaived: "",
    AdjustmentMaturityDate: "",
    PrincipalOutstandingAmount: "",
    InterestOutstandingAmount: "",
    TotalOutstandingAmountPerLoanType: "",
    PrincipalArrears: "",
    InterestArrears: "",
    TotalArrears: "",
    TotalApprovedLoanAmount: "",
    TotalOutstandingAmount: "",
    CrossSelling: "",
    NPLStatus: "",
  });

  const handleInputChangeEditCreditPerformance = (event) => {
    const { name, value } = event.target;
    setEditCreditPerformance({
      ...editCreditPerformance,
      [name]: value,
    });
  };

  const getCreditPerformanceId = (editCreditPerformanceId) => {
    CreditPerformanceService.getCreditPerformanceById(editCreditPerformanceId)
      .then((res) => {
        setEditCreditPerformance({
          ...editCreditPerformance,
          Id: editCreditPerformanceId,
          LoanAccount: res.data.Result.LoanAccount,
          LoanTypeId: res.data.Result.LoanTypeId,
          ApprovedAmount: res.data.Result.ApprovedAmount,
          ApprovalDate: res.data.Result.ApprovalDate,
          TotalCommitment: res.data.Result.TotalCommitment,
          CurrencyId: res.data.Result.CurrencyId,
          StartDate: res.data.Result.StartDate,
          MaturityDate: res.data.Result.MaturityDate,
          RepaymentFrequency: res.data.Result.RepaymentFrequency,
          ScheduledPayment: res.data.Result.ScheduledPayment,
          LoanStatus: res.data.Result.LoanStatus,
          Restructuring: res.data.Result.Restructuring,
          LoanAccountRestructured: res.data.Result.LoanAccountRestructured,
          SystemAdjustmentDate4WaiverAndExtension:
            res.data.Result.SystemAdjustmentDate4WaiverAndExtension,
          NoOfScheduledPrincipalPaymentWaived:
            res.data.Result.NoOfScheduledPrincipalPaymentWaived,
          NoOfScheduledRepaymentWaived:
            res.data.Result.NoOfScheduledRepaymentWaived,
          AdjustmentMaturityDate: res.data.Result.AdjustmentMaturityDate,
          PrincipalOutstandingAmount:
            res.data.Result.PrincipalOutstandingAmount,
          InterestOutstandingAmount: res.data.Result.InterestOutstandingAmount,
          TotalOutstandingAmountPerLoanType:
            res.data.Result.TotalOutstandingAmountPerLoanType,
          PrincipalArrears: res.data.Result.PrincipalArrears,
          InterestArrears: res.data.Result.InterestArrears,
          TotalArrears: res.data.Result.TotalArrears,
          TotalApprovedLoanAmount: res.data.Result.TotalApprovedLoanAmount,
          TotalOutstandingAmount: res.data.Result.TotalOutstandingAmount,
          CrossSelling: res.data.Result.CrossSelling,
          NPLStatus: res.data.Result.NPLStatus,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateCreditFacilityUtilizitionById = (e) => {
    e.preventDefault();
    CreditPerformanceService.updateCreditPerformanceById(
      creditPerformanceId,
      editCreditPerformance
    )
      .then((response) => {
        if (response.data.Code === 200) {
          creditPerformance();
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const [loanTypeIdForPerformance, setLoanTypeIdForPerformance] = useState([]);
  const getLoanType = () => {
    setTimeout(() => {
      CommonServices.getLoanType()
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setLoanTypeIdForPerformance(response.data.Result);
            setCreditPerformanceErrorLoading(false);
          } else {
            setCreditPerformanceErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit performance not found");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getLoanType();
  }, []);
  const [currency, setCurrency] = useState([]);
  const getCurrency = () => {
    setTimeout(() => {
      CommonServices.getCurrency()
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setCurrency(response.data.Result);
          } else {
            setCreditPerformanceErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit performance not found");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getCurrency();
  }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCreditPerformanceModalOpen}
        onClose={handleCreditPerformanceCloseModal}
        title=" Credit utilazition"
      >
        <div className="px-4 py-3">
          <div className="flex flex-wrap">
            <div className="w-full">
              <div>
                <h4 className="text-lg font-semibold eading-tight p-2"></h4>
                <AddCreditFacilityUtilizition
              handleSkip={handleSkip}
              creditPerformances={creditPerformances}
              handleInputChangeCreditPerformance={handleInputChangeCreditPerformance}
              saveCreditPerformance={saveCreditPerformance}
              loanTypeIdForPerformance={loanTypeIdForPerformance}
              setLoanTypeId={setLoanTypeId}
              currency={currency}
              setCurrencyId={setCurrencyId}
              />
                <div>
                  {/* {error && <p className="text-red-500">{error}</p>}
                  {!error && setCreditPerformanceErrorLoading ? (
                   <span>
                   <FaSpinner
                     className="animate-spin rounded-full h-6 w-6 text-purple-600"
                     fontSize="small"
                     data-toggle="tooltip"
                     title="Click it to sign in"
                   />
                   Loading...
                 </span>
                  ) : (
                    !error &&
                    !setCreditPerformanceErrorLoading && ( */}
                  <div className="overflow-x-auto">
                    <table className="min-w-full table table-bordered table-striped bg-purple-100">
                      <thead>
                        <tr className="bg-purple-200">
                          <th className="px-2 py-4"> Loan Account</th>
                          <th className="px-2 py-4"> Loan Type </th>
                          <th className="px-2 py-4"> Approved Amount </th>
                          <th className="px-2 py-4"> Approval Date </th>
                          <th className="px-2 py-4"> Total Commitment </th>
                          <th className="px-2 py-4"> Currency </th>
                          <th className="px-2 py-4"> Start Date </th>
                          <th className="px-2 py-4"> Maturity Date </th>
                          <th className="px-2 py-4"> Repayment Frequency </th>
                          <th className="px-2 py-4"> Scheduled Payment </th>
                          <th className="px-2 py-4"> Loan Status </th>
                          <th className="px-2 py-4"> Restructuring </th>
                          <th className="px-2 py-4">
                            {" "}
                            Loan Account Restructured{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            System Adjustment Date Waiver And Extension{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            No Of Scheduled Principal Payment Waived{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            No Of Scheduled Repayment Waived{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Adjustment Maturity Date{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Principal Outstanding Amount{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Interest Outstanding Amount{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Outstanding Amount Per Loan Type{" "}
                          </th>
                          <th className="px-2 py-4"> Principal Arrears </th>
                          <th className="px-2 py-4"> Interest Arrears </th>
                          <th className="px-2 py-4"> Total Arrears </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Approved Loan Amount{" "}
                          </th>
