  const [LCFacilityErrorLoading, setLCFacilityErrorLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [expiredDate, setExpiredDate] = useState(new Date());
  const [PDLD, setPDLD] = useState(new Date());
  const [deleteLCFacilityError, setLCFacilityDeleteError] = useState(null);
  const [error, setError] = useState(null);
  const [isLCFacilityEditModalOpen, setIsLCFacilityEditModalOpen] =
    useState(false);
  function handleLCFacilityEditOpenModal() {
    setIsLCFacilityEditModalOpen(true);
  }
  function handleLCFacilityEditCloseModal() {
    setIsLCFacilityEditModalOpen(false);
  }
  function handleLCFacilityEditOpenModal() {
    setIsLCFacilityEditModalOpen(true);
  }
  function handleLCFacilityEditCloseModal() {
    setIsLCFacilityEditModalOpen(false);
  }
  const [LCFacilityId, setLCFacilityId] = useState(null);
  const handleInputChangeLCFacility = (event) => {
    const { name, value } = event.target;
    setLCFacilitys({
      ...LCFacilitys,
      [name]: value,
    });
  };
  const initialLCFacilityState = {
    Id: null,
    ApprovalDate: "",
    ApprovedAmount: "",
    ExpiredDate: "",
    DayPastDue: "",
    PDLD: "",
    OutstandingBalance: "",
  };
  const [LCFacilitys, setLCFacilitys] = useState(initialLCFacilityState);
  const newLCFacility = () => {
    setLCFacilitys(initialLCFacilityState);
    // setSubmitted(false);
  };
  const saveLCFacility = () => {
    var data = {
      ApprovalDate: LCFacilitys.ApprovalDate,
      ApprovedAmount: LCFacilitys.ApprovedAmount,
      ExpiredDate: LCFacilitys.ExpiredDate,
      DayPastDue: LCFacilitys.DayPastDue,
      PDLD: LCFacilitys.PDLD,
      OutstandingBalance: LCFacilitys.OutstandingBalance,
    };
    LCFacilityService.addLCFacility(data, creditFacilityUtilizitionId)
      .then((response) => {
        setLCFacilitys({
          Id: response.data.Id,
          ApprovalDate: response.data.ApprovalDate,
          ApprovedAmount: response.data.ApprovedAmount,
          ExpiredDate: response.data.ExpiredDate,
          DayPastDue: response.data.DayPastDue,
          PDLD: response.data.PDLD,
          OutstandingBalance: response.data.OutstandingBalance,
        });
        if (response.data.Code === 200) {
          setLCFacilitys(initialLCFacilityState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          LCFacility();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "PreshipmentFacility",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (LCFacility) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${LCFacility.Id}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deletePreshipmentFacility",
      });
      LCFacilityService.deleteLCFacilityById(LCFacility.Id)
        .then(() => {
          LCFacility((previousData) =>
            previousData.filter((comp) => comp.Id !== LCFacility.Id)
          );
        })
        .catch((e) => {
          setLCFacilityDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editLCFacility, setEditLCFacility] = useState({
    //  Id: editAddresAccountDetailId,
    ApprovalDate: "",
    ApprovedAmount: "",
    ExpiredDate: "",
    DayPastDue: "",
    PDLD: "",
    OutstandingBalance: "",
  });

  const handleInputChangeEditLCFacility = (event) => {
    const { name, value } = event.target;
    setEditLCFacility({
      ...editLCFacility,
      [name]: value,
    });
  };

  const getLCFacilityId = (editLCFacilityId) => {
    LCFacilityService.getLcFacilityById(editLCFacilityId) //here is
      .then((res) => {
        setEditLCFacility({
          ...editLCFacility,
          Id: editLCFacilityId,
          ApprovalDate: res.data.Result.ApprovalDate,
          ApprovedAmount: res.data.Result.ApprovedAmount,
          ExpiredDate: res.data.Result.ExpiredDate,
          DayPastDue: res.data.Result.DayPastDue,
          PDLD: res.data.Result.PDLD,
          OutstandingBalance: res.data.Result.OutstandingBalance,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateLCFacilityById = (e) => {
    e.preventDefault();
    LCFacilityService.updateLCFacilityById(LCFacilityId, editLCFacility)
      .then((response) => {
        if (response.data.Code === 200) {
          LCFacility();
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <CompanyModal
        isOpen={isLCFacilityModalOpen}
        onClose={handleLCFacilityCloseModal}
        title=" over draft Facility"
      >
        <div className="px-4 py-3 ">
          <div className="w-flex flex-col bg-gray-800w-96 py-8 px-4 text-center">
            <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll">
              {/* <div className="bg-gray-100 flex items-center justify-center h-screen"> */}
              <div>
                <h4 className="text-lg font-semibold eading-tight p-2"></h4>
                <LCFacility
                  handleInputChangeLCFacility={handleInputChangeLCFacility}
                  saveLCFacility={saveLCFacility}
                  LCFacilitys={LCFacilitys}
                  setLCFacilitys={setLCFacilitys}
                />
                <div>
                  {error && <p className="text-red-500">{error}</p>}
                  {!error && LCFacilityErrorLoading ? (
                    <span>
                      <FaSpinner
                        className="animate-spin rounded-full h-6 w-6 text-purple-600"
                        fontSize="small"
                        data-toggle="tooltip"
                        title="Click it to sign in"
                      />
                      Loading...
                    </span>
                  ) : (
                    !error &&
                    !LCFacilityErrorLoading && (
                      <div className="overflow-x-auto">
                        <ToastContainer />
                        <table className="min-w-full table table-bordered table-striped bg-purple-100">
                          <thead>
                            <tr className="bg-purple-200">
                              <th className="px-2 py-4"> Approval Date</th>
                              <th className="px-2 py-4"> Approved Amount </th>
                              <th className="px-2 py-4"> Expired Date</th>
                              <th className="px-2 py-4"> Day Past Due </th>
                              <th className="px-2 py-4"> PDLD </th>
                              <th className="px-2 py-4">
                                {" "}
                                Outstanding Balance{" "}
                              </th>
                              <th className="px-2 py-4"> Action </th>
                            </tr>
                          </thead>
                          <tbody>
                            {LCFacility.length === 0 ? (
                              <p className="text-red-600 p-4">Data not found</p>
                            ) : (
                              LCFacility.map((LcFacilitys, index) => (
                                <tr
                                  key={index}
                                  className="bg-white
                    border-b border-purple-200"
                                >
                                  <td className="p-2">
                                    {LcFacilitys.ApprovalDate}
                                  </td>
                                  <td className="p-2">
                                    {LcFacilitys.ApprovedAmount}
                                  </td>
                                  <td className="p-2">
                                    {LcFacilitys.ExpiredDate}
                                  </td>
                                  <td className="p-2">
                                    {LcFacilitys.DayPastDue}
                                  </td>
                                  <td className="p-2">{LcFacilitys.PDLD}</td>
                                  <td className="p-2">
                                    {LcFacilitys.OutstandingBalance}
                                  </td>
                                  <td className="p-2">
                                    <>
                                      <LoanIconButton
                                        style={{ color: "green" }}
                                        icon={<MdModeEdit />}
                                        text="Edit"
                                        onClick={() => {
                                          handleLCFacilityEditOpenModal();
                                          getLCFacilityId(LcFacilitys.Id);
                                          setLCFacilityId(LcFacilitys.Id);
                                        }}
                                      />
                                    </>
                                    <>
                                      <LoanIconButton
                                        style={{ color: "red" }}
                                        text=" Delete"
                                        onClick={() => {
                                          handleDelete(LcFacilitys);
                                        }}
                                      >
                                        <ToastContainer />
                                      </LoanIconButton>
                                    </>
                                  </td>
                                </tr>
                              ))
                            )}
                          </tbody>
                        </table>
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isLCFacilityEditModalOpen}
        onClose={handleLCFacilityEditCloseModal}
        title="Modify account detail information"
      >
        <div>
          <ToastContainer />
          <form onSubmit={updateLCFacilityById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <p>
                    Approval Date
                    <input
                      id="ApprovalDate"
                      name="ApprovalDate"
                      label="ApprovalDate"
                      placeholder="ApprovalDate"
                      type="date"
                      value={
                        approvalDate.getFullYear().toString() +
                        "-" +
                        (approvalDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        approvalDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setApprovalDate(new Date(e.target.value));
                        editLCFacility.ApprovalDate = approvalDate;
                      }}
                    />{" "}
                  </p>
                  <LoanTextField
                    id="ApprovedAmount"
                    label="ApprovedAmount"
                    value={editLCFacility.ApprovedAmount}
                    onChange={handleInputChangeEditLCFacility}
                    name="ApprovedAmount"
                  />
                  <p>
                    Expired Date:
                    <input
                      id="ExpiredDate"
                      name="ExpiredDate"
                      label="ExpiredDate"
                      placeholder="ExpiredDate"
                      type="date"
                      value={
                        expiredDate.getFullYear().toString() +
                        "-" +
                        (expiredDate.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        expiredDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setExpiredDate(new Date(e.target.value));
                        editLCFacility.ExpiredDate = expiredDate;
                      }}
                    />{" "}
                  </p>

                  <LoanTextField
                    id="DayPastDue"
                    label="DayPastDue"
                    value={editLCFacility.DayPastDue}
                    onChange={handleInputChangeEditLCFacility}
                    name="DayPastDue"
                  />
                  <p>
                    PDLD:
                    <input
                      id="PDLD"
                      name="PDLD"
                      label="PDLD"
                      placeholder="PDLD"
                      type="date"
                      value={
                        PDLD.getFullYear().toString() +
                        "-" +
                        (PDLD.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        PDLD.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setPDLD(new Date(e.target.value));
                        editLCFacility.PDLD = PDLD;
                      }}
                    />{" "}
                  </p>
                  <LoanTextField
                    id="OutstandingBalance"
                    label="OutstandingBalance"
                    value={editLCFacility.OutstandingBalance}
                    onChange={handleInputChangeEditLCFacility}
                    name="OutstandingBalance"
                  />
                </div>
                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                  >
                    Update
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewLCFacility;
