import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import LoanIconButton from "../../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../../Common/CompanyModal";
import LoanTextField from "../../../../Common/LoanTextField";
import LCFacilityService from "./LGFacilityService";
import AddLCFacility from "./AddLGFacility";
import LGFacilityService from "./LGFacilityService";
import AddLGFacility from "./AddLGFacility";
const ViewLGFacility = ({
  isLGFacilityModalOpen,
  handleLGFacilityOpenModal,
  handleLGFacilityCloseModal,
  LGFacility,
  creditFacilityUtilizitionId,
}) => {
  const [LGFacilityErrorLoading, setLGFacilityErrorLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [expiredDate, setExpiredDate] = useState(new Date());
  const [PDLD, setPDLD] = useState(new Date());
  const [deleteLGFacilityError, setLGFacilityDeleteError] = useState(null);
  const [error, setError] = useState(null);
  const [isLGFacilityEditModalOpen, setIsLGFacilityEditModalOpen] =
    useState(false);
  function handleLGFacilityEditOpenModal() {
    setIsLGFacilityEditModalOpen(true);
  }
  function handleLGFacilityEditCloseModal() {
    setIsLGFacilityEditModalOpen(false);
  }
  function handleLGFacilityEditOpenModal() {
    setIsLGFacilityEditModalOpen(true);
  }
  function handleLGFacilityEditCloseModal() {
    setIsLGFacilityEditModalOpen(false);
  }
  const [LGFacilityId, setLGFacilityId] = useState(null);
  const handleInputChangeLGFacility = (event) => {
    const { name, value } = event.target;
    setLGFacilitys({
      ...LGFacilitys,
      [name]: value,
    });
  };
  const initialLGFacilityState = {
    Id: null,
    LGType: "",
    MDRefNumber: "",
    ApprovalDate: "",
    ApprovedAmount: "",
    ExpiredDate: "",
    DayPastDue: "",
    ClaimCreditDate: "",
    InvocationAmount: "",
    DebitAccount: "",
    DebitAccountHolderName: "",
    ClaimSettledBy: "",
  };

  const [LGFacilitys, setLGFacilitys] = useState(initialLGFacilityState);
  const newLGFacility = () => {
    setLGFacilitys(initialLGFacilityState);
    // setSubmitted(false);
  };
  const saveLGFacility = () => {
    var data = {
      LGType: LGFacilitys.LGType,
      MDRefNumber: LGFacilitys.MDRefNumber,
      ApprovalDate: LGFacilitys.ApprovalDate,
      ApprovedAmount: LGFacilitys.ApprovedAmount,
      ExpiredDate: LGFacilitys.ExpiredDate,
      DayPastDue: LGFacilitys.DayPastDue,
      ClaimCreditDate: LGFacilitys.ClaimCreditDate,
      InvocationAmount: LGFacilitys.InvocationAmount,
      DebitAccount: LGFacilitys.DebitAccount,
      DebitAccountHolderName: LGFacilitys.DebitAccountHolderName,
      ClaimSettledBy: LGFacilitys.ClaimSettledBy,
    };
    LGFacilityService.addLGFacility(data, creditFacilityUtilizitionId)
      .then((response) => {
        setLGFacilitys({
          Id: response.data.Id,
          LGType: response.data.LGType,
          MDRefNumber: response.data.MDRefNumber,
          ApprovalDate: response.data.ApprovalDate,
          ApprovedAmount: response.data.ApprovedAmount,
          ExpiredDate: response.data.ExpiredDate,
          DayPastDue: response.data.DayPastDue,
          ClaimCreditDate: response.data.ClaimCreditDate,
          InvocationAmount: response.data.InvocationAmount,
          DebitAccount: response.data.DebitAccount,
          DebitAccountHolderName: response.data.DebitAccountHolderName,
          ClaimSettledBy: response.data.ClaimSettledBy,
        });
        if (response.data.Code === 200) {
          setLGFacilitys(initialLGFacilityState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          LGFacility();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "PreshipmentFacility",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (LGFacility) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${LGFacility.Id}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deletePreshipmentFacility",
      });
      LGFacilityService.deleteLGFacilityById(LGFacility.Id)
        .then(() => {
          LGFacility((previousData) =>
            previousData.filter((comp) => comp.Id !== LGFacility.Id)
          );
        })
        .catch((e) => {
          setLGFacilityDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editLGFacility, setEditLGFacility] = useState({
    //  Id: editAddresAccountDetailId,
    LGType: "",
    MDRefNumber: "",
    ApprovalDate: "",
    ApprovedAmount: "",
    ExpiredDate: "",
    DayPastDue: "",
    ClaimCreditDate: "",
    InvocationAmount: "",
    DebitAccount: "",
    DebitAccountHolderName: "",
    ClaimSettledBy: "",
  });

  const handleInputChangeEditLGFacility = (event) => {
    const { name, value } = event.target;
    setEditLGFacility({
      ...editLGFacility,
      [name]: value,
    });
  };

  const getLGFacilityId = (editLGFacilityId) => {
    LGFacilityService.getLGFacilityById(editLGFacilityId) //here is
      .then((res) => {
        setEditLGFacility({
          ...editLGFacility,
          ApprovalDate: res.data.Result.ApprovalDate,
          Id: editLGFacilityId,
          LGType: res.data.Result.LGType,
          MDRefNumber: res.data.Result.MDRefNumber,
          ApprovalDate: res.data.Result.ApprovalDate,
          ApprovedAmount: res.data.Result.ApprovedAmount,
          ExpiredDate: res.data.Result.ExpiredDate,
          DayPastDue: res.data.Result.DayPastDue,
          ClaimCreditDate: res.data.Result.ClaimCreditDate,
          InvocationAmount: res.data.Result.InvocationAmount,
          DebitAccount: res.data.Result.DebitAccount,
          DebitAccountHolderName: res.data.Result.DebitAccountHolderName,
          ClaimSettledBy: res.data.Result.ClaimSettledBy,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateLGFacilityById = (e) => {
    e.preventDefault();
    LGFacilityService.updateLGFacilityById(LGFacilityId, editLGFacility)
      .then((response) => {
        if (response.data.Code === 200) {
          LGFacility();
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <CompanyModal
        isOpen={isLGFacilityModalOpen}
        onClose={handleLGFacilityCloseModal}
        title=" LG Facility"
      >
        <div className="px-4 py-3 ">
          <div className="w-flex flex-col bg-gray-800w-96 py-8 px-4 text-center">
            <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll">
              {/* <div className="bg-gray-100 flex items-center justify-center h-screen"> */}
              <div>
                <h4 className="text-lg font-semibold eading-tight p-2"></h4>
                <AddLGFacility
                  handleInputChangeLGFacility={handleInputChangeLGFacility}
                  saveLGFacility={saveLGFacility}
                  LGFacilitys={LGFacilitys}
                  setLGFacilitys={setLGFacilitys}
                />
                <div>
                  {error && <p className="text-red-500">{error}</p>}
                  {!error && LGFacilityErrorLoading ? (
                    <span>
                      <FaSpinner
                        className="animate-spin rounded-full h-6 w-6 text-purple-600"
                        fontSize="small"
                        data-toggle="tooltip"
                        title="Click it to sign in"
                      />
                      Loading...
                    </span>
                  ) : (
                    !error &&
                    !LGFacilityErrorLoading && (
                      <div className="overflow-x-auto">
                        <ToastContainer />
                        <table className="min-w-full table table-bordered table-striped bg-purple-100">
                          <thead>
                            <tr className="bg-purple-200">
                              <th className="px-2 py-4"> LG Type </th>
                              <th className="px-2 py-4"> Approval Date</th>
                              <th className="px-2 py-4"> Approval Date </th>
                              <th className="px-2 py-4"> Approved Amount </th>
                              <th className="px-2 py-4"> Expired Date </th>
                              <th className="px-2 py-4"> Day Past Due </th>
                              <th className="px-2 py-4"> Claim Credit Date </th>
                              <th className="px-2 py-4"> Invocation Amount </th>
                              <th className="px-2 py-4"> Debit Account </th>
                              <th className="px-2 py-4">
                                {" "}
                                DebitAccountHolder Name{" "}
                              </th>
                              <th className="px-2 py-4"> ClaimSettledBy </th>

                              <th className="px-2 py-4"> Action </th>
                            </tr>
                          </thead>
                          <tbody>
                            {LGFacility.length === 0 ? (
                              <p className="text-red-600 p-4">Data not found</p>
                            ) : (
                              LGFacility.map((LgFacilitys, index) => (
                                <tr
                                  key={index}
                                  className="bg-white
                    border-b border-purple-200"
                                >
                                  <td className="p-2">{LgFacilitys.LGType}</td>
                                  <td className="p-2">{LgFacilitys.LGType}</td>
                                  <td className="p-2">
                                    {LgFacilitys.ApprovalDate}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.ApprovalDate}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.ApprovedAmount}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.ExpiredDate}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.DayPastDue}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.ClaimCreditDate}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.InvocationAmount}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.DebitAccount}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.DebitAccountHolderName}
                                  </td>
                                  <td className="p-2">
                                    {LgFacilitys.ClaimSettledBy}
                                  </td>
                                  <td className="p-2">
                                    <>
                                      <LoanIconButton
                                        style={{ color: "green" }}
                                        icon={<MdModeEdit />}
                                        text="Edit"
                                        onClick={() => {
                                          handleLGFacilityEditOpenModal();
                                          getLGFacilityId(LgFacilitys.Id);
                                          setLGFacilityId(LgFacilitys.Id);
                                        }}
                                      />
                                    </>
                                    <>
                                      <LoanIconButton
                                        style={{ color: "red" }}
                                        text=" Delete"
                                        onClick={() => {
                                          handleDelete(LgFacilitys);
                                        }}
                                      >
                                        <ToastContainer />
                                      </LoanIconButton>
                                    </>
                                  </td>
                                </tr>
                              ))
                            )}
                          </tbody>
                        </table>
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isLGFacilityEditModalOpen}
        onClose={handleLGFacilityEditCloseModal}
        title="Modify LG information"
      >
        <div>
          <ToastContainer />
          <form onSubmit={updateLGFacilityById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <LoanTextField
                    id="LGType"
                    label="LGType"
                    value={editLGFacility.LGType}
                    onChange={handleInputChangeEditLGFacility}
                    name="LGType"
                  />
                  <LoanTextField
                    id="MDRefNumber"
                    label="MDRefNumber"
                    value={editLGFacility.MDRefNumber}
                    onChange={handleInputChangeEditLGFacility}
                    name="MDRefNumber"
                  />
                  <p>
                    Approval Date
                    <input
                      id="ApprovalDate"
                      name="ApprovalDate"
                      label="ApprovalDate"
                      placeholder="ApprovalDate"
                      type="date"
                      value={
                        approvalDate.getFullYear().toString() +
                        "-" +
                        (approvalDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        approvalDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setApprovalDate(new Date(e.target.value));
                        LGFacilitys.ApprovalDate = approvalDate;
                      }}
                    />{" "}
                  </p>
                  <LoanTextField
                    id="ApprovedAmount"
                    label="ApprovedAmount"
                    value={LGFacilitys.ApprovedAmount}
                    onChange={handleInputChangeLGFacility}
                    name="ApprovedAmount"
                  />
                  <p>
                    Expired Date:
                    <input
                      id="ExpiredDate"
                      name="ExpiredDate"
                      label="ExpiredDate"
                      placeholder="ExpiredDate"
                      type="date"
                      value={
                        expiredDate.getFullYear().toString() +
                        "-" +
                        (expiredDate.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        expiredDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setExpiredDate(new Date(e.target.value));
                        LGFacilitys.ExpiredDate = expiredDate;
                      }}
                    />{" "}
                  </p>

                  <LoanTextField
                    id="DayPastDue"
                    label="DayPastDue"
                    value={LGFacilitys.DayPastDue}
                    onChange={handleInputChangeLGFacility}
                    name="DayPastDue"
                  />
                  <p>
                    Claim Credit Date:
                    <input
                      id="ClaimCreditDate"
                      name="ClaimCreditDate"
                      label="ClaimCreditDate"
                      placeholder="ClaimCreditDate"
                      type="date"
                      value={
                        PDLD.getFullYear().toString() +
                        "-" +
                        (PDLD.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        PDLD.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setPDLD(new Date(e.target.value));
                        LGFacilitys.ClaimCreditDate = PDLD;
                      }}
                    />{" "}
                  </p>
                  <LoanTextField
                    id="InvocationAmount"
                    label="InvocationAmount"
                    value={LGFacilitys.InvocationAmount}
                    onChange={handleInputChangeLGFacility}
                    name="InvocationAmount"
                  />
                  <LoanTextField
                    id="DebitAccount"
                    label="DebitAccount"
                    value={LGFacilitys.DebitAccount}
                    onChange={handleInputChangeLGFacility}
                    name="DebitAccount"
                  />
                  <LoanTextField
                    id="DebitAccountHolderName"
                    label="DebitAccountHolderName"
                    value={LGFacilitys.DebitAccountHolderName}
                    onChange={handleInputChangeLGFacility}
                    name="DebitAccountHolderName"
                  />
                  <LoanTextField
                    id="ClaimSettledBy"
                    label="ClaimSettledBy"
                    value={LGFacilitys.ClaimSettledBy}
                    onChange={handleInputChangeLGFacility}
                    name="ClaimSettledBy"
                  />
                </div>
                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                  >
                    Update
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewLGFacility;
