import { useState, useEffect } from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import CompanyModal from "../Common/CompanyModal";
import { ToastContainer, toast } from "react-toastify";
import SoleAndPartnershipService from "./SoleAndPartnershipService";
import CommonServices from "../Common/CommonServices";
import LoanTextField from "../../Common/LoanTextField";
const EditSole = ({
  isCompanySoleEditModalOpen,
  handleCompanySoleEditCloseModal,
  handleCompanySoleEditOpenModal,
  editSoleId,
  editSole,
  setEditSole,
  // setSelectedPathIdentity,
}) => {
  const [country, setContry] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [maritalStatus, setMaritalStatus] = useState([]);
  const [martailStatusHold, setMartailStatusHold] = useState();
  const handleInputChangeEditSole = (event) => {
    const { name, value } = event.target;
    setEditSole({ ...editSole, [name]: value });
  };
  const [fromDate, setFromDate] = useState(new Date());
  const [proofOfIdentityExpiredDate, setProofOfIdentityExpiredDate] = useState(
    new Date()
  );
  //upload file
  const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFileIdentity = (event) => {
    setSelectedFiles(event.target.files);
  };
  const uploadIdentity = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  const updateSoleById = (e) => {
    e.preventDefault();
    SoleAndPartnershipService.updateSoleById(editSoleId, editSole)
      .then((response) => {
        if (response.data.Code === 200) {
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompanyAddressSuccess",
          });
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateCompanyAddressError",
        });
      });
  };
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setContry(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);
  const fetchProofOfIdentityType = () => {
    CommonServices.getProofOfIdentityTypes()
      .then((response) => {
        setProofOfIdentityType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchProofOfIdentityType();
  }, []);
  const fetchMaritalStatus = () => {
    CommonServices.getMaritalStatus()
      .then((response) => {
        setMaritalStatus(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchMaritalStatus();
  }, []);
  return (
    <CompanyModal
      isOpen={isCompanySoleEditModalOpen}
      onClose={handleCompanySoleEditCloseModal}
      title="Modify company basic information"
    >
      <div>
        <form onSubmit={updateSoleById}>
          <div className="grid grid-cols-1 gap-4 submit-form">
            <div className="submit-form ">
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <LoanTextField
                  id="FirstName"
                  label="FirstName"
                  value={editSole.FirstName}
                  onChange={handleInputChangeEditSole}
                  name="FirstName"
                />
                <LoanTextField
                  id="MiddleName"
                  label="MiddleName"
                  value={editSole.MiddleName}
                  onChange={handleInputChangeEditSole}
                  name="MiddleName"
                />
                <LoanTextField
                  id="LastName"
                  label="LastName"
                  value={editSole.LastName}
                  onChange={handleInputChangeEditSole}
                  name="LastName"
                />
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Nationality
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (editSole.NationalityId = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Country Of Origin{" "}
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (editSole.CountryOfOriginId = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Country Of Residence
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (editSole.CountryOfResidenceId = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {country.map((yourCounty) => (
                      <MenuItem
                        key={yourCounty.Id}
                        NationalityID={yourCounty.Id}
                        value={yourCounty.Id}
                      >
                        {yourCounty.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Proof Of Identity Type{" "}
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (editSole.ProofOfIdentityTypeId = event.target.value)
                    }
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {proofOfIdentityType.map((yourProofOfIdentityType) => (
                      <MenuItem
                        key={yourProofOfIdentityType.Id}
                        ProofOfIdentityTypeID={yourProofOfIdentityType.Id}
                        value={yourProofOfIdentityType.Id}
                      >
                        {yourProofOfIdentityType.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                {/* <LoanTextField
                  id="UploadProofOfIdentity"
                  label="UploadProofOfIdentity"
                  value={editSole.UploadProofOfIdentity}
                  onChange={handleInputChangeEditSole}
                  name="UploadProofOfIdentity"
                /> */}
                <p>
                  <div>
                    {currentFile && (
                      <div className="progress">
                        <div
                          className="progress-bar progress-bar-info progress-bar-striped"
                          role="progressbar"
                          aria-valuenow={progress}
                          aria-valuemin="0"
                          aria-valuemax="100"
                          style={{ width: progress + "%" }}
                        >
                          {progress}%
                        </div>
                      </div>
                    )}
                    <label className="btn btn-default">
                      <input type="file" onChange={selectFileIdentity} />
                    </label>
                    <button
                      className="bg-[#be7ebe] hover:bg-purple-300 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline  "
                      disabled={!selectedFiles}
                      onChange={editSole.UploadProofOfIdentity = selectedPathIdentity}
                      onClick={uploadIdentity}
                    >
                      Upload Proof Of Identity
                    </button>
                    <div className="alert alert-light" role="alert">
                      {message}
                    </div>
                  </div>
                </p>

                <p>
                  Proof Of Identity Issued Date:
                  <input
                    id="ProofOfIdentityIssuedDate"
                    name="ProofOfIdentityIssuedDate"
                    label="ProofOfIdentityIssuedDate"
                    placeholder="ProofOfIdentityIssuedDate"
                    type="date"
                    value={
                      fromDate.getFullYear().toString() +
                      "-" +
                      (fromDate.getMonth() + 1).toString().padStart(2, 0) +                      
                      "-" +
                      fromDate.getDate().toString().padStart(2, 0)
                    }
                    onChange={(e) => {
                      setFromDate(new Date(e.target.value));
                      editSole.ProofOfIdentityIssuedDate = fromDate;
                    }                    
                  }
                  />{" "}
                </p>

                <p>
                  Proof Of Identity Expired Date:
                  <input
                    id="ProofOfIdentityExpiredDate"
                    name="ProofOfIdentityExpiredDate"
                    label="ProofOfIdentityExpiredDate"
                    placeholder="ProofOfIdentityExpiredDate"
                    type="date"
                    value={
                      proofOfIdentityExpiredDate.getFullYear().toString() +
                      "-" +
                      (proofOfIdentityExpiredDate.getMonth() + 1)
                        .toString()
                        .padStart(2, 0) +
                      "-" +
                      proofOfIdentityExpiredDate
                        .getDate()
                        .toString()
                        .padStart(2, 0)
                    }
                    onChange={(e) => {
                      setProofOfIdentityExpiredDate(new Date(e.target.value));
                      editSole.ProofOfIdentityExpiredDate =
                        proofOfIdentityExpiredDate;
                    }}
                  />{" "}
                </p>
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Martail Status
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) => {
                      editSole.MartailStatusTypeId = event.target.value;
                      setMartailStatusHold(
                        (editSole.MartailStatusTypeId = event.target.value)
                      );
                    }}
                    labelId="countrylbl"
                    id="countrySelect"
                    label="Country"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {maritalStatus.map((maritalSt) => (
                      <MenuItem
                        key={maritalSt.Id}
                        NationalityID={maritalSt.Id}
                        value={maritalSt.Id}
                      >
                        <button>{maritalSt.Name}</button>
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </div>
              <div className="flex justify-center items-center">
                <button
                  type="submit"
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                >
                  Update
                </button>
              </div>
            </div>
          </div>
        </form>
      </div>
    </CompanyModal>
  );
};

export default EditSole;
