import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import CommonServices from "../../Common/CommonServices";
const ViewSoleTax = ({
  holdSoleTax,
  taxId,
  isCompanyTaxModalOpen,
  handleCompanyTaxCloseModal,
}) => {
  const [
    isCompanyTaxEditModalOpen,
    setIsCompanyTaxEditModalOpen,
  ] = useState(false);
  function handleCompanyMarriedTaxEditCloseModal() {
    setIsCompanyMarriedTaxEditModalOpen(false);
  }
  function handleCompanyMarriedTaxEditOpenModal() {
    setIsCompanyMarriedTaxEditModalOpen(true);
  }
  function handleCompanyMarriedTaxEditCloseModal() {
    setIsCompanyMarriedTaxEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editMarriedTax, setEditMarriedTax] = useState({
    Id: taxInformationId,
    Name: "",
    TinNo: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebit: "",
    Certificate: "",
  });
  const handleInputChangeEditMarriedTax = (event) => {
    const { name, value } = event.target;
    setEditMarriedTax({ ...editMarriedTax, [name]: value });
  };
  const updateMarriedTaxById = (taxInformationId) => {
    CommonServices.getMarriedTax(taxInformationId)
      .then((res) => {
        setEditMarriedTax({
          ...editMarriedTax,
          Id: taxInformationId,
          Name: res.data.Result.Name,
          TinNo: res.data.Result.TinNo,
          IssueDate: res.data.Result.IssueDate,
          Expireddate: res.data.Result.Expireddate,
          TaxDebit: res.data.Result.TaxDebit,
          Certificate: res.data.Result.Certificate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateMarriedTaxById();
  }, []);
  const updateMarriedTax = () => {
    CommonServices.updateMarriedTax(taxInformationId, editMarriedTax)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateMarriedTax();
  }, []);
  //add married tax
  const [toggleMarriedTax, setToggleMarriedTax] = useState(false);
  const initialMarriedTaxState = {
    Id: null,
    Name: "",
    TinNo: "",
    Certificate: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebit: "",
  };
  const [submitted, setSubmitted] = useState(false);

  const [marriedTax, setMarriedTax] = useState(initialMarriedTaxState);
  const newMarriedTax = () => {
    setMarriedTax(initialMarriedTaxState);
    setSubmitted(false);
  };
  const handleInputChangeMarriedTax = (event) => {
    const { name, value } = event.target;
    setMarriedTax({ ...marriedTax, [name]: value });
  };
  const saveMarriedTax = () => {
    var data = {
      Name: marriedTax.Name,
      TinNo: marriedTax.TinNo,
      Certificate: marriedTax.Certificate,
      IssueDate: marriedTax.IssueDate,
      Expireddate: marriedTax.Expireddate,
      TaxDebit: marriedTax.TaxDebit,
    };
    CommonServices.addMarriedTax(data, marriedTaxId)
      .then((response) => {
        setMarriedTax({
          Id: response.data.Id,
          Name: response.data.Name,
          TinNo: response.data.TinNo,
          Certificate: response.data.Certificate,
          IssueDate: response.data.IssueDate,
          Expireddate: response.data.Expireddate,
          TaxDebit: response.data.TaxDebit,
        });
        setSubmitted(true);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //end add married tax
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMarriedTaxModalOpen}
        onClose={handleCompanyMarriedTaxCloseModal}
        title="view married Tax information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdMarriedTax ? (
            <>
              <table className="table table-bordered table-striped bg-purple-100">
                <thead>
                  <th className="px-2 py-4"> Name </th>
                  <th className="px-2 py-4"> TinNo </th>
                  <th className="px-2 py-4"> Issue Date </th>
                  <th className="px-2 py-4"> Expired date </th>
                  <th className="px-2 py-4"> Tax Debi </th>
                  <th className="px-2 py-4">Certificate</th>
                </thead>
                <tbody>
                  <tr>
                    <td>{holdMarriedTax.Name}</td>
                    <td>{holdMarriedTax.TinNo}</td>
                    <td>{holdMarriedTax.IssueDate}</td>
                    <td>{holdMarriedTax.Expireddate}</td>
                    <td>{holdMarriedTax.TaxDebit}</td>
                    <td>{holdMarriedTax.Certificate}</td>
                    <td>
                      <button
                        onClick={() => {
                          handleCompanyMarriedTaxEditOpenModal();
                          updateMarriedTaxById(holdMarriedTax.Id);
                          setTaxInformationId(holdMarriedTax.Id);
                        }}
                        className="text-purple-600 rounded-md font-semibold "
                      >
                        Edit
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </>
          ) : (
            ""
          )}
        </div>
        <button
          onClick={() => setToggleMarriedTax(!toggleMarriedTax)}
          className="text-purple-600 rounded-md font-medium "
        >
          Add tax
        </button>

        {toggleMarriedTax && (
          <div className="submit-form">
            {submitted ? (
              <div>
                <h4>You submitted successfully!</h4>
              </div>
            ) : (
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <LoanTextField
                  id="Name"
                  label="Name"
                  value={marriedTax.Name}
                  onChange={handleInputChangeMarriedTax}
                  name="Name"
                />
                <LoanTextField
                  id="TinNo"
                  label="TinNo"
                  value={marriedTax.TinNo}
                  onChange={handleInputChangeMarriedTax}
                  name="TinNo"
                />
                <LoanTextField
                  id="IssueDate"
                  label="IssueDate"
                  value={marriedTax.IssueDate}
                  onChange={handleInputChangeMarriedTax}
                  name="IssueDate"
                />
                <LoanTextField
                  id="Expireddate"
                  label="Expireddate"
                  value={marriedTax.Expireddate}
                  onChange={handleInputChangeMarriedTax}
                  name="Expireddate"
                />
                <LoanTextField
                  id="TaxDebit"
                  label="TaxDebit"
                  value={marriedTax.TaxDebit}
                  onChange={handleInputChangeMarriedTax}
                  name="TaxDebit"
                />
                <LoanTextField
                  id="TaxClearanceCertificate"
                  label="TaxClearanceCertificate"
                  value={marriedTax.Certificate}
                  onChange={handleInputChangeMarriedTax}
                  name="TaxClearanceCertificate"
                />
              </div>
            )}
            <div>
              <button
                className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => {
                  saveMarriedTax();
                }}
              >
                save
              </button>
            </div>
          </div>
        )}
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyMarriedTaxEditModalOpen}
        onClose={handleCompanyMarriedTaxEditCloseModal}
        title="tax edit information"
      >
        <div>
          <form onSubmit={updateMarriedTax}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="Name"
                label="Name"
                value={editMarriedTax.Name}
                onChange={handleInputChangeEditMarriedTax}
                name="Name"
              />
              <LoanTextField
                id="TinNo"
                label="TinNo"
                value={editMarriedTax.TinNo}
                onChange={handleInputChangeEditMarriedTax}
                name="TinNo"
              />
              <LoanTextField
                id="IssueDate"
                label="IssueDate"
                value={editMarriedTax.IssueDate}
                onChange={handleInputChangeEditMarriedTax}
                name="IssueDate"
              />
              <LoanTextField
                id="Expireddate"
                label="Expireddate"
                value={editMarriedTax.Expireddate}
                onChange={handleInputChangeEditMarriedTax}
                name="Expireddate"
              />
              <LoanTextField
                id="TaxDebit"
                label="TaxDebit"
                value={editMarriedTax.TaxDebit}
                onChange={handleInputChangeEditMarriedTax}
                name="TaxDebit"
              />
              <LoanTextField
                id="Certificate"
                label="Certificate"
                value={editMarriedTax.Certificate}
                onChange={handleInputChangeEditMarriedTax}
                name="Certificate"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewSoleTax;
