import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import CommonServices from "../../Common/CommonServices";
import guarantorMarried from "./guarantorMarried"
import guarantorUnMarried from "./guarantorUnMarried"
const ViewSoleGuarantor = ({
  holdSoleGuarantor,
  isCompanySoleGuarantorModalOpen,
  handleCompanySoleGuarantorCloseModal,
  handleCompanySoleGuarantorOpenModal,
  soleID, //well transfer to add sole dependent
}) => {
  const [country, setContry] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [dependentRelationshipType, setGuarantorRelationshipType] = useState([]);
  const [
    isCompanySoleGuarantorEditedModalOpen,
    setIsCompanySoleGuarantorEditedModalOpen,
  ] = useState(false);
  function handleCompanySoleGuarantorEditCloseModal() {
    setIsCompanySoleGuarantorEditedModalOpen(false);
  }
  function handleCompanySoleGuarantorEditedModalOpen() {
    setIsCompanySoleGuarantorEditedModalOpen(true);
  }
  function handleCompanyGuarantorEditCloseModal() {
    setIsCompanySoleGuarantorEditedModalOpen(false);
  }
  const [guarantorId, setGuarantorId] = useState();
  const [editSoleGuarantor, setEditSoleGuarantor] = useState({
    // SoleAndPartnerShipId: soleID,
    Name: "",
    MartialStatusTypeId:"",
    TaxClerance: {
      Name: "",
      TinNo: "",
      Certificate: "",
      IssueDate: "",
      Expireddate: "",
      TaxDebit: "",
    },
  });
  const handleInputChangeEditGuarantor = (event) => {
    const { name, value } = event.target;
    setEditSoleGuarantor({ ...editSoleGuarantor, [name]: value });
  };
  const handleInputChangeEditTaxClearance = (event) => {
    const { name, value } = event.target;
    setEditSoleGuarantor({
      ...editSoleGuarantor,
      TaxClerance: {
        ...editSoleGuarantor.TaxClerance,
        [name]: value,
      },
    });
  };
  const getGuarantorById = (Id) => {
    CommonServices.getGuarantorById(Id)
      .then((res) => {
        setEditSoleGuarantor({
          ...editSoleGuarantor,
          SoleAndPartnerShipId: res.data.Result.SoleAndPartnerShipId,
          Name: res.data.Result.Name,
          MartialStatusTypeId: res.data.Result.MartialStatusTypeId,
          TaxClerance: {
            Name: res.data.Result.TaxClerance.Name,
            MartialStatusTypeId: res.data.Result.TaxClerance.MartialStatusTypeId,
            TinNo: res.data.Result.TaxClerance.TinNo,
            Certificate: res.data.Result.TaxClerance.Certificate,
            IssueDate: res.data.Result.TaxClerance.IssueDate,
            Expireddate: res.data.Result.TaxClerance.Expireddate,
            TaxDebit: res.data.Result.TaxClerance.TaxDebit,
          },
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getGuarantorById();
  }, []);
  const updateGuarantor = () => {
    CommonServices.updateGuarantor(guarantorId, editSoleGuarantor)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateGuarantor();
  }, []);
  //add dependent
  const [toggleSoleGuarantor, setToggleSoleGuarantor] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const initialSoleGuarantorState = {
    Id: null,
    // SoleAndPartnerShipId: soleID,
    Name: "",
    MartialStatusTypeId:"",
    TaxClerance: {
      Name: "",
      TinNo: "",
      Certificate: "",
      IssueDate: "",
      Expireddate: "",
      TaxDebit: "",
    },
  };
  const [soleGuarantor, setSoleGuarantor] = useState(initialSoleGuarantorState);
  // const handleInputChangeSoleGuarantor = (event) => {
  //   const { name, value } = event.target;
  //   setSoleGuarantor({ ...soleGuarantor, [name]: value });
  // };
  const handleInputChangeSoleGuarantor = (event) => {
    const { name, value } = event.target;
    setSoleGuarantor({ ...soleGuarantor, [name]: value });
  };
  const handleInputChangeTaxClearance = (event) => {
    const { name, value } = event.target;
    setSoleGuarantor({
      ...soleGuarantor,
      TaxClerance: {
        ...soleGuarantor.TaxClerance,
        [name]: value,
      },
    });
  };
  const saveSoleGuarantor = () => {
    var data = {
      Name: soleGuarantor.Name,
      MartialStatusTypeId: soleGuarantor.MartialStatusTypeId,
      TaxClerance: {
        Name: soleGuarantor.TaxClerance.Name,
        TinNo: soleGuarantor.TaxClerance.TinNo,
        Certificate: soleGuarantor.TaxClerance.Certificate,
        IssueDate: soleGuarantor.TaxClerance.IssueDate,
        Expireddate: soleGuarantor.TaxClerance.Expireddate,
        TaxDebit: soleGuarantor.TaxClerance.TaxDebit,
      },
    };
    CommonServices.addGuarantor(soleID, data)
      .then((response) => {
        setSoleGuarantor({
          Id: response.data.Id,
          Name: response.data.Name,
          MartialStatusTypeId: response.data.MartialStatusTypeId,
          TaxClerance: {
            Name: response.data.TaxClerance.Name,
            TinNo: response.data.TaxClerance.TinNo,
            Certificate: response.data.TaxClerance.Certificate,
            IssueDate: response.data.TaxClerance.IssueDate,
            Expireddate: response.data.TaxClerance.Expireddate,
            TaxDebit: response.data.TaxClerance.TaxDebit,
          },
        });
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //end dependent
  const deleteGuarantor = (Id) => {
    CommonServices.deleteDependent(Id)
      .then((response) => {
        // setDependentRelationshipType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    deleteGuarantor();
  }, []);
  
  const [maritalStatus, setMaritalStatus] = useState([]);
  const getMaritailStatusGuarantor = () => {
    CommonServices.getMaritalStatus()
      .then((response) => {
        setMaritalStatus(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getMaritailStatusGuarantor();
  }, []);
  const [isCompanyMartailStatusModalOpen, setIsCompanyMartailStatusModalOpen] =
  useState(false);
const [
  isCompanyMartailStatusUnmarriedModalOpen,
  setIsCompanyMartailStatusUnmarriedModalOpen,
] = useState(false);
  function handleCompanyMartailStatusOpenModal() {
    setIsCompanyMartailStatusModalOpen(true);
  }
  function handleCompanyMartailStatusUnmarriedOpenModal() {
    setIsCompanyMartailStatusUnmarriedModalOpen(true);
  }
  function handleCompanyMartailStatusUnmarriedOpenModal() {
    setIsCompanyMartailStatusUnmarriedModalOpen(true);
  }
  function handleCompanyMartailStatusUnmarriedCloseModal() {
    setIsCompanyMartailStatusUnmarriedModalOpen(false);
  }
function handleGeneralCloseModal() {
    isCompanyMartailStatusModalOpen(false);
  }
  function handleCompanyMartailStatusCloseModal() {
    setIsCompanyMartailStatusModalOpen(false);
  }
  const fetchMartialStatus = (martailStatus) => {
    if (martailStatus == "Married") {
      handleCompanyMartailStatusOpenModal();
    } 
    else  {
      handleCompanyMartailStatusUnmarriedOpenModal();
    }
  };
  const [holdMarried, setMarried] = useState([]);
  const [holdUnMarried, setUnMarried] = useState([]);
  const getGuarantorSpouse = (guarantorId) => {
    CommonServices.getGuarantor(soleAndPartnershipId)
      .then((response) => {
        setMarried(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const getGuarantorUnmarried = (guarantorId) => {
    CommonServices.getUnMartialStatusBySoleMarriedId(soleAndPartnershipId)
      .then((response) => {
        setUnMarried(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <>
        <CompanyModal
          isOpen={isCompanySoleGuarantorModalOpen}
          onClose={handleCompanySoleGuarantorCloseModal}
          title="view Guarantor information"
        >
           <guarantorMarried
                // holdMarried={holdMarried}
                isCompanyMartailStatusModalOpen={
                  isCompanyMartailStatusModalOpen
                }
                handleCompanyMartailStatusCloseModal={
                  handleCompanyMartailStatusCloseModal
                }
                handleMaritalStatusModalOpen={
                  handleCompanyMartailStatusOpenModal
                }
                // SoleId={martialStatusIdInformationId}
              />
              <guarantorUnMarried
                // holdUnMarried={holdUnMarried}
                isCompanyMartailStatusUnmarriedModalOpen={
                  isCompanyMartailStatusUnmarriedModalOpen
                }
                handleCompanyMartailStatusUnmarriedCloseModal={
                  handleCompanyMartailStatusUnmarriedCloseModal
                }
                handleCompanyMartailStatusUnmarriedOpenModal={
                  handleCompanyMartailStatusUnmarriedOpenModal
                }
                // SoleId={martialStatusIdInformationId}
              />
          <div className="rounded-lg overflow-hidden">
            {holdSoleGuarantor.TaxClerance ? (
              <table className="table table-bordered table-striped bg-purple-100">
                <thead>
                  <th className="px-2 py-4">Name</th>
                  <th className="px-2 py-4">marital status</th>
                  <th className="px-2 py-4"> Tax Name</th>
                  <th className="px-2 py-4"> TinNo</th>
                  <th className="px-2 py-4"> Certificate</th>
                  <th className="px-2 py-4"> Issue Date</th>
                  <th className="px-2 py-4"> Expired date</th>
                  <th className="px-2 py-4"> Tax Debit</th>
                  <th className="px-2 py-4"> Marital Status details</th>
                  <th className="px-2 py-4"> Action </th>
                </thead>
                <tbody>
                  <>
                    <tr>
                      <td>{holdSoleGuarantor.Name}</td>
                      <td>{holdSoleGuarantor.MartialStatusType}</td>
                      <td>{holdSoleGuarantor.TaxClerance.Name}</td>
                      <td>{holdSoleGuarantor.TaxClerance.TinNo}</td>
                      <td>{holdSoleGuarantor.TaxClerance.Certificate}</td>
                      <td>{holdSoleGuarantor.TaxClerance.IssueDate}</td>
                      <td>{holdSoleGuarantor.TaxClerance.Expireddate}</td>
                      <td>{holdSoleGuarantor.TaxClerance.TaxDebit}</td>
                      <td>{holdSoleGuarantor.MartialStatusType}</td>
                      <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        fetchMartialStatus(holdSoleGuarantor.MartialStatusType);
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      Marital status detail
                                    </button>
                                  </td>
                      <td>
                        <button
                          onClick={() => {
                            handleCompanySoleGuarantorEditedModalOpen();
                            getGuarantorById(holdSoleGuarantor.Id);
                            setGuarantorId(holdSoleGuarantor.Id);
                          }}
                          className="text-purple-600 rounded-md font-semibold "
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  </>
                  {/* ))} */}
                </tbody>
              </table>
            ) : (
              ""
            )}
            <button
              onClick={() => setToggleSoleGuarantor(!toggleSoleGuarantor)}
              className="text-purple-600 rounded-md font-medium "
            >
              Add guarantor
            </button>
            {toggleSoleGuarantor && (
              <div className="submit-form">
                {submitted ? (
                  <div>
                    <h4>You submitted successfully!</h4>
                  </div>
                ) : (
                  <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    <LoanTextField
                      id="Name"
                      label="Name"
                      value={soleGuarantor.Name}
                      onChange={handleInputChangeSoleGuarantor}
                      name="Name"
                    />
                    <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="countrylbl" color="secondary">
                    marital status
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) =>
                        (soleGuarantor.MartialStatusTypeId = event.target.value)
                      }
                      labelId="loanType"
                      id="loanTypeSelected"
                      label="loanType"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {maritalStatus.map((yourMarital) => (
                        <MenuItem
                          key={yourMarital.Id}
                          MartialStatusTypeId={yourMarital.Id}
                          value={yourMarital.Id}
                        >
                          {yourMarital.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                    <LoanTextField
                      id="Name"
                      label="Name"
                      value={soleGuarantor.TaxClerance.Name}
                      onChange={handleInputChangeTaxClearance}
                      name="Name"
                    />
                    <LoanTextField
                      id="TinNo"
                      label="TinNo"
                      value={soleGuarantor.TaxClerance.TinNo}
                      onChange={handleInputChangeTaxClearance}
                      name="TinNo"
                    />
                    <LoanTextField
                      id="Certificate"
                      label="Certificate"
                      value={soleGuarantor.TaxClerance.Certificate}
                      onChange={handleInputChangeTaxClearance}
                      name="Certificate"
                    />
                    <LoanTextField
                      id="IssueDate"
                      label="IssueDate"
                      value={soleGuarantor.TaxClerance.IssueDate}
                      onChange={handleInputChangeTaxClearance}
                      name="IssueDate"
                    />
                    <LoanTextField
                      id="Expireddate"
                      label="Expireddate"
                      value={soleGuarantor.TaxClerance.Expireddate}
                      onChange={handleInputChangeTaxClearance}
                      name="Expireddate"
                    />
                    <LoanTextField
                      id="TaxDebit"
                      label="TaxDebit"
                      value={soleGuarantor.TaxClerance.TaxDebit}
                      onChange={handleInputChangeTaxClearance}
                      name="TaxDebit"
                    />
                  </div>
                )}
                <div>
                  <button
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    onClick={() => {
                      saveSoleGuarantor();
                    }}
                  >
                    save
                  </button>
                </div>
              </div>
            )}
          </div>
        </CompanyModal>
        <CompanyModal
          isOpen={isCompanySoleGuarantorEditedModalOpen}
          onClose={handleCompanySoleGuarantorEditCloseModal}
          title="guarantor edit information"
        >
          <div>
            <form onSubmit={updateGuarantor}>
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <LoanTextField
                  id="Name"
                  label="Name"
                  value={editSoleGuarantor.Name}
                  onChange={handleInputChangeEditGuarantor}
                  name="Name"
                />
                 <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="countrylbl" color="secondary">
                    marital status
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) =>
                        (editSoleGuarantor.MartialStatusTypeId = event.target.value)
                      }
                      labelId="loanType"
                      id="loanTypeSelected"
                      label="loanType"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {maritalStatus.map((yourMarital) => (
                        <MenuItem
                          key={yourMarital.Id}
                          MartialStatusTypeId={yourMarital.Id}
                          value={yourMarital.Id}
                        >
                          {yourMarital.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                <LoanTextField
                  id="Name"
                  label="Name"
                  value={editSoleGuarantor.TaxClerance.Name}
                  onChange={handleInputChangeEditTaxClearance}
                  name="Name"
                />
                <LoanTextField
                  id="TinNo"
                  label="TinNo"
                  value={editSoleGuarantor.TaxClerance.TinNo}
                  onChange={handleInputChangeEditTaxClearance}
                  name="TinNo"
                />
                <LoanTextField
                  id="Certificate"
                  label="Certificate"
                  value={editSoleGuarantor.TaxClerance.Certificate}
                  onChange={handleInputChangeEditTaxClearance}
                  name="Certificate"
                />
                <LoanTextField
                  id="IssueDate"
                  label="IssueDate"
                  value={editSoleGuarantor.TaxClerance.IssueDate}
                  onChange={handleInputChangeEditTaxClearance}
                  name="IssueDate"
                />
                <LoanTextField
                  id="Expireddate"
                  label="Expireddate"
                  value={editSoleGuarantor.TaxClerance.Expireddate}
                  onChange={handleInputChangeEditTaxClearance}
                  name="Expireddate"
                />
                <LoanTextField
                  id="Expireddate"
                  label="Expireddate"
                  value={editSoleGuarantor.TaxClerance.Expireddate}
                  onChange={handleInputChangeEditTaxClearance}
                  name="Expireddate"
                />
              </div>
              <div>
                <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                  Update
                </button>
              </div>
            </form>
          </div>
        </CompanyModal>
      </>
      
        
    </div>
  );
};
export default ViewSoleGuarantor;
