import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanIconButton from "../../../Common/LoanIconButton";
import { BsPlus } from "react-icons/bs";
import { IconButton } from "@material-tailwind/react";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {
  InputLabel,
  TextField,
  MenuItem,
  FormControl,
  Select,
} from "@mui/material";
import CommonServices from "../../Common/CommonServices";
const AddMaritalStatus = ({ SoleId }) => {
  //upload start

const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
const [selectedPathMarriege, setSelectedPathMarriege] = useState(null);
const [selectedFiles, setSelectedFiles] = useState(undefined);
const [currentFile, setCurrentFile] = useState(undefined);
const [fileInfos, setFileInfos] = useState([]);
const [progress, setProgress] = useState(0);
const [message, setMessage] = useState("");
const selectFile = (event) => {
  setSelectedFiles(event.target.files);
};
//upload 1
const uploadIdentity = () => {
  let currentFile = selectedFiles[0];
  setProgress(0);
  setCurrentFile(currentFile);
  CommonServices.upload(currentFile, (event) => {
    setProgress(Math.round((100 * event.loaded) / event.total));
  })
    .then((response) => {
      setMessage(response.data.message);
      setSelectedPathIdentity(response.data.Result);
    })
    .then((files) => {
      setFileInfos(files.data);
    })
    .catch(() => {
      setProgress(0);
      setCurrentFile(undefined);
    });
  setSelectedFiles(undefined);
};
//end upload
//upload 2
const uploadMarriegeCertificate = () => {
  let currentFile = selectedFiles[0];
  setProgress(0);
  setCurrentFile(currentFile);
  CommonServices.upload(currentFile, (event) => {
    setProgress(Math.round((100 * event.loaded) / event.total));
  })
    .then((response) => {
      setMessage(response.data.message);
      setSelectedPathMarriege(response.data.Result);
    })
    .then((files) => {
      setFileInfos(files.data);
    })
    .catch(() => {
      setProgress(0);
      setCurrentFile(undefined);
    });
  setSelectedFiles(undefined);
};
//endupload2
  //uppload end
  //date
  const [marriageDate, setMarriageDate] = useState(new Date());
  const [proofOfIdentityIssueDate, setProofOfIdentityIssueDate] = useState(new Date());
  const [proofOfIdentityExpirDate, setProofOfIdentityExpirDate] = useState(new Date());
  //end date
  const [submitted, setSubmitted] = useState(false);
  const [isCompanyMaritalStatusModalOpen, setIsCompanyMaritalStatusModalOpen] =
    useState(false);
  function handleGeneralCloseModal() {
    isCompanyMaritalStatusModalOpen(false);
  }
  function handleCompanyMaritalStatusCloseModal() {
    setIsCompanyMaritalStatusModalOpen(false);
  }
  const [toggleMarriedInfo, setToggleMarriedInfo] = useState(false);
  const [country, setContry] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const initialMaritalStatusMarriedState = {
    Id: null,
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    ProofOfIdentity: "",
    MarriageDate: "",
    MarriageCertificate: "",
    MarriageCertificateIssuer: "",
    ProofOfIdentityIssueDate: "",
    ProofOfIdentityExpirDate: "",
  };
  const [maritalStatusMarried, setMaritalStatusMarried] = useState(
    initialMaritalStatusMarriedState
  );
  const newMaritalStatusMarried = () => {
    setMaritalStatusMarried(initialMaritalStatusMarriedState);
    setSubmitted(false);
  };
  const handleInputChangeMaritalStatusMarried = (event) => {
    const { name, value } = event.target;
    setMaritalStatusMarried({ ...maritalStatusMarried, [name]: value });
  };
  const saveMarried = () => {
    var data = {
      
      FirstName: maritalStatusMarried.FirstName,
      MiddleName: maritalStatusMarried.MiddleName,
      LastName: maritalStatusMarried.LastName,
      NationalityID: maritalStatusMarried.NationalityID,
      CountryOfOriginID: maritalStatusMarried.CountryOfOriginID,
      CountryOfResidenceID: maritalStatusMarried.CountryOfResidenceID,
      ProofOfIdentityTypeID: maritalStatusMarried.ProofOfIdentityTypeID,
      ProofOfIdentity: selectedPathIdentity,
      MarriageDate: maritalStatusMarried.MarriageDate,
      MarriageCertificate: selectedPathMarriege,
      MarriageCertificateIssuer: maritalStatusMarried.MarriageCertificateIssuer,
      ProofOfIdentityIssueDate: maritalStatusMarried.ProofOfIdentityIssueDate,
      ProofOfIdentityExpirDate: maritalStatusMarried.ProofOfIdentityExpirDate,
    };
    CommonServices.marriedSole(data, SoleId)
      .then((response) => {
        setMaritalStatusMarried({
          Id: response.data.Id,
          FirstName: response.data.FirstName,
          MiddleName: response.data.MiddleName,
          LastName: response.data.LastName,
          NationalityID: response.data.NationalityID,
          CountryOfOriginID: response.data.CountryOfOriginID,
          CountryOfResidenceID: response.data.CountryOfResidenceID,
          ProofOfIdentityTypeID: response.data.ProofOfIdentityTypeID,
          ProofOfIdentity: response.data.ProofOfIdentity,
          MarriageDate: response.data.MarriageDate,
          MarriageCertificate: response.data.MarriageCertificate,
          MarriageCertificateIssuer: response.data.MarriageCertificateIssuer,
          ProofOfIdentityIssueDate: response.data.ProofOfIdentityIssueDate,
          ProofOfIdentityExpirDate: response.data.ProofOfIdentityExpirDate,
        });
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setContry(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);

  const fetchProofOfIdentityType = () => {
    CommonServices.getProofOfIdentityTypes()
      .then((response) => {
        setProofOfIdentityType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchProofOfIdentityType();
  }, []);
  return (
    <div>
      <button
        onClick={() => setToggleMarriedInfo(!toggleMarriedInfo)}
        className="text-purple-600 rounded-md font-medium "
      >
        Add Married
      </button>

      {toggleMarriedInfo && (
        <div className="submit-form">
          {submitted ? (
            <div>
              <h4>You submitted successfully!</h4>             
            </div>
          ) : (
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <TextField
                id="FirstName"
                label="FirstName"
                variant="standard"
                color="secondary"
                size=""
                required
                value={maritalStatusMarried.FirstName}
                onChange={handleInputChangeMaritalStatusMarried}
                name="FirstName"
              />
              <TextField
                id="MiddleName"
                label="MiddleName"
                variant="standard"
                color="secondary"
                size=""
                required
                value={maritalStatusMarried.MiddleName}
                onChange={handleInputChangeMaritalStatusMarried}
                name="MiddleName"
              />
              <TextField
                id="LastName"
                label="LastName"
                variant="standard"
                color="secondary"
                size=""
                required
                value={maritalStatusMarried.LastName}
                onChange={handleInputChangeMaritalStatusMarried}
                name="LastName"
              />
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Nationality{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (maritalStatusMarried.NationalityID = event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Country Of Origin{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (maritalStatusMarried.CountryOfOriginID =
                      event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Country Of Residence{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (maritalStatusMarried.CountryOfResidenceID =
                      event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Proof Of Identity Type{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (maritalStatusMarried.ProofOfIdentityTypeID =
                      event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {proofOfIdentityType.map((yourProofOfIdentityType) => (
                    <MenuItem
                      key={yourProofOfIdentityType.Id}
                      ProofOfIdentityTypeID={yourProofOfIdentityType.Id}
                      value={yourProofOfIdentityType.Id}
                    >
                      {yourProofOfIdentityType.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>             
               <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
               className="bg-[#c94fc9] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  disabled={!selectedFiles}
                  onClick={uploadIdentity}
                >
                  Upload Proof Of Identity
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <p>
              Marriage Date:
                <div>
                  <DatePicker
                    selected={marriageDate}
                    onChange={(marriageDate) => setMarriageDate(marriageDate)}
                  />
                </div>
              </p>
           
               <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
              className="bg-[#c94fc9] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"

                  disabled={!selectedFiles}
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>

              <TextField
                id="MarriageCertificateIssuer"
                label="MarriageCertificateIssuer"
                variant="standard"
                color="secondary"
                size=""
                required
                value={maritalStatusMarried.MarriageCertificateIssuer}
                onChange={handleInputChangeMaritalStatusMarried}
                name="MarriageCertificateIssuer"
              />
               <p>
               Proof Of Identity Issue Date:
                <input
                  id="ProofOfIdentityIssueDate"
                  name="ProofOfIdentityIssueDate"
                  label="ProofOfIdentityIssueDate"
                  placeholder="ProofOfIdentityIssueDate"
                  type="date"
                  value={
                    proofOfIdentityIssueDate.getFullYear().toString() +
                    "-" +
                    (proofOfIdentityIssueDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    proofOfIdentityIssueDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setProofOfIdentityIssueDate(new Date(e.target.value));
                    maritalStatusMarried.ProofOfIdentityIssueDate =
                    proofOfIdentityIssueDate;
                  }}
                />{" "}
              </p>
               <p>
               Proof Of Identity Expir Date:
                <input
                  id="ProofOfIdentityExpirDate"
                  name="ProofOfIdentityExpirDate"
                  label="ProofOfIdentityExpirDate"
                  placeholder="ProofOfIdentityExpirDate"
                  type="date"
                  value={
                    proofOfIdentityExpirDate.getFullYear().toString() +
                    "-" +
                    (proofOfIdentityExpirDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    proofOfIdentityExpirDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setProofOfIdentityExpirDate(new Date(e.target.value));
                    maritalStatusMarried.ProofOfIdentityExpirDate =
                    proofOfIdentityExpirDate;
                  }}
                />{" "}
              </p>
            </div>
          )}
          <div>
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveMarried();
              }}
            >
              save
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
export default AddMaritalStatus;
