import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import CommonServices from "../../Common/CommonServices";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
const ViewSoleTax = ({
  holdSoleTax,
  taxId,
  isCompanyTaxModalOpen,
  handleCompanyTaxCloseModal,
}) => {
  //upload
  const [newCertificate, setNewCertificate] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  //upload 1
  const uploadCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setNewCertificate(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  const [date, setDate] = useState(new Date());
  const [issueDate, setIssueDate] = useState(new Date());
  const [expireddate, setExpireddate] = useState(new Date());
  const [isCompanyTaxEditModalOpen, setIsCompanyTaxEditModalOpen] =
    useState(false);
  function handleCompanyTaxEditCloseModal() {
    setIsCompanyTaxEditModalOpen(false);
  }
  function handleCompanyTaxEditOpenModal() {
    setIsCompanyTaxEditModalOpen(true);
  }
  function handleCompanyTaxEditCloseModal() {
    setIsCompanyTaxEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editTax, setEditTax] = useState({
    Id: taxInformationId,
    Name: "",
    TinNo: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebit: "",
    Certificate: "",
  });
  const handleInputChangeEditTax = (event) => {
    const { name, value } = event.target;
    setEditTax({ ...editTax, [name]: value });
  };
  const updateTaxById = (taxInformationId) => {
    CommonServices.getTax(taxInformationId)
      .then((res) => {
        setEditTax({
          ...editTax,
          Id: taxInformationId,
          Name: res.data.Result.Name,
          TinNo: res.data.Result.TinNo,
          IssueDate: res.data.Result.issueDate,
          Expireddate: res.data.Result.xpireddate,
          TaxDebit: res.data.Result.TaxDebit,
          Certificate: res.data.Result.Certificate,
        });        
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateTaxById();
  }, []);
  const updateTax = () => {  
    CommonServices.updateTax(taxInformationId, editTax)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateTax();
  }, []);
  //add married tax
  const [toggleTax, setToggleTax] = useState(false);
  const initialTaxState = {
    Id: null,
    Name: "",
    TinNo: "",
    Certificate: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebit: "",
  };
  const [submitted, setSubmitted] = useState(false);

  const [tax, setTax] = useState(initialTaxState);
  const newTax = () => {
    setTax(initialTaxState);
    setSubmitted(false);
  };
  const handleInputChangeTax = (event) => {
    const { name, value } = event.target;
    setTax({ ...tax, [name]: value });
  };
  const saveTax = () => {
    var data = {
      Name: tax.Name,
      TinNo: tax.TinNo,
      Certificate: newCertificate,
      IssueDate: issueDate,
      Expireddate: expireddate,
      TaxDebit: tax.TaxDebit,
    };
    CommonServices.addTax(data, taxId)
      .then((response) => {
        setTax({
          Id: response.data.Id,
          Name: response.data.Name,
          TinNo: response.data.TinNo,
          Certificate: response.data.Certificate,
          IssueDate: response.data.IssueDate,
          Expireddate: response.data.Expireddate,
          TaxDebit: response.data.TaxDebit,
        });
        setSubmitted(true);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //end add married tax
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyTaxModalOpen}
        onClose={handleCompanyTaxCloseModal}
        title="view Tax information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdSoleTax ? (
            <>
              <table className="table table-bordered table-striped bg-purple-100">
                <thead>
                  <th className="px-2 py-4"> Name </th>
                  <th className="px-2 py-4"> TinNo </th>
                  <th className="px-2 py-4"> Issue Date </th>
                  <th className="px-2 py-4"> Expired date </th>
                  <th className="px-2 py-4"> Tax Debi </th>
                  <th className="px-2 py-4">Certificate</th>
                </thead>
                <tbody>
                  <tr>
                    <td>{holdSoleTax.Name}</td>
                    <td>{holdSoleTax.TinNo}</td>
                    <td>{holdSoleTax.IssueDate}</td>
                    <td>{holdSoleTax.Expireddate}</td>
                    <td>{holdSoleTax.TaxDebit}</td>
                    <td>{""}</td>
                    <td>{holdSoleTax.Certificate}</td>
                    <td>
                      <button
                        onClick={() => {
                          handleCompanyTaxEditOpenModal();
                          updateTaxById(holdSoleTax.Id);
                          setTaxInformationId(holdSoleTax.Id);
                        }}
                        className="text-purple-600 rounded-md font-semibold "
                      >
                        Edit
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </>
          ) : (
            ""
          )}
        </div>
        <button
          onClick={() => setToggleTax(!toggleTax)}
          className="text-purple-600 rounded-md font-medium "
        >
          Add tax
        </button>

        {toggleTax && (
          <div className="submit-form">
            {submitted ? (
              <div>
                <h4>You submitted successfully!</h4>
              </div>
            ) : (
              <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                <LoanTextField
                  id="Name"
                  label="Name"
                  value={tax.Name}
                  onChange={handleInputChangeTax}
                  name="Name"
                />
                <LoanTextField
                  id="TinNo"
                  label="TinNo"
                  value={tax.TinNo}
                  onChange={handleInputChangeTax}
                  name="TinNo"
                />
                <div>
                  {currentFile && (
                    <div className="progress">
                      <div
                        className="progress-bar progress-bar-info progress-bar-striped"
                        role="progressbar"
                        aria-valuenow={progress}
                        aria-valuemin="0"
                        aria-valuemax="100"
                        style={{ width: progress + "%" }}
                      >
                        {progress}%
                      </div>
                    </div>
                  )}
                  <label className="btn btn-default">
                    <input type="file" onChange={selectFile} />
                  </label>
                  <button
                    className="btn btn-success"
                    disabled={!selectedFiles}
                    onClick={uploadCertificate}
                  >
                    Tax Certificate
                  </button>
                  <div className="alert alert-light" role="alert">
                    {message}
                  </div>
                </div>
                <p>
                  issue Date:
                  <div>
                    <DatePicker
                      selected={issueDate}
                      onChange={(issueDate) => setIssueDate(issueDate)}
                    />
                  </div>
                </p>
                <p>
                  Expired Date:
                  <div>
                    <DatePicker
                      selected={expireddate}
                      onChange={(expireddate) => setExpireddate(expireddate) }                      
                    />                   
                  </div>
                </p>
                <LoanTextField
                  id="TaxDebit"
                  label="TaxDebit"
                  value={tax.TaxDebit}
                  onChange={handleInputChangeTax}
                  name="TaxDebit"
                />
              </div>
            )}
            <div>
              <button
                className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => {
                  saveTax();
                }}
              >
                save
              </button>
            </div>
          </div>
        )}
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyTaxEditModalOpen}
        onClose={handleCompanyTaxEditCloseModal}
        title="tax edit information"
      >
        <div>
          <form onSubmit={updateTax}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="Name"
                label="Name"
                value={editTax.Name}
                onChange={handleInputChangeEditTax}
                name="Name"
              />
              <LoanTextField
                id="TinNo"
                label="TinNo"
                value={editTax.TinNo}
                onChange={handleInputChangeEditTax}
                name="TinNo"
              />
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onChange={(editTax.Certificate = newCertificate)}
                  onClick={uploadCertificate}
                >
                  Tax Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <p>
                Issue Date:
                <div>
                  <DatePicker
                    selected={issueDate}
                    onChange={(issueDate) => setIssueDate(issueDate)} 
                  />
                  </div>
              </p>
              <p>
                Expired Date:
                <div>
                  <DatePicker
                    selected={expireddate}
                    onChange={(expireddate) => setExpireddate(expireddate)                      
                    }                              
                    />  
                </div>
              </p>
              <LoanTextField
                id="TaxDebit"
                label="TaxDebit"
                value={editTax.TaxDebit}
                onChange={handleInputChangeEditTax}
                name="TaxDebit"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
             
              >
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewSoleTax;
