import { useState, useEffect } from "react";
import {
  BsArrowLeftShort,
  BsChevronDown,
  BsPlus,
  BsSearch,
} from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link, Navigate } from "react-router-dom";
import { useParams, useNavigate } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from "@mui/material";
// import { BsArrowLeftShort, BsSearch, BsChevronDown, BsPlus } from "react-icons/bs";
import CompanyModal from "./CompanyModal";
import { IconButton } from "@mui/material";
import { BsWindowPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { useForm } from "react-hook-form";
import { useUploadFile } from "dex-react-file-upload";
const LeftNav = () => {
  let navigate = useNavigate();
  const { Id } = useParams();

  const initialCompanyState = {
    Id: null,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  };
  //related to upload file
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  // const [folderName, setFolderName] = useState("");
  // debugger;
  const [currentFile, setCurrentFile] = useState(undefined);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const [fileInfos, setFileInfos] = useState([]);
  const [selectedPathLin, setSelectedPathLin] = useState();
  const [selectedPathTRN, setSelectedPathTRN] = useState();
  const [selectedPathLinEdit, setSelectedPathLinEdit] = useState();
  const [selectedPathTRNEdit, setSelectedPathTRNEdit] = useState();
  //related to registration number
  //edit Trade
  const selectFile1Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload1Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathTRNEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //releted to trade licence
  const selectFileEdit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const uploadEdit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLinEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end edit trade
  const selectFile1 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload1 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathTRN(response.data.Result);
        debugger;
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //releted to trade licence
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLin(response.data.Result);
        console.log("new path");
        console.log(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload file
  //edit company
  const [activeStep, setActiveStep] = useState(0);
  const [open, setOpen] = useState(true);
  const [subMenuOpen, setSubMenuOpen] = useState(false);
  const [leftNav, setLeftNav] = useState();
  const [submitted, setSubmitted] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [companyTypes, setCompanyTypes] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [companyId, setCompanyId] = useState();
  const [addressId, setAddressId] = useState();
  const [addressIdForLocal, setAddressIdForLocal] = useState();

  const [getAddress, setGetAddress] = useState();
  const [getContactInformation, setGetContactInformation] = useState();
  const [contactInformationId, setContactInformationId] = useState();
  const [getAccountInformation, setGetAccountInformation] = useState();
  const [getTradeInformation, setGetTradeInformation] = useState();
  const [
    getSoleAndPartinershipInformation,
    setGetSoleAndPartinershipInformation,
  ] = useState();
  const [accountInformationId, setAccountInformationId] = useState();
  const [tradeInformationId, setTradeInformationId] = useState();

  const [company, setCompany] = useState(initialCompanyState);
  //update company
  localStorage.setItem("CompanyId", companyId);
  // localStorage.setItem("addressIdForLocal", addressIdForLocal);
  // localStorage.setItem("ContactInformationId", contactInformationId);
  const [contactInfoIdForLocal, setContactInfoIdForLocal] = useState();
  const [accountInfoIdForLocal, setAccountInfoIdForLocal] = useState();
  const [tradeInfoIdForLocal, setTradeInfoIdForLocal] = useState();

  // const { Id } = useParams();AddressId

  //edit address end
  //update company information
  const [editCompany, setEditCompany] = useState({
    Id: companyId,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    VATNumber: "",
    VATCertificate: "",
    EntityTypeID: "",
  });
  const [editAddress, setEditAddress] = useState({
    Id: addressId,
    CountryId: "",
    RegionId: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  });
  const [editContactInformation, setEditContactInformation] = useState({
    Id: contactInformationId,
    DesignatedPersonFName: "",
    DesignatedPersonMName: "",
    DesignatedPersonLName: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
  });
  const [editAccountInfo, setEditAccountInfo] = useState({
    Id: accountInformationId,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    District: "",
    Other: "",
  });
  const [editTradeInfo, setEditTradeInfo] = useState({
    Id: tradeInformationId,
    TradeRegistrationNo: "",
    UploadTradeRegCerti: selectedPathTRNEdit,
    TradeLicenseNo: "",
    IssueDate: "",
    RenewedDate: "",
    ValidityDate: "",
    TradeBusinessTypeID: "",
    UploadTradeLicense: selectedPathLinEdit,
  });
  const updateCompanyById = (companyId) => {
    loanDataService
      .getCompanyById(companyId)
      .then((res) => {
        setEditCompany({
          ...editCompany,
          Id: companyId,
          Name: res.data.Result.Name,
          NoOfBranch: res.data.Result.NoOfBranch,
          EstablishmentYear: res.data.Result.EstablishmentYear,
          TINNumber: res.data.Result.TINNumber,
          TINCertificate: res.data.Result.TINCertificate,
          CompanyType: res.data.Result.CompanyType,
          VATNumber: res.data.Result.VATNumber,
          VATCertificate: res.data.Result.VATCertificate,
          EntityType: res.data.Result.EntityType,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateCompanyById();
  }, []);
  const updateCompanyInfo = () => {
    loanDataService
      .updateCompany(companyId, editCompany)
      .then((response) => {
        // navigation("/company/1");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateCompanyInfo();
  }, []);
  const getAddressByCompanyId = (addressId) => {
    loanDataService
      .getAddressById(addressId)
      .then((res) => {
        setEditAddress({
          ...editAddress,
          Id: addressId,
          CountryId: res.data.Result.CountryId,
          RegionId: res.data.Result.RegionId,
          ZoneSubCity: res.data.Result.ZoneSubCity,
          Woreda: res.data.Result.Woreda,
          City: res.data.Result.City,
          Kebele: res.data.Result.Kebele,
          HouseNo: res.data.Result.HouseNo,
          BuildingNo: res.data.Result.BuildingNo,
          StreetAddress: res.data.Result.StreetAddress,
        });
        // debugger;
        setAddressIdForLocal(addressId);
        debugger;
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getAddressByCompanyId();
  }, []);
  const updateCompanyAddressById = () => {
    // event.preventDefault();
    loanDataService
      .updateCompanyAddress(addressIdForLocal, editAddress)
      .then((response) => {
        // navigation("/company/1");

        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateCompanyAddressById();
  }, []);
  const getContactInformationBy = (contactInformationId) => {
    loanDataService
      .getContactInformationById(contactInformationId)
      .then((res) => {
        setEditContactInformation({
          ...editContactInformation,
          Id: contactInformationId,
          DesignatedPersonFName: res.data.Result.DesignatedPersonFName,
          DesignatedPersonMName: res.data.Result.DesignatedPersonMName,
          DesignatedPersonLName: res.data.Result.DesignatedPersonLName,
          Position: res.data.Result.Position,
          MobileNo: res.data.Result.MobileNo,
          Telephone: res.data.Result.Telephone,
          Email: res.data.Result.Email,
        });
        // debugger;
        setContactInfoIdForLocal(contactInformationId);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getContactInformationBy();
  }, []);
  const updateCompanyContactInformationById = () => {
    // event.preventDefault();
    loanDataService
      .updateCompanyContactInformation(
        contactInfoIdForLocal,
        editContactInformation
      )
      .then((response) => {
        // navigation("/company/1");
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
    // debugger;
  };
  useEffect(() => {
    updateCompanyContactInformationById();
  }, []);

  const getAccountInformationBy = (accountInformationId) => {
    loanDataService
      .getAccountInformationById(accountInformationId)
      .then((res) => {
        setEditAccountInfo({
          ...editAccountInfo,
          Id: accountInformationId,
          AccountNo: res.data.Result.AccountNo,
          CustomerId: res.data.Result.CustomerId,
          Branch: res.data.Result.Branch,
          District: res.data.Result.District,
          Other: res.data.Result.Other,
        });
        // debugger;
        setAccountInfoIdForLocal(accountInformationId);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getContactInformationBy();
  }, []);
  const updateCompanyAccountInformationById = () => {
    // event.preventDefault();
    loanDataService
      .updateCompanyAccountInformation(accountInfoIdForLocal, editAccountInfo)
      .then((response) => {
        // navigation("/company/1");
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
    // debugger;
  };
  useEffect(() => {
    updateCompanyAccountInformationById();
  }, []);

  const getTradeInformationBy = (tradeInformationId) => {
    loanDataService
      .getTradeInformationById(tradeInformationId)
      .then((res) => {
        setEditTradeInfo({
          ...editTradeInfo,
          Id: tradeInformationId,
          TradeRegistrationNo: res.data.Result.TradeRegistrationNo,
          UploadTradeRegCerti: res.data.Result.UploadTradeRegCerti,
          TradeLicenseNo: res.data.Result.TradeLicenseNo,
          IssueDate: res.data.Result.IssueDate,
          RenewedDate: res.data.Result.RenewedDate,
          ValidityDate: res.data.Result.ValidityDate,
          TradeBusinessTypeID: res.data.Result.TradeBusinessTypeID,
          UploadTradeLicense: res.data.Result.UploadTradeLicense,
        });
        setTradeInfoIdForLocal(tradeInformationId);
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getTradeInformationBy();
  }, []);
  const updateCompanyTradeInformationById = () => {
    loanDataService
      .updateCompanyTradeInformation(tradeInfoIdForLocal, editTradeInfo)
      .then((response) => {
        // navigation("/company/1");
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
    debugger;
  };
  useEffect(() => {
    updateCompanyTradeInformationById();
  }, []);

  //end update information
  //adress
  const initialAdressState = {
    Id: null,
    CountryID: "",
    RegionID: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  };
  const [address, setAddress] = useState(initialAdressState);
  //end address
  //start contact information
  const initialContactInfoState = {
    Id: null,
    DesignatedPersonFName: "",
    DesignatedPersonMName: "",
    DesignatedPersonLName: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
  };
  const [contactInfo, setContactInfo] = useState(initialContactInfoState);
  //end address
  //account Info
  const initialAccountInfoState = {
    Id: null,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    District: "",
    Other: "",
  };
  const [accountInfo, setAccountInfo] = useState(initialAccountInfoState);
  //tax
  const initialTaxSoleInfoState = {
    Id: null,
    Name: "",
    TinNo: "",
    IssueDate: "",
    ExpiredDate: "",
    TaxDebi: "",
    TaxClearanceCertificate: "",
  };
  const [taxSoleInfo, setTaxSoleInfo] = useState(initialTaxSoleInfoState);
  //trade information
  const initialTradeInfoState = {
    Id: null,
    TradeRegistrationNo: "",
    UploadTradeRegCerti: "",
    TradeLicenseNo: "",
    IssueDate: "",
    RenewedDate: "",
    ValidityDate: "",
    TradeBusinessTypeID: "",
    UploadTradeLicense: "",
  };
  const [tradeInfo, setTradeInfo] = useState(initialTradeInfoState);
  // const [country, setCountry] = useState("");
  const initialSoleAndPartnershipState = {
    Id: null,
    TaxClearanceID: "",
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    MartailStatusID: "",
    // UploadTradeLicense: "",
  };

  const [soleAndPartnership, setSoleAndPartnership] = useState(
    initialSoleAndPartnershipState
  );

  const [city, setCity] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [selectedValue, setSelectedValue] = useState(null);
  //new
  const [country, setContry] = useState([]);
  const [region, setRegion] = useState([]);
  const [holdId, setHoldId] = useState([]);
  const [holdUserId, setHoldUserId] = useState([]);

  const [tradeSector, setTradeSector] = useState([]);
  const [holdTradeSectorId, setHoldTradeSectorId] = useState([]);
  const [tradeIndustryType, setTradeIndustryType] = useState([]);
  const [tradeIndustryTypeId, setTradeIndustryTypeId] = useState([]);
  const [tradeBusinessType, setTradeBusinessType] = useState([]);
  const [maritalStatus, setMaritalStatus] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCompanyModalOpen, setIsCompanyModalOpen] = useState(false);
  const [isCompanyAddressModalOpen, setIsCompanyAddressModalOpen] =
    useState(false);
  //update company information
  const [
    isCompanyTradeInformationEditModalOpen,
    setIsCompanyTradeInformationEditModalOpen,
  ] = useState(false);
  const [isCompanyAddressEditModalOpen, setIsCompanyAddressEditModalOpen] =
    useState(false);
  const [
    isCompanyAccountInformationEditModalOpen,
    setIsCompanyAccountInformationEditModalOpen,
  ] = useState(false);
  const [
    isCompanyContactInformationEditModalOpen,
    setIsCompanyContactInformationEditModalOpen,
  ] = useState(false);
  const [isCompanyEditModalOpen, setIsCompanyEditModalOpen] = useState(false);
  //update company information end
  const [isCompanyContactModalOpen, setIsCompanyContactModalOpen] =
    useState(false);
  const [isCompanyAccountModalOpen, setIsCompanyAccountModalOpen] =
    useState(false);
  const [isCompanyTradeModalOpen, setIsCompanyTradeModalOpen] = useState(false);
  function handleGeneralCloseModal() {
    isCompanyTradeModalOpen(false);
  }
  const [isCompanyTaxModalOpen, setIsCompanyTaxModalOpen] = useState(false);
  const [HandleSoleId, setHandleSoleId] = useState(false);
  function handleGeneralCloseModal() {
    isCompanyTaxModalOpen(false);
  }
  const [isCompanyTaxSoleModalOpen, setIsCompanyTaxSoleModalOpen] =
    useState(false);
  function handleGeneralCloseModal() {
    isCompanyTaxSoleModalOpen(false);
  }
  const [
    isCompanySoleAndPrtinershipModalOpen,
    setIsCompanySoleAndPrtinershipModalOpen,
  ] = useState(false);
  function handleGeneralCloseModal() {
    isCompanySoleAndPrtinershipModalOpen(false);
  }
  function handleGeneralCloseModal() {
    isCompanyAccountModalOpen(false);
  }
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }
  // company modal start
  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }
  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
  function handleCompanyTaxSoleOpenModal(Id) {
    setIsCompanyTaxSoleModalOpen(true);
    setHandleSoleId(Id)
    
  }
  function handleCompanyTaxSoleCloseModal() {
    setIsCompanyTaxSoleModalOpen(false);
  }
  function handleCompanySoleAndPrtinershipOpenModal() {
    setIsCompanySoleAndPrtinershipModalOpen(true);
  }
  function handleCompanySoleAndPrtinershipCloseModal() {
    setIsCompanySoleAndPrtinershipModalOpen(false);
  }
  function handleCompanyTradeOpenModal() {
    setIsCompanyTradeModalOpen(true);
  }
  function handleCompanyTradeCloseModal() {
    setIsCompanyTradeModalOpen(false);
  }
  function handleCompanyTaxOpenModal() {
    setIsCompanyTaxModalOpen(true);
  }
  function handleCompanyTaxCloseModal() {
    setIsCompanyTaxModalOpen(false);
  }
  function handleCompanyAccountOpenModal() {
    setIsCompanyAccountModalOpen(true);
  }
  function handleCompanyAccountCloseModal() {
    setIsCompanyAccountModalOpen(false);
  }
  function handleCompanyAddressCloseModal() {
    setIsCompanyAddressModalOpen(false);
  }

  function handleCompanyAddressOpenModal() {
    setIsCompanyAddressModalOpen(true);
  }
  //update company information
  function handleCompanyTradeInformationEditOpenModal() {
    setIsCompanyTradeInformationEditModalOpen(true);
  }
  function handleCompanyTradeInformationEditCloseModal() {
    setIsCompanyTradeInformationEditModalOpen(false);
  }
  function handleCompanyAccountInformationEditOpenModal() {
    setIsCompanyAccountInformationEditModalOpen(true);
  }
  function handleCompanyAccountInformationEditCloseModal() {
    setIsCompanyAccountInformationEditModalOpen(false);
  }
  function handleCompanyContactInformationEditOpenModal() {
    setIsCompanyContactInformationEditModalOpen(true);
  }
  function handleCompanyContactInformationEditCloseModal() {
    setIsCompanyContactInformationEditModalOpen(false);
  }
  function handleCompanyAddressEditOpenModal() {
    setIsCompanyAddressEditModalOpen(true);
  }
  function handleCompanyAddressEditCloseModal() {
    setIsCompanyAddressEditModalOpen(false);
  }
  function handleCompanyEditOpenModal() {
    // this.setCompanyId(compId); //test
    setIsCompanyEditModalOpen(true);
  }
  function handleCompanyEditCloseModal() {
    setIsCompanyEditModalOpen(false);
  }
  //end update company information

  function handleCompanyContactCloseModal() {
    setIsCompanyContactModalOpen(false);
  }

  function handleCompanyContactOpenModal() {
    setIsCompanyContactModalOpen(true);
  }
  // company modal end
  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setCompany({ ...company, [name]: value });
  };

  // debugger;
  const saveCompany = () => {
    var data = {
      Name: company.Name,
      NoOfBranch: company.NoOfBranch,
      EstablishmentYear: company.EstablishmentYear,
      TINNumber: company.TINNumber,
      TINCertificate: company.TINCertificate,
      CompanyTypeID: company.CompanyTypeID,
      EntityTypeID: company.EntityTypeID,
      VATNumber: company.VATNumber,
      VATCertificate: company.VATCertificate,
    };
    loanDataService
      .addCompany(data, holdUserId)
      .then((response) => {
        setCompany({
          Id: response.data.Id,
          Name: response.data.Name,
          NoOfBranch: response.data.NoOfBranch,
          EstablishmentYear: response.data.EstablishmentYear,
          TINNumber: response.data.TINNumber,
          TINCertificate: response.data.TINCertificate,
          CompanyTypeID: response.data.CompanyTypeID,
          EntityTypeID: response.data.EntityTypeID,
          VATNumber: response.data.VATNumber,
          VATCertificate: response.data.VATCertificate,
        });
        setSubmitted(true);
        console.log(response.data);
        setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //contact Info
  const handleInputChangeContactInfo = (event) => {
    const { name, value } = event.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };
  const saveContactInfo = () => {
    var data = {
      DesignatedPersonFName: contactInfo.DesignatedPersonFName,
      DesignatedPersonMName: contactInfo.DesignatedPersonMName,
      DesignatedPersonLName: contactInfo.DesignatedPersonLName,
      Position: contactInfo.Position,
      MobileNo: contactInfo.MobileNo,
      Telephone: contactInfo.Telephone,
      Email: contactInfo.Email,
    };
    loanDataService
      .addContactInfo(data, holdId)
      .then((response) => {
        setContactInfo({
          Id: response.data.Id,
          DesignatedPersonFName: response.data.DesignatedPersonFName,
          DesignatedPersonMName: response.data.DesignatedPersonMName,
          DesignatedPersonLName: response.data.DesignatedPersonLName,
          Position: response.data.Position,
          MobileNo: response.data.MobileNo,
          Telephone: response.data.Telephone,
          Email: response.data.Email,
        });
        setSubmitted(true);
        console.log(response.data);
        // setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
