                            {currentFile && (
                              <div className="progress">
                                <div
                                  className="progress-bar progress-bar-info progress-bar-striped"
                                  role="progressbar"
                                  aria-valuenow={progress}
                                  aria-valuemin="0"
                                  aria-valuemax="100"
                                  style={{ width: progress + "%" }}
                                >
                                  {progress}%
                                </div>
                              </div>
                            )}

                            <label className="btn btn-default">
                              <input type="file" onChange={selectFileEdit} />
                            </label>

                            <button
                              className="btn btn-success"
                              disabled={!selectedFiles}
                              onClick={uploadEdit}
                            >
                              Upload
                            </button>
                            <div className="alert alert-light" role="alert">
                              {message}
                            </div>
                          </div>
                        </div>
                        <button type="submit" className="btn btn-info">
                          Update
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </CompanyModal>
            </>
          )}
          {activeStep === 6 && (
            <>
              <div className="flex">
                <button
                  className="text-purple-600 inline w-[150px] rounded-md font-medium "
                  onClick={handleBack}
                >
                  <span>
                    <IconButton aria-label="Skip" color="secondary">
                      <RiSkipBackMiniFill /> Back
                    </IconButton>
                  </span>
                </button>
                <button
                  onClick={() => {
                    handleCompanySoleAndPrtinershipOpenModal();
                    newSoleAndPartnership();
                  }}
                  className="text-purple-600 rounded-md font-medium "
                >
                  <span>
                    <IconButton aria-label="Add" color="secondary">
                      <BsPlus /> Add
                    </IconButton>
                  </span>
                  {/* if the company is already registered, Skip button will active  */}
                </button>
                <button
                  className="text-purple-600 inline w-[150px] rounded-md font-medium "
                  onClick={handleSkip}
                >
                  <span>
                    <IconButton aria-label="Skip" color="secondary">
                      <BiSkipNext /> Skip
                    </IconButton>
                  </span>
                </button>
              </div>
              <CompanyModal
                isOpen={isCompanySoleAndPrtinershipModalOpen}
                onClose={handleCompanySoleAndPrtinershipCloseModal}
                title="Add company sole and partinership information"
              >
                <div>
                  <div className="submit-form">
                    {submitted ? (
                      <div>
                        <h4>You submitted successfully!</h4>
                        <button
                          className="btn btn-success"
                          onClick={newTradeInfo}
                        >
                          Add
                        </button>
                      </div>
                    ) : (
                      <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {/* <TextField
                          id="TaxClearanceID"
                          label="Tax Clearance"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.TaxClearanceID}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="TaxClearanceID"
                        /> */}
                        <TextField
                          id="FirstName"
                          label="FirstName"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.FirstName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="FirstName"
                        />
                        <TextField
                          id="MiddleName"
                          label="Middle Name"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.MiddleName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="MiddleName"
                        />
                        <TextField
                          id="LastName"
                          label="Last Name"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.LastName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="LastName"
                        />
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Nationality{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.NationalityID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Country Of Origin{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.CountryOfOriginID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Country Of Residence{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.CountryOfResidenceID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Proof Of Identity Type{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.ProofOfIdentityTypeID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {proofOfIdentityType.map(
                              (yourProofOfIdentityType) => (
                                <MenuItem
                                  key={yourProofOfIdentityType.Id}
                                  ProofOfIdentityTypeID={
                                    yourProofOfIdentityType.Id
                                  }
                                  value={yourProofOfIdentityType.Id}
                                >
                                  {yourProofOfIdentityType.Name}
                                </MenuItem>
                              )
                            )}
                          </Select>
                        </FormControl>
                        {/* <TextField
                      id="ProofOfIdentityTypeID"
                      label="Proof Of Identity Type"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.ProofOfIdentityTypeID}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="ProofOfIdentityTypeID"
                    /> */}

                        <TextField
                          id="UploadProofOfIdentity"
                          label="UploadProofOfIdentity"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.UploadProofOfIdentity}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="UploadProofOfIdentity"
                        />
                        <TextField
                          id="ProofOfIdentityIssuedDate"
                          label="ProofOfIdentityIssuedDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.ProofOfIdentityIssuedDate}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="ProofOfIdentityIssuedDate"
                        />
                        <TextField
                          id="ProofOfIdentityExpiredDate"
                          label="ProofOfIdentityExpiredDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.ProofOfIdentityExpiredDate}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="ProofOfIdentityExpiredDate"
                        />
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Martail Status{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.MartailStatusID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {maritalStatus.map((maritalSt) => (
                              <MenuItem
                                key={maritalSt.Id}
                                NationalityID={maritalSt.Id}
                                value={maritalSt.Id}
                              >
                                <button>{maritalSt.Name}</button>
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        {/* <div>
                        if({maritalSt.Name} === "Married" ){
                          <h>here is married</h>
                        }
                        else if({maritalSt.Name} === "unMarried"){
                          <h1>unmarried</h1>
                        }
                        else if(maritalSt.Name === widowed){
                          <h1>widowed</h1>
                        }
                        else{
                          <h1>divorced</h1>
                        }
                        </div> */}
                        {/* here is go 
                    <TextField
                      id="MartailStatusID"
                      label="MartailStatusID"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.MartailStatusID}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="MartailStatusID"
                    /> */}
                      </div>
                    )}
                  </div>
                  <div className="flex justify-between">
                    <button
                      className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      onClick={handleBack}
                    >
                      Back
                    </button>
                    <button
                      className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      onClick={saveSoleAndPartnership}
                    >
                      submit
                    </button>
                  </div>
                </div>
              </CompanyModal>
              <div className="rounded-lg overflow-hidden">
                <table className="table table-bordered table-striped bg-purple-100">
                  <thead>
                    <th className="px-2 py-4"> First Name </th>
                    <th className="px-2 py-4"> Middle Name </th>
                    <th className="px-2 py-4"> Last Name </th>
                    <th className="px-2 py-4"> Nationality ID </th>
                    <th className="px-2 py-4"> Country Of Origin </th>
                    <th className="px-2 py-4"> Country Of Residence </th>
                    <th className="px-2 py-4"> Proof Of Identity Type</th>

                    <th className="px-2 py-4">
                      {" "}
                      Proof Of Identity Issued Date
                    </th>
                    <th className="px-2 py-4">
                      {" "}
                      Proof Of Identity Expired Date
                    </th>
                    <th className="px-2 py-4"> Martail Status</th>
                    <th className="px-2 py-4"> Tax Clearance</th>
                    <th className="px-2 py-4"> Action</th>
                  </thead>
                  <tbody>
                    {getSoleAndPartinershipInformation.map((sole, index) => (
                      // <tr key={loan.loan_id} rowSpan="3">
                      <tr key={index}>
                        <td>{sole.FirstName}</td>
                        <td>{sole.MiddleName}</td>
                        <td>{sole.LastName}</td>
                        <td>{sole.country}</td>
                        <td>{sole.CountryOfOriginID}</td>
                        <td>{sole.CountryOfResidenceID}</td>
                        <td>{sole.ProofOfIdentityTypeID}</td>
                        <td>{sole.ProofOfIdentityIssuedDate}</td>
                        <td>{sole.ProofOfIdentityExpiredDate}</td>
                        <td>{sole.MartailStatusID}</td>
                        <td>
                          <button  onClick={() => {
                              handleCompanyTaxSoleOpenModal(sole.Id);
                              // getTradeInformationBy(sole.Id);
                              // saveTaxSoleInfo(sole.Id);
                            }}
                            className="text-purple-600 rounded-md font-semibold ">Tax detail</button>
                        </td>
                        <td>
                          {" "}
                          <button
                            onClick={() => {
                              handleCompanyTradeInformationEditOpenModal();
                              // saveTaxSoleInfo(sole.Id);
                            }}
                            className="text-purple-600 rounded-md font-semibold "
                          >
                            <span>
                              <IconButton aria-label="Add" color="secondary">
                                <BsPlus /> Edit
                              </IconButton>
                            </span>
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              {/* tiztaw */}
              <CompanyModal
                isOpen={isCompanyTaxSoleModalOpen}
                onClose={handleCompanyTaxSoleCloseModal}
                title="Add company sole and partinership information"
              >
                <div className="submit-form">
                    {submitted ? (
                      <div>
                        <h4>You submitted successfully!</h4>
                        <button
                          className="btn btn-success"
                          onClick={newTaxSoleInfo}
                        >
                          Add
                        </button>
                      </div>
                    ) : (
                      <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        <TextField
                          id="PrincipalName"
                          label="Principal Name"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.PrincipalName}
                          onChange={handleInputChangeTaxInfo}
                          name="PrincipalName"
                        />

                        <TextField
                          id="TINNo"
                          label="TINNo"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.TINNo}
                          onChange={handleInputChangeTaxInfo}
                          name="TINNo"
                        />
                        <TextField
                          id="IssueDate"
                          label="IssueDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.IssueDate}
                          onChange={handleInputChangeTaxInfo}
                          name="IssueDate"
                        />
                        <TextField
                          id="ExpiredDate"
                          label="ExpiredDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.ExpiredDate}
                          onChange={handleInputChangeTaxInfo}
                          name="ExpiredDate"
                        />
                        <TextField
                          id="TaxDebitIfAny"
                          label="TaxDebitIfAny"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.TaxDebitIfAny}
                          onChange={handleInputChangeTaxInfo}
                          name="TaxDebitIfAny"
                        />
                        <TextField
                          id="UploadTaxClearanceCert"
                          label="UploadTaxClearanceCert"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={taxInfo.UploadTaxClearanceCert}
                          onChange={handleInputChangeTaxInfo}
                          name="UploadTaxClearanceCert"
                        />
                      </div>
                    )}
                  </div>
              </CompanyModal>
            </>
          )}
          {activeStep === 7 && (
            <div>
              <h2 className="text-2xl font-bold mb-4">
                Thank you for submitting the form!
              </h2>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
export default LeftNav;
