import { useState, useEffect } from "react";
import CompanyModal from "../Common/CompanyModal";
import LoanTextField from "../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import PLCAndShareService from "./PLCAndShareService";
const ShareHolder = ({
    holdShareHolder,
    PLCAndShareInformationId,
    isCompanyShareHolderModalOpen,
    handleCompanyShareHolderCloseModal,
}) => {
    const [currency, setCurrency] = useState([]);
    const [
        isCompanyShareHolderEditedModalOpen,
        setIsCompanyShareHolderEditedModalOpen,
    ] = useState(false);
    function handleCompanyShareHolderEditCloseModal() {
        setIsCompanyShareHolderEditedModalOpen(false);
    }
    function handleCompanyShareHolderEditedModalOpen() {
        setIsCompanyShareHolderEditedModalOpen(true);
    }
    const [ShareHolderInformationId, setShareHolderId] = useState();
    const [editShareHolder, setEditShareHolder] = useState({
        CurrencyId: "",
        FirstName: "",
        LastName: "",
        MiddleName: "",
        PerValue: "",
        NumberOfShare: "",
        ShareAmount: "",
        SharePercentage: "",
        CumulativeShare: "",


    });
    const handleInputChangeEditShareHolder = (event) => {
        const { name, value } = event.target;
        setEditShareHolder({ ...editShareHolder, [name]: value });
    };
    const getShareHolderById = (ShareHolderInformationId) => {
        PLCAndShareService.getShareHolderById(ShareHolderInformationId)
            .then((res) => {
                setEditShareHolder({
                    ...editShareHolder,
                    CurrencyId: res.data.Result.CurrencyId,
                    FirstName: res.data.Result.FirstName,
                    LastName: res.data.Result.LastName,
                    MiddleName: res.data.Result.MiddleName,
                    PerValue: res.data.Result.PerValue,
                    NumberOfShare: res.data.Result.NumberOfShare,
                    ShareAmount: res.data.Result.ShareAmount,
                    SharePercentage: res.data.Result.SharePercentage,
                    CumulativeShare: res.data.Result.CumulativeShare,

                });
            })
            .catch((err) => console.log(err));
    };
    useEffect(() => {
        getShareHolderById();
    }, []);
    const updateShareHolderById = () => {
        PLCAndShareService.updateShareHolder(ShareHolderInformationId, editShareHolder)
            .then((response) => { })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        updateShareHolderById();
    }, []);



    const [toggleShareHolder, setToggleShareHolder] = useState(false);
    const initialShareHolderState = {
        Id: null,
        CurrencyId: "",
        FirstName: "",
        LastName: "",
        MiddleName: "",
        PerValue: "",
        NumberOfShare: "",
        ShareAmount: "",
        SharePercentage: "",
        CumulativeShare: "",

    };
    const [ShareHolder, setShareHolder] = useState(initialShareHolderState);
    const handleInputChangeShareHolder = (event) => {
        const { name, value } = event.target;
        setShareHolder({ ...ShareHolder, [name]: value });
    };
    const saveShareHolder = () => {
        var data = {
            CurrencyId: ShareHolder.CurrencyId,
            FirstName: ShareHolder.FirstName,
            LastName: ShareHolder.LastName,
            MiddleName: ShareHolder.MiddleName,
            PerValue: ShareHolder.PerValue,
            NumberOfShare: ShareHolder.NumberOfShare,
            ShareAmount: ShareHolder.ShareAmount,
            SharePercentage: ShareHolder.SharePercentage,
            CumulativeShare: ShareHolder.CumulativeShare,

        };
        PLCAndShareService.addShareHolder(data, PLCAndShareInformationId)
            .then((response) => {
                setShareHolder({
                    Id: response.data.Id,
                    CurrencyId: response.data.CurrencyId,
                    FirstName: response.data.FirstName,
                    LastName: response.data.LastName,
                    MiddleName: response.data.MiddleName,
                    PerValue: response.data.PerValue,
                    NumberOfShare: response.data.NumberOfShare,
                    ShareAmount: response.data.ShareAmount,
                    SharePercentage: response.data.SharePercentage,
                    CumulativeShare: response.data.CumulativeShare,

                });
            })
            .catch((e) => {
                console.log(e);
            });
    };

    
  const getCurrency = () => {

    PLCAndShareService.getCurrency()

      .then((response) => {

        setCurrency(response.data.Result);

        console.log(response.data.Result);

      })

      .catch((e) => {

        console.log(e);

      });

  };

  useEffect(() => {

    getCurrency();

  }, []);
    return (
        <div>
            <CompanyModal
                isOpen={isCompanyShareHolderModalOpen}
                onClose={handleCompanyShareHolderCloseModal}
                title="view Share Holder information"
            >
                <div className="rounded-lg overflow-hidden">
                    {holdShareHolder ? (
                        <table className="table table-bordered table-striped bg-purple-100">
                            <thead>
                                <th className="px-2 py-4"> ID</th>
                                <th className="px-2 py-4"> FirstName</th>
                                <th className="px-2 py-4"> LastName</th>
                                <th className="px-2 py-4"> MiddleName</th>
                                <th className="px-2 py-4"> Currency</th>
                                <th className="px-2 py-4"> PerValue</th>
                                <th className="px-2 py-4"> NumberOfShare</th>
                                <th className="px-2 py-4"> ShareAmount</th>
                                <th className="px-2 py-4"> SharePercentage</th>
                                <th className="px-2 py-4"> CumulativeShare</th>
                                <th className="px-2 py-4"> Action </th>
                            </thead>
                            <tbody>
                                <>
                                    {holdShareHolder.map((holdShareHolder, index) => (
                                        <tr>
                                            <td>{holdShareHolder.Id}</td>
                                            <td>{holdShareHolder.FirstName}</td>
                                            <td>{holdShareHolder.LastName}</td>
                                            <td>{holdShareHolder.MiddleName}</td>
                                            <td>{holdShareHolder.Currency}</td>
                                            <td>{holdShareHolder.PerValue}</td>
                                            <td>{holdShareHolder.NumberOfShare}</td>
                                            <td>{holdShareHolder.ShareAmount}</td>
                                            <td>{holdShareHolder.SharePercentage}</td>
                                            <td>{holdShareHolder.CumulativeShare}</td>
                                            <td>
                                                <button
                                                    onClick={() => {
                                                        handleCompanyShareHolderEditedModalOpen();
                                                        getShareHolderById(holdShareHolder.Id);
                                                        setShareHolderId(holdShareHolder.Id);
                                                    }}
                                                    className="text-purple-600 rounded-md font-semibold "
                                                >
                                                    Edit
                                                </button>
                                            </td>
                                            <td>
                                            </td>
                                        </tr>
                                    ))}
                                </>

                            </tbody>
                        </table>
                    ) : (
                        ""
                    )}
                    <button
                        onClick={() => setToggleShareHolder(!toggleShareHolder)}
                        className="text-purple-600 rounded-md font-medium "
                    >
                        Add Share Holder
                    </button>
                    {toggleShareHolder &&
                        <div className="submit-form">
                            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">

                                <LoanTextField
                                    id="FirstName"
                                    label="FirstName"
                                    value={ShareHolder.FirstName}
                                    onChange={handleInputChangeShareHolder}
                                    name="FirstName"
                                />
                                <LoanTextField
                                    id="LastName"
                                    label="LastName"
                                    value={ShareHolder.LastName}
                                    onChange={handleInputChangeShareHolder}
                                    name="LastName"
                                />
                                <LoanTextField
                                    id="MiddleName"
                                    label="MiddleName"
                                    value={ShareHolder.MiddleName}
                                    onChange={handleInputChangeShareHolder}
                                    name="MiddleName"
                                />
                                <LoanTextField
                                    id="PerValue"
                                    label="PerValue"
                                    value={ShareHolder.PerValue}
                                    onChange={handleInputChangeShareHolder}
                                    name="PerValue"
                                />
                                <LoanTextField
                                    id="NumberOfShare"
                                    label="NumberOfShare"
                                    value={ShareHolder.NumberOfShare}
                                    onChange={handleInputChangeShareHolder}
                                    name="NumberOfShare"
                                />
                                <LoanTextField
                                    id="ShareAmount"
                                    label="ShareAmount"
                                    value={ShareHolder.ShareAmount}
                                    onChange={handleInputChangeShareHolder}
                                    name="ShareAmount"
                                />
                                <FormControl variant="standard" sx={{ minWidth: 120 }}>

                                    <InputLabel id="countrylbl" color="secondary">

                                        currency

                                    </InputLabel>

                                    <Select

                                        color="secondary"

                                        onChange={(event) =>

                                            (ShareHolder.CurrencyId = event.target.value)

                                        }

                                        labelId="loanType"

                                        id="loanTypeSelected"

                                        label="loanType"

                                    >

                                        <MenuItem value="">

                                            <em>None</em>

                                        </MenuItem>

                                        {currency.map((yourCurrency) => (

                                            <MenuItem

                                                key={yourCurrency.Id}

                                                CurrencyId={yourCurrency.Id}

                                                value={yourCurrency.Id}

                                            >

                                                {yourCurrency.Name}

                                            </MenuItem>

                                        ))}

                                    </Select>

                                </FormControl>
                                <LoanTextField
                                    id="SharePercentage"
                                    label="SharePercentage"
                                    value={ShareHolder.SharePercentage}
                                    onChange={handleInputChangeShareHolder}
                                    name="SharePercentage"
                                />
                                <LoanTextField
                                    id="CumulativeShare"
                                    label="CumulativeShare"
                                    value={ShareHolder.CumulativeShare}
                                    onChange={handleInputChangeShareHolder}
                                    name="CumulativeShare"
                                />


                            </div>

                            <div>
                                <button
                                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={() => {
                                        saveShareHolder();
                                    }}
                                >
                                    save
                                </button>
                            </div>
                        </div>
                    }
                </div>
            </CompanyModal>
            <CompanyModal
                isOpen={isCompanyShareHolderEditedModalOpen}
                onClose={handleCompanyShareHolderEditCloseModal}
                title="Edit Share Holder information"
            >
                <div>
                    <form onSubmit={updateShareHolderById}>

                        <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">


                            <LoanTextField
                                id="FirstName"
                                label="FirstName"
                                value={editShareHolder.FirstName}
                                onChange={handleInputChangeEditShareHolder}
                                name="FirstName"
                            />
                            <LoanTextField
                                id="LastName"
                                label="LastName"
                                value={editShareHolder.LastName}
                                onChange={handleInputChangeEditShareHolder}
                                name="LastName"
                            />
                            <LoanTextField
                                id="MiddleName"
                                label="MiddleName"
                                value={editShareHolder.MiddleName}
                                onChange={handleInputChangeEditShareHolder}
                                name="MiddleName"
                            />
                            <LoanTextField
                                id="PerValue"
                                label="PerValue"
                                value={editShareHolder.PerValue}
                                onChange={handleInputChangeEditShareHolder}
                                name="PerValue"
                            />
                            <LoanTextField
                                id="NumberOfShare"
                                label="NumberOfShare"
                                value={editShareHolder.NumberOfShare}
                                onChange={handleInputChangeEditShareHolder}
                                name="NumberOfShare"
                            />
                            <LoanTextField
                                id="ShareAmount"
                                label="ShareAmount"
                                value={editShareHolder.ShareAmount}
                                onChange={handleInputChangeEditShareHolder}
                                name="ShareAmount"
                            />
                            <FormControl variant="standard" sx={{ minWidth: 120 }}>

                                <InputLabel id="countrylbl" color="secondary">

                                    currency

                                </InputLabel>

                                <Select

                                    color="secondary"

                                    onChange={(event) =>

                                        (editShareHolder.CurrencyId = event.target.value)

                                    }

                                    labelId="loanType"

                                    id="loanTypeSelected"

                                    label="loanType"

                                >

                                    <MenuItem value="">

                                        <em>None</em>

                                    </MenuItem>

                                    {currency.map((yourCurrency) => (

                                        <MenuItem

                                            key={yourCurrency.Id}

                                            CurrencyId={yourCurrency.Id}

                                            value={yourCurrency.Id}

                                        >

                                            {yourCurrency.Name}

                                        </MenuItem>

                                    ))}

                                </Select>

                            </FormControl>
                            <LoanTextField
                                id="SharePercentage"
                                label="SharePercentage"
                                value={editShareHolder.SharePercentage}
                                onChange={handleInputChangeEditShareHolder}
                                name="SharePercentage"
                            />
                            <LoanTextField
                                id="CumulativeShare"
                                label="CumulativeShare"
                                value={editShareHolder.CumulativeShare}
                                onChange={handleInputChangeEditShareHolder}
                                name="CumulativeShare"
                            />



                        </div>
                        <div>
                            <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                                Update
                            </button>
                        </div>
                    </form>

                </div>

            </CompanyModal>
        </div>
    );
};
export default ShareHolder;
