import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import { IoTrashOutline } from "react-icons/io5";
import { useParams, useNavigate } from "react-router-dom";
import CompanyNavigation from "../../../Common/CompanyNavigation";
import LoanIconButton from "../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../Common/CompanyModal";
import { Link } from "react-router-dom";
import CommonServices from "../../Common/CommonServices";
// import AddCreditFacilityUtilizition from "./AddCreditPerformance";
import preshipmentFacilityServivice from "./PreshipmentFacilityService";
import AddPreshipmentFacility from "./AddPreshipmentFacility";
import LoanTextField from "../../../Common/LoanTextField";
const ViewPreshipmentFacility = ({
  isPreshipmentFacilityModalOpen,
  handlePreshipmentFacilityCloseModal,
  setIsPreshipmentFacilityModalOpen,
  creditFacilityUtilizitionId,
  preshipmentFacility,
}) => {
  const [CreditPerformanceErrorLoading, setCreditPerformanceErrorLoading] =
    useState(false);
  const navigate = useNavigate();
  // const { companyId } = useParams();
  const [loanTypeId, setLoanTypeId] = useState([]);
  const [currencyId, setCurrencyId] = useState([]);
  const [preshipmentFacilityId, setPreshipmentFacilityId] = useState([]);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [deletePreshipmentFacilityError, setPreshipmentFacilityDeleteError] =
    useState(null);
  const [error, setError] = useState(null);
  const [
    isPreshipmentFacilityEditModalOpen,
    setIsPreshipmentFacilityEditModalOpen,
  ] = useState(false);
  function handlePreshipmentFacilityEditOpenModal() {
    setIsPreshipmentFacilityEditModalOpen(true);
  }
  function handlePreshipmentFacilityEditCloseModal() {
    setIsPreshipmentFacilityEditModalOpen(false);
  }
  function handlePreshipmentFacilityEditOpenModal() {
    setIsPreshipmentFacilityEditModalOpen(true);
  }
  function handlePreshipmentFacilityEditCloseModal() {
    setIsPreshipmentFacilityEditModalOpen(false);
  }
  const handleInputChangePreshipmentFacility = (event) => {
    const { name, value } = event.target;
    setPreshipmentFacilitys({
      ...preshipmentFacilitys,
      [name]: value,
    });
  };
  const initialPreshipmentFacilityState = {
    Id: null,
    ApprovedFacilityLimit: "",
    TypesOfGoods: "",
    StartDate: "",
    MaturityDate: "",
    SalesContractVersion: "",
    SalesContractDate: "",
    SectorTypeId: "",
    CurrencyId: "",
    ContractValue: "",
    ShipmentDate: "",
    CountryId: "",
    IsPartialShipmentAllowed: "",
    IsLOTDefined: "",
    LOT1Value: "",
    LOT1ShipmentDate: "",
    LOT1DisbursementDate: "",
    LOT1DisbursementAmount: "",
    IsLOT1ShipmentMade: "",
    LOT1IncomingDate: "",
    LOT1ProceedValue: "",
    LOT1CurrencyId: "",
    LOT1ExchangeRate: "",
    LOT1LocalCurrencyAmount: "",
    IsProceedSettledDisbursementAmount: "",
    IsThereProceedRemainedToBeCollected: "",
    IsCustomerPresentedNewContractForAmendment: "",
    TurnOver: "",
    NextAction: "",
  };
  const [preshipmentFacilitys, setPreshipmentFacilitys] = useState(
    initialPreshipmentFacilityState
  );
  const newPreshipmentFacility = () => {
    setPreshipmentFacilitys(initialPreshipmentFacilityState);
    // setSubmitted(false);
  };
  const savePreshipmentFacility = () => {
    var data = {
      ApprovedFacilityLimit: preshipmentFacilitys.ApprovedFacilityLimit,
      TypesOfGoods: preshipmentFacilitys.preshipmentFacilitysTypesOfGoods,
      StartDate: preshipmentFacilitys.preshipmentFacilitysStartDate,
      MaturityDate: preshipmentFacilitys.preshipmentFacilitysMaturityDate,
      SalesContractVersion:
        preshipmentFacilitys.preshipmentFacilitysSalesContractVersion,
      SalesContractDate:
        preshipmentFacilitys.preshipmentFacilitysSalesContractDate,
      SectorTypeId: preshipmentFacilitys.preshipmentFacilitysSectorTypeId,
      CurrencyId: preshipmentFacilitys.preshipmentFacilitysCurrencyId,
      ContractValue: preshipmentFacilitys.preshipmentFacilitysContractValue,
      ShipmentDate: preshipmentFacilitys.preshipmentFacilitysShipmentDate,
      CountryId: preshipmentFacilitys.preshipmentFacilitysCountryId,
      IsPartialShipmentAllowed: preshipmentFacilitys.IsPartialShipmentAllowed,
      IsLOTDefined: preshipmentFacilitys.IsLOTDefined,
      LOT1Value: preshipmentFacilitys.LOT1Value,
      LOT1ShipmentDate: preshipmentFacilitys.LOT1ShipmentDate,
      LOT1DisbursementDate:
        preshipmentFacilitys.preshipmentFacilitysLOT1DisbursementDate,
      LOT1DisbursementAmount:
        preshipmentFacilitys.preshipmentFacilitysLOT1DisbursementAmount,
      IsLOT1ShipmentMade:
        preshipmentFacilitys.preshipmentFacilitysIsLOT1ShipmentMade,
      LOT1IncomingDate: preshipmentFacilitys.LOT1IncomingDate,
      LOT1ProceedValue:
        preshipmentFacilitys.preshipmentFacilitysLOT1ProceedValue,
      LOT1CurrencyId: preshipmentFacilitys.LOT1CurrencyId,
      LOT1ExchangeRate: preshipmentFacilitys.LOT1ExchangeRate,
      LOT1LocalCurrencyAmount: preshipmentFacilitys.LOT1LocalCurrencyAmount,
      IsProceedSettledDisbursementAmount:
        preshipmentFacilitys.IsProceedSettledDisbursementAmount,
      IsThereProceedRemainedToBeCollected:
        preshipmentFacilitys.IsThereProceedRemainedToBeCollected,
      IsCustomerPresentedNewContractForAmendment:
        preshipmentFacilitys.IsCustomerPresentedNewContractForAmendment,
      TurnOver: preshipmentFacilitys.TurnOver,
      NextAction: preshipmentFacilitys.NextAction,
    };
    preshipmentFacilityServivice
      .addPreshipmentFacility(data, accountDetialId)
      .then((response) => {
        setPreshipmentFacilitys({
          Id: response.data.Id,
          ApprovedFacilityLimit: response.data.ApprovedFacilityLimit,
          TypesOfGoods: response.data.TypesOfGoods,
          StartDate: response.data.StartDate,
          MaturityDate: response.data.MaturityDate,
          SalesContractVersion: response.data.SalesContractVersion,
          SalesContractDate: response.data.SalesContractDate,
          SectorTypeId: response.data.SectorTypeId,
          CurrencyId: response.data.CurrencyId,
          ContractValue: response.data.ContractValue,
          ShipmentDate: response.data.ShipmentDate,
          CountryId: response.data.CountryId,
          IsPartialShipmentAllowed: response.data.IsPartialShipmentAllowed,
          IsLOTDefined: response.data.IsLOTDefined,
          LOT1Value: response.data.LOT1Value,
          LOT1ShipmentDate: response.data.LOT1ShipmentDate,
          LOT1DisbursementDate: response.data.LOT1DisbursementDate,
          LOT1DisbursementAmount: response.data.LOT1DisbursementAmount,
          IsLOT1ShipmentMade: response.data.IsLOT1ShipmentMade,
          LOT1IncomingDate: response.data.LOT1IncomingDate,
          LOT1ProceedValue: response.data.LOT1ProceedValue,
          LOT1CurrencyId: response.data.LOT1CurrencyId,
          LOT1ExchangeRate: response.data.LOT1ExchangeRate,
          LOT1LocalCurrencyAmount: response.data.LOT1LocalCurrencyAmount,
          IsProceedSettledDisbursementAmount:
            response.data.IsProceedSettledDisbursementAmount,
          IsThereProceedRemainedToBeCollected:
            response.data.IsThereProceedRemainedToBeCollected,
          IsCustomerPresentedNewContractForAmendment:
            response.data.IsCustomerPresentedNewContractForAmendment,
          TurnOver: response.data.TurnOver,
          NextAction: response.data.NextAction,
        });
        if (response.data.Code === 200) {
          setPreshipmentFacilitys(initialPreshipmentFacilityState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          PreshipmentFacilitys();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "PreshipmentFacility",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (preshipmentFacility) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${preshipmentFacility.LoanAccount}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deletePreshipmentFacility",
      });
      preshipmentFacilityServivice
        .deletepreshipmentFacilityById(preshipmentFacility.Id)
        .then(() => {
          preshipmentFacility((previousData) =>
            previousData.filter((comp) => comp.Id !== preshipmentFacility.Id)
          );
        })
        .catch((e) => {
          setPreshipmentFacilityDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editPreshipmentFacility, setEditPreshipmentFacility] = useState({
    //  Id: editAddresAccountDetailId,
    ApprovedFacilityLimit: "",
    TypesOfGoods: "",
    StartDate: "",
    MaturityDate: "",
    SalesContractVersion: "",
    SalesContractDate: "",
    SectorTypeId: "",
    CurrencyId: "",
    ContractValue: "",
    ShipmentDate: "",
    CountryId: "",
    IsPartialShipmentAllowed: "",
    IsLOTDefined: "",
    LOT1Value: "",
    LOT1ShipmentDate: "",
    LOT1DisbursementDate: "",
    LOT1DisbursementAmount: "",
    IsLOT1ShipmentMade: "",
    LOT1IncomingDate: "",
    LOT1ProceedValue: "",
    LOT1CurrencyId: "",
    LOT1ExchangeRate: "",
    LOT1LocalCurrencyAmount: "",
    IsProceedSettledDisbursementAmount: "",
    IsThereProceedRemainedToBeCollected: "",
    IsCustomerPresentedNewContractForAmendment: "",
    TurnOver: "",
    NextAction: "",
  });

  const handleInputChangeEditPreshipmentFacility = (event) => {
    const { name, value } = event.target;
    setEditPreshipmentFacility({
      ...editPreshipmentFacility,
      [name]: value,
    });
  };

  const getPreshipmentFacilityId = (editPreshipmentFacilityId) => {
    preshipmentFacilityServivice
      .getpreshipmentFacilityById(editPreshipmentFacilityId)
      .then((res) => {
        setEditPreshipmentFacility({
          ...editPreshipmentFacility,
          Id: editPreshipmentFacilityId,
          ApprovedFacilityLimit: res.data.Result.ApprovedFacilityLimit,
          TypesOfGoods: res.data.Result.TypesOfGoods,
          StartDate: res.data.Result.StartDate,
          MaturityDate: res.data.Result.MaturityDate,
          SalesContractVersion: res.data.Result.SalesContractVersion,
          SalesContractDate: res.data.Result.SalesContractDate,
          SectorTypeId: res.data.Result.SectorTypeId,
          CurrencyId: res.data.Result.CurrencyId,
          ContractValue: res.data.Result.ContractValue,
          ShipmentDate: res.data.Result.ShipmentDate,
          CountryId: res.data.Result.CountryId,
          IsPartialShipmentAllowed: res.data.Result.IsPartialShipmentAllowed,
          IsLOTDefined: res.data.Result.IsLOTDefined,
          LOT1Value: res.data.Result.LOT1Value,
          LOT1ShipmentDate: res.data.Result.LOT1ShipmentDate,
          LOT1DisbursementDate: res.data.Result.LOT1DisbursementDate,
          LOT1DisbursementAmount: res.data.Result.LOT1DisbursementAmount,
          IsLOT1ShipmentMade: res.data.Result.IsLOT1ShipmentMade,
          LOT1IncomingDate: res.data.Result.LOT1IncomingDate,
          LOT1ProceedValue: res.data.Result.LOT1ProceedValue,
          LOT1CurrencyId: res.data.Result.LOT1CurrencyId,
          LOT1ExchangeRate: res.data.Result.LOT1ExchangeRate,
          LOT1LocalCurrencyAmount: res.data.Result.LOT1LocalCurrencyAmount,
          IsProceedSettledDisbursementAmount:
            res.data.Result.IsProceedSettledDisbursementAmount,
          IsThereProceedRemainedToBeCollected:
            res.data.Result.IsThereProceedRemainedToBeCollected,
          IsCustomerPresentedNewContractForAmendment:
            res.data.Result.IsCustomerPresentedNewContractForAmendment,
          TurnOver: res.data.Result.TurnOver,
          NextAction: res.data.Result.NextAction,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updatePreshipmentFacilityById = (e) => {
    e.preventDefault();
    preshipmentFacilityServivice
      .updatePreshipmentFacilityById(
        preshipmentFacilityId,
        editPreshipmentFacility
      )
      .then((response) => {
        if (response.data.Code === 200) {
          preshipmentFacility();
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const [currency, setCurrency] = useState([]);
  const getCurrency = () => {
    setTimeout(() => {
      CommonServices.getCurrency()
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setCurrency(response.data.Result);
          } else {
            setCreditPerformanceErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit performance not found");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setCreditPerformanceErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getCurrency();
  }, []);
  return (
    <div className="fixed inset-0 flex items-center justify-center ">
      <CompanyModal
        isOpen={isPreshipmentFacilityModalOpen}
        onClose={handlePreshipmentFacilityCloseModal}
        title="credit performance detail "
      >
        {/* <div className=" min-w-screen relative min-h-screen "> */}
        <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll ">
          <div className="flex flex-wrap ">
            <div className="w-full ">
              <div>
                {/* <AddCreditPerformance
                  // submitted={submitted}
                  // newAccountDetials={newCreditFacilityUtilizition}
                  creditPerformances={creditPerformances}
                  handleInputChangeCreditPerformance={
                    handleInputChangeCreditPerformance
                  }
                  setLoanTypeId={setLoanTypeId}
                  // handleBack={handleBack}
                  // handleSkip={handleSkip}
                  // handleNext={handleNext}
                  loanTypeIdForPerformance={loanTypeIdForPerformance}
                  currency={currency}
                  saveCreditPerformance={saveCreditPerformance}
                  setCurrencyId={setCurrencyId}
                /> */}
                <ToastContainer />
                {error && <p className="text-red-500">{error}</p>}
                {!error && CreditPerformanceErrorLoading ? (
                  <span>
                    <FaSpinner
                      className="animate-spin rounded-full h-6 w-6 text-purple-600"
                      fontSize="small"
                      data-toggle="tooltip"
                      title="Click it to sign in"
                    />
                    Loading...
                  </span>
                ) : (
                  !error &&
                  !CreditPerformanceErrorLoading && (
                    <table className="table table-bordered table-striped bg-purple-100">
                      <thead>
                        <tr className="bg-purple-200 ">
                          <th className="px-2 py-4"> Loan Account</th>
                          <th className="px-2 py-4"> Loan Type </th>
                          <th className="px-2 py-4"> Approved Amount </th>
                          <th className="px-2 py-4"> Approval Date </th>
                          <th className="px-2 py-4"> Total Commitment </th>
                          <th className="px-2 py-4"> Currency </th>
                          <th className="px-2 py-4"> Start Date </th>
                          <th className="px-2 py-4"> Maturity Date </th>
                          <th className="px-2 py-4"> Repayment Frequency </th>
                          <th className="px-2 py-4"> Scheduled Payment </th>
                          <th className="px-2 py-4"> Loan Status </th>
                          <th className="px-2 py-4"> Restructuring </th>
                          <th className="px-2 py-4">{" "}Loan Account Restructured{" "}</th>
                          <th className="px-2 py-4">{" "}system Adjustment Date Waiver And Extension{" "}</th>
                          <th className="px-2 py-4">{" "}No Of Scheduled Principal Payment Waived{" "}</th>
                          <th className="px-2 py-4">{" "}No Of Scheduled Repayment Waived{" "}</th>             
                           <th className="px-2 py-4">{" "}Adjustment Maturity Date{" "}</th>
                          <th className="px-2 py-4">{" "} Principal Outstanding Amount{" "}</th>
                          <th className="px-2 py-4">{" "}Interest Outstanding Amount{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Outstanding Amount Per Loan Type{" "}
                          </th>
                          <th className="px-2 py-4"> Principal Arrears </th>
                          <th className="px-2 py-4"> Interest Arrears </th>
                          <th className="px-2 py-4"> Total Arrears </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Approved Loan Amount{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Outstanding Amount{" "}
                          </th>
                          <th className="px-2 py-4"> Cross Selling </th>
                          <th className="px-2 py-4"> NPL Status </th>
                          <th className="px-2 py-4"> Action </th>
                        </tr>
                      </thead>
                      <tbody>
                        {creditPerformance.length === 0 ? (
                          <p className="text-red-600 p-4">Data not found</p>
                        ) : (
                          creditPerformance.map(
                            (FacilityUtilizition, index) => (
                              <>
                                <tr
                                  className="bg-white
                    border-b border-purple-200"
                                  key={FacilityUtilizition.Id}
                                >
                                  <td className="p-2">
                                    {FacilityUtilizition.LoanAccount}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.LoanTypeId}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.ApprovedAmount}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.ApprovalDate}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.TotalCommitment}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.CurrencyId}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.StartDate}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.MaturityDate}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.RepaymentFrequency}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.ScheduledPayment}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.LoanStatus}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.Restructuring}
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.LoanAccountRestructured
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.SystemAdjustmentDate4WaiverAndExtension
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.NoOfScheduledPrincipalPaymentWaived
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.NoOfScheduledRepaymentWaived
                                    }
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.AdjustmentMaturityDate}
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.PrincipalOutstandingAmount
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.InterestOutstandingAmount
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.TotalOutstandingAmountPerLoanType
                                    }
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.PrincipalArrears}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.InterestArrears}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.TotalArrears}
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.TotalApprovedLoanAmount
                                    }
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.TotalOutstandingAmount}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.CrossSelling}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.NPLStatus}
                                  </td>
                                  <td>
                                    <LoanIconButton
                                      style={{ color: "red" }}
                                      icon={<IoTrashOutline />}
                                      text="Delete"
                                      onClick={() => {
                                        handleDelete(FacilityUtilizition);
                                      }}
                                    >
                                      <ToastContainer />
                                    </LoanIconButton>
                                  </td>
                                  <td className="p-2">
                                    <LoanIconButton
                                      style={{ color: "green" }}
                                      icon={<MdModeEdit />}
                                      text="Edit"
                                      onClick={() => {
                                        handleCreditPerformanceEditOpenModal();
                                        getCreditPerformanceId(
                                          FacilityUtilizition.Id
                                        );
                                        setCreditPerformanceId(
                                          FacilityUtilizition.Id
                                        );
                                      }}
                                    />
                                  </td>
                                </tr>
                              </>
                            )
                          )
                        )}
                      </tbody>
                    </table>
                  )
                )}
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCreditPerformanceEditModalOpen}
        onClose={handleCreditPerformanceEditCloseModal}
        title="Modify account detail information"
      >
        <div>
          <form onSubmit={updateCreditFacilityUtilizitionById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <TextField
                    id="LoanAccount"
                    label="LoanAccount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.LoanAccount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        LoanAccount: e.target.value,
                      })
                    }
                    name="LoanAccount"
                  />
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="LoanTypeId" color="secondary">
                      Loan Type{" "}
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) => {
                        editCreditPerformance.LoanTypeId = event.target.value;
                        setLoanTypeId(event.target.value);
                      }}
                      labelId="LoanTypeId"
                      id="LoanTypeId"
                      label="LoanTypeId"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {loanTypeIdForPerformance.map((yourLoan) => (
                        <MenuItem
                          key={yourLoan.Id}
                          LoanTypeId={yourLoan.Id}
                          value={yourLoan.Id}
                        >
                          {yourLoan.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <TextField
                    id="ApprovedAmount"
                    label="ApprovedAmount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.ApprovedAmount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        ApprovedAmount: e.target.value,
                      })
                    }
                    name="ApprovedAmount"
                  />
                  <p>
                    Approval Date:
                    <input
                      id="ApprovalDate"
                      name="ApprovalDate"
                      label="ApprovalDate"
                      placeholder="ApprovalDate"
                      type="date"
                      value={
                        approvalDate.getFullYear().toString() +
                        "-" +
                        (approvalDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        approvalDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setApprovalDate(new Date(e.target.value));
                        editCreditPerformance.ApprovalDate = approvalDate;
                      }}
                    />{" "}
                  </p>
                  <TextField
                    id="TotalCommitment"
                    label="TotalCommitment"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.TotalCommitment}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        TotalCommitment: e.target.value,
                      })
                    }
                    name="TotalCommitment"
                  />
                  {/* CurrencyId*/}
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="LoanTypeId" color="secondary">
                      currency type{" "}
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) => {
                        editCreditPerformance.CurrencyId = event.target.value;
                        // setCurrencyId(event.target.value);
                      }}
                      labelId="CurrencyId"
                      id="CurrencyId"
                      label="CurrencyId"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {currency.map((yourCurrency) => (
                        <MenuItem
                          key={yourCurrency.Id}
                          CurrencyId={yourCurrency.Id}
                          value={yourCurrency.Id}
                        >
                          {yourCurrency.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <p>
                    Start Date:
                    <input
                      id="StartDate"
                      name="StartDate"
                      label="StartDate"
                      placeholder="StartDate"
                      type="date"
                      value={
                        startDate.getFullYear().toString() +
                        "-" +
                        (startDate.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        startDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setStartDate(new Date(e.target.value));
                        editCreditPerformance.StartDate = startDate;
                      }}
                    />{" "}
                  </p>
                  <p>
                    Maturity Date:
                    <input
                      id="MaturityDate"
                      name="MaturityDate"
                      label="MaturityDate"
                      placeholder="MaturityDate"
                      type="date"
                      value={
                        maturityDate.getFullYear().toString() +
                        "-" +
                        (maturityDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        maturityDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setMaturityDate(new Date(e.target.value));
                        editCreditPerformance.MaturityDate = maturityDate;
                      }}
                    />{" "}
                  </p>
                  <TextField
                    id="RepaymentFrequency"
                    label="RepaymentFrequency"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.RepaymentFrequency}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        RepaymentFrequency: e.target.value,
                      })
                    }
                    name="RepaymentFrequency"
                  />
                  <p>
                    Scheduled Payment:
                    <input
                      id="ScheduledPayment"
                      name="ScheduledPayment"
                      label="ScheduledPayment"
                      placeholder="ScheduledPayment"
                      type="date"
                      value={
                        scheduledPayment.getFullYear().toString() +
                        "-" +
                        (scheduledPayment.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        scheduledPayment.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setScheduledPayment(new Date(e.target.value));
                        editCreditPerformance.ScheduledPayment =
                          scheduledPayment;
                      }}
                    />{" "}
                  </p>
                  <TextField
                    id="LoanStatus"
                    label="LoanStatus"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.LoanStatus}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        LoanStatus: e.target.value,
                      })
                    }
                    name="LoanStatus"
                  />
                  <TextField
                    id="Restructuring"
                    label="Restructuring"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.Restructuring}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        Restructuring: e.target.value,
                      })
                    }
                    name="Restructuring"
                  />
                  <TextField
                    id="LoanAccountRestructured"
                    label="LoanAccountRestructured"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.LoanAccountRestructured}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        LoanAccountRestructured: e.target.value,
                      })
                    }
                    name="LoanAccountRestructured"
                  />
                  <p>
                    System Adjustment Date Waiver And Extension:
                    <input
                      id="SystemAdjustmentDate4WaiverAndExtension"
                      name="SystemAdjustmentDate4WaiverAndExtension"
                      label="SystemAdjustmentDate4WaiverAndExtension"
                      placeholder="SystemAdjustmentDate4WaiverAndExtension"
                      type="date"
                      value={
                        systemAdjustmentDate4WaiverAndExtension
                          .getFullYear()
                          .toString() +
                        "-" +
                        (systemAdjustmentDate4WaiverAndExtension.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        systemAdjustmentDate4WaiverAndExtension
                          .getDate()
                          .toString()
                          .padStart(2, 0)
                      }
                      onChange={(e) => {
                        setSystemAdjustmentDate4WaiverAndExtension(
                          new Date(e.target.value)
                        );
                        editCreditPerformance.SystemAdjustmentDate4WaiverAndExtension =
                          systemAdjustmentDate4WaiverAndExtension;
                      }}
                    />{" "}
                  </p>
                  <TextField
                    id="NoOfScheduledPrincipalPaymentWaived"
                    label="NoOfScheduledPrincipalPaymentWaived"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={
                      editCreditPerformance.NoOfScheduledPrincipalPaymentWaived
                    }
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        NoOfScheduledPrincipalPaymentWaived: e.target.value,
                      })
                    }
                    name="NoOfScheduledPrincipalPaymentWaived"
                  />
                  <TextField
                    id="NoOfScheduledRepaymentWaived"
                    label="NoOfScheduledRepaymentWaived"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.NoOfScheduledRepaymentWaived}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        NoOfScheduledRepaymentWaived: e.target.value,
                      })
                    }
                    name="NoOfScheduledRepaymentWaived"
                  />
                  <p>
                    System Adjustment Date Waiver And Extension:
                    <input
                      id="AdjustmentMaturityDate"
                      name="AdjustmentMaturityDate"
                      label="AdjustmentMaturityDate"
                      placeholder="AdjustmentMaturityDate"
                      type="date"
                      value={
                        adjustmentMaturityDate.getFullYear().toString() +
                        "-" +
                        (adjustmentMaturityDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        adjustmentMaturityDate
                          .getDate()
                          .toString()
                          .padStart(2, 0)
                      }
                      onChange={(e) => {
                        setAdjustmentMaturityDate(new Date(e.target.value));
                        editCreditPerformance.AdjustmentMaturityDate =
                          adjustmentMaturityDate;
                      }}
                    />{" "}
                  </p>
                  <TextField
                    id="PrincipalOutstandingAmount"
                    label="PrincipalOutstandingAmount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.PrincipalOutstandingAmount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        PrincipalOutstandingAmount: e.target.value,
                      })
                    }
                    name="PrincipalOutstandingAmount"
                  />
                  <TextField
                    id="InterestOutstandingAmount"
                    label="InterestOutstandingAmount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.InterestOutstandingAmount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        InterestOutstandingAmount: e.target.value,
                      })
                    }
                    name="InterestOutstandingAmount"
                  />
                  <TextField
                    id="TotalOutstandingAmountPerLoanType"
                    label="TotalOutstandingAmountPerLoanType"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={
                      editCreditPerformance.TotalOutstandingAmountPerLoanType
                    }
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        TotalOutstandingAmountPerLoanType: e.target.value,
                      })
                    }
                    name="TotalOutstandingAmountPerLoanType"
                  />
                  <TextField
                    id="PrincipalArrears"
                    label="PrincipalArrears"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.PrincipalArrears}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        PrincipalArrears: e.target.value,
                      })
                    }
                    name="PrincipalArrears"
                  />
                  <TextField
                    id="InterestArrears"
                    label="InterestArrears"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.InterestArrears}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        InterestArrears: e.target.value,
                      })
                    }
                    name="InterestArrears"
                  />
                  <TextField
                    id="TotalArrears"
                    label="TotalArrears"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.TotalArrears}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        TotalArrears: e.target.value,
                      })
                    }
                    name="TotalArrears"
                  />
                  <TextField
                    id="TotalApprovedLoanAmount"
                    label="TotalApprovedLoanAmount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.TotalApprovedLoanAmount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        TotalApprovedLoanAmount: e.target.value,
                      })
                    }
                    name="TotalApprovedLoanAmount"
                  />
                  <TextField
                    id="TotalOutstandingAmount"
                    label="TotalOutstandingAmount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.TotalOutstandingAmount}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        TotalOutstandingAmount: e.target.value,
                      })
                    }
                    name="TotalOutstandingAmount"
                  />
                  <TextField
                    id="CrossSelling"
                    label="CrossSelling"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.CrossSelling}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        CrossSelling: e.target.value,
                      })
                    }
                    name="CrossSelling"
                  />
                  <TextField
                    id="NPLStatus"
                    label="NPLStatus"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editCreditPerformance.NPLStatus}
                    onChange={(e) =>
                      setEditCreditPerformance({
                        ...editCreditPerformance,
                        NPLStatus: e.target.value,
                      })
                    }
                    name="NPLStatus"
                  />
                </div>
                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                  >
                    Update
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};

export default ViewPreshipmentFacility;
