import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import { useNavigate } from "react-router-dom";
import { FaSpinner } from "react-icons/fa";
import "react-datepicker/dist/react-datepicker.css";
const ViewCreditFacilityUtilizition = ({
  isCompanySoleOtherBankModalOpen,
  handleCompanySoleOtherBankCloseModal,
  creditFacilityUtilizition
}) => {
  const [soleAndPartnershipErrorLoading, setSoleAndPartnershipErrorLoading] =
  useState(false);
const navigate = useNavigate();
const [error, setError] = useState(null);

  return (
    <div>
      <CompanyModal
        isOpen={isCompanySoleOtherBankModalOpen}
        onClose={handleCompanySoleOtherBankCloseModal}
        title="view Other Bank information"
      >
        <div className="px-4 py-3">
        <div className="flex flex-wrap">
          <div className="w-full">
            <div>
              <h4 className="text-lg font-semibold eading-tight p-2">
                Sole Or partnership
              </h4>
              <div>
                {error && <p className="text-red-500">{error}</p>}
                {!error && soleAndPartnershipErrorLoading ? (
                  <span>
                    <FaSpinner
                      className="animate-spin rounded-full h-6 w-6 text-purple-600"
                      fontSize="small"
                      data-toggle="tooltip"
                      title="Click it to sign in"
                    />
                    Loading...
                  </span>
                ) : (
                  !error &&
                  !soleAndPartnershipErrorLoading && (
                    <div className="overflow-x-auto">
                      <table className="min-w-full table table-bordered table-striped bg-purple-100">
                        <thead>
                          <tr className="bg-purple-200">
                          <th className="px-2 py-4"> CustomerId </th>
                          <th className="px-2 py-4"> DepositAccount </th>
                          <th className="px-2 py-4"> DepositBalance </th>
                          <th className="px-2 py-4"> Action </th>
                          </tr>
                        </thead>
                        <tbody>
                          {getSoleAndPartinershipInformation.length === 0 ? (
                            <p className="text-red-600 p-4">Data not found</p>
                          ) : (
                            getSoleAndPartinershipInformation.map(
                              (details, index) => (
                                <tr
                                  key={index}
                                  className="bg-white
                    border-b border-purple-200"
                                >
                              <td className="p-2">{details.CustomerId}</td>
                              <td className="p-2">{details.DepositAccount}</td>
                              <td className="p-2">{details.DepositBalance}</td>                             
                                  
                                </tr>
                              )
                            )
                          )}
                        </tbody>
                      </table>
                    </div>
                  )
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      </CompanyModal>
    </div>
  );
};
export default ViewCreditFacilityUtilizition;
