import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import AddWidowedStatus from "./AddWidowedStatus";
import CommonServices from "../../Common/CommonServices";
const ViewWidowedStatus = ({
  holdWidowed,
  SoleId,
  isCompanyMartailStatusWidowedModalOpen,
  handleCompanyMartailStatusWidowedCloseModal,
  handleCompanyMartailStatusWidowedOpenModal,
}) => {
  //upload
  const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
  const [selectedPathUnmarriege, setSelectedPathUnmarriege] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  //upload 1
  const uploadMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  //upload 2
  const uploadUnMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathUnmarriege(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //endupload2
 const [dateOfWidowed, setDateOfWidowed] = useState(new Date());
 const [unMarriedCertficateIssueDate, setUnMarriedCertficateIssueDate] =
   useState(new Date());
 const [unMarriedCertficateExpiredDate, setUnMarriedCertficateExpiredDate] =
   useState(new Date());
  const [
    isCompanyWidowedInformationEditModalOpen,
    setIsCompanyWidowedInformationEditModalOpen,
  ] = useState(false);
  function handleCompanyWidowedEditCloseModal() {
    setIsCompanyWidowedInformationEditModalOpen(false);
  }
  function handleCompanyWidowedInformationEditOpenModal() {
    setIsCompanyWidowedInformationEditModalOpen(true);
  }
  function handleCompanyWidowedInformationEditCloseModal() {
    setIsCompanyWidowedInformationEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editWidowedInfo, setEditWidowedInfo] = useState({
    DateOfWidowed: "",
    MarraigeCertificate: "",
    MarraigeCertificateIssuer: "",
    UnmarriedCertificate: "",
    UnmarraiedCertificateIssuer: "",
    UnmarriedCertificateIssuDate: "",
    UnmarriedCertificateExpiredDate: "",
    CourtDecision: "",
  });
  const handleInputChangeEditWidowedStatus = (event) => {
    const { name, value } = event.target;
    setEditWidowedInfo({ ...editWidowedInfo, [name]: value });
  };
  const updateWidowedById = (taxInformationId1) => {
    CommonServices.getWidowed(taxInformationId1)
      .then((res) => {
        setEditWidowedInfo({
          ...editWidowedInfo,
          DateOfWidowed: res.data.Result.DateOfWidowed,
          MarraigeCertificate: res.data.Result.MarraigeCertificate,
          MarraigeCertificateIssuer: res.data.Result.MarraigeCertificateIssuer,
          UnmarriedCertificate: res.data.Result.UnmarriedCertificate,
          UnmarraiedCertificateIssuer:
            res.data.Result.UnmarraiedCertificateIssuer,
          UnmarriedCertificateIssuDate:
            res.data.Result.UnmarriedCertificateIssuDate,
          UnmarriedCertificateExpiredDate:
            res.data.Result.UnmarriedCertificateExpiredDate,
          CourtDecision: res.data.Result.CourtDecision,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateWidowedById();
  }, []);
  const updateWidowedInfo = () => {
    CommonServices.updateWidowed(taxInformationId, editWidowedInfo)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateWidowedInfo();
  }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMartailStatusWidowedModalOpen}
        onClose={handleCompanyMartailStatusWidowedCloseModal}
        title="View Widowed status information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdWidowed ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
                <th className="px-2 py-4"> Date Of Widowed</th>
                <th className="px-2 py-4"> Marraige Certificate</th>
                <th className="px-2 py-4"> Marraige Certificate Issuer</th>
                <th className="px-2 py-4"> Unmarried Certificate</th>
                <th className="px-2 py-4"> Unmarraied Certificate Issuer</th>
                <th className="px-2 py-4"> Unmarried Certificate Issu Date</th>
                <th className="px-2 py-4"> Unmarried Certificate Expired Date</th>
                <th className="px-2 py-4"> Court Decision</th>
                <th className="px-2 py-4"> Action </th>
              </thead>
              <tbody>
                <tr>
                  <td>{holdWidowed.DateOfWidowed}</td>
                  <td>{holdWidowed.MarraigeCertificate}</td>
                  <td>{holdWidowed.MarraigeCertificateIssuer}</td>
                  <td>{holdWidowed.UnmarriedCertificate}</td>
                  <td>{holdWidowed.UnmarraiedCertificateIssuer}</td>
                  <td>{holdWidowed.UnmarriedCertificateIssuDate}</td>
                  <td>{holdWidowed.UnmarriedCertificateExpiredDate}</td>
                  <td>{holdWidowed.CourtDecision}</td>
                  <td>
                    <button
                      onClick={() => {
                        handleCompanyWidowedInformationEditOpenModal();
                        updateWidowedById(holdWidowed.Id);
                        setTaxInformationId(holdWidowed.Id);
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Edit
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          ) : (
            ""
          )}
        </div>
        <div>
          {holdWidowed <= 0 && (
            <AddWidowedStatus Add marital status information SoleId={SoleId} />
          )}
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyWidowedInformationEditModalOpen}
        onClose={handleCompanyWidowedEditCloseModal}
        title="widowed edit information"
      >
        <div>
          <form onSubmit={updateWidowedInfo}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <p>
              Date Of Widowed:
                <input
                  id="DateOfWidowed"
                  name="DateOfWidowed"
                  label="DateOfWidowed"
                  placeholder="DateOfWidowed"
                  type="date"
                  value={
                    dateOfWidowed.getFullYear().toString() +
                    "-" +
                    (dateOfWidowed.getMonth() + 1).toString().padStart(2, 0) +
                    "-" +
                    dateOfWidowed.getDate().toString().padStart(2, 0)
                  }
                  onChange={(e) => {
                    setDateOfWidowed(new Date(e.target.value));
                    editWidowedInfo.DateOfWidowed = dateOfWidowed;
                  }}
                />{" "}
              </p>
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onChange={  
                    editWidowedInfo.MarraigeCertificate = selectedPathIdentity
                  }
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div> 
              {/* <LoanTextField
                id="DateOfWidowed"
                label="DateOfWidowed"
                value={editWidowedInfo.DateOfWidowed}
                onChange={handleInputChangeEditWidowedStatus}
                name="DateOfWidowed"
              /> */}
              {/* <LoanTextField
                id="MarraigeCertificate"
                label="MarraigeCertificate"
                value={editWidowedInfo.MarraigeCertificate}
                onChange={handleInputChangeEditWidowedStatus}
                name="MarraigeCertificate"
              /> */}
              <LoanTextField
                id="MarraigeCertificateIssuer"
                label="MarraigeCertificateIssuer"
                value={editWidowedInfo.MarraigeCertificateIssuer}
                onChange={handleInputChangeEditWidowedStatus}
                name="MarraigeCertificateIssuer"
              />
               <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onClick={uploadUnMarriegeCertificate}
                >
                  Upload UnMarriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <LoanTextField
                id="UnmarriedCertificate"
                label="UnmarriedCertificate"
                value={editWidowedInfo.UnmarriedCertificate}
                onChange={handleInputChangeEditWidowedStatus}
                name="UnmarriedCertificate"
              />
              <LoanTextField
                id="UnmarraiedCertificateIssuer"
                label="UnmarraiedCertificateIssuer"
                value={editWidowedInfo.UnmarraiedCertificateIssuer}
                onChange={handleInputChangeEditWidowedStatus}
                name="UnmarraiedCertificateIssuer"
              />
               <LoanTextField
                id="UnmarriedCertificateIssuDate"
                label="UnmarriedCertificateIssuDate"
                value={editWidowedInfo.UnmarriedCertificateIssuDate}
                onChange={handleInputChangeEditWidowedStatus}
                name="UnmarriedCertificateIssuDate"
              />
              <LoanTextField
                id="UnmarriedCertificateExpiredDate"
                label="UnmarriedCertificateExpiredDate"
                value={editWidowedInfo.UnmarriedCertificateExpiredDate}
                onChange={handleInputChangeEditWidowedStatus}
                name="UnmarriedCertificateExpiredDate"
              />
              <LoanTextField
                id="CourtDecision"
                label="CourtDecision"
                value={editWidowedInfo.CourtDecision}
                onChange={handleInputChangeEditWidowedStatus}
                name="CourtDecision"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewWidowedStatus;
