import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../../Common/LoanIconButton";
import CompanyModal from "../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
import LoanTextField from "../../../Common/LoanTextField";
const AddDepositPerformance = ({
  // submitted,
  // handleBack,
  handleSkip,
  // handleNext,
  // newAccountDetials,
  depositPerformances,
  handleInputChangeDepositPerformance,
  setLoanTypeId,
  loanTypeIdForPerformance,
  currency,
  saveDepositPerformance,
  setCurrencyId,
}) => {
  const [isCreditPerformanceModalOpen, setIsCreditPerformanceModalOpen] =
    useState(false);
  function handleCreditPerformanceOpenModal() {
    setIsCreditPerformanceModalOpen(true);
  }
  function handlCreditPerformanceCloseModal() {
    setIsCreditPerformanceModalOpen(false);
  }
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );

  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCreditPerformanceOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={isCreditPerformanceModalOpen}
        onClose={handlCreditPerformanceCloseModal}
        title="Add handle Credit Facility Utilizition "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="BudgetYear"
              label="BudgetYear"
              value={creditPerformances.BudgetYear}
              onChange={handleInputChangeCreditPerformance}
              name="BudgetYear"
            />
            <p>
              Approval Date:
              <input
                id="StartDate"
                name="StartDate"
                label="StartDate"
                placeholder="StartDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  creditPerformances.StartDate = approvalDate;
                }}
              />{" "}
            </p>
            <p>
              End Date:
              <input
                id="EndDate"
                name="EndDate"
                label="EndDate"
                placeholder="EndDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  creditPerformances.EndDate = approvalDate;
                }}
              />{" "}
            </p>
            <FormControl variant="standard" sx={{ minWidth: 120 }}>
              <InputLabel id="LoanTypeId" color="secondary">
                Loan Type{" "}
              </InputLabel>
              <Select
                color="secondary"
                onChange={(event) => {
                  creditPerformances.LoanTypeId = event.target.value;
                  setLoanTypeId(event.target.value);
                }}
                labelId="LoanTypeId"
                id="LoanTypeId"
                label="LoanTypeId"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {loanTypeIdForPerformance.map((yourLoan) => (
                  <MenuItem
                    key={yourLoan.Id}
                    LoanTypeId={yourLoan.Id}
                    value={yourLoan.Id}
                  >
                    {yourLoan.Name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <LoanTextField
              id="DepositeAccount"
              label="DepositeAccount"
              value={creditPerformances.DepositeAccount}
              onChange={handleInputChangeCreditPerformance}
              name="DepositeAccount"
            />
            <LoanTextField
              id="DepositBalance"
              label="DepositBalance"
              value={creditPerformances.DepositBalance}
              onChange={handleInputChangeCreditPerformance}
              name="DepositBalance"
            />
            <LoanTextField
              id="AverageMonthlyDepositPerAnnum"
              label="AverageMonthlyDepositPerAnnum"
              value={creditPerformances.AverageMonthlyDepositPerAnnum}
              onChange={handleInputChangeCreditPerformance}
              name="AverageMonthlyDepositPerAnnum"
            />
            <LoanTextField
              id="TotalCreditTransactionAmountPerAnnum"
              label="TotalCreditTransactionAmountPerAnnum"
              value={creditPerformances.TotalCreditTransactionAmountPerAnnum}
              onChange={handleInputChangeCreditPerformance}
              name="TotalCreditTransactionAmountPerAnnum"
            />
            {/* ApprovalDate */}
            <p>
              AccountOpeningDate:
              <input
                id="AccountOpeningDate"
                name="AccountOpeningDate"
                label="AccountOpeningDate"
                placeholder="AccountOpeningDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  creditPerformances.AccountOpeningDate = approvalDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="TotalCreditTransactionNumberPerAnnum"
              label="TotalCreditTransactionNumberPerAnnum"
              value={creditPerformances.TotalCreditTransactionNumberPerAnnum}
              onChange={handleInputChangeCreditPerformance}
              name="TotalCreditTransactionNumberPerAnnum"
            />
            <LoanTextField
              id="DepositSalesRatio"
              label="DepositSalesRatio"
              value={creditPerformances.DepositSalesRatio}
              onChange={handleInputChangeCreditPerformance}
              name="DepositSalesRatio"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveCreditPerformance();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddDepositPerformance;
