import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { registerSchema } from "./AccountValidation.js";
import {
  TextField,
  InputLabel,
  MenuItem,
  Select,
  FormControl,
  Checkbox,
  Switch,
} from "@mui/material";
import Button from "@mui/material/Button";
import { useEffect, useState } from "react";
import { makeStyles } from "@mui/styles";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import { InputAdornment } from "@mui/material";
import AccountService from "./AccountService.js";

// I will clean the code if any by tommorrow

const useStyles = makeStyles((theme) => ({
  form: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    maxWidth: 400,
    margin: "0 auto",
  },
  // field: {
  //     marginBottom: theme.spacing(2),
  // },
  error: {
    color: "red",
  },
}));

const textInputClassName =
  "bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500";
const Account = () => {
  // Account info
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  // const fetchUsers = () => {
  //     AccountService.getUsers()
  //         .then(response => {
  //             setUsers(response.data);
  //             console.log(response.data);
  //         })
  //         .catch(e => {
  //             console.log(e);
  //         });
  // }

  const classes = useStyles();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(registerSchema),
    mode: "onChange", // enable on-typing validation
  });

  const onSubmit = (data) => {
    console.log(data);
  };
  const [isFieldValid, setIsFieldValid] = useState(false);
  const [isPasswordValid, setIsPasswordValid] = useState(false);
  const [isConfirmPasswordValid, setIsConfirmPasswordValid] = useState(false);
  const [isAccountTypeValid, setIsAccountTypeValid] = useState(false);

  const validateAccountTypeField = (value) => {
    return registerSchema.fields.accountType.isValidSync(value);
  };

  const handleAccountTypeChange = (event) => {
    const isValid = validateAccountTypeField(event.target.value);
    setIsAccountTypeValid(isValid);
  };

  const validateField = (value) => {
    return registerSchema.fields.email.isValidSync(value);
  };

  const validatePasswordField = (value) => {
    return registerSchema.fields.password.isValidSync(value);
  };

  const validateConfirmPasswordField = (value) => {
    return registerSchema.fields.confirmPassword.isValidSync(value);
  };

  const handleChange = (event) => {
    const isValid = validateField(event.target.value);
    setIsFieldValid(isValid);
  };

  const handlePasswordChange = (event) => {
    const isValid = validatePasswordField(event.target.value);
    setIsPasswordValid(isValid);
  };

  const handleConfirmPasswordChange = (event) => {
    const isValid = validateConfirmPasswordField(event.target.value);
    setIsConfirmPasswordValid(isValid);
  };
  return (
    <div
      className={`${classes.form}md:w-full shadow-sm shadow-white bg-white w-[1200px] mx-auto py-4 rounded-xl`}
    >
      <form onSubmit={handleSubmit(onSubmit)} className="md:w-[350px]">
        <div className="mb-6">
          <TextField
            {...register("email")}
            sx={{
              "& > :not(style)": { mb: 1 },
            }}
            label="Email"
            variant="standard"
            onChange={handleChange}
            color="secondary"
            className={textInputClassName}
            InputProps={{
              endAdornment: isFieldValid && (
                <InputAdornment position="end">
                  <CheckCircleIcon color="success" />
                </InputAdornment>
              ),
            }}
            inputProps={register("email")}
            error={Boolean(errors.email)}
            helperText={errors.email && errors.email.message}
          />
        </div>
        <div className="mb-6">
          <TextField
            {...register("password")}
            sx={{
              "& > :not(style)": { mb: 1 },
            }}
            label="Password"
            type="password"
            variant="standard"
            name="password"
            onChange={handlePasswordChange}
            autoComplete="current-password"
            color="secondary"
            className={`mb-6${textInputClassName}`}
            InputProps={{
              endAdornment: isPasswordValid && (
                <InputAdornment position="end">
                  <CheckCircleIcon color="success" />
                </InputAdornment>
              ),
            }}
            inputProps={register("password")}
            error={Boolean(errors.password)}
            helperText={errors.password && errors.password.message}
          />
        </div>
        <div className="mb-6">
          <TextField
            {...register("confirmPassword")}
            label="Confirm Password"
            type="password"
            variant="standard"
            name="confirmPassword"
            onChange={handleConfirmPasswordChange}
            autoComplete="current-password"
            color="secondary"
            sx={{
              "& > :not(style)": { mb: 1 },
            }}
            className={`mb-6${textInputClassName}`}
            InputProps={{
              endAdornment: isConfirmPasswordValid && (
                <InputAdornment position="end">
                  <CheckCircleIcon color="success" />
                </InputAdornment>
              ),
            }}
            inputProps={register("confirmPassword")}
            error={Boolean(errors.confirmPassword)}
            helperText={
              errors.confirmPassword && errors.confirmPassword.message
            }
          />
        </div>
        <div className="mb-6">
          <FormControl
            variant="standard"
            color="secondary"
            className={`mb-6${textInputClassName}`}
            sx={{
              "& > :not(style)": { mb: 1 },
            }}
          >
            <InputLabel id="accountType">Account Type</InputLabel>
            <Select
              {...register("accountType")}
              labelId="accountType"
              id="accountType"
              onChange={handleAccountTypeChange}
              name="accountType"
              label="Account Type"
              inputProps={register("accountType")}
              error={Boolean(errors.accountType)}
              helperText={errors.accountType && errors.accountType.message}
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value="personal">Personal</MenuItem>
              <MenuItem value="commercial">Commercial</MenuItem>
            </Select>
            {errors.accountType ? (
              <span className="text-red-600">{errors.accountType.message}</span>
            ) : (
              <></>
            )}
          </FormControl>
        </div>
        <div className="flex justify-between mb-6">
          <div>
            <div className="flex">
              <div className="flex items-center h-5">
                <Checkbox
                  name="remember"
                  {...register("remember")}
                  color="secondary"
                />
              </div>
              <label
                htmlFor="remember"
                className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300"
              >
                Remember me
              </label>
            </div>
            {errors.remember ? (
              <span className="text-red-600">{errors.remember.message}</span>
            ) : (
              <></>
            )}
          </div>
          <div className="flex">
            <div className="flex items-center h-5">
              <Switch
                color="secondary"
                {...register("toggle")}
                name="toggle"
                id="toggle"
              />
              <label
                htmlFor="default-toggle"
                className="inline-flex relative items-center cursor-pointer"
              >
                <span className="ml-3 text-sm font-medium text-gray-900 dark:text-gray-300">
                  Toggle me
                </span>
              </label>
            </div>
          </div>
          {errors.toggle ? (
            <span className="text-red-600">{errors.toggle.message}</span>
          ) : (
            <></>
          )}
        </div>
        {errors.toggle ||
        errors.accountType ||
        errors.confirmPassword ||
        errors.email ||
        errors.password ||
        errors.remember ? (
          <Button
            type="submit"
            sx={{ "&:hover": { backgroundColor: "purple" } }}
            variant="outlined"
            color="secondary"
            className="hover:text-white hover: bg-purple-700"
            disabled
          >
            Create
          </Button>
        ) : (
          <Button
            type="submit"
            sx={{ "&:hover": { backgroundColor: "purple" } }}
            variant="outlined"
            color="secondary"
            className="hover:text-white hover: bg-purple-700"
          >
            Create
          </Button>
        )}
      </form>
      <div className="w-auto">
        <table>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Street</th>
            <th>Suite</th>
            <th>Zipcode</th>
            <th>Geo/Latitude</th>
            <th>Geo/Longitude</th>
          </tr>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.address.street}</td>
              <td>{user.address.suite}</td>
              <td>{user.address.zipcode}</td>
              <td>{user.address.geo.lat}</td>
              <td>{user.address.geo.lng}</td>
            </tr>
          ))}
        </table>
      </div>
    </div>
  );
};

export default Account;
