import CompanyNavigation from "../../Common/CompanyNavigation";
import SoleAndPartnershipService from "./SoleAndPartnershipService";
import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import AddSoleAndPartnership from "./AddSoleAndPartnership";
import CommonServices from "../Common/CommonServices";
import ViewMaritalStatus from "./MaritalStatus/ViewMaritalStatus";
import ViewUnMarriedStatus from "./MaritalStatus/ViewUnMarriedStatus";
import ViewWidowedStatus from "./MaritalStatus/ViewWidowedStatus";
import ViewDivorcedStatus from "./MaritalStatus/ViewDivorcedStatus";
import ViewSoleTax from "./SoleTax/ViewSoleTax";
import EditSole from "./EditSole";
import ViewSoleDependent from "./dependent/ViewSoleDependent";
import ViewSoleGuarantor from "./guarantor/ViewSoleGuarantor";
const ViewSoleAndPartnership = () => {
  const { companyId } = useParams();
  const navigate = useNavigate();
  const [error, setError] = useState(null);
  const [country, setContry] = useState([]);
  const [editSoleId, setEditSoleId] = useState([]);
  const [taxId, setTaxId] = useState([]);
  const [maritalStatus, setMaritalStatus] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [martailStatus, setMartailStatus] = useState();
  const [martailStatusHold, setMartailStatusHold] = useState();
  const [soleAndPartnershipErrorLoading, setSoleAndPartnershipErrorLoading] =
    useState(false);
  const [martialStatusIdInformationId, setMartialStatusIdInformationId] =
    useState();
  const [
    getSoleAndPartinershipInformation,
    setGetSoleAndPartinershipInformation,
  ] = useState([]);
  const getCompanySoleAndPartinershipInformationById = () => {
    setSoleAndPartnershipErrorLoading(true);
    setTimeout(() => {
      SoleAndPartnershipService.getCompanySoleAndPartinershipInformation(
        companyId
      )
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setGetSoleAndPartinershipInformation(response.data.Result);
            setSoleAndPartnershipErrorLoading(false);
          } else {
            setSoleAndPartnershipErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Sole and Partnership not found");
            setSoleAndPartnershipErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setSoleAndPartnershipErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getCompanySoleAndPartinershipInformationById();
  }, []);
  const initialSoleAndPartnershipState = {
    Id: null,
    CompanyID: companyId,
    GuarantorId: "",
    TaxClearanceID: "",
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    ProofOfIdentityTypeId: "",
    // UploadTradeLicense: "",
  };
  const [soleAndPartnership, setSoleAndPartnership] = useState(
    initialSoleAndPartnershipState
  );
  const handleInputChangeSoleAndPartnership = (event) => {
    const { name, value } = event.target;
    setSoleAndPartnership({ ...soleAndPartnership, [name]: value });
  };
  const saveSoleAndPartnership = (e) => {
    e.preventDefault();
    var data = {
      TaxClearanceID: null,
      CompanyID: soleAndPartnership.CompanyID,
      GuarantorId: null,
      FirstName: soleAndPartnership.FirstName,
      MiddleName: soleAndPartnership.MiddleName,
      LastName: soleAndPartnership.LastName,
      NationalityID: soleAndPartnership.NationalityID,
      CountryOfOriginID: soleAndPartnership.CountryOfOriginID,
      CountryOfResidenceID: soleAndPartnership.CountryOfResidenceID,
      ProofOfIdentityTypeID: soleAndPartnership.ProofOfIdentityTypeID,
      UploadProofOfIdentity: soleAndPartnership.UploadProofOfIdentity,
      ProofOfIdentityIssuedDate: soleAndPartnership.ProofOfIdentityIssuedDate,
      ProofOfIdentityExpiredDate: soleAndPartnership.ProofOfIdentityExpiredDate,
      MartailStatusTypeId: soleAndPartnership.MartailStatusTypeId,
    };
    SoleAndPartnershipService.addSoleAndPartnership(data, companyId)
      .then((response) => {
        setSoleAndPartnership({
          Id: response.data.Id,
          TaxClearanceID: response.data.TaxClearanceID,
          CompanyID: companyId,
          GuarantorId: response.data.GuarantorId,
          FirstName: response.data.FirstName,
          MiddleName: response.data.MiddleName,
          LastName: response.data.LastName,
          NationalityID: response.data.NationalityID,
          CountryOfOriginID: response.data.CountryOfOriginID,
          CountryOfResidenceID: response.data.CountryOfResidenceID,
          ProofOfIdentityTypeID: response.data.ProofOfIdentityTypeID,
          UploadProofOfIdentity: response.data.UploadProofOfIdentity,
          ProofOfIdentityIssuedDate: response.data.ProofOfIdentityIssuedDate,
          ProofOfIdentityExpiredDate: response.data.ProofOfIdentityExpiredDate,
          MartailStatusTypeId: response.data.MartailStatusTypeId,
        });
        setMartailStatus(response.data.Message);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setContry(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);
  const fetchProofOfIdentityType = () => {
    CommonServices.getProofOfIdentityTypes()
      .then((response) => {
        setProofOfIdentityType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchProofOfIdentityType();
  }, []);
  const fetchMaritalStatus = () => {
    CommonServices.getMaritalStatus()
      .then((response) => {
        setMaritalStatus(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchMaritalStatus();
  }, []);
  const [isCompanyMartailStatusModalOpen, setIsCompanyMartailStatusModalOpen] =
    useState(false);
  const [
    isCompanyMartailStatusUnmarriedModalOpen,
    setIsCompanyMartailStatusUnmarriedModalOpen,
  ] = useState(false);
  const [
    isCompanyMartailStatusWidowedModalOpen,
    setIsCompanyMartailStatusWidowedModalOpen,
  ] = useState(false);
  const [
    isCompanyMartailStatusDivorcedModalOpen,
    setIsCompanyMartailStatusDivorcedModalOpen,
  ] = useState(false);
  function handleCompanyMartailStatusOpenModal() {
    setIsCompanyMartailStatusModalOpen(true);
  }
  function handleCompanyMartailStatusUnmarriedOpenModal() {
    setIsCompanyMartailStatusUnmarriedModalOpen(true);
  }
  function handleCompanyMartailStatusUnmarriedCloseModal() {
    setIsCompanyMartailStatusUnmarriedModalOpen(false);
  }
  function handleCompanyMartailStatusWidowedOpenModal() {
    setIsCompanyMartailStatusWidowedModalOpen(true);
  }
  function handleCompanyMartailStatusWidowedCloseModal() {
    setIsCompanyMartailStatusWidowedModalOpen(false);
  }
  function handleCompanyMartailStatusDivorcedOpenModal() {
    setIsCompanyMartailStatusDivorcedModalOpen(true);
  }
  function handleCompanyMartailStatusDivorcedCloseModal() {
    setIsCompanyMartailStatusDivorcedModalOpen(false);
  }
  function handleGeneralCloseModal() {
    isCompanyMartailStatusModalOpen(false);
  }
  function handleCompanyMartailStatusCloseModal() {
    setIsCompanyMartailStatusModalOpen(false);
  }
  //tax
  const [isCompanyTaxModalOpen, setIsCompanyTaxModalOpen] = useState(false);
  function handleCompanyTaxOpenModal() {
    setIsCompanyTaxModalOpen(true);
  }
  function handleCompanyTaxCloseModal() {
    setIsCompanyTaxModalOpen(false);
  }
  //end tax
  //edit sole
  const [isCompanySoleEditModalOpen, setIsCompanySoleEditModalOpen] =
    useState(false);
  function handleCompanySoleEditOpenModal() {
    setIsCompanySoleEditModalOpen(true);
  }
  function handleCompanySoleEditCloseModal() {
    setIsCompanySoleEditModalOpen(false);
  }
  //end sole
  //dependent
  const [isCompanySoleDependentModalOpen, setIsCompanySoleDependentModalOpen] =
    useState(false);
  function handleCompanySoleDependentOpenModal() {
    setIsCompanySoleDependentModalOpen(true);
  }
  function handleCompanySoleDependentCloseModal() {
    setIsCompanySoleDependentModalOpen(false);
  }
  const [holdSoleDependent, setHoldSoleDependent] = useState([]);
  //end dependent
  

  const [holdMarried, setMarried] = useState([]);
  const [holdUnMarried, setUnMarried] = useState([]);
  const [holdWidowed, setWidowed] = useState([]);
  const [holdDivorced, setDivorced] = useState([]);
  const [holdSoleTax, setHoldSoleTax] = useState([]);
  const getsoleMarriedMatialStatus = (soleAndPartnershipId) => {
    CommonServices.getMartialStatusBySoleMarriedId(soleAndPartnershipId)
      .then((response) => {
        setMarried(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const getsoleUnMarriedMatialStatus = (soleAndPartnershipId) => {
    CommonServices.getUnMartialStatusBySoleMarriedId(soleAndPartnershipId)
      .then((response) => {
        setUnMarried(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const getsoleWidowedMatialStatus = (soleAndPartnershipId) => {
    CommonServices.getWidowedStatusBySoleMarriedId(soleAndPartnershipId)
      .then((response) => {
        setWidowed(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const getsoleDivorcedMatialStatus = (soleAndPartnershipId) => {
    CommonServices.getDivorcedStatusBySoleMarriedId(soleAndPartnershipId)
      .then((response) => {
        debugger;
        setDivorced(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const fetchMartialStatus = (martailStatus) => {
    if (martailStatus == "Married") {
      handleCompanyMartailStatusOpenModal();
    } else if (martailStatus == "unMarried") {
      handleCompanyMartailStatusUnmarriedOpenModal();
    } else if (martailStatus == "widowed") {
      handleCompanyMartailStatusWidowedOpenModal();
    } else if (martailStatus == "divorced") {
      handleCompanyMartailStatusDivorcedOpenModal();
    } else {
      console.log("please select martial status");
    }
  };
  const getsoleTax = (soleAndPartnershipId) => {
    CommonServices.getSoleTax(soleAndPartnershipId)
      .then((response) => {
        debugger;
        setHoldSoleTax(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const getsoleDependent = (soleAndPartnershipId) => {
    CommonServices.getSoleDependent(soleAndPartnershipId)
      .then((response) => {
        debugger;
        setHoldSoleDependent(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //sole edit
  const [editSole, setEditSole] = useState({
    Id: editSoleId,
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityId: "",
    CountryOfOriginId: "",
    CountryOfResidenceId: "",
    ProofOfIdentityTypeId: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    MartailStatusTypeId: "",
  });
  const getSoleById = (editSoleId) => {
    SoleAndPartnershipService.getSole(editSoleId)
      .then((res) => {
        setEditSole({
          ...editSole,
          Id: editSoleId,
          FirstName: res.data.Result.FirstName,
          MiddleName: res.data.Result.MiddleName,
          LastName: res.data.Result.LastName,
          NationalityId: res.data.Result.NationalityId,
          CountryOfOriginId: res.data.Result.CountryOfOriginId,
          CountryOfResidenceId: res.data.Result.CountryOfResidenceId,
          ProofOfIdentityTypeId: res.data.Result.ProofOfIdentityTypeId,
          UploadProofOfIdentity: res.data.Result.UploadProofOfIdentity,
          ProofOfIdentityIssuedDate: res.data.Result.ProofOfIdentityIssuedDate,
          ProofOfIdentityExpiredDate:
            res.data.Result.ProofOfIdentityExpiredDate,
          MartailStatusTypeId: res.data.Result.MartailStatusTypeId,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getSoleById();
  }, []);
  //end sole edit
  return (
    <div className="flex">
      <CompanyNavigation />
      <div className="px-4 py-3">
        <div className="flex flex-wrap">
          <div className="w-full">
            <div>
              <h4 className="text-lg font-semibold eading-tight p-2">
                Sole and partnership
              </h4>
              <AddSoleAndPartnership
                saveSoleAndPartnership={saveSoleAndPartnership}
                soleAndPartnership={soleAndPartnership}
                handleInputChangeSoleAndPartnership={
                  handleInputChangeSoleAndPartnership
                }
                country={country}
                proofOfIdentityType={proofOfIdentityType}
                setMartailStatusHold={setMartailStatusHold}
                maritalStatus={maritalStatus}
              />
              <ViewMaritalStatus
                holdMarried={holdMarried}
                isCompanyMartailStatusModalOpen={
                  isCompanyMartailStatusModalOpen
                }
                handleCompanyMartailStatusCloseModal={
                  handleCompanyMartailStatusCloseModal
                }
                handleMaritalStatusModalOpen={
                  handleCompanyMartailStatusOpenModal
                }
                SoleId={martialStatusIdInformationId}
              />
              <ViewUnMarriedStatus
                holdUnMarried={holdUnMarried}
                isCompanyMartailStatusUnmarriedModalOpen={
                  isCompanyMartailStatusUnmarriedModalOpen
                }
                handleCompanyMartailStatusUnmarriedCloseModal={
                  handleCompanyMartailStatusUnmarriedCloseModal
                }
                handleCompanyMartailStatusUnmarriedOpenModal={
                  handleCompanyMartailStatusUnmarriedOpenModal
                }
                SoleId={martialStatusIdInformationId}
              />
              <ViewWidowedStatus
                holdWidowed={holdWidowed}
                isCompanyMartailStatusWidowedModalOpen={
                  isCompanyMartailStatusWidowedModalOpen
                }
                handleCompanyMartailStatusWidowedCloseModal={
                  handleCompanyMartailStatusWidowedCloseModal
                }
                handleCompanyMartailStatusWidowedOpenModal={
                  handleCompanyMartailStatusWidowedOpenModal
                }
                SoleId={martialStatusIdInformationId}
              />
              <ViewDivorcedStatus
                holdDivorced={holdDivorced}
                isCompanyMartailStatusDivorcedModalOpen={
                  isCompanyMartailStatusDivorcedModalOpen
                }
                handleCompanyMartailStatusDivorcedCloseModal={
                  handleCompanyMartailStatusDivorcedCloseModal
                }
                handleCompanyMartailStatusDivorcedOpenModal={
                  handleCompanyMartailStatusDivorcedOpenModal
                }
                SoleId={martialStatusIdInformationId}
              />
              <ViewSoleTax
                holdSoleTax={holdSoleTax}
                isCompanyTaxModalOpen={isCompanyTaxModalOpen}
                handleCompanyTaxCloseModal={handleCompanyTaxCloseModal}
                handleCompanyTaxOpenModal={handleCompanyTaxOpenModal}
                taxId={taxId}
              />
              <EditSole
                editSole={editSole}
                editSoleId={editSole.Id}
                setEditSole={setEditSole}
                isCompanySoleEditModalOpen={isCompanySoleEditModalOpen}
                handleCompanySoleEditCloseModal={
                  handleCompanySoleEditCloseModal
                }
                handleCompanySoleEditOpenModal={handleCompanySoleEditOpenModal}
              />
              <ViewSoleDependent
                holdSoleDependent={holdSoleDependent}
                soleID ={taxId}
                isCompanySoleDependentModalOpen={
                  isCompanySoleDependentModalOpen
                }
                handleCompanySoleDependentCloseModal={
                  handleCompanySoleDependentCloseModal
                }
                handleCompanySoleDependentOpenModal={
                  handleCompanySoleDependentOpenModal
                }
              />
<ViewSoleGuarantor
                holdSoleGuarantor={holdSoleGuarantor}
                soleID ={taxId}
                isCompanySoleGuarantorModalOpen={
                  isCompanySoleGuarantorModalOpen
                }
                handleCompanySoleGuarantorCloseModal={
                  handleCompanySoleGuarantorCloseModal
                }
                handleCompanySoleGuarantorOpenModal={
                  handleCompanySoleGuarantorOpenModal
                }
              />
              <div>
                {error && <p className="text-red-500">{error}</p>}
                {!error && soleAndPartnershipErrorLoading ? (
                  <span>
                    <FaSpinner
                      className="animate-spin rounded-full h-6 w-6 text-purple-600"
                      fontSize="small"
                      data-toggle="tooltip"
                      title="Click it to sign in"
                    />
                    Loading...
                  </span>
                ) : (
                  !error &&
                  !soleAndPartnershipErrorLoading && (
                    <div className="overflow-x-auto">
                      <table className="min-w-full table table-bordered table-striped bg-purple-100">
                        <thead>
                          <tr className="bg-purple-200">
                            <th className="px-2 py-4"> First Name </th>
                            <th className="px-2 py-4"> Middle Name </th>
                            <th className="px-2 py-4"> Last Name </th>
                            <th className="px-2 py-4"> Nationality ID </th>
                            <th className="px-2 py-4"> Country Of Origin </th>
                            <th className="px-2 py-4">
                              {" "}
                              Country Of Residence{" "}
                            </th>
                            <th className="px-2 py-4">
                              {" "}
                              Proof Of Identity Type
                            </th>
                            <th className="px-2 py-4">
                              Proof Of Identity Issued Date
                            </th>
                            <th className="px-2 py-4">
                              Proof Of Identity Expired Date
                            </th>
                            <th className="px-2 py-4">
                              {" "}
                              Marital Status datail
                            </th>
                            <th className="px-2 py-4"> Tax Clearance</th>
                            <th className="px-2 py-4"> dependent</th>
                            <th className="px-2 py-4"> Grauntor</th>
                            <th className="px-2 py-4"> Action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {getSoleAndPartinershipInformation.length === 0 ? (
                            <p className="text-red-600 p-4">Data not found</p>
                          ) : (
                            getSoleAndPartinershipInformation.map(
                              (sole, index) => (
                                <tr
                                  key={index}
                                  className="bg-white
                    border-b border-purple-200"
                                >
                                  <td>{sole.Id}</td>
                                  <td>{sole.FirstName}</td>
                                  <td>{sole.MiddleName}</td>
                                  <td>{sole.LastName}</td>
                                  <td>{sole.Nationality}</td>
                                  <td>{sole.CountryOfOrigin}</td>
                                  <td>{sole.CountryOfResidence}</td>
                                  <td>{sole.ProofOfIdentityType}</td>
                                  <td>{sole.ProofOfIdentityIssuedDate}</td>
                                  <td>{sole.ProofOfIdentityExpiredDate}</td>
                                  <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        getsoleMarriedMatialStatus(sole.Id);
                                        getsoleUnMarriedMatialStatus(sole.Id);
                                        getsoleWidowedMatialStatus(sole.Id);
                                        getsoleDivorcedMatialStatus(sole.Id);
                                        fetchMartialStatus(
                                          sole.MartailStatusType
                                        );
                                        setMartialStatusIdInformationId(
                                          sole.Id
                                        );
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      Marital status detail
                                    </button>
                                  </td>

                                  <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        handleCompanyTaxOpenModal();
                                        getsoleTax(sole.Id);
                                        setTaxId(sole.Id);
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      Tax detail
                                    </button>
                                  </td>
                                  <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        handleCompanySoleDependentOpenModal();
                                        getsoleDependent(sole.Id);
                                        setTaxId(sole.Id);
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      dependent detail
                                    </button>
                                  </td>


                                  <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        handleCompanySoleGuarantorOpenModal();
                                        getsoleGuarantor(sole.Id);
                                        setTaxId(sole.Id);
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      grauntor detail
                                    </button>
                                  </td>
                                  <td>
                                    <button className="text-purple-600 rounded-md font-semibold ">
                                      grauntor detail
                                    </button>
                                  </td>
                                  <td>
                                    <button
                                      type="submit"
                                      onClick={() => {
                                        handleCompanySoleEditOpenModal();
                                        setEditSoleId(sole.Id);
                                        getSoleById(sole.Id);
                                      }}
                                      className="text-purple-600 rounded-md font-semibold "
                                    >
                                      Edit
                                    </button>
                                  </td>
                                </tr>
                              )
                            )
                          )}
                        </tbody>
                      </table>
                    </div>
                  )
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default ViewSoleAndPartnership;
