import { useState, useEffect } from "react";
import {
  BsArrowLeftShort,
  BsChevronDown,
  BsPlus,
  BsSearch,
} from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link, Navigate } from "react-router-dom";
import { useParams, useNavigate } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from "@mui/material";
// import { BsArrowLeftShort, BsSearch, BsChevronDown, BsPlus } from "react-icons/bs";
import CompanyModal from "./CompanyModal";
import { IconButton } from "@mui/material";
import { BsWindowPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
const LeftNav = () => {
  const { Id }= useParams();
  let navigate = useNavigate();
  // const { Id } = useParams();

  const initialCompanyState = {
    Id: null,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  };
  //edit company
  const [editCompany, setEditCompany] = useState(initialCompanyState);
  const [message, setMessage] = useState("");

  const [age, setAge] = useState("");
  const handleChange1 = (event) => {
    setAge(event.target.value);
  };
  const [activeStep, setActiveStep] = useState(0);
  const [open, setOpen] = useState(true);
  const [subMenuOpen, setSubMenuOpen] = useState(false);
  const [leftNav, setLeftNav] = useState();
  const [submitted, setSubmitted] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [companyTypes, setCompanyTypes] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [companyId, setCompanyId] = useState();
  const [getAddress, setGetAddress] = useState();
  const [company, setCompany] = useState(initialCompanyState);
  //update company
  // localStorage.setItem("CompanyId", companyId);
  // const { Id } = useParams();

  //edit address end
  //update company information
  
//   const [editCompany, setEditCompany] = useState({
//     Id: companyId,
//     Name: "",
//     NoOfBranch: "",
//     EstablishmentYear: "",
//     TINNumber: "",
//     TINCertificate: "",
//     CompanyTypeID: "",
//     VATNumber: "",
//     VATCertificate: "",
//     EntityTypeID: "",
//   });
// debugger
//   const [editAddress, setEditAddress] = useState({
//     Id: Id,
//     Country: "",
//     Region: "",
//     ZoneSubCity: "",
//     Woreda: "",
//     City: "",
//     Kebele: "",
//     HouseNo: "",
//     BuildingNo: "",
//     StreetAddress: "",
//   });
  //   ContactInformation: {
  //     Id: "",
  //     DesignatedPersonFName: "",
  //     DesignatedPersonMName: "",
  //     DesignatedPersonLName: "",
  //     Position: "",
  //     MobileNo: "",
  //     Telephone: "",
  //     Email: "",
  //   },
  //   AccountInfo: {
  //     Id: "",
  //     AccountNo: "",
  //     CustomerId: "",
  //     Branch: "",
  //     District: "",
  //     Other: "",
  //   },
  // });
  const updateCompanyById = (Id) => {
    loanDataService
      .getCompanyById(Id)
      .then((res) => {
        setEditCompany(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  useEffect(() => {
    if (Id)
    updateCompanyById(Id);
  }, [id]);
          // {
         
          // ...editCompany,
          // // Id:companyId,
          // Name: res.data.Result.Name,
          // NoOfBranch: res.data.Result.NoOfBranch,
          // EstablishmentYear: res.data.Result.EstablishmentYear,
          // TINNumber: res.data.Result.TINNumber,
          // TINCertificate: res.data.Result.TINCertificate,
          // CompanyTypeID: res.data.Result.CompanyTypeID,
          // VATNumber: res.data.Result.VATNumber,
          // VATCertificate: res.data.Result.VATCertificate,
          // EntityTypeID: res.data.Result.EntityTypeID,
          // Address: {
          //   Country: res.data.Result.Address.Country,
          //   Region: res.data.Result.Address.Region,
          //   ZoneSubCity: res.data.Result.Address.ZoneSubCity,
          //   Woreda: res.data.Result.Address.Woreda,
          //   City: res.data.Result.Address.City,
          //   Kebele: res.data.Result.Address.Kebele,
          //   HouseNo: res.data.Result.Address.HouseNo,
          //   BuildingNo: res.data.Result.Address.BuildingNo,
          //   StreetAddress: res.data.Result.Address.StreetAddress,
          // },
          // ContactInformation: {
          //   Id: Id,
          //   DesignatedPersonFName:
          //     res.data.Result.ContactInformation.DesignatedPersonFName,
          //   DesignatedPersonMName:
          //     res.data.Result.ContactInformation.DesignatedPersonMName,
          //   DesignatedPersonLName:
          //     res.data.Result.ContactInformation.DesignatedPersonLName,
          //   Position: res.data.Result.ContactInformation.Position,
          //   MobileNo: res.data.Result.ContactInformation.MobileNo,
          //   Telephone: res.data.Result.ContactInformation.Telephone,
          //   Email: res.data.Result.ContactInformation.Email,
          // },
          // AccountInfo: {
          //   Id: Id,
          //   AccountNo: res.data.Result.AccountInfo.AccountNo,
          //   CustomerId: res.data.Result.AccountInfo.CustomerId,
          //   Branch: res.data.Result.AccountInfo.Branch,
          //   District: res.data.Result.AccountInfo.District,
          //   Other: res.data.Result.AccountInfo.Other,
          // },
  //       }
  //       );
  //       debugger;
  //     })
  //     .catch((err) => console.log(err));
  // };
  // useEffect(() => {
  //   updateCompanyById();
  // }, []);
  const updateCompanyAddress = () => {
    loanDataService
      .updateCompany(companyId, editCompany)
      .then((response) => {
        // navigation("/company/1");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateCompanyAddress();
  }, []);
  //end update information
  //adress

  const initialAdressState = {
    Id: null,
    CountryID: "",
    RegionID: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  };
  const [address, setAddress] = useState(initialAdressState);
  //end address
  //start contact information
  const initialContactInfoState = {
    Id: null,
    DesignatedPersonFName: "",
    DesignatedPersonMName: "",
    DesignatedPersonLName: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
  };
  const [contactInfo, setContactInfo] = useState(initialContactInfoState);
  //end address
  //account Info
  const initialAccountInfoState = {
    Id: null,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    District: "",
    Other: "",
  };
  const [accountInfo, setAccountInfo] = useState(initialAccountInfoState);
  //trade information
  const initialTradeInfoState = {
    Id: null,
    TradeRegistrationNo: "",
    // UploadTradeRegCerti: "",
    TradeLicenseNo: "",
    IssueDate: "",
    RenewedDate: "",
    ValidityDate: "",
    TradeBusinessTypeID: "",
    // UploadTradeLicense: "",
  };
  const [tradeInfo, setTradeInfo] = useState(initialTradeInfoState);
  // const [country, setCountry] = useState("");
  const initialSoleAndPartnershipState = {
    Id: null,
    CompanyID: "",
    // TaxClearanceID: "",
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    MartailStatusID: "",
    // UploadTradeLicense: "",
  };

  const [soleAndPartnership, setSoleAndPartnership] = useState(
    initialSoleAndPartnershipState
  );

  const [city, setCity] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [selectedValue, setSelectedValue] = useState(null);
  //new
  const [country, setContry] = useState([]);
  const [region, setRegion] = useState([]);
  const [holdId, setHoldId] = useState([]);
  const [holdUserId, setHoldUserId] = useState([]);

  const [tradeSector, setTradeSector] = useState([]);
  const [holdTradeSectorId, setHoldTradeSectorId] = useState([]);
  const [tradeIndustryType, setTradeIndustryType] = useState([]);
  const [tradeIndustryTypeId, setTradeIndustryTypeId] = useState([]);
  const [tradeBusinessType, setTradeBusinessType] = useState([]);
  const [maritalStatus, setMaritalStatus] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCompanyModalOpen, setIsCompanyModalOpen] = useState(false);
  const [isCompanyAddressModalOpen, setIsCompanyAddressModalOpen] =
    useState(false);
  //update company information
