                    ) : (
                      <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {/* <TextField
                      id="TaxClearanceID"
                      label="Tax Clearance"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.TaxClearanceID}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="TaxClearanceID"
                    /> */}
                        <TextField
                          id="FirstName"
                          label="FirstName"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.FirstName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="FirstName"
                        />
                        <TextField
                          id="MiddleName"
                          label="Middle Name"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.MiddleName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="MiddleName"
                        />
                        <TextField
                          id="LastName"
                          label="Last Name"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.LastName}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="LastName"
                        />
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Nationality{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.NationalityID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Country Of Origin{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.CountryOfOriginID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Country Of Residence{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.CountryOfResidenceID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Proof Of Identity Type{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.ProofOfIdentityTypeID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {country.map((yourCounty) => (
                              <MenuItem
                                key={yourCounty.Id}
                                NationalityID={yourCounty.Id}
                                value={yourCounty.Id}
                              >
                                {yourCounty.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        {/* <TextField
                      id="ProofOfIdentityTypeID"
                      label="Proof Of Identity Type"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.ProofOfIdentityTypeID}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="ProofOfIdentityTypeID"
                    /> */}
                        {/* <TextField
                      id="UploadProofOfIdentity"
                      label="UploadProofOfIdentity"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.UploadProofOfIdentity}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="UploadProofOfIdentity"
                    /> */}
                        {/* <TextField
                      id="UploadProofOfIdentity"
                      label="UploadProofOfIdentity"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.UploadProofOfIdentity}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="UploadProofOfIdentity"
                    /> */}
                        <TextField
                          id="ProofOfIdentityIssuedDate"
                          label="ProofOfIdentityIssuedDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.ProofOfIdentityIssuedDate}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="ProofOfIdentityIssuedDate"
                        />
                        <TextField
                          id="ProofOfIdentityExpiredDate"
                          label="ProofOfIdentityExpiredDate"
                          variant="standard"
                          color="secondary"
                          size=""
                          required
                          value={soleAndPartnership.ProofOfIdentityExpiredDate}
                          onChange={handleInputChangeSoleAndPartnership}
                          name="ProofOfIdentityExpiredDate"
                        />
                        <FormControl variant="standard" sx={{ minWidth: 120 }}>
                          <InputLabel id="countrylbl" color="secondary">
                            {" "}
                            Martail Status{" "}
                          </InputLabel>
                          <Select
                            color="secondary"
                            onChange={(event) =>
                              (soleAndPartnership.MartailStatusID =
                                event.target.value)
                            }
                            labelId="countrylbl"
                            id="countrySelect"
                            label="Country"
                          >
                            <MenuItem value="">
                              <em>None</em>
                            </MenuItem>
                            {maritalStatus.map((maritalSt) => (
                              <MenuItem
                                key={maritalSt.Id}
                                NationalityID={maritalSt.Id}
                                value={maritalSt.Id}
                              >
                                {maritalSt.Name}
                              </MenuItem>
                            ))}
                          </Select>
                        </FormControl>
                        {/* here is go 
                    <TextField
                      id="MartailStatusID"
                      label="MartailStatusID"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.MartailStatusID}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="MartailStatusID"
                    /> */}
                      </div>
                    )}
                  </div>
                  <div className="flex justify-between">
                    <button
                      className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      onClick={handleBack}
                    >
                      Back
                    </button>
                    <button
                      className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      onClick={saveSoleAndPartnership}
                    >
                      submit
                    </button>
                  </div>
                </div>
              </CompanyModal>
              <div>
                <table
                  className="table table-bordered table-striped"
                  border="1"
                >
                  <thead>
                    <th> FirstName </th>
                    <th> MiddleName </th>
                    <th> LastName </th>
                    <th> NationalityID </th>
                    <th> CountryOfOriginID </th>
                    <th> CountryOfResidenceID </th>
                    <th> ProofOfIdentityTypeID</th>
                    <th> ProofOfIdentityIssuedDate</th>
                    <th> ProofOfIdentityExpiredDate</th>
                    <th> MartailStatusID</th>
                  </thead>
                  <tbody>
                    {companies.map((company, index) => (
                      // <tr key={loan.loan_id} rowSpan="3">
                      <tr key={index}>
                        <td>{company.FirstName}</td>
                        <td>{company.MiddleName}</td>
                        <td>{company.LastName}</td>
                        <td>{company.NationalityID}</td>
                        <td>{company.CountryOfOriginID}</td>
                        <td>{company.CountryOfResidenceID}</td>
                        <td>{company.ProofOfIdentityTypeID}</td>
                        <td>{company.ProofOfIdentityIssuedDate}</td>
                        <td>{company.ProofOfIdentityExpiredDate}</td>
                        <td>{company.MartailStatusID}</td>
                        <td>
                          <button
                            className="badge badge-danger mr-2"
                            onClick={deleteCompanyUsingId(company.Id)}
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </>
          )}
          {activeStep === 6 && (
            <div>
              <h2 className="text-2xl font-bold mb-4">
                Thank you for submitting the form!
              </h2>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
export default LeftNav;
