          <FormControl variant="standard" sx={{ minWidth: 120 }}>
            <InputLabel id="countrylbl" color="secondary">
              marital status
            </InputLabel>
            <Select
              color="secondary"
              onChange={(event) =>
                (editSoleGuarantor.MartialStatusTypeId = event.target.value)
              }
              labelId="loanType"
              id="loanTypeSelected"
              label="loanType"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              {maritalStatus.map((yourMarital) => (
                <MenuItem
                  key={yourMarital.Id}
                  MartialStatusTypeId={yourMarital.Id}
                  value={yourMarital.Id}
                >
                  {yourMarital.Name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <LoanTextField
            id="Name"
            label="Name"
            value={editSoleGuarantor.TaxClerance.Name}
            onChange={handleInputChangeEditTaxClearance}
            name="Name"
          />
          <LoanTextField
            id="TinNo"
            label="TinNo"
            value={editSoleGuarantor.TaxClerance.TinNo}
            onChange={handleInputChangeEditTaxClearance}
            name="TinNo"
          />
          <LoanTextField
            id="Certificate"
            label="Certificate"
            value={editSoleGuarantor.TaxClerance.Certificate}
            onChange={handleInputChangeEditTaxClearance}
            name="Certificate"
          />
          <LoanTextField
            id="IssueDate"
            label="IssueDate"
            value={editSoleGuarantor.TaxClerance.IssueDate}
            onChange={handleInputChangeEditTaxClearance}
            name="IssueDate"
          />
          <LoanTextField
            id="Expireddate"
            label="Expireddate"
            value={editSoleGuarantor.TaxClerance.Expireddate}
            onChange={handleInputChangeEditTaxClearance}
            name="Expireddate"
          />
          <LoanTextField
            id="Expireddate"
            label="Expireddate"
            value={editSoleGuarantor.TaxClerance.Expireddate}
            onChange={handleInputChangeEditTaxClearance}
            name="Expireddate"
          />
        </div>
        <div>
          <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
            Update
          </button>
        </div>
      </form>*/}
