import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import { IoTrashOutline } from "react-icons/io5";
import { useParams, useNavigate } from "react-router-dom";
import CompanyNavigation from "../../../Common/CompanyNavigation";
import LoanIconButton from "../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../Common/CompanyModal";
import { Link } from "react-router-dom";
import CommonServices from "../../Common/CommonServices";
// import AddCreditFacilityUtilizition from "./AddCreditPerformance";
import AddCreditPerformance from "./AddFcyInflowPerformance";
import LoanTextField from "../../../Common/LoanTextField";
import FcyInflowPerformanceService from "./FcyInflowPerformanceService";
import AddDepositPerformance from "./AddFcyInflowPerformance";
const ViewFcyInflowPerformance = ({
  isFCYInflowPerformanceModalOpen,
  handleFCYInflowPerformanceCloseModal,
  setIFCYInflowPerformanceModalOpen,
  accountDetialId,
  fCYInflowPerformance,
}) => {
  const [
    FCYInflowPerformanceErrorLoading,
    setFCYInflowePerformanceErrorLoading,
  ] = useState(false);
  const navigate = useNavigate();
  // const { companyId } = useParams();
  const [loanTypeId, setLoanTypeId] = useState([]);
  const [currencyId, setCurrencyId] = useState([]);
  const [fCYInflowPerformanceId, setFCYInflowPerformanceId] = useState([]);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [
    deleteFCYInflowFCYInflowPerformanceError,
    setFCYInflowFCYInflowPerformanceDeleteError,
  ] = useState(null);
  const [error, setError] = useState(null);
  const [
    isFCYInflowPerformanceEditModalOpen,
    setIsFCYInflowPerformanceEditModalOpen,
  ] = useState(false);
  function handleFCYInflowPerformanceEditOpenModal() {
    setIsFCYInflowPerformanceEditModalOpen(true);
  }
  function handleFCYInflowPerformanceEditCloseModal() {
    setIsFCYInflowPerformanceEditModalOpen(false);
  }
  function handleFCYInflowPerformanceEditOpenModal() {
    setIsFCYInflowPerformanceEditModalOpen(true);
  }
  function handleFCYInflowPerformanceEditCloseModal() {
    setIsFCYInflowPerformanceEditModalOpen(false);
  }
  const handleInputChangeFCYInflowPerformance = (event) => {
    const { name, value } = event.target;
    setFCYInflowPerformances({
      ...fCYInflowPerformances,
      [name]: value,
    });
  };
  const initialFCYInflowPerformanceState = {
    Id: null,
    BudgetYear: "",
    StartDate: "",
    EndDate: "",
    CurrencyId: "",
    FCYAccount: "",
    DepositBalance: "",
    PreShipmentLoanAccount: "",
    PreShipmentFacilityLimit: "",
    ExportOverdraftAccount: "",
    ExportTermLoanAccount: "",
    TotalExportCreditFacility: "",
    TotalFCYInflowTransactionAmountPerYear: "",
  };
  const [fCYInflowPerformances, setFCYInflowPerformances] = useState(
    initialFCYInflowPerformanceState
  );
  const newFCYInflowPerformance = () => {
    setFCYInflowPerformances(initialFCYInflowPerformanceState);
    // setSubmitted(false);
  };
  const saveFCYInflowPerformance = () => {
    var data = {
      BudgetYear: fCYInflowPerformances.BudgetYear,
      StartDate: fCYInflowPerformances.StartDate,
      EndDate: fCYInflowPerformances.EndDate,
      CurrencyId: currencyId,
      FCYAccount: fCYInflowPerformances.FCYAccount,
      DepositBalance: fCYInflowPerformances.DepositBalance,
      PreShipmentLoanAccount: fCYInflowPerformances.PreShipmentLoanAccount,
      PreShipmentFacilityLimit: fCYInflowPerformances.PreShipmentFacilityLimit,
      ExportOverdraftAccount: fCYInflowPerformances.ExportOverdraftAccount,
      ExportTermLoanAccount: fCYInflowPerformances.ExportTermLoanAccount,
      TotalExportCreditFacility:
        fCYInflowPerformances.TotalExportCreditFacility,
      TotalFCYInflowTransactionAmountPerYear:
        fCYInflowPerformances.TotalFCYInflowTransactionAmountPerYear,
    };
    FcyInflowPerformanceService.addFCYInflowPerformance(data, accountDetialId)
      .then((response) => {
        setFCYInflowPerformances({
          Id: response.data.Id,
          BudgetYear: response.data.BudgetYear,
          StartDate: response.data.StartDate,
          EndDate: response.data.EndDate,
          CurrencyId: response.data.CurrencyId,
          FCYAccount: response.data.FCYAccount,
          DepositBalance: response.data.DepositBalance,
          PreShipmentLoanAccount: response.data.PreShipmentLoanAccount,
          PreShipmentFacilityLimit: response.data.PreShipmentFacilityLimit,
          ExportOverdraftAccount: response.data.ExportOverdraftAccount,
          ExportTermLoanAccount: response.data.ExportTermLoanAccount,
          TotalExportCreditFacility: response.data.TotalExportCreditFacility,
          TotalFCYInflowTransactionAmountPerYear:
            response.data.TotalFCYInflowTransactionAmountPerYear,
        });
        if (response.data.Code === 200) {
          setFCYInflowPerformances(initialFCYInflowPerformanceState);
          console.log(response.data);
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "DepositePerformance",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (fCYInflowPerformance) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${fCYInflowPerformance.FCYAccount}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deleteFCYInflowPerformance",
      });
      FcyInflowPerformanceService.deleteFCYInflowPerformanceById(
        fCYInflowPerformance.Id
      )
        .then(() => {
          fCYInflowPerformance((previousData) =>
            previousData.filter((comp) => comp.Id !== fCYInflowPerformance.Id)
          );
        })
        .catch((e) => {
          setDeleteLoading(false);
        });
    }
  };
  const [editFCYInflowPerformance, setEditFCYInflowPerformance] = useState({
    //  Id: editAddresAccountDetailId,
    BudgetYear: "",
    StartDate: "",
    EndDate: "",
    CurrencyId: "",
    FCYAccount: "",
    DepositBalance: "",
    PreShipmentLoanAccount: "",
    PreShipmentFacilityLimit: "",
    ExportOverdraftAccount: "",
    ExportTermLoanAccount: "",
    TotalExportCreditFacility: "",
    TotalFCYInflowTransactionAmountPerYear: "",
  });

  const handleInputChangeEditFCYInflowPerformance = (event) => {
    const { name, value } = event.target;
    setEditFCYInflowPerformance({
      ...editFCYInflowPerformance,
      [name]: value,
    });
  };

  const getFCYInflowPerformanceId = (editFCYInflowPerformanceId) => {
    FcyInflowPerformanceService.getFCYInflowPerformanceById(
      editFCYInflowPerformanceId
    )
      .then((res) => {
        setEditFCYInflowPerformance({
          ...editFCYInflowPerformance,
          Id: editFCYInflowPerformanceId,
          BudgetYear: res.data.Result.BudgetYear,
          StartDate: res.data.Result.StartDate,
          EndDate: res.data.Result.EndDate,
          CurrencyId: res.data.Result.CurrencyId,
          FCYAccount: res.data.Result.FCYAccount,
          DepositBalance: res.data.Result.DepositBalance,
          PreShipmentLoanAccount: res.data.Result.PreShipmentLoanAccount,
          PreShipmentFacilityLimit: res.data.Result.PreShipmentFacilityLimit,
          ExportOverdraftAccount: res.data.Result.ExportOverdraftAccount,
          ExportTermLoanAccount: res.data.Result.ExportTermLoanAccount,
          TotalExportCreditFacility: res.data.Result.TotalExportCreditFacility,
          TotalFCYInflowTransactionAmountPerYear:
            res.data.Result.TotalFCYInflowTransactionAmountPerYear,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateFCYInflowPerformanceById = (e) => {
    e.preventDefault();
    FcyInflowPerformanceService.updateDepositePerformanceById(
      fCYInflowPerformanceId,
      editFCYInflowPerformance
    )
      .then((response) => {
        if (response.data.Code === 200) {
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const [currency, setCurrency] = useState([]);
  const getCurrency = () => {
    setTimeout(() => {
      CommonServices.getCurrency()
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setCurrency(response.data.Result);
          } else {
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Credit performance not found");
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getCurrency();
  }, []);
  return (
    <div className="fixed inset-0 flex items-center justify-center ">
      <CompanyModal
        isOpen={isFCYInflowPerformanceModalOpen}
        onClose={handleFCYInflowPerformanceCloseModal}
        title="FCYInflow performance detail "
      >
        {/* <div className=" min-w-screen relative min-h-screen "> */}
        <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll ">
          <div className="flex flex-wrap ">
            <div className="w-full ">
              <div>
                {/* <AddDepositPerformance
                  // submitted={submitted}
                  // newAccountDetials={newCreditFacilityUtilizition}
                  depositPerformances={depositePerformances}
                  handleInputChangeDepositPerformance={
                    handleInputChangeDepositePerformance
                  }
                  setLoanTypeId={setLoanTypeId}
                  // handleBack={handleBack}
                  // handleSkip={handleSkip}
                  // handleNext={handleNext}
                  loanTypeIdForPerformance={loanTypeIdForPerformance}
                  currency={currency}
                  saveDepositPerformance={saveDepositePerformance}
                  setCurrencyId={setCurrencyId}
                /> */}
                <ToastContainer />
                {error && <p className="text-red-500">{error}</p>}
                {!error && FCYInflowPerformanceErrorLoading ? (
                  <span>
                    <FaSpinner
                      className="animate-spin rounded-full h-6 w-6 text-purple-600"
                      fontSize="small"
                      data-toggle="tooltip"
                      title="Click it to sign in"
                    />
                    Loading...
                  </span>
                ) : (
                  !error &&
                  !DepositePerformanceErrorLoading && (
                    <table className="table table-bordered table-striped bg-purple-100">
                      <thead>
                        <tr className="bg-purple-200 ">
                          <th className="px-2 py-4"> Budget Year</th>
                          <th className="px-2 py-4"> Start Date </th>
                          <th className="px-2 py-4"> End Date </th>
                          <th className="px-2 py-4"> Currency</th>
                          <th className="px-2 py-4"> FCY Account </th>
                          <th className="px-2 py-4"> Deposit Balance </th>
                          <th className="px-2 py-4">
                            {" "}
                            Pre ShipmentLoan Account{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Pre-Shipment Facility Limit{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Export Over draft Account
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Export Term Loan Account{" "}
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total Export Credit Facility
                          </th>
                          <th className="px-2 py-4">
                            {" "}
                            Total FCYInflow Transaction Amount Per Year{" "}
                          </th>
                          <th className="px-2 py-4"> Action </th>
                        </tr>
                      </thead>
                      <tbody>
                        {depositePerformance.length === 0 ? (
                          <p className="text-red-600 p-4">Data not found</p>
                        ) : (
                          depositePerformance.map(
                            (FacilityUtilizition, index) => (
                              <>
                                <tr
                                  className="bg-white
                    border-b border-purple-200"
                                  key={FacilityUtilizition.Id}
                                >
                                  <td className="p-2">
                                    {FacilityUtilizition.BudgetYear}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.StartDate}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.EndDate}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.CurrencyId}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.FCYAccount}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.DepositBalance}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.PreShipmentLoanAccount}
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.PreShipmentFacilityLimit
                                    }
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.ExportOverdraftAccount}
                                  </td>
                                  <td className="p-2">
                                    {FacilityUtilizition.ExportTermLoanAccount}
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.TotalExportCreditFacility
                                    }
                                  </td>
                                  <td className="p-2">
                                    {
                                      FacilityUtilizition.TotalFCYInflowTransactionAmountPerYear
                                    }
                                  </td>
                                  {/* <td>
                                    <LoanIconButton
                                      style={{ color: "red" }}
                                      icon={<IoTrashOutline />}
                                      text="Delete"
                                      onClick={() => {
                                        handleDelete(FacilityUtilizition);
                                      }}
                                    >
                                      <ToastContainer />
                                    </LoanIconButton>
                                  </td> */}
                                  {/* <td className="p-2">
                                    <LoanIconButton
                                      style={{ color: "green" }}
                                      icon={<MdModeEdit />}
                                      text="Edit"
                                      onClick={() => {
                                        handleDepositePerformanceEditOpenModal();
                                        getDepositePerformanceId(
                                          FacilityUtilizition.Id
                                        );
                                        setDepositePerformanceId(
                                          FacilityUtilizition.Id
                                        );
                                      }}
                                    />
                                  </td> */}
                                </tr>
                              </>
                            )
                          )
                        )}
                      </tbody>
                    </table>
                  )
                )}
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isFCYInflowPerformanceEditModalOpen}
        onClose={handleFCYInflowPerformanceEditCloseModal}
        title="Modify account detail information"
      >
        <div>
          <form onSubmit={updateFCYInflowPerformanceById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <TextField
                    id="BudgetYear"
                    label="BudgetYear"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editFCYInflowPerformance.BudgetYear}
                    onChange={(e) =>
                      setEditFCYInflowPerformance({
                        ...editFCYInflowPerformance,
                        BudgetYear: e.target.value,
                      })
                    }
                    name="BudgetYear"
                  />
                  <p>
                    Start Date:
                    <input
                      id="StartDate"
                      name="StartDate"
                      label="StartDate"
                      placeholder="StartDate"
                      type="date"
                      value={
                        startDate.getFullYear().toString() +
                        "-" +
                        (startDate.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        startDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setStartDate(new Date(e.target.value));
                        editFCYInflowPerformance.StartDate = startDate;
                      }}
                    />{" "}
                  </p>
                  <p>
                    Maturity Date:
                    <input
                      id="EndDate"
                      name="EndDate"
                      label="EndDate"
                      placeholder="EndDate"
                      type="date"
                      value={
                        maturityDate.getFullYear().toString() +
                        "-" +
                        (maturityDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        maturityDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setMaturityDate(new Date(e.target.value));
                        editFCYInflowPerformance.EndDate = maturityDate;
                      }}
                    />{" "}
                  </p>
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="LoanTypeId" color="secondary">
                      currency type{" "}
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) => {
                        editDepositePerformance.CurrencyId = event.target.value;
                        // setCurrencyId(event.target.value);
                      }}
                      labelId="CurrencyId"
                      id="CurrencyId"
                      label="CurrencyId"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {currency.map((yourCurrency) => (
                        <MenuItem
                          key={yourCurrency.Id}
                          CurrencyId={yourCurrency.Id}
                          value={yourCurrency.Id}
                        >
                          {yourCurrency.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <TextField
                    id="FCYAccount"
                    label="FCYAccount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editFCYInflowPerformance.FCYAccount}
                    onChange={(e) =>
                      setEditFCYInflowPerformance({
                        ...editFCYInflowPerformance,
                        FCYAccount: e.target.value,
                      })
                    }
                    name="FCYAccount"
                  />
                  <TextField
                    id="PreShipmentLoanAccount"
                    label="PreShipmentLoanAccount"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editFCYInflowPerformance.PreShipmentLoanAccount}
                    onChange={(e) =>
                      setEditFCYInflowPerformance({
                        ...editFCYInflowPerformance,
                        PreShipmentLoanAccount: e.target.value,
                      })
                    }
                    name="PreShipmentLoanAccount"
                  />
                  <TextField
                    id="PreShipmentFacilityLimit"
                    label="PreShipmentFacilityLimit"
                    variant="standard"
                    color="secondary"
                    size=""
                    required
                    value={editFCYInflowPerformance.PreShipmentFacilityLimit}
                    onChange={(e) =>
                      setEditFCYInflowPerformance({
