import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import AddUnMaritalStatus from "./AddUnMarriedStatus";
import CommonServices from "../../Common/CommonServices";
const ViewUnMarriedStatus = ({
  holdUnMarried,
  SoleId,
  isCompanyMartailStatusUnmarriedModalOpen,
  handleCompanyMartailStatusUnmarriedCloseModal,
  handleCompanyMartailStatusUnmarriedOpenModal,
}) => {
  // const [marriedId, setMarriedId] = useState();
  // const [country, setContry] = useState([]);
  // const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [
    isCompanyUnmarriedInformationEditModalOpen,
    setIsCompanyUnmarriedInformationEditModalOpen,
  ] = useState(false);
  function handleCompanyUnmarriedEditCloseModal() {
    setIsCompanyUnmarriedInformationEditModalOpen(false);
  }
  function handleCompanyUnmarriedInformationEditOpenModal() {
    setIsCompanyUnmarriedInformationEditModalOpen(true);
  }
  function handleCompanyUnmarriedInformationEditCloseModal() {
    setIsCompanyUnmarriedInformationEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editUnmarriedInfo, setEditUnmarriedInfo] = useState({
    // Id: taxInformationId,
    UnMarriedCertficate: "",
    UnMarriedCertficateIssueDate: "",
    UnMarriedCertficateExpiredDate: "",
    UnMarriedCertficateIssuer: "",
  });
  const handleInputChangeEditUnMarriedStatus = (event) => {
    const { name, value } = event.target;
    setEditUnmarriedInfo({ ...editUnmarriedInfo, [name]: value });
  };
  const updateUnmarriedById = (taxInformationId1) => {
    CommonServices.getUnmarried(taxInformationId1)
      .then((res) => {
        setEditUnmarriedInfo({
          ...editUnmarriedInfo,
          UnMarriedCertficate: res.data.Result.UnMarriedCertficate,
          UnMarriedCertficateIssueDate:
            res.data.Result.UnMarriedCertficateIssueDate,
          UnMarriedCertficateExpiredDate:
            res.data.Result.UnMarriedCertficateExpiredDate,
          UnMarriedCertficateIssuer: res.data.Result.UnMarriedCertficateIssuer,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateUnmarriedById();
  }, []);
  const updateUnmarriedInfo = () => {
    CommonServices.updateUnmarried(taxInformationId, editUnmarriedInfo)    
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateUnmarriedInfo();
  }, []);

  // const fetchContry = () => {
  //   CommonServices.getCountry()
  //     .then((response) => {
  //       setContry(response.data.Result);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };
  // useEffect(() => {
  //   fetchContry();
  // }, []);

  // const fetchProofOfIdentityType = () => {
  //   CommonServices.getProofOfIdentityTypes()
  //     .then((response) => {
  //       setProofOfIdentityType(response.data.Result);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };
  // useEffect(() => {
  //   fetchProofOfIdentityType();
  // }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMartailStatusUnmarriedModalOpen}
        onClose={handleCompanyMartailStatusUnmarriedCloseModal}
        title="Add marital status information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdUnMarried ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
                <th className="px-2 py-4"> UnMarried Certficate</th>
                <th className="px-2 py-4"> UnMarried Certficate Issue Date</th>
                <th className="px-2 py-4">
                  {" "}
                  UnMarried Certficate Expired Date
                </th>
                <th className="px-2 py-4"> UnMarried Certficate Issue</th>
                <th className="px-2 py-4"> Action </th>
              </thead>
              <tbody>
                <tr>
                  <td>{holdUnMarried.UnMarriedCertficate}</td>
                  <td>{holdUnMarried.UnMarriedCertficateIssueDate}</td>
                  <td>{holdUnMarried.UnMarriedCertficateExpiredDate}</td>
                  <td>{holdUnMarried.UnMarriedCertficateIssuer}</td>
                  <td>
                    <button
                      onClick={() => {
                        handleCompanyUnmarriedInformationEditOpenModal();
                        updateUnmarriedById(holdUnMarried.Id);
                        setTaxInformationId(holdUnMarried.Id);
                        // saveTaxSoleInfo(sole.Id);
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Edit
                    </button>
                  </td>
                  <td>
                    <button className="text-purple-600 rounded-md font-semibold ">
                      Tax detail
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          ) : (
            ""
          )}
        </div>
        <div>
          {holdUnMarried <= 0 && (
            <AddUnMaritalStatus
              Add
              marital
              status
              information
              SoleId={SoleId}
            />
          )}
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyUnmarriedInformationEditModalOpen}
        onClose={handleCompanyUnmarriedEditCloseModal}
        title="UnMarried edit information"
      >
        <div>
          <form onSubmit={updateUnmarriedInfo}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="UnMarriedCertficate"
                label="UnMarriedCertficate"
                value={editUnmarriedInfo.UnMarriedCertficate}
                onChange={handleInputChangeEditUnMarriedStatus}
                name="UnMarriedCertficate"
              />
              <LoanTextField
                id="UnMarriedCertficateIssueDate"
                label="UnMarriedCertficateIssueDate"
                value={editUnmarriedInfo.UnMarriedCertficateIssueDate}
                onChange={handleInputChangeEditUnMarriedStatus}
                name="UnMarriedCertficateIssueDate"
              />
              <LoanTextField
                id="UnMarriedCertficateExpiredDate"
                label="UnMarriedCertficateExpiredDate"
                value={editUnmarriedInfo.UnMarriedCertficateExpiredDate}
                onChange={handleInputChangeEditUnMarriedStatus}
                name="UnMarriedCertficateExpiredDate"
              />
              <LoanTextField
                id="UnMarriedCertficateIssuer"
                label="UnMarriedCertficateIssuer"
                value={editUnmarriedInfo.UnMarriedCertficateIssuer}
                onChange={handleInputChangeEditUnMarriedStatus}
                name="UnMarriedCertficateIssuer"
              />
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewUnMarriedStatus;
