      icon: <GiProgression />,
      spacing: true,
      link: "company/:1/:3",
    },
    {
      title: "projects",
      icon: <GrProjects />,
      submenu: true,
      submenuItems: [
        { title: "submenu 1" },
        { title: "submenu 2" },
        { title: "submenu 3" },
