                        {" "}
                        Proof Of Identity Type{" "}
                      </InputLabel>
                      <Select
                        color="secondary"
                        onChange={(event) =>
                          (soleAndPartnership.ProofOfIdentityTypeID =
                            event.target.value)
                        }
                        labelId="countrylbl"
                        id="countrySelect"
                        label="Country"
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        {proofOfIdentityType.map((yourProofOfIdentityType) => (
                          <MenuItem
                            key={yourProofOfIdentityType.Id}
                            ProofOfIdentityTypeID={yourProofOfIdentityType.Id}
                            value={yourProofOfIdentityType.Id}
                          >
                            {yourProofOfIdentityType.Name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    <TextField
                      id="UploadProofOfIdentity"
                      label="UploadProofOfIdentity"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.UploadProofOfIdentity}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="UploadProofOfIdentity"
                    />
                    <TextField
                      id="ProofOfIdentityIssuedDate"
                      label="ProofOfIdentityIssuedDate"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.ProofOfIdentityIssuedDate}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="ProofOfIdentityIssuedDate"
                    />
                    <TextField
                      id="ProofOfIdentityExpiredDate"
                      label="ProofOfIdentityExpiredDate"
                      variant="standard"
                      color="secondary"
                      size=""
                      required
                      value={soleAndPartnership.ProofOfIdentityExpiredDate}
                      onChange={handleInputChangeSoleAndPartnership}
                      name="ProofOfIdentityExpiredDate"
                    />
                    <FormControl variant="standard" sx={{ minWidth: 120 }}>
                      <InputLabel id="countrylbl" color="secondary">
                        {" "}
                        Martail Status{" "}
                      </InputLabel>
                      <Select
                        color="secondary"
                        onChange={(event) => {
                          soleAndPartnership.MartailStatusID =
                            event.target.value;
                          setMartailStatusHold(
                            (soleAndPartnership.MartailStatusID =
                              event.target.value)
                          );
                        }}
                        labelId="countrylbl"
                        id="countrySelect"
                        label="Country"
                      >
                        <MenuItem value="">
                          <em>None</em>
                        </MenuItem>
                        {maritalStatus.map((maritalSt) => (
                          <MenuItem
                            key={maritalSt.Id}
                            NationalityID={maritalSt.Id}
                            value={maritalSt.Id}
                          >
                            <button>{maritalSt.Name}</button>
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </div>
                </CompanyModal>
              </div>
              <div>
                <CompanyModal
                  isOpen={isCompanyTaxSoleModalOpen}
                  onClose={handleCompanyTaxSoleCloseModal}
                  title="Add company sole and partinership tax information"
                >
                  <div className="rounded-lg overflow-hidden">
                    {getSoleTax && (
                      <table className="table table-bordered table-striped bg-purple-100">
                        <thead>
                          <th className="px-2 py-4"> Name </th>
                          <th className="px-2 py-4"> TinNo </th>
                          <th className="px-2 py-4"> Issue Date </th>
                          <th className="px-2 py-4"> Expired date </th>
                          <th className="px-2 py-4"> Tax Debi </th>
                          <th className="px-2 py-4">
                            Tax Clearance Certificate
                          </th>
                          <th className="px-2 py-4"> Action </th>
                        </thead>
                        <tbody>
                          {/* {getSoleTax.map((soleTax, index) => ( */}
                          <tr
                          // key={index}
                          >
                            <td>{getSoleTax.Id}</td>
                            <td>{getSoleTax.Name}</td>
                            <td>{getSoleTax.TinNo}</td>
                            <td>{getSoleTax.IssueDate}</td>
                            <td>{getSoleTax.Expireddate}</td>
                            <td>{getSoleTax.TaxDebi}</td>
                            <td>{getSoleTax.TaxClearanceCertificate}</td>
                            <button
                              onClick={() => {
                                handleCompanyTaxInformationEditOpenModal();
                                // saveTaxSoleInfo(sole.Id);
                              }}
                              className="text-purple-600 rounded-md font-semibold "
                            >
                              <span>
                                <IconButton aria-label="Add" color="secondary">
                                  <BsPlus /> Edit
                                </IconButton>
                              </span>
                            </button>
                          </tr>
                          {/* ))}  */}
                        </tbody>
                      </table>
                    )}
                  </div>
                  <button
                    onClick={() => setToggleTaxInfo(!toggleTaxSoleInfo)}
                    className="text-purple-600 rounded-md font-medium "
                  >
                    Add Tax
                  </button>
                  {toggleTaxSoleInfo && (
                    <div className="submit-form">
                      {submitted ? (
                        <div>
                          <h4>You submitted successfully!</h4>
                          <button
                            className="btn btn-success"
                            onClick={newTaxSoleInfo}
                          >
                            Add
                          </button>
                        </div>
                      ) : (
                        <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                          <TextField
                            id="Name"
                            label="Name"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.Name}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="Name"
                          />
                          <TextField
                            id="TinNo"
                            label="TinNo"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TinNo}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TinNo"
                          />
                          <TextField
                            id="IssueDate"
                            label="IssueDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.IssueDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="IssueDate"
                          />
                          <TextField
                            id="ExpiredDate"
                            label="ExpiredDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.ExpiredDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="ExpiredDate"
                          />
                          <TextField
                            id="TaxDebi"
                            label="TaxDebi"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxDebi}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxDebi"
                          />
                          <TextField
                            id="TaxClearanceCertificate"
                            label="TaxClearanceCertificate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxClearanceCertificate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxClearanceCertificate"
                          />
                        </div>
                      )}
                      <button
                        className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        onClick={() => {
                          saveTaxSoleInfo();
                          setToggleTaxInfo(!toggleTaxSoleInfo);
                        }}
                      >
                        save Tax
                      </button>
                    </div>
                  )}
                </CompanyModal>
              </div>
              <CompanyModal
                isOpen={isCompanyTaxInformationEditModalOpen}
                onClose={handleCompanyTaxEditCloseModal}
                title="Add company sole and partinership tax edit information"
              >
                <div>
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                          <TextField
                            id="Name"
                            label="Name"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.Name}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="Name"
                          />
                          <TextField
                            id="TinNo"
                            label="TinNo"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TinNo}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TinNo"
                          />
                          <TextField
                            id="IssueDate"
                            label="IssueDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.IssueDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="IssueDate"
                          />
                          <TextField
                            id="ExpiredDate"
                            label="ExpiredDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.ExpiredDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="ExpiredDate"
                          />
                          <TextField
                            id="TaxDebi"
                            label="TaxDebi"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxDebi}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxDebi"
                          />
                          <TextField
                            id="TaxClearanceCertificate"
                            label="TaxClearanceCertificate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxClearanceCertificate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxClearanceCertificate"
                          />
                        </div>   
                        <div>
                        <button type="submit" className="btn btn-info">
                          Update
                        </button>
                        </div>                    
                </div>
                
              </CompanyModal>
            </>
          )}
          {activeStep === 7 && (
            <div>
              <h2 className="text-2xl font-bold mb-4">
                Thank you for submitting the form!
              </h2>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
export default LeftNav;
