import { useState, useEffect } from "react";
import LoanTextField from "../../../Common/LoanTextField";
import CommonServices from "../../Common/CommonServices";
const AddDivorcedStatus = ({ SoleId }) => {
  //upload
  const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
  const [selectedPathUnmarriege, setSelectedPathUnmarriege] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  //upload 1
  const uploadMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathIdentity(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  //upload 2
  const uploadUnMarriegeCertificate = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathUnmarriege(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //endupload2
  const [submitted, setSubmitted] = useState(false);
  const [isCompanyMaritalStatusModalOpen, setIsCompanyMaritalStatusModalOpen] =
    useState(false);
  function handleGeneralCloseModal() {
    isCompanyMaritalStatusModalOpen(false);
  }
  function handleCompanyMaritalStatusCloseModal() {
    setIsCompanyMaritalStatusModalOpen(false);
  }
  const [toggleDivorcedInfo, setToggleDivorcedInfo] = useState(false);
  const [divorceDate, setDivorceDate] = useState(new Date());
  const [unMarriedCertficateIssueDate, setUnMarriedCertficateIssueDate] =
    useState(new Date());
  const [unMarriedCertficateExpiredDate, setUnMarriedCertficateExpiredDate] =
    useState(new Date());
  const initialMartailStatusDivorcedState = {
    Id: null,
    SoleAndPartnerShipID: SoleId,
    DivorceDate: "",
    MarriageCertificate: "",
    MarriageCertificateIssuer: "",
    UnMarriedCertficate: "",
    UnMarriedCertficateIssueDate: "",
    UnMarriedCertficateExpiredDate: "",
    UnMarriedCertficateIssuer: "",
    CourtDecision: "",
  };
  const [martailStatusDivorced, setMartailStatusDivorced] = useState(
    initialMartailStatusDivorcedState
  );
  const newMartailStatusDivorced = () => {
    setMartailStatusDivorced(initialMartailStatusDivorcedState);
    setSubmitted(false);
  };
  const handleInputChangeMartailStatusDivorced = (event) => {
    const { name, value } = event.target;
    setMartailStatusDivorced({ ...martailStatusDivorced, [name]: value });
  };
  const saveDivorced = () => {
    var data = {
      SoleAndPartnerShipID: martailStatusDivorced.SoleAndPartnerShipID,
      DivorceDate: martailStatusDivorced.DivorceDate,
      MarriageCertificate: selectedPathIdentity,
      MarriageCertificateIssuer:
        martailStatusDivorced.MarriageCertificateIssuer,
      UnMarriedCertficate: selectedPathUnmarriege,
      UnMarriedCertficateIssueDate:
        martailStatusDivorced.UnMarriedCertficateIssueDate,
      UnMarriedCertficateExpiredDate:
        martailStatusDivorced.UnMarriedCertficateExpiredDate,
      UnMarriedCertficateIssuer:
        martailStatusDivorced.UnMarriedCertficateIssuer,
      CourtDecision: martailStatusDivorced.CourtDecision,
    };
    CommonServices.divorcedSole(data, SoleId)
      .then((response) => {
        setMartailStatusDivorced({
          Id: response.data.Id,
          SoleAndPartnerShipID: response.data.SoleAndPartnerShipID,
          DivorceDate: response.data.DivorceDate,
          MarriageCertificate: response.data.MarriageCertificate,
          MarriageCertificateIssuer: response.data.MarriageCertificateIssuer,
          UnMarriedCertficate: response.data.UnMarriedCertficate,
          UnMarriedCertficateIssueDate:
            response.data.UnMarriedCertficateIssueDate,
          UnMarriedCertficateExpiredDate:
            response.data.UnMarriedCertficateExpiredDate,
          UnMarriedCertficateIssuer: response.data.UnMarriedCertficateIssuer,
          CourtDecision: response.data.CourtDecision,
        });
        setSubmitted(true);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <div>
      <button
        onClick={() => setToggleDivorcedInfo(!toggleDivorcedInfo)}
        className="text-purple-600 rounded-md font-medium "
      >
        Add Divorced
      </button>
      {toggleDivorcedInfo && (
        <div className="submit-form">
          {submitted ? (
            <div>
              <h4>You submitted successfully!</h4>
            </div>
          ) : (
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <p>
                Divorce Date:
                <input
                  id="DivorceDate"
                  name="DivorceDate"
                  label="DivorceDate"
                  placeholder="DivorceDate"
                  type="date"
                  value={
                    divorceDate.getFullYear().toString() +
                    "-" +
                    (divorceDate.getMonth() + 1).toString().padStart(2, 0) +
                    "-" +
                    divorceDate.getDate().toString().padStart(2, 0)
                  }
                  onChange={(e) => {
                    setDivorceDate(new Date(e.target.value));
                    martailStatusDivorced.DivorceDate = divorceDate;
                  }}
                />{" "}
              </p>

              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <LoanTextField
                id="MarriageCertificateIssuer"
                label="MarriageCertificateIssuer"
                value={martailStatusDivorced.MarriageCertificateIssuer}
                onChange={handleInputChangeMartailStatusDivorced}
                name="MarriageCertificateIssuer"
              />
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="btn btn-success"
                  disabled={!selectedFiles}
                  onClick={uploadUnMarriegeCertificate}
                >
                  Upload UnMarriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              <p>
                UnMarried Certficate Issue Date:
                <input
                  id="UnMarriedCertficateIssueDate"
                  name="UnMarriedCertficateIssueDate"
                  label="UnMarriedCertficateIssueDate"
                  placeholder="UnMarriedCertficateIssueDate"
                  type="date"
                  value={
                    unMarriedCertficateIssueDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateIssueDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateIssueDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateIssueDate(new Date(e.target.value));
                    martailStatusDivorced.UnMarriedCertficateIssueDate =
                      unMarriedCertficateIssueDate;
                  }}
                />{" "}
              </p>
              <p>
                UnMarried Certficate Expired Date:
                <input
                  id="UnMarriedCertficateExpiredDate"
                  name="UnMarriedCertficateExpiredDate"
                  label="UnMarriedCertficateExpiredDate"
                  placeholder="UnMarriedCertficateExpiredDate"
                  type="date"
                  value={
                    unMarriedCertficateExpiredDate.getFullYear().toString() +
                    "-" +
                    (unMarriedCertficateExpiredDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    unMarriedCertficateExpiredDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setUnMarriedCertficateExpiredDate(new Date(e.target.value));
                    martailStatusDivorced.UnMarriedCertficateExpiredDate =
                      unMarriedCertficateExpiredDate;
                  }}
                />{" "}
              </p>
              <LoanTextField
                id="UnMarriedCertficateIssuer"
                label="UnMarriedCertficateIssuer"
                value={martailStatusDivorced.UnMarriedCertficateIssuer}
                onChange={handleInputChangeMartailStatusDivorced}
                name="UnMarriedCertficateIssuer"
              />
              <LoanTextField
                id="CourtDecision"
                label="CourtDecision"
                value={martailStatusDivorced.CourtDecision}
                onChange={handleInputChangeMartailStatusDivorced}
                name="CourtDecision"
              />
            </div>
          )}
          <div>
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveDivorced();
              }}
            >
              save
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
export default AddDivorcedStatus;
