import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import CommonServices from "../../Common/CommonServices";
// import AddSoleDependent from "./AddSoleDependent";
const OtherBank = ({
  getOtherBankCredit,
  isCompanySoleOtherBankModalOpen,
  handleCompanySoleOtherBankCloseModal,
  handleCompanySoleOtherBankOpenModal,
  soleID, //well transfer to add sole dependent 
  companyId,
}) => {
  const [isCompanySoleOtherBankEditedModalOpen, setIsCompanySoleOtherBankEditedModalOpen, ] = useState(false);
  function handleCompanySoleOtherBankEditCloseModal() {
    setIsCompanySoleOtherBankEditedModalOpen(false);
  }
  function handleCompanySoleOtherBankEditedModalOpen() {
    setIsCompanySoleOtherBankEditedModalOpen(true);
  }
  function handleCompanyOtherBankEditCloseModal() {
    setIsCompanySoleOtherBankEditedModalOpen(false);
  }
  const [otherBankId, setOtherBankId] = useState();
  const [editSoleOtherBank, setEditSoleOtherBank] = useState({
    SoleAndPartnerShipId: soleID,
    FullName: "",
    TinNumber: "",
    BankName: "",
    BranchName: "",
    AccountNumber: "",
    LoanTypeId: "",
    ReportingDate: "",
    CurrencyId: "",
    ApprovedAmount: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequencyId: "",
    NumberOfInstallment: "",
    InstallmentAmount: "",
    OutstandingLoanBalance: "",
    LoanStatusId: "",
    NoOfDaysInArrears: "",
    BalanceOverdue: "",
    NoOfRestructuring: "",
    RestructuredCreditReferenceNumber: "",
    SectorTypeId: "",
    IndustryTypeId: "",
    CreditInformationDocument: "",
  });
  const handleInputChangeEditOtherBank = (event) => {
    const { name, value } = event.target;
    setEditSoleOtherBank({ ...editSoleOtherBank, [name]: value });
  };
  const getOtherBankById = (taxInformationId) => {
    CommonServices.getGuarantorById(taxInformationId)
      .then((res) => {
        setEditSoleOtherBank({
          ...editSoleOtherBank,
          SoleAndPartnerShipId: res.data.Result.SoleAndPartnerShipId,
          FullName:res.data.Result.FullName,
          TinNumber:res.data.Result.TinNumber,
          BankName:res.data.Result.BankName,
          BranchName:res.data.Result.BranchName,
          AccountNumber:res.data.Result.AccountNumber,
          LoanTypeId:res.data.Result.LoanTypeId,
          ReportingDate:res.data.Result.ReportingDate,
          CurrencyId:res.data.Result.CurrencyId,
          ApprovedAmount:res.data.Result.ApprovedAmount,
          StartDate:res.data.Result.StartDate,
          MaturityDate:res.data.Result.MaturityDate,
          RepaymentFrequencyId:res.data.Result.RepaymentFrequencyId,
          NumberOfInstallment:res.data.Result.NumberOfInstallment,
          InstallmentAmount:res.data.Result.InstallmentAmount,
          OutstandingLoanBalance:res.data.Result.OutstandingLoanBalance,
          LoanStatusId:res.data.Result.LoanStatusId,
          NoOfDaysInArrears:res.data.Result.NoOfDaysInArrears,
          BalanceOverdue:res.data.Result.BalanceOverdue,
          NoOfRestructuring:res.data.Result.NoOfRestructuring,
          RestructuredCreditReferenceNumber:res.data.Result.RestructuredCreditReferenceNumber,
          SectorTypeId:res.data.Result.SectorTypeId,
          IndustryTypeId:res.data.Result.IndustryTypeId,
          CreditInformationDocument:res.data.Result.CreditInformationDocument
      })
      .catch((err) => console.log(err));
  });
}
  const updateOtherBank = () => {
    CommonServices.updateGuarantor(otherBankId, editSoleOtherBank)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateOtherBank();
  }, []); 
  //add dependent
  const [toggleSoleOtherBank, setToggleSoleOtherBank] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const initialSoleOtherBankState = {
    Id: null,
    SoleAndPartnerShipId: soleID,
    FullName: "",
    TinNumber: "",
    BankName: "",
    BranchName: "",
    AccountNumber: "",
    LoanTypeId: "",
    ReportingDate: "",
    CurrencyId: "",
    ApprovedAmount: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequencyId: "",
    NumberOfInstallment: "",
    InstallmentAmount: "",
    OutstandingLoanBalance: "",
    LoanStatusId: "",
    NoOfDaysInArrears: "",
    BalanceOverdue: "",
    NoOfRestructuring: "",
    RestructuredCreditReferenceNumber: "",
    SectorTypeId: "",
    IndustryTypeId: "",
    CreditInformationDocument: "",   
  };
  const [soleOtherBank, setSoleOtherBank] = useState(initialSoleOtherBankState);
  // const handleInputChangeSoleGuarantor = (event) => {
  //   const { name, value } = event.target;
  //   setSoleGuarantor({ ...soleGuarantor, [name]: value });
  // };
  const handleInputChangeSoleOtherBank = (event) => {
    const { name, value } = event.target;
    setSoleOtherBank({ ...soleOtherBank, [name]: value });
  };

  const saveSoleOtherBank = () => {
    var data = {
    FullName: soleOtherBank.FullName,
    TinNumber: soleOtherBank.TinNumber,
    BankName: soleOtherBank.BankName,
    BranchName: soleOtherBank.BranchName,
    AccountNumber: soleOtherBank.AccountNumber,
    LoanTypeId: soleOtherBank.LoanTypeId,
    ReportingDate: soleOtherBank.ReportingDate,
    CurrencyId: soleOtherBank.CurrencyId,
    ApprovedAmount: soleOtherBank.ApprovedAmount,
    StartDate: soleOtherBank.StartDate,
    MaturityDate: soleOtherBank.MaturityDate,
    RepaymentFrequencyId: soleOtherBank.RepaymentFrequencyId,
    NumberOfInstallment: soleOtherBank.NumberOfInstallment,
    InstallmentAmount: soleOtherBank.InstallmentAmount,
    OutstandingLoanBalance: soleOtherBank.OutstandingLoanBalance,
    LoanStatusId: soleOtherBank.LoanStatusId,
    NoOfDaysInArrears: soleOtherBank.NoOfDaysInArrears,
    BalanceOverdue: soleOtherBank.BalanceOverdue,
    NoOfRestructuring: soleOtherBank.NoOfRestructuring,
    RestructuredCreditReferenceNumber: soleOtherBank.RestructuredCreditReferenceNumber,
    SectorTypeId: soleOtherBank.SectorTypeId,
    IndustryTypeId: soleOtherBank.IndustryTypeId,
    CreditInformationDocument: soleOtherBank.CreditInformationDocument,
    };
    CommonServices.addOtherBank(soleID,data)
      .then((response) => {
        setSoleOtherBank({
          Id: response.data.Id,
          FullName:response.data.FullName,
          TinNumber:response.data.TinNumber,
          BankName:response.data.BankName,
          BranchName:response.data.BranchName,
          AccountNumber:response.data.AccountNumber,
          LoanTypeId:response.data.LoanTypeId,
          ReportingDate:response.data.ReportingDate,
          CurrencyId:response.data.CurrencyId,
          ApprovedAmount:response.data.ApprovedAmount,
          StartDate:response.data.StartDate,
          MaturityDate:response.data.MaturityDate,
          RepaymentFrequencyId:response.data.RepaymentFrequencyId,
          NumberOfInstallment:response.data.NumberOfInstallment,
          InstallmentAmount:response.data.InstallmentAmount,
          OutstandingLoanBalance:response.data.OutstandingLoanBalance,
          LoanStatusId:response.data.LoanStatusId,
          NoOfDaysInArrears:response.data.NoOfDaysInArrears,
          BalanceOverdue:response.data.BalanceOverdue,
          NoOfRestructuring:response.data.NoOfRestructuring,
          RestructuredCreditReferenceNumber:response.data.RestructuredCreditReferenceNumber,
          SectorTypeId:response.data.SectorTypeId,
          IndustryTypeId:response.data.IndustryTypeId,
          CreditInformationDocument:response.data.CreditInformationDocument
        });
      })
      .catch((e) => {
        console.log(e);
      });
  };
  //end dependent
  const deleteGuarantor = (Id) => {
    CommonServices.deleteDependent(Id)
      .then((response) => {
        // setDependentRelationshipType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    deleteGuarantor();
  }, []);
  
  const [loan, setLoan] = useState([]);
  const [currency, setCurrency] = useState([]);
  const [GetRepayment, setGetRepayment] = useState([]);
  const [selectedSector, setSelectedSector] = useState([]);
  const [getIndustry, setGetIndustry] = useState([]);
  
  const loantype = () => {
    CommonServices.getLoanType()
      .then((response) => {
        setLoan(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    loantype();
  }, []);
  const getCurrency = () => {
    CommonServices.getCurrency()
      .then((response) => {
        setCurrency(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getCurrency();
  }, []);
  
  const repayment = () => {
    CommonServices.payment()
      .then((response) => {
        setGetRepayment(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    repayment();
  }, []);
  
  const getSector = () => {
    CommonServices.sector()
      .then((response) => {
        setSelectedSector(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getSector();
  }, []);  
  const industry = () => {
    CommonServices.holdIndustry(companyId)
      .then((response) => {
        setGetIndustry(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    industry();
  }, []);
  
  // const getOtherBankCredit = () => {
  //   CommonServices.holdOtherBank(companyId)
  //     .then((response) => {
  //       setGetIndustry(response.data.Result);
  //       console.log(response.data.Result);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };
  // useEffect(() => {
  //   industry();
  // }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCompanySoleOtherBankModalOpen}
        onClose={handleCompanySoleOtherBankCloseModal}
        title="view Other Bank information"
      >
        <div className="rounded-lg overflow-hidden">
          {getOtherBankCredit ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
                <th className="px-2 py-4"> Name</th>                
                <th className="px-2 py-4"> TinNumber</th>                
                <th className="px-2 py-4"> BankName</th>    
                <th className="px-2 py-4"> BranchName</th>    
                <th className="px-2 py-4"> AccountNumber</th>    
                <th className="px-2 py-4"> LoanTypeId</th>    
                <th className="px-2 py-4"> ReportingDate</th>    
                <th className="px-2 py-4"> CurrencyId</th>    
                <th className="px-2 py-4"> ApprovedAmount</th>    
                <th className="px-2 py-4"> StartDate</th>    
                <th className="px-2 py-4"> MaturityDate</th>    
                <th className="px-2 py-4"> RepaymentFrequencyId</th>    
                <th className="px-2 py-4"> NumberOfInstallment</th>    
                <th className="px-2 py-4"> InstallmentAmount</th>    
                <th className="px-2 py-4"> OutstandingLoanBalance</th>    
                <th className="px-2 py-4"> LoanStatusId</th>    
                <th className="px-2 py-4"> NoOfDaysInArrears</th>    
                <th className="px-2 py-4"> BalanceOverdue</th>    
                <th className="px-2 py-4"> NoOfRestructuring</th>    
                <th className="px-2 py-4"> RestructuredCreditReferenceNumber</th>    
                <th className="px-2 py-4"> SectorTypeId</th>    
                <th className="px-2 py-4"> IndustryTypeId</th>    
                <th className="px-2 py-4"> IndustryTypeId</th>    
                <th className="px-2 py-4"> Action </th>
              </thead>
              <tbody>
                {getOtherBankCredit.map((otherBankData, index) => (
                  <tr>
                    {/* <td>{soleDependent.Id}</td> */}
                    <td>
                      {otherBankData.FullName} 
                    </td>
                    <td>
                      {otherBankData.BankName} 
                    </td>
                    <td>
                      <button
                        onClick={() => {
                          handleCompanySoleOtherBankEditedModalOpen();
                          getOtherBankById(otherBankData.Id);
                          setOtherBankId(otherBankData.Id);
                        }}
                        className="text-purple-600 rounded-md font-semibold "
                      >
                        Edit
                      </button>
                    </td>
                    <td>
                      <button
                        onClick={() => {
                          deleteGuarantor(otherBankData.Id);
                        }}
                        className="text-purple-600 rounded-md font-semibold "
                      >
                        delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            ""
          )}
          <button
            onClick={() => setToggleSoleOtherBank(!toggleSoleOtherBank)}
            className="text-purple-600 rounded-md font-medium "
          >
            Add guarantor
          </button>
          {toggleSoleOtherBank && (
            <div className="submit-form">
              {submitted ? (
                <div>
                  <h4>You submitted successfully!</h4>
                </div>
              ) : (
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <LoanTextField
                    id="FullName"
                    label="FullName"
                    value={soleOtherBank.FullName}
                    onChange={handleInputChangeSoleOtherBank}
                    name="FullName"
                  /> 
                   <LoanTextField
                    id="TinNumber"
                    label="TinNumber"
                    value={soleOtherBank.TinNumber}
                    onChange={handleInputChangeSoleOtherBank}
                    name="TinNumber"
                  /> 
                   <LoanTextField
                    id="BankName"
                    label="BankName"
                    value={soleOtherBank.BankName}
                    onChange={handleInputChangeSoleOtherBank}
                    name="BankName"
                  /> 
                  <LoanTextField
                    id="BranchName"
                    label="BranchName"
                    value={soleOtherBank.BranchName}
                    onChange={handleInputChangeSoleOtherBank}
                    name="BranchName"
                  /> 
                  <LoanTextField
                    id="AccountNumber"
                    label="AccountNumber"
                    value={soleOtherBank.AccountNumber}
                    onChange={handleInputChangeSoleOtherBank}
                    name="AccountNumber"
                  /> 
                <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    loan type
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleOtherBank.LoanTypeId = event.target.value)
                    }
                    labelId="loanType"
                    id="loanTypeSelected"
                    label="loanType"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {loan.map((yourLoan) => (
                      <MenuItem
                        key={yourLoan.Id}
                        LoanTypeId={yourLoan.Id}
                        value={yourLoan.Id}
                      >
                        {yourLoan.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                  <LoanTextField
                    id="ReportingDate"
                    label="ReportingDate"
                    value={soleOtherBank.ReportingDate}
                    onChange={handleInputChangeSoleOtherBank}
                    name="ReportingDate"
                  /> 
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    currency
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleOtherBank.CurrencyId = event.target.value)
                    }
                    labelId="loanType"
                    id="loanTypeSelected"
                    label="loanType"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {currency.map((yourCurrency) => (
                      <MenuItem
                        key={yourCurrency.Id}
                        LoanTypeId={yourCurrency.Id}
                        value={yourCurrency.Id}
                      >
                        {yourCurrency.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                  <LoanTextField
                    id="ApprovedAmount"
                    label="ApprovedAmount"
                    value={soleOtherBank.ApprovedAmount}
                    onChange={handleInputChangeSoleOtherBank}
                    name="ApprovedAmount"
                  /> 
                  <LoanTextField
                    id="StartDate"
                    label="StartDate"
                    value={soleOtherBank.StartDate}
                    onChange={handleInputChangeSoleOtherBank}
                    name="StartDate"
                  /> 
                  <LoanTextField
                    id="MaturityDate"
                    label="MaturityDate"
                    value={soleOtherBank.MaturityDate}
                    onChange={handleInputChangeSoleOtherBank}
                    name="MaturityDate"
                  /> 
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                    Repayment Frequency
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleOtherBank.RepaymentFrequencyId = event.target.value)
                    }
                    labelId="repayment"
                    id="repaymentSelected"
                    label="repayment"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {GetRepayment.map((yourRepayment) => (
                      <MenuItem
                        key={yourRepayment.Id}
                        RepaymentFrequencyId={yourRepayment.Id}
                        value={yourRepayment.Id}
                      >
                        {yourRepayment.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                  <LoanTextField
                    id="NumberOfInstallment"
                    label="NumberOfInstallment"
                    value={soleOtherBank.NumberOfInstallment}
                    onChange={handleInputChangeSoleOtherBank}
                    name="NumberOfInstallment"
                  /> 
                  <LoanTextField
                    id="InstallmentAmount"
                    label="InstallmentAmount"
                    value={soleOtherBank.InstallmentAmount}
                    onChange={handleInputChangeSoleOtherBank}
                    name="InstallmentAmount"
                  /> 
                   <LoanTextField
                    id="OutstandingLoanBalance"
                    label="OutstandingLoanBalance"
                    value={soleOtherBank.OutstandingLoanBalance}
                    onChange={handleInputChangeSoleOtherBank}
                    name="OutstandingLoanBalance"
                  /> 
                  {/* LoanStatusId */}
                  <LoanTextField
                    id="NoOfDaysInArrears"
                    label="NoOfDaysInArrears"
                    value={soleOtherBank.NoOfDaysInArrears}
                    onChange={handleInputChangeSoleOtherBank}
                    name="NoOfDaysInArrears"
                  /> 
                    <LoanTextField
                    id="BalanceOverdue"
                    label="BalanceOverdue"
                    value={soleOtherBank.BalanceOverdue}
                    onChange={handleInputChangeSoleOtherBank}
                    name="BalanceOverdue"
                  /> 
                   <LoanTextField
                    id="NoOfRestructuring"
                    label="NoOfRestructuring"
                    value={soleOtherBank.NoOfRestructuring}
                    onChange={handleInputChangeSoleOtherBank}
                    name="NoOfRestructuring"
                  /> 
                   <LoanTextField
                    id="RestructuredCreditReferenceNumber"
                    label="RestructuredCreditReferenceNumber"
                    value={soleOtherBank.RestructuredCreditReferenceNumber}
                    onChange={handleInputChangeSoleOtherBank}
                    name="RestructuredCreditReferenceNumber"
                  /> 
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                  Sector Type
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleOtherBank.SectorTypeId = event.target.value)
                    }
                    labelId="SectorTypeId"
                    id="SectorTypeSelected"
                    label="SectorTypeId"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {selectedSector.map((yourSector) => (
                      <MenuItem
                        key={yourSector.Id}
                        SectorTypeId={yourSector.Id}
                        value={yourSector.Id}
                      >
                        {yourSector.Name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                  {/* IndustryTypeId */}
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                  <InputLabel id="countrylbl" color="secondary">
                  industry Type
                  </InputLabel>
                  <Select
                    color="secondary"
                    onChange={(event) =>
                      (soleOtherBank.IndustryTypeId = event.target.value)
                    }
                    labelId="IndustryTypeId"
                    id="IndustryTypeSelected"
                    label="IndustryTypeId"
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    {getIndustry.map((yourIndustry) => (
                      <MenuItem
                        key={yourIndustry.Id}
                        IndustryTypeId={yourIndustry.Id}
                        value={yourIndustry.Id}
                      >
                        {yourIndustry.Product}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                  <LoanTextField
                    id="CreditInformationDocument"
                    label="CreditInformationDocument"
                    value={soleOtherBank.CreditInformationDocument}
                    onChange={handleInputChangeSoleOtherBank}
                    name="CreditInformationDocument"
                  />
                </div>
              )}
              <div>
                <button
                  className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => {
                    saveSoleOtherBank();
                  }}
                >
                  save
                </button>
              </div>
            </div>
          )}
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanySoleOtherBankEditedModalOpen}
        onClose={handleCompanySoleOtherBankEditCloseModal}
        title="guarantor edit information"
      >
        <div>
          <form onSubmit={updateOtherBank}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="Name"
                label="Name"
                value={editSoleOtherBank.FullName}
                onChange={handleInputChangeEditOtherBank}
                name="Name"
              />
             
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default OtherBank;
