import { useState, useEffect } from "react";
import { BsArrowLeftShort, BsSearch, BsChevronDown } from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from '@mui/material';


const LeftNav = () => {
    const initialCompanyState = {
        Id: null,
        Name: "",
        NoOfBranch: "",
        EstablishmentYear: "",
        TINNumber: "",
        TINCertificate: "",
        CompanyTypeID: "",
        EntityTypeID: "",
        VATNumber: "",
        VATCertificate: "",
    };

    const [age, setAge] = useState('');

    const handleChange1 = (event) => {
        setAge(event.target.value);
    };


    const [activeStep, setActiveStep] = useState(0);
    const [open, setOpen] = useState(true);
    const [subMenuOpen, setSubMenuOpen] = useState(false);
    const [leftNav, setLeftNav] = useState();
    const [submitted, setSubmitted] = useState(false);



    const [currentIndex, setCurrentIndex] = useState(-1);
    const [companyTypes, setCompanyTypes] = useState([]);
    const [entityTypes, setEntityTypes] = useState([]);
    const [companies, setCompanies] = useState([]);



    const [company, setCompany] = useState(initialCompanyState);
    //adress
    const initialAdressState = {
        Id: null,
        CountryID: "",
        RegionID: "",
        ZoneSubCity: "",
        Woreda: "",
        City: "",
        Kebele: "",
        HouseNo: "",
        BuildingNo: "",
        StreetAddress: "",
    };
    const [address, setAddress] = useState(initialAdressState);
    //end address
    //start contact information
    const initialContactInfoState = {
        Id: null,
        DesignatedPersonFName: "",
        DesignatedPersonMName: "",
        DesignatedPersonLName: "",
        Position: "",
        MobileNo: "",
        Telephone: "",
        Email: "",
    };
    const [contactInfo, setContactInfo] = useState(initialContactInfoState);
    //end address
//account Info
const initialAccountInfoState = {
    Id: null,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
};
const [accountInfo, setAccountInfo] = useState(initialAccountInfoState);

    // const [country, setCountry] = useState("");
    const [city, setCity] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [selectedValue, setSelectedValue] = useState(null);
    //new
    const [country, setContry] = useState([]);
    const [region, setRegion] = useState([]);
    const [holdId, setHoldId] = useState([]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setCompany({ ...company, [name]: value });
    };
    const saveCompany = () => {
        var data = {
            Name: company.Name,
            NoOfBranch: company.NoOfBranch,
            EstablishmentYear: company.EstablishmentYear,
            TINNumber: company.TINNumber,
            TINCertificate: company.TINCertificate,
            CompanyTypeID: company.CompanyTypeID,
            EntityTypeID: company.EntityTypeID,
            VATNumber: company.VATNumber,
            VATCertificate: company.VATCertificate,
        };
        loanDataService
            .addCompany(data)
            .then((response) => {
                setCompany({
                    Id: response.data.Id,
                    Name: response.data.Name,
                    NoOfBranch: response.data.NoOfBranch,
                    EstablishmentYear: response.data.EstablishmentYear,
                    TINNumber: response.data.TINNumber,
                    TINCertificate: response.data.TINCertificate,
                    CompanyTypeID: response.data.CompanyTypeID,
                    EntityTypeID: response.data.EntityTypeID,
                    VATNumber: response.data.VATNumber,
                    VATCertificate: response.data.VATCertificate,
                });
                setSubmitted(true);
                console.log(response.data);
                setHoldId(response.data.Result.Id);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    //contact Info
    const handleInputChangeContactInfo = (event) => {
        const { name, value } = event.target;
        setContactInfo({ ...contactInfo, [name]: value });
    };
    const saveContactInfo = () => {
        var data = {
            DesignatedPersonFName: contactInfo.DesignatedPersonFName,
            DesignatedPersonMName: contactInfo.DesignatedPersonMName,
            DesignatedPersonLName: contactInfo.DesignatedPersonLName,
            Position: contactInfo.Position,
            MobileNo: contactInfo.MobileNo,
            Telephone: contactInfo.Telephone,
            Email: contactInfo.Email,
        };        
        loanDataService
            .addContactInfo(data, holdId)
            .then((response) => {
                setContactInfo({
                    Id: response.data.Id,
                    DesignatedPersonFName: response.data.DesignatedPersonFName,
                    DesignatedPersonMName: response.data.DesignatedPersonMName,
                    DesignatedPersonLName: response.data.DesignatedPersonLName,
                    Position: response.data.Position,
                    MobileNo: response.data.MobileNo,
                    Telephone: response.data.Telephone,
                    Email: response.data.Email,
                });
                setSubmitted(true);
                console.log(response.data);
                // setHoldId(response.data.Result.Id);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    const newContactInfo = () => {
        setContactInfo(initialContactInfoState);
        setSubmitted(false);
    };
    //end contact info
    //account Info
    const handleInputChangeAccountInfo = (event) => {
        const { name, value } = event.target;
        setAccountInfo({ ...accountInfo, [name]: value });
    };
    const saveAccountInfo = () => {
        var data = {
            DesignatedPersonFName: contactInfo.DesignatedPersonFName,
            DesignatedPersonMName: contactInfo.DesignatedPersonMName,
            DesignatedPersonLName: contactInfo.DesignatedPersonLName,
            Position: contactInfo.Position,
            MobileNo: contactInfo.MobileNo,
            Telephone: contactInfo.Telephone,
            Email: contactInfo.Email,
        };        
        loanDataService
            .addAccountInfo(data, holdId)
            .then((response) => {
                setAccountInfo({
                    Id: response.data.Id,
                    DesignatedPersonFName: response.data.DesignatedPersonFName,
                    DesignatedPersonMName: response.data.DesignatedPersonMName,
                    DesignatedPersonLName: response.data.DesignatedPersonLName,
                    Position: response.data.Position,
                    MobileNo: response.data.MobileNo,
                    Telephone: response.data.Telephone,
                    Email: response.data.Email,
                });
                setSubmitted(true);
                console.log(response.data);
                // setHoldId(response.data.Result.Id);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    const newAccountInfo = () => {
        setAccountInfo(initialAccountInfoState);
        setSubmitted(false);
    };
    //end Account Info
    //adress
    const handleInputChangeAdress = (event) => {
        const { name, value } = event.target;
        setAddress({ ...address, [name]: value });
    };
    const saveAddress = () => {
        var data = {
            CountryID: address.CountryID,
            RegionID: address.RegionID,
            ZoneSubCity: address.ZoneSubCity,
            Woreda: address.Woreda,
            City: address.City,
            Kebele: address.Kebele,
            HouseNo: address.HouseNo,
            BuildingNo: address.BuildingNo,
            StreetAddress: address.StreetAddress,
        };
        loanDataService
            .addAdress(data, holdId)
            .then((response) => {
                setAddress({
                    Id: response.data.Id,
                    CountryID: response.data.CountryID,
                    RegionID: response.data.RegionID,
                    ZoneSubCity: response.data.ZoneSubCity,
                    Woreda: response.data.Woreda,
                    City: response.data.City,
                    Kebele: response.data.Kebele,
                    HouseNo: response.data.HouseNo,
                    BuildingNo: response.data.BuildingNo,
                    StreetAddress: response.data.StreetAddress,
                });
                setSubmitted(true);
                console.log(response.data);
                // setHoldId(response.data.Result.Id);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    const newAddress = () => {
        setAddress(initialAdressState);
        setSubmitted(false);
    };
    //end adress
    const newCompany = () => {
        setCompany(initialCompanyState);
        setSubmitted(false);
    };
    const handleChange = (value) => {
        setSelectedValue(value);
    };
    const fetchCompanyEntity = () => {
        loanDataService
            .getEntitype()
            .then((response) => {
                console.log(response.data.Result);
                setEntityTypes(response.data.Result);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        fetchCompanyEntity();
    }, []);

    //view

    const getCompanies = () => {
        loanDataService
            .companies()
            .then((response) => {
                console.log(response.data.Result);
                setCompanies(response.data.Result);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        getCompanies();
    }, []);


    const fetchCompanies = () => {
        loanDataService
            .getCompanyType()
            .then((response) => {
                console.log(response.data.Result);
                setCompanyTypes(response.data.Result);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        fetchCompanies();
    }, []);

    const fetchContry = () => {
        loanDataService
            .getCountry()
            .then((response) => {
                console.log(response.data.Result);
                setContry(response.data.Result);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        fetchContry();
    }, []);
    const fetchRegion = () => {
        loanDataService
            .getRegion()
            .then((response) => {
                console.log(response.data.Result);
                setRegion(response.data.Result);
            })
            .catch((e) => {
                console.log(e);
            });
    };
    useEffect(() => {
        fetchRegion();
    }, []);
    //end new
    const handleNext = () => {
        if (activeStep < 3) setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };
    const handleBack = () => {
        if (activeStep !== 0) setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };
    const handleSkip = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };
    const handleSubmit = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };



    const handleSelection = (navigate) => {
        setLeftNav(navigate);
    }


    const Menus = [
        { title: "Company", icon: <RiDashboardFill />, link: "company/:1/:1" },
        { title: "Stepper ", icon: <FcOrganization />, link: "company/:1/:2" },
        {
            title: "Loan Status",
            icon: <GiProgression />,
            spacing: true,
            link: "company/:1/:3"
        },
        {
            title: "projects",
            icon: <GrProjects />,
            submenu: true,
            submenuItems: [
                { title: "submenu 1" },
                { title: "submenu 2" },
                { title: "submenu 3" },
            ],
        },
    ];
    return (
        <div className="flex">
            <div
                className={`bg-purple-500 h-screen p-5 pt-8 ${open ? "w-72" : "w-20"
                    } duration-300 relative   `}
            >
                <BsArrowLeftShort
                    className={`bg-white text-dark-purple text-3xl rounded-full
          absolute -right-3 top-9 border border-dark-purpule cursor-pointer ${!open && "rotate-180 "
                        }`}
                    onClick={() => setOpen(!open)}
                />
                <div className="inline-flex">
                    {/* <AiFillEnvironment
                        className={`bg-amber-300 text-4xl rounded cursor-pointer block float-left mr-2 duration-500 ${open && "rotate-[360deg]  "
                            }`}
                    /> */}
                    <img src={cbeLogo} height="40px" width="40px" className={`bg-amber-300 text-4xl rounded cursor-pointer block float-left mr-2 duration-500 ${open && "rotate-[360deg]"
                        }`} />
                    <h1
                        className={`text-white origin-left font-medium text-2xl duration-300 ${!open && "scale-0"
                            }`}
                    >
                        Customer page
                    </h1>
                </div>
                <ui className=" ">
                    {Menus.map((menu, index) => (
                        <>
                            <li
                                key={index}
                                className={`text-gray-300 text-sm flex items-center gap-4 cursor-pointer p-2 hover:bg-white
                   tooltip  dark:bg-gray-700 rounded-md  ${menu.spacing ? "mt-9" : "mt-2"
                                    }`}
                            >
                                {/* <div>
                                <Tooltips text="Hello, world!">
                                    <button>Hover me</button>
                                </Tooltips>
                                </div> */}
                                {
                                    open && (
                                        <span className="text-2xl block float-left">
                                            {
                                                menu.icon
                                            }
                                        </span>
                                    )
                                }

                                {
                                    !open && (
                                        <span className="text-2xl block float-left mb-4" data-toggle="tooltip" title={`${menu.title}`}>
                                            <Tooltips text={`${menu.title}`}>
                                                {
                                                    menu.icon
                                                }
                                            </Tooltips>

                                        </span>
                                    )
                                }
                                <span
                                    className={`text-base font-medium flex-1 duration-200  ${!open && "hidden"
                                        }`}
                                >
                                    <button >
                                    </button>
                                    <button onClick={() => handleSelection(menu.link)}>
                                        {/* company/:customerId/:subId */}
                                        {menu.title}
                                    </button>
                                    {/* {menu.title} */}
                                    {/* <button>add company</button> */}
                                </span>
                                {menu.submenu && open && (
                                    <BsChevronDown
                                        className={`${subMenuOpen && "rotate-180"}`}
                                        onClick={() => setSubMenuOpen(!subMenuOpen)}
                                    />
                                )}
                            </li>
                            {menu.submenu && open && subMenuOpen && (
                                <ul>
                                    {menu.submenuItems.map((submenuItem, index) => (
                                        <li
                                            key={index}
                                            className="text-gray-300 text-sm flex items-center gap-4 cursor-pointer p-2 px-5 hover:bg-white rounded-md"
                                        >
                                            {submenuItem.title}
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </>
                    ))}
                </ui>
            </div>
            <div className="px-4">
                {/* Steeper start */}
                <div className="w-full max-w-md mb-6 items-center pt-2 pb-2 px-4">
                    <div className="relative pt-1">
                        <div className="flex mb-2 items-center justify-between">
                            <div className="flex items-center">
                                <div
                                    className={classNames(
                                        "flex items-center justify-center w-8 h-8 rounded-full mr-2",
                                        activeStep >= 0
                                            ? "bg-[purple] text-white"
                                            : "bg-gray-300 text-gray-500"
                                    )}
                                >
                                    {activeStep >= 0 ? (
                                        <span className="text-sm font-bold">1</span>
                                    ) : (
                                        <span className="text-sm font-bold">1</span>
                                    )}
                                </div>
                                <div
                                    className={classNames(
                                        "flex-grow h-1",
                                        activeStep >= 1 ? "bg-[purple]" : "bg-gray-300"
                                    )}
                                ></div>
                            </div>
                            <div className="flex items-center">
                                <div
                                    className={classNames(
                                        "flex items-center justify-center w-8 h-8 rounded-full mx-2",
                                        activeStep >= 1
                                            ? "bg-[purple] text-white"
                                            : "bg-gray-300 text-gray-500"
                                    )}
                                >
                                    {activeStep >= 1 ? (
                                        <span className="text-sm font-bold">2</span>
                                    ) : (
                                        <span className="text-sm font-bold">2</span>
                                    )}
                                </div>
                                <div
                                    className={classNames(
                                        "flex-grow h-1",
                                        activeStep >= 2 ? "bg-[purple]" : "bg-gray-300"
                                    )}
                                ></div>
                            </div>
                            
                            <div className="flex items-center">
                                <div
                                    className={classNames(
                                        "flex items-center justify-center w-8 h-8 rounded-full mx-2",
                                        activeStep >= 2
                                            ? "bg-[purple] text-white"
                                            : "bg-gray-300 text-gray-500"
                                    )}
                                >
                                    {activeStep >= 2 ? (
                                        <span className="text-sm font-bold">3</span>
                                    ) : (
                                        <span className="text-sm font-bold">3</span>
                                    )}
                                </div>
                                <div
                                    className={classNames(
                                        "flex-grow h-1",
                                        activeStep >= 3 ? "bg-[purple]" : "bg-gray-300"
                                    )}
                                ></div>
                            </div>



                            <div className="flex items-center">
                                <div
                                    className={classNames(
                                        "flex items-center justify-center w-8 h-8 rounded-full mx-2",
                                        activeStep >= 3
                                            ? "bg-[purple] text-white"
                                            : "bg-gray-300 text-gray-500"
                                    )}
                                >
                                    {activeStep >= 2 ? (
                                        <span className="text-sm font-bold">4</span>
                                    ) : (
                                        <span className="text-sm font-bold">4</span>
                                    )}
                                </div>
                            </div>
                            
                        </div>
                        <div className="flex mb-2 items-center justify-between">
                            <div className="text-xs text-gray-500 font-bold">Company</div>
                            <div className="text-xs text-gray-500 font-bold">Address</div>
                            <div className="text-xs text-gray-500 font-bold">Contact</div>
                            <div className="text-xs text-gray-500 font-bold">Account Info</div>
                            
                        </div>
                    </div>
                </div>
                {/* Steeper end */}
                <div>
                    {activeStep === 0 && (
                        <div className="grid grid-cols-1 gap-4 submit-form">
                            <div className="submit-form ">
                                {submitted ? (
                                    <div>
                                        <h4>You submitted successfully!</h4>
                                        <button className="btn btn-success" onClick={newCompany}>
                                            Add
                                        </button>
                                    </div>
                                ) : (
                                    <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                        <TextField
                                            id="Name"
                                            label="Name"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            sx={{ width: '45ch' }}
                                            value={company.Name}
                                            onChange={handleInputChange}
                                            name="Name"
                                            className="w-full max-w-full"
                                        />
                                        <TextField
                                            id="NoOfBranch"
                                            label="Number Of Branch"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.NoOfBranch}
                                            onChange={handleInputChange}
                                            name="NoOfBranch"
                                        />
                                        <TextField
                                            id="EstablishmentYear"
                                            label="Establishment Year"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.EstablishmentYear}
                                            onChange={handleInputChange}
                                            name="EstablishmentYear"
                                        />
                                        <TextField
                                            id="TINNumber"
                                            label="TIN Number"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.TINNumber}
                                            onChange={handleInputChange}
                                            name="TINNumber"
                                        />
                                        <TextField
                                            id="TINCertificate"
                                            label="TIN Certificate"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.TINCertificate}
                                            onChange={handleInputChange}
                                            name="TINCertificate"
                                        />
                                        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
                                            <InputLabel id="CompanyType" color="secondary"> Company Type</InputLabel>
                                            <Select
                                                color="secondary"
                                                onChange={(event) =>
                                                    (company.CompanyTypeID = event.target.value)
                                                }
                                                labelId="CompanyType"
                                                id="CompanyTypeSelect"
                                                // value={age}
                                                label="Company Type"
                                            >
                                                <MenuItem value="">
                                                    <em>None</em>
                                                </MenuItem>
                                                {companyTypes.map((company) => (
                                                    <MenuItem key={company.Id} value={company.Id}>
                                                        {company.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>


                                        <TextField
                                            id="VATNumber"
                                            label="VAT Number"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.VATNumber}
                                            onChange={handleInputChange}
                                            name="VATNumber"
                                        />
                                        <TextField
                                            id="VATCertificate"
                                            label="VAT Certificate"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={company.VATCertificate}
                                            onChange={handleInputChange}
                                            name="VATCertificate"
                                        />


                                        {/* <div className="mb-2">
                                            <label
                                                className="block text-gray-700 font-bold mb-2"
                                                htmlFor="EntityTypeListID"
                                            >
                                                {" "}
                                                Entity Type
                                            </label>
                                            <select
                                                onChange={(event) =>
                                                    (company.EntityTypeID = event.target.value)
                                                }
                                            >
                                                {entityTypes.map((entity) => (
                                                    <option
                                                        key={entity.Id}
                                                        EntityTypeListID={entity.Id}
                                                        value={entity.Id}
                                                    >
                                                        {entity.Name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div> */}

                                        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
                                            <InputLabel id="EntityTypelbl" color="secondary"> Entity Type</InputLabel>
                                            <Select
                                                color="secondary"
                                                onChange={(event) =>
                                                    (company.EntityTypeID = event.target.value)
                                                }
                                                labelId="EntityTypelbl"
                                                id="EntityTypeSelect"
                                                label="Entity Type"
                                            >
                                                <MenuItem value="">
                                                    <em>None</em>
                                                </MenuItem>
                                                {entityTypes.map((entity) => (
                                                    <MenuItem
                                                        key={entity.Id}
                                                        EntityTypeListID={entity.Id}
                                                        value={entity.Id}
                                                    >
                                                        {entity.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    </div>
                                )}
                            </div>

                            <div className="flex justify-between">
                                <button
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={handleSkip}
                                >
                                    Skip
                                </button>
                                <button
                                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={() => {
                                        handleNext();
                                        saveCompany();
                                    }}
                                >
                                    Save and Next
                                </button>
                            </div>
                        </div>
                    )}

                    {activeStep === 1 && (
                        <div>
                            <div className="submit-form">
                                {submitted ? (
                                    <div>
                                        <h4>You submitted successfully!</h4>
                                        <button className="btn btn-success" onClick={newAddress}>
                                            Add
                                        </button>
                                    </div>
                                ) : (
                                    <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                        {/* <div className="mb-2">
                                            <label
                                                className="block text-gray-700 font-bold mb-2"
                                                htmlFor="CountryID"
                                            >
                                                Country
                                            </label>
                                            <select
                                                onChange={(event) =>
                                                    (address.CountryID = event.target.value)
                                                }
                                            >
                                                {country.map((newContry) => (
                                                    <option
                                                        key={newContry.Id}
                                                        CountryID={newContry.Id}
                                                        value={newContry.Id}
                                                    >
                                                        {newContry.Name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div> */}

                                        <FormControl variant="standard" sx={{minWidth: 120 }}>
                                            <InputLabel id="countrylbl" color="secondary"> Country </InputLabel>
                                            <Select
                                                color="secondary"
                                                onChange={(event) =>
                                                    (address.CountryID = event.target.value)
                                                }
                                                labelId="countrylbl"
                                                id="countrySelect"
                                                label="Country"
                                            >
                                                <MenuItem value="">
                                                    <em>None</em>
                                                </MenuItem>
                                                {country.map((newContry) => (
                                                    <MenuItem
                                                        key={newContry.Id}
                                                        CountryID={newContry.Id}
                                                        value={newContry.Id}
                                                    >
                                                        {newContry.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                        {/* <div className="mb-2">
                                            <label
                                                className="block text-gray-700 font-bold mb-2"
                                                htmlFor="RegionID"
                                            >
                                                Region
                                            </label>
                                            <select
                                                onChange={(event) =>
                                                    (address.RegionID = event.target.value)
                                                }
                                            >
                                                {region.map((newRegion) => (
                                                    <option
                                                        key={newRegion.Id}
                                                        RegionID={newRegion.Id}
                                                        value={newRegion.Id}
                                                    >
                                                        {newRegion.Name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div> */}

                                        <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
                                            <InputLabel id="regionlbl" color="secondary"> Region </InputLabel>
                                            <Select
                                                color="secondary"
                                                onChange={(event) =>
                                                    (address.RegionID = event.target.value)
                                                }
                                                labelId="regionlbl"
                                                id="regionSelect"
                                                label="Region"
                                            >
                                                <MenuItem value="">
                                                    <em>None</em>
                                                </MenuItem>
                                                {region.map((newRegion) => (
                                                    <MenuItem
                                                        key={newRegion.Id}
                                                        RegionID={newRegion.Id}
                                                        value={newRegion.Id}
                                                    >
                                                        {newRegion.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>

                                        <TextField
                                            id="ZoneSubCity"
                                            label="Zone Sub City"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.ZoneSubCity}
                                            onChange={handleInputChangeAdress}
                                            name="ZoneSubCity"
                                        />
                                        <TextField
                                            id="Woreda"
                                            label="Woreda"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.Woreda}
                                            onChange={handleInputChangeAdress}
                                            name="Woreda"
                                        />
                                        <TextField
                                            id="City"
                                            label="City"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.City}
                                            onChange={handleInputChangeAdress}
                                            name="City"
                                        />
                                        <TextField
                                            id="Kebele"
                                            label="Kebele"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.Kebele}
                                            onChange={handleInputChangeAdress}
                                            name="Kebele"
                                        />
                                        <TextField
                                            id="HouseNo"
                                            label="HouseNo"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.HouseNo}
                                            onChange={handleInputChangeAdress}
                                            name="HouseNo"
                                        />
                                        <TextField
                                            id="BuildingNo"
                                            label="BuildingNo"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.BuildingNo}
                                            onChange={handleInputChangeAdress}
                                            name="BuildingNo"
                                        />
                                        <TextField
                                            id="StreetAddress"
                                            label="StreetAddress"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={address.StreetAddress}
                                            onChange={handleInputChangeAdress}
                                            name="StreetAddress"
                                        />
                                    </div>
                                )}
                            </div>

                            <div className="flex justify-between">
                                <button
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={handleBack}
                                >
                                    Back
                                </button>
                                <button
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={handleSkip}
                                >
                                    Skip
                                </button>
                                <button
                                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={() => {
                                        handleNext();
                                        saveAddress();
                                    }}
                                >
                                    Save and Next
                                </button>
                            </div>
                        </div>
                    )}
                    {activeStep === 2 && (
                        <div>
                            <div className="submit-form">
                                {submitted ? (
                                    <div>
                                        <h4>You submitted successfully!</h4>
                                        <button className="btn btn-success" onClick={newContactInfo}>
                                            Add
                                        </button>
                                    </div>
                                ) : (
                                    <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                        <TextField
                                            id="DesignatedPersonFName"
                                            label="Designated Person First Name"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.DesignatedPersonFName}
                                            onChange={handleInputChangeContactInfo}
                                            name="DesignatedPersonFName"
                                        />
                                        <TextField
                                            id="DesignatedPersonMName"
                                            label="Designated Person Middle Name"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.DesignatedPersonMName}
                                            onChange={handleInputChangeContactInfo}
                                            name="DesignatedPersonMName"
                                        />
                                        <TextField
                                            id="DesignatedPersonLName"
                                            label="Designated Person Last Name"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.DesignatedPersonLName}
                                            onChange={handleInputChangeContactInfo}
                                            name="DesignatedPersonLName"
                                        />
                                        <TextField
                                            id="Position"
                                            label="Position"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.Position}
                                            onChange={handleInputChangeContactInfo}
                                            name="Position"
                                        />
                                        <TextField
                                            id="MobileNo"
                                            label="Mobile Number"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.MobileNo}
                                            onChange={handleInputChangeContactInfo}
                                            name="MobileNo"
                                        />
                                        <TextField
                                            id="Telephone"
                                            label="Telephone"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.Telephone}
                                            onChange={handleInputChangeContactInfo}
                                            name="Telephone"
                                        />
                                        <TextField
                                            id="Email"
                                            label="Email"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.Email}
                                            onChange={handleInputChangeContactInfo}
                                            name="Email"
                                        />
                                    </div>
                                )}
                            </div>
                            <div className="flex justify-between">
                                <button
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={handleBack}
                                >
                                    Back
                                </button>
                                <button
                                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    
                                    onClick={() => {
                                        handleNext();
                                        saveContactInfo();
                                    }}
                                >
                                    Save and next
                                </button>
                            </div>
                        </div>
                    )}
                    {activeStep === 3 && (
                        <div>
                            <div className="submit-form">
                                {submitted ? (
                                    <div>
                                        <h4>You submitted successfully!</h4>
                                        <button className="btn btn-success" onClick={newAccountInfo}>
                                            Add
                                        </button>
                                    </div>
                                ) : (
                                    <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                                        <TextField
                                            id="AccountNo"
                                            label="Account Number"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.AccountNo}
                                            onChange={handleInputChangeAccountInfo}
                                            name="AccountNo"
                                        />
                                        <TextField
                                            id="CustomerId"
                                            label="Customer Id"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.CustomerId}
                                            onChange={handleInputChangeAccountInfo}
                                            name="CustomerId"
                                        />
                                        <TextField
                                            id="Branch"
                                            label="Branch"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.Branch}
                                            onChange={handleInputChangeAccountInfo}
                                            name="Branch"
                                        />
                                        <TextField
                                            id="District"
                                            label="District"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.District}
                                            onChange={handleInputChangeAccountInfo}
                                            name="District"
                                        />
                                        <TextField
                                            id="Other"
                                            label="Other"
                                            variant="standard"
                                            color="secondary"
                                            size=""
                                            required
                                            value={contactInfo.Other}
                                            onChange={handleInputChangeAccountInfo}
                                            name="Other"
                                        />                                        
                                    </div>
                                )}
                            </div>
                            <div className="flex justify-between">
                                <button
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={handleBack}
                                >
                                    Back
                                </button>
                                <button
                                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                                    onClick={saveAccountInfo}
                                >
                                    Submit
                                </button>
                            </div>
                        </div>
                    )}
                    
                    
                    
                    {activeStep === 4 && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4">
                                Thank you for submitting the form!
                            </h2>
                        </div>
                    )}
                </div>
                <table className="table table-bordered table-striped" border="1">
                    <thead>
                        <th> Number </th>
                        <th> Name </th>
                        <th> No Branch </th>
                        <th> Establishment Year </th>
                        <th> TIN Number </th>
                        <th> TIN Certificate </th>
                        <th> VAT Number </th>
                        <th> VAT Certificate </th>
                    </thead>
                    <tbody>
                        {
                            companies.map((company, index) => (
                                // <tr key={loan.loan_id} rowSpan="3">
                                <tr key={index}>
                                    <td>{company.Name}</td>
                                    <td>{company.NoOfBranch}</td>
                                    <td>{company.EstablishmentYear}</td>
                                    <td>{company.TINNumber}</td>
                                    <td>{company.TINCertificate}</td>
                                    <td>{company.VATNumber}</td>
                                    <td>{company.VATCertificate}</td>
                                </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default LeftNav;
